  `noidung` text,
  `ngaypost` datetime default NULL,
  PRIMARY KEY  (`id`),
  KEY `FK_comments_articles` (`article_id`),
  CONSTRAINT `FK_comments_articles` FOREIGN KEY (`article_id`) REFERENCES `tintucs` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

