/*Table structure for table `duans` */

DROP TABLE IF EXISTS `duans`;

CREATE TABLE `duans` (
  `id` bigint(20) NOT NULL auto_increment,
  `tenduan` varchar(255) character set utf8 default NULL,
  `alias` varchar(255) character set utf8 default NULL,
  `mota` varchar(255) character set utf8 default NULL,
  `image_id` bigint(20) default NULL,
  `thongtinchitiet` text character set utf8,
  `account_id` bigint(20) default NULL,
  `datepost` datetime default NULL,
  `dateupdate` datetime default NULL,
  `loaiduan_id` varchar(100) default NULL,
  `backuped` tinyint(1) default '0',
  `valuesearch` text,
  PRIMARY KEY  (`id`),
  KEY `FK_duans_loaiduans` (`loaiduan_id`),
  KEY `FK_duans_accounts` (`account_id`),
  KEY `FK_duans_images` (`image_id`),
  CONSTRAINT `FK_duans_accounts` FOREIGN KEY (`account_id`) REFERENCES `accounts` (`id`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `FK_duans_images` FOREIGN KEY (`image_id`) REFERENCES `images` (`id`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `FK_duans_loaiduan` FOREIGN KEY (`loaiduan_id`) REFERENCES `loaiduans` (`id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=latin1;

