/*Table structure for table `phuongxas` */

DROP TABLE IF EXISTS `phuongxas`;

CREATE TABLE `phuongxas` (
  `id` bigint(20) NOT NULL auto_increment,
  `tenphuongxa` varchar(100) character set utf8 default NULL,
  `quanhuyen_id` bigint(20) default NULL,
  PRIMARY KEY  (`id`),
  KEY `FK_phuongxas_quanhuyens` (`quanhuyen_id`),
  CONSTRAINT `FK_phuongxas_quanhuyens` FOREIGN KEY (`quanhuyen_id`) REFERENCES `quanhuyens` (`id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

/*Data for the table `phuongxas` */

/*Table structure for table `ppl_onlines` */

DROP TABLE IF EXISTS `ppl_onlines`;

CREATE TABLE `ppl_onlines` (
  `id` bigint(20) NOT NULL auto_increment,
  `activity` datetime NOT NULL default '0000-00-00 00:00:00',
  `access_time` datetime default NULL,
  `account_id` bigint(20) default NULL,
  `ip_address` varchar(255) default '',
  `refurl` varchar(255) default '',
  `user_agent` varchar(255) default NULL,
  PRIMARY KEY  (`id`),
  KEY `id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=33 DEFAULT CHARSET=utf8;

