/*Table structure for table `quanhuyens` */

DROP TABLE IF EXISTS `quanhuyens`;

CREATE TABLE `quanhuyens` (
  `id` bigint(20) NOT NULL auto_increment,
  `tenquanhuyen` varchar(100) character set utf8 default NULL,
  `tinhthanh_id` bigint(20) default NULL,
  PRIMARY KEY  (`id`),
  KEY `FK_quanhuyens_tinhthanhs` (`tinhthanh_id`),
  CONSTRAINT `FK_quanhuyens_tinhthanhs` FOREIGN KEY (`tinhthanh_id`) REFERENCES `tinhthanhs` (`id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

/*Data for the table `quanhuyens` */

/*Table structure for table `resetpasswords` */

DROP TABLE IF EXISTS `resetpasswords`;

CREATE TABLE `resetpasswords` (
  `id` bigint(20) NOT NULL auto_increment,
  `account_id` bigint(20) default NULL,
  `times` int(11) default NULL,
  `verify` varchar(20) default NULL,
  PRIMARY KEY  (`id`),
  KEY `FK_resetpasswords_accounts` (`account_id`),
  CONSTRAINT `FK_resetpasswords_accounts` FOREIGN KEY (`account_id`) REFERENCES `accounts` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `resetpasswords` */

/*Table structure for table `sendmails` */

DROP TABLE IF EXISTS `sendmails`;

CREATE TABLE `sendmails` (
  `id` bigint(20) NOT NULL auto_increment,
  `to` varchar(100) default NULL,
  `subject` varchar(255) default NULL,
  `content` text,
  `isprior` tinyint(1) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `sendmails` */

/*Table structure for table `tinhthanhs` */

DROP TABLE IF EXISTS `tinhthanhs`;

CREATE TABLE `tinhthanhs` (
  `id` bigint(20) NOT NULL auto_increment,
  `tentinhthanh` varchar(100) character set utf8 default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

/*Data for the table `tinhthanhs` */

/*Table structure for table `tintucs` */

DROP TABLE IF EXISTS `tintucs`;

CREATE TABLE `tintucs` (
  `id` bigint(20) NOT NULL auto_increment,
  `alias` varchar(255) default NULL,
  `title` varchar(255) default NULL,
  `image_id` bigint(20) default NULL,
  `mota` varchar(255) default NULL,
  `thongtinchitiet` text,
  `datemodified` datetime default NULL,
  `usermodified` varchar(100) default NULL,
  `viewcount` int(11) default NULL,
  `backuped` tinyint(1) default NULL,
  `valuesearch` text,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=40 DEFAULT CHARSET=utf8;

/*Data for the table `tintucs` */

insert  into `tintucs`(`id`,`alias`,`title`,`image_id`,`mota`,`thongtinchitiet`,`datemodified`,`usermodified`,`viewcount`,`backuped`,`valuesearch`) values (6,'ky-nang-tim-cong-viec-part-time','Kỹ năng tìm công việc part-time',0,'(Dân trí) - Nhu cầu về việc làm thêm hay còn gọi là công việc part-time khá lớn trong đời sống. Từ học sinh, sinh viên, những người đã nghỉ hưu hay cả những người đang đi làm nhưng muốn kiếm thêm thu nhập ngoài giờ hành chính. Tuy nhiên, để thực hiện mong','<p><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"Kỹ năng t&igrave;m c&ocirc;ng việc part-time\" src=\"http://www.jobbid.vn/upload/images/1299161903_job_search.jpg\" alt=\"Kỹ năng t&igrave;m c&ocirc;ng việc part-time\" width=\"250\" height=\"159\" /><span style=\"color: #888888;\"><strong>(D&acirc;n tr&iacute;) - Nhu cầu về việc l&agrave;m th&ecirc;m hay c&ograve;n gọi l&agrave; <a title=\"c&ocirc;ng việc part time\" href=\"http://www.jobbid.vn\">c&ocirc;ng việc part time</a> kh&aacute;  lớn trong đời sống. Từ học sinh, sinh vi&ecirc;n, những người đ&atilde; nghỉ hưu hay  cả những người đang đi l&agrave;m nhưng muốn kiếm th&ecirc;m thu nhập ngo&agrave;i giờ h&agrave;nh  ch&iacute;nh. Tuy nhi&ecirc;n, để thực hiện mong muốn kh&ocirc;ng phải l&agrave; dễ.</strong></span></p>\n<p><strong>1. Lu&ocirc;n sẵn s&agrave;ng</strong></p>\n<p>Bạn n&ecirc;n chuẩn bị một  chiếc điện thoại cố định c&oacute; chức năng trả lời v&agrave; ghi lại lời nhắn của  người gọi tới khi kh&ocirc;ng c&oacute; ai trực m&aacute;y. Điều n&agrave;y rất c&oacute; &iacute;ch trong trường  hợp nh&agrave; tuyển dụng muốn li&ecirc;n hệ với bạn. B&ecirc;n cạnh đ&oacute;, h&atilde;y chuẩn bị tinh  thần để lu&ocirc;n sẵn s&agrave;ng với những cuộc phỏng vấn nhanh để c&oacute; thể nhận  được c&ocirc;ng việc ngay l&uacute;c đ&oacute;.</p>\n<p>&nbsp;</p>\n<p><strong>2. Sử dụng c&ocirc;ng cụ t&igrave;m kiếm online</strong></p>\n<p>Tr&ecirc;n mạng hiện nay c&oacute;  kh&aacute; nhiều website cung cấp <a title=\"việc part-time\" href=\"http://www.jobbid.vn\">việc l&agrave;m part-time</a> như 24h.com.vn,  kiemviec.com, tuyendung.com,v.v. Tất cả những trang n&agrave;y đều c&oacute; ph&acirc;n loại  c&aacute;c c&ocirc;ng việc theo ca, theo giờ v&agrave; c&oacute; c&ocirc;ng cụ lọc kh&aacute; hiệu quả theo  từng nhu cầu của ứng vi&ecirc;n. Với nhiều trang web, bạn c&oacute; thể nộp đơn trực  tuyến tới nh&agrave; tuyển dụng.</p>\n<p>&nbsp;</p>\n<p><strong>3. Nộp hồ sơ trực tuyến</strong></p>\n<p>C&oacute; kh&ocirc;ng &iacute;t nh&agrave; tuyển  dụng của c&aacute;c <a title=\"c&ocirc;ng việc part time\" href=\"http://www.jobbid.vn\">c&ocirc;ng việc part time</a> chấp nhận h&igrave;nh thức nộp hồ sơ online.  Do đ&oacute;, với những c&ocirc;ng việc quan t&acirc;m, bạn cần xem x&eacute;t xem m&igrave;nh c&oacute; thể nộp  hồ sơ bằng h&igrave;nh thức n&agrave;y kh&ocirc;ng.</p>\n<p>&nbsp;</p>\n<p><strong>4. Nộp g&igrave; trong hồ sơ trực tuyến?</strong></p>\n<p>Hầu hết trong c&aacute;c hồ  sơ xin việc trực tuyến, nh&agrave; tuyển dụng đều mong muốn c&oacute; được th&ocirc;ng tin  li&ecirc;n hệ, th&ocirc;ng tin về tr&igrave;nh độ học vấn cũng như kinh nghiệm nghề nghiệp  của ứng vi&ecirc;n. B&ecirc;n cạnh đ&oacute;, họ cũng muốn biết về mức lương bạn nhận được  trong c&aacute;c c&ocirc;ng việc trước đ&acirc;y. Ngo&agrave;i ra, bạn cũng cần hỏi để được biết  r&otilde; khi n&agrave;o bạn c&oacute; thể bắt tay l&agrave;m việc.</p>\n<p>&nbsp;</p>\n<p><strong>5. Hiểu r&otilde; thời gian l&agrave;m việc của m&igrave;nh</strong></p>\n<p>Thường th&igrave; nh&agrave; tuyển  dụng c&oacute; thể hỏi bạn về thời gian cụ thể bạn c&oacute; thể l&agrave;m việc, chẳng hạn  trong tuần bạn l&agrave;m việc được v&agrave;o những ng&agrave;y n&agrave;o, giờ n&agrave;o, do đ&oacute;, bạn n&ecirc;n  chuẩn bị sẵn phần trả lời cho nội dung n&agrave;y.</p>\n<p>&nbsp;</p>\n<p><strong>6. Ch&uacute; &yacute; tới c&aacute;c tấm biển cần tuyển nh&acirc;n vi&ecirc;n</strong></p>\n<p>Nếu bạn l&agrave; người quan  t&acirc;m tới c&ocirc;ng việc b&aacute;n h&agrave;ng, bạn n&ecirc;n chịu kh&oacute; để mắt tới những tấm biển  cần tuyển nh&acirc;n vi&ecirc;n ở c&aacute;c cửa h&agrave;ng, cửa hiệu trong thị trấn, th&agrave;nh phố  gần nơi m&igrave;nh ở. Tuy nhi&ecirc;n, ngay cả kh&ocirc;ng nh&igrave;n thấy biển th&ocirc;ng b&aacute;o, nếu  quan t&acirc;m, bạn vẫn c&oacute; thể hỏi chủ cửa h&agrave;ng về nhu cầu tuyển dụng.</p>\n<p>&nbsp;</p>\n<p><strong>7. Để &yacute; mục rao vặt tr&ecirc;n b&aacute;o</strong></p>\n<p>B&ecirc;n cạnh việc quan t&acirc;m  tới c&aacute;c tấm biển th&ocirc;ng b&aacute;o tuyển dụng, bạn cũng n&ecirc;n thường xuy&ecirc;n để mắt  thường xuy&ecirc;n tới c&aacute;c mục rao vặt, tờ rơi quảng c&aacute;o k&egrave;m theo c&aacute;c b&aacute;o.  Cũng kh&ocirc;ng &iacute;t c&aacute;c nh&agrave; tuyển dụng vừa hoặc nhỏ th&iacute;ch đăng tin tuyển dụng  tr&ecirc;n b&aacute;o hơn l&agrave; tr&ecirc;n Internet.</p>\n<p>&nbsp;</p>\n<p><strong>8. Đăng tuyển trực tiếp</strong></p>\n<p>Việc trực tiếp gặp gỡ  v&agrave; trao đổi c&ocirc;ng việc với nh&agrave; tuyển dụng thường l&agrave; c&aacute;ch tốt nhất để <a title=\"t&igrave;m việc part-time\" href=\"http://www.jobbid.vn\">t&igrave;m  việc part-time</a>. Vẫn cần nhắc lại với bạn khi đăng tuyển trực tiếp như  thế n&agrave;y, bạn cần ăn vận chỉnh tề, sạch sẽ, m&oacute;ng ch&acirc;n m&oacute;ng tay cắt tỉa  gọn g&agrave;ng, giầy d&eacute;p lịch sự,v.v.<br /><br /><strong>9. Đăng tuyển trực tiếp n&ecirc;n mang theo g&igrave;?</strong></p>\n<p>Bạn n&ecirc;n chuẩn bị sẵn  những th&ocirc;ng tin cần phải điền v&agrave;o đơn xin việc, kể cả t&ecirc;n v&agrave; địa chỉ của  những nh&agrave; tuyển dụng trước đ&acirc;y của bạn, ng&agrave;y th&aacute;ng tuyển dụng, danh  s&aacute;ch những người c&oacute; thể gi&uacute;p nh&agrave; tuyển dụng mới tham khảo th&ocirc;ng tin th&ecirc;m  về bạn v&agrave; một bản CV nếu bạn c&oacute;. H&atilde;y nhớ l&agrave; lu&ocirc;n sẵn s&agrave;ng tinh thần cho  một cuộc phỏng vấn tại chỗ, hiểu r&otilde; khi n&agrave;o bạn c&oacute; thể bắt đầu c&ocirc;ng  việc v&agrave; số giờ mỗi tuần bạn c&oacute; thể đảm nhiệm.</p>\n<p>&nbsp;</p>\n<p><strong>10. Sẵn s&agrave;ng danh s&aacute;ch những người tham khảo th&ocirc;ng tin</strong></p>\n<p>Bạn n&ecirc;n c&oacute; trong tay  &iacute;t nhất danh s&aacute;ch ba người gồm t&ecirc;n tuổi v&agrave; số điện thoại, địa chỉ email  của họ để sẵn s&agrave;ng đưa ra trong buổi phỏng vấn. Nếu bạn chưa từng l&agrave;m  việc trước đ&oacute;, bạn n&ecirc;n nhờ những người kh&aacute;c như h&agrave;ng x&oacute;m, bạn b&egrave; th&acirc;n  thiết, những người c&oacute; thể hỗ trợ bạn tốt nhất.</p>\n<p>&nbsp;</p>\n<p><strong>11. Sắp xếp danh s&aacute;ch nh&agrave; tuyển dụng</strong></p>\n<p>Bạn n&ecirc;n c&oacute; một danh  s&aacute;ch ghi lại những c&ocirc;ng ty bạn đ&atilde; nộp đơn đăng tuyển. Sau mỗi cuộc phỏng  vấn, bạn n&ecirc;n ghi lại t&ecirc;n người đ&atilde; n&oacute;i chuyện với bạn v&igrave; c&oacute; thể bạn rất  dễ qu&ecirc;n hay nhầm lẫn người nọ với người kia khi c&ugrave;ng một l&uacute;c đăng tuyển ở  nhiều nơi. C&aacute;ch n&agrave;y cũng sẽ gi&uacute;p bạn lu&ocirc;n sẵn s&agrave;ng khi nhận được điện  thoại phản hồi từ nh&agrave; tuyển dụng sau n&agrave;y.</p>\n<p>&nbsp;</p>\n<p><strong>12. Tận dụng mọi nguồn hỗ trợ th&ocirc;ng tin</strong></p>\n<p>Tất nhi&ecirc;n t&igrave;m việc vẫn  l&agrave; c&ocirc;ng việc tự th&acirc;n vận động nhưng bạn cũng kh&ocirc;ng n&ecirc;n bỏ qua c&aacute;c dịch  vụ hỗ trợ miễn ph&iacute; hoặc c&oacute; chi ph&iacute; kh&ocirc;ng lớn với người t&igrave;m việc như c&aacute;c  văn ph&ograve;ng hướng nghiệp cho sinh vi&ecirc;n, c&aacute;c trung t&acirc;m dịch vụ việc l&agrave;m v&agrave;  thư viện c&ocirc;ng cộng.</p>\n<p>&nbsp;</p>\n<p><strong>13. Khai th&aacute;c th&ocirc;ng tin từ những người xung quanh</strong></p>\n<p>Tr&ecirc;n thực tế, rất  nhiều c&ocirc;ng việc c&ograve;n bỏ ngỏ m&agrave; kh&ocirc;ng được đăng tuyển. H&atilde;y n&oacute;i với tất cả  những người quen th&acirc;n của bạn về việc bạn đang muốn c&oacute; một chỗ l&agrave;m v&agrave;  nhờ họ chỉ gi&uacute;p nếu c&oacute; th&ocirc;ng tin. Đ&atilde; c&oacute; người t&igrave;m được việc l&agrave;m nhờ được  người b&aacute;c sỹ trị bệnh m&aacute;ch cho, lại cũng c&oacute; người xin được v&agrave;o thực tập  tại một c&ocirc;ng ty rất tốt v&igrave; đ&atilde; đề cập tới vấn đề n&agrave;y trong một bữa tiệc  sinh nhật,v.v. C&ocirc;ng việc sẽ kh&ocirc;ng bao giờ l&agrave; c&aacute;nh cửa hẹp với những  người lu&ocirc;n cố gắng nỗ lực hết m&igrave;nh trong cuộc sống.</p>\n<p>&nbsp;</p>\n<p>Đỗ Dương <br /><em>Theo About</em></p>','2011-03-03 21:24:29','admin@jobbid.vn',419,1,NULL),(7,'thich-viec-lam-tu-do','Thích việc làm tự do',0,'Không chọn công việc cố định mà làm việc bán thời gian, làm việc tự do là xu hướng được nhiều người lao động lựa chọn hiện nay','<h3><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"việc l&agrave;m tự do\" src=\"http://www.jobbid.vn/upload/images/1299297328_1448178206chot2.jpg\" alt=\"việc l&agrave;m tự do\" width=\"250\" height=\"188\" /><span style=\"font-size: small; color: #888888;\">Kh&ocirc;ng chọn c&ocirc;ng việc cố định m&agrave; <a title=\"l&agrave;m việc b&aacute;n thời gian\" href=\"http://www.jobbid.vn\">l&agrave;m việc b&aacute;n thời gian</a>, l&agrave;m việc  theo dự &aacute;n l&agrave; xu hướng được nhiều người lao động lựa chọn hiện nay</span></h3>\n<div class=\"content\">\n<div><span style=\"font-size: small;\">&ldquo;Xin  mời qu&yacute; kh&aacute;ch bốc thăm v&agrave; c&agrave;o ở phần dưới phiếu. Nếu may mắn, qu&yacute; kh&aacute;ch  c&oacute; thể tr&uacute;ng một chuyến du lịch ch&acirc;u &Acirc;u hoặc những qu&agrave; tặng kh&aacute;c&rdquo;. Vừa  nhận phiếu mua h&agrave;ng của kh&aacute;ch, Cao Thị Giang Sang (sinh vi&ecirc;n năm 2 ng&agrave;nh  kế to&aacute;n của Trường ĐH Kinh tế TPHCM) vừa phổ biến th&ocirc;ng tin của chương  tr&igrave;nh khuyến m&atilde;i d&agrave;nh cho kh&aacute;ch h&agrave;ng. Hơn một tuần nay, v&agrave;o l&uacute;c 9 giờ,  Sang đ&atilde; c&oacute; mặt ở si&ecirc;u thị Co.opMart Nguyễn Đ&igrave;nh Chiểu (quận 3 - TPHCM)  để l&agrave;m c&ocirc;ng việc ph&aacute;t qu&agrave; khuyến m&atilde;i cho C&ocirc;ng ty Procter &amp; Gamble.  C&ocirc; kết th&uacute;c ca l&agrave;m việc của m&igrave;nh v&agrave;o l&uacute;c 15 giờ mỗi ng&agrave;y.</span></div>\n<div>&nbsp;</div>\n<div>\n<p><span style=\"font-size: small;\">Sinh vi&ecirc;n Cao Thị Giang Sang (phải) đang l&agrave;m c&ocirc;ng việc b&aacute;n thời gian tại Si&ecirc;u thị Co.opMart Nguyễn Đ&igrave;nh Chiểu</span></p>\n</div>\n<div>&nbsp;</div>\n<div><span style=\"font-size: small;\"><strong>C&ugrave;ng l&uacute;c c&oacute; thể l&agrave;m nhiều việc</strong></span></div>\n<div>&nbsp;</div>\n<div><span style=\"font-size: small;\">Mỗi  ng&agrave;y l&agrave;m việc 6 giờ với thu nhập 80.000 đồng nhưng Sang rất vui. Chương  tr&igrave;nh ph&aacute;t qu&agrave; khuyến m&atilde;i m&agrave; c&ocirc; đang thực hiện k&eacute;o d&agrave;i trong 16 ng&agrave;y.  Sau c&ocirc;ng việc ph&aacute;t qu&agrave; khuyến m&atilde;i, c&ocirc; sẽ tiếp tục l&agrave;m việc cho c&aacute;c c&ocirc;ng  ty kh&aacute;c từ nay đến hết Tết Nguy&ecirc;n đ&aacute;n. Sang n&oacute;i: &ldquo;L&agrave;m việc b&aacute;n thời  gian, t&ocirc;i kh&ocirc;ng cảm thấy bị &aacute;p lực. Ngo&agrave;i c&ocirc;ng việc ở đ&acirc;y, t&ocirc;i c&ograve;n đi  dạy k&egrave;m cho c&aacute;c học sinh ở gần nh&agrave; v&agrave;o buổi chiều v&agrave; tối. Nếu l&agrave;m việc  to&agrave;n thời gian, t&ocirc;i kh&ocirc;ng thể đảm nhận hai việc trong c&ugrave;ng một l&uacute;c&rdquo;.</span></div>\n<div>&nbsp;</div>\n<div>\n<table cellspacing=\"5\" cellpadding=\"0\" align=\"left\">\n<tbody>\n<tr>\n<td>\n<div><span style=\"font-size: small;\">Hiện  nay, thay v&igrave; l&agrave;m cho c&aacute;c c&ocirc;ng ty, x&iacute; nghiệp, nhiều bạn trẻ lại chọn xu  hướng l&agrave;m việc tự do hoặc l&agrave;m việc b&aacute;n thời gian. Đ&acirc;y cũng l&agrave; một cảnh  b&aacute;o cho c&aacute;c doanh nghiệp: Nếu kh&ocirc;ng c&oacute; ch&iacute;nh s&aacute;ch tốt, kh&ocirc;ng c&oacute; ưu đ&atilde;i  l&acirc;u d&agrave;i đối với c&aacute;c lao động sẽ kh&oacute; c&oacute; nguồn nh&acirc;n lực tốt trong tương  lai.</span></div>\n<div>&nbsp;</div>\n<p><span style=\"font-size: small;\">&Ocirc;ng Trần Anh Tuấn (Ph&oacute; Gi&aacute;m đốc Trung t&acirc;m Dự b&aacute;o nhu cầu nh&acirc;n lực v&agrave; Th&ocirc;ng tin thị trường lao động TPHCM)</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<span style=\"font-size: small;\"> Từ  khi đứa con đầu l&ograve;ng ra đời, chị N.T.N.L quyết định th&ocirc;i l&agrave;m việc tại  C&ocirc;ng ty May S&agrave;i G&ograve;n 3. Chị kể: &ldquo;Con nhỏ, kh&ocirc;ng c&oacute; người chăm s&oacute;c m&agrave; gửi  nh&agrave; trẻ th&igrave; t&ocirc;i kh&ocirc;ng an t&acirc;m. T&ocirc;i quyết định th&ocirc;i việc để ở nh&agrave; chăm s&oacute;c  con, lo cho chồng&rdquo;. Chồng chị cũng l&agrave; c&ocirc;ng nh&acirc;n, thu nhập kh&ocirc;ng cao n&ecirc;n  chị b&agrave;n với chồng mua một chiếc m&aacute;y may c&ocirc;ng nghiệp để may h&agrave;ng gia  c&ocirc;ng b&ecirc;n ngo&agrave;i.</span></div>\n<div>&nbsp;</div>\n<div><span style=\"font-size: small;\">Ngo&agrave;i  thời gian chăm s&oacute;c cho con, l&uacute;c rảnh, chị lại may h&agrave;ng. &ldquo;Coi như vậy  chứ mỗi th&aacute;ng t&ocirc;i cũng kiếm được gần 2 triệu đồng để mua sữa cho con,  phụ gi&uacute;p gia đ&igrave;nh&rdquo;. Khi được hỏi c&oacute; &yacute; định trở lại c&ocirc;ng ty l&agrave;m việc khi  con đ&atilde; lớn, chị L. cho biết: &ldquo;L&agrave;m việc ở c&ocirc;ng ty giờ giấc b&oacute; buộc, t&ocirc;i  kh&ocirc;ng thể đưa đ&oacute;n con được. C&ograve;n l&agrave;m ở nh&agrave;, rảnh  l&uacute;c n&agrave;o l&agrave;m l&uacute;c đ&oacute; m&agrave; thu nhập cũng kh&ocirc;ng thấp. Ngo&agrave;i giờ l&agrave;m việc, t&ocirc;i  c&ograve;n c&oacute; thời gian để chăm s&oacute;c gia đ&igrave;nh&rdquo;.</span></div>\n<div>&nbsp;</div>\n<div><a title=\"l&agrave;m việc b&aacute;n thời gian\" href=\"http://www.jobbid.vn\"><strong><span style=\"font-size: small;\">L&agrave;m việc theo dự &aacute;n</span></strong></a></div>\n<div>&nbsp;</div>\n<div><span style=\"font-size: small;\">Từng  l&agrave;m gi&aacute;m đốc nh&acirc;n sự cho c&ocirc;ng ty chuy&ecirc;n sản xuất linh kiện điện tử đ&oacute;ng  tại tỉnh B&igrave;nh Dương, c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, chị K.T.K.H xin th&ocirc;i việc trong  sự tiếc nuối của l&atilde;nh đạo v&agrave; đồng nghiệp. Ngay sau khi nghỉ việc, K.H  được nhiều c&ocirc;ng ty mời gọi nhưng chị cũng đ&agrave;nh từ chối. Chị t&acirc;m sự: &ldquo;Với  kinh nghiệm gần 20 năm trong lĩnh vực nh&acirc;n sự, ngo&agrave;i việc đi dạy cho  c&aacute;c trung t&acirc;m, t&ocirc;i c&ograve;n tư vấn cho c&aacute;c c&ocirc;ng ty nhỏ v&agrave; vừa&rdquo;. Hiện chị vừa  đi dạy vừa l&agrave;m việc theo dự &aacute;n cho c&aacute;c c&ocirc;ng ty cần t&aacute;i cấu tr&uacute;c nguồn  nh&acirc;n lực. Song song đ&oacute;, chị c&ograve;n quản l&yacute; một cửa h&agrave;ng thức ăn nhanh do  ch&iacute;nh chị l&agrave;m chủ.</span></div>\n<div>&nbsp;</div>\n<div><span style=\"font-size: small;\">C&ograve;n  Nguyễn Minh Vũ, trước vốn l&agrave; trưởng ph&ograve;ng của một doanh nghiệp lớn trong  ng&agrave;nh viễn th&ocirc;ng ở quận 1 - TPHCM. Nay hằng ng&agrave;y, anh kh&ocirc;ng đến c&ocirc;ng ty  m&agrave; c&oacute; h&ocirc;m ngồi l&igrave; cả buổi s&aacute;ng trong qu&aacute;n c&agrave; ph&ecirc; c&ugrave;ng chiếc laptop.  Cũng c&oacute; h&ocirc;m, anh ph&oacute;ng xe l&ecirc;n tận B&igrave;nh Dương, T&acirc;y Ninh để kiểm tra việc  lắp đặt c&aacute;c thiết bị viễn th&ocirc;ng của c&aacute;c c&ocirc;ng tr&igrave;nh. Vũ cho biết: &ldquo;Thu  nhập của t&ocirc;i thấp nhất mỗi th&aacute;ng cũng hơn 50 triệu đồng. Nhưng điều quan  trọng l&agrave; t&ocirc;i thấy rất tự do. Nhiều bạn b&egrave; c&ugrave;ng học ở Trường ĐH B&aacute;ch  khoa TPHCM với t&ocirc;i ng&agrave;y xưa, giờ cũng th&iacute;ch l&agrave;m việc như vậy&rdquo;.</span></div>\n<div>&nbsp;</div>\n<div><span style=\"font-size: small;\">&Ocirc;ng  Trần Kim Ng&ocirc;, Gi&aacute;m đốc C&ocirc;ng ty Tam An (quận 11 - TPHCM), nh&igrave;n nhận: &ldquo;Xu  hướng l&agrave;m việc tự do kh&ocirc;ng dừng lại ở những bạn trẻ &iacute;t kinh nghiệm m&agrave;  ngay cả những người c&oacute; tr&igrave;nh độ chuy&ecirc;n m&ocirc;n cao cũng th&iacute;ch. Vừa qua, c&ocirc;ng  ty ch&uacute;ng t&ocirc;i thử việc 10 kỹ sư điện v&agrave; đề cập vấn đề k&yacute; hợp đồng th&igrave;  chỉ c&oacute; 2 người đồng &yacute; k&yacute; hợp đồng d&agrave;i hạn, số c&ograve;n lại chỉ đồng &yacute; k&yacute; 6  th&aacute;ng&rdquo;.</span></div>\n</div>\n<div><span style=\"font-size: small;\">B&agrave;i v&agrave; ảnh: Huỳnh Nga</span></div>\n<div><span style=\"font-size: small;\"><em>(Nguồn nld.com.vn)</em></span></div>','2011-03-05 11:00:00','admin@jobbid.vn',243,1,NULL),(8,'freelancer-va-cong-viec-ban-thoi-gian','Freelancer và công việc bán thời gian',0,'Freelancer - lao động tự do – là người làm việc độc lập không có đơn vị quản lý trong những ngành nghề mà người làm toàn thời gian chiếm đa số. Khái niệm này đã từng được Walter Scott đưa ra trong quyển tiểu thuyết nổi tiếng Ivanhoe để mô tả về những ngườ','<p><span style=\"font-size: 12px; font-family: Arial;\"><span id=\"lblDetail\">\n<p><span style=\"color: #888888;\"><strong>Freelancer - lao động tự do &ndash; l&agrave; người l&agrave;m việc độc lập kh&ocirc;ng c&oacute; đơn vị quản l&yacute; trong những ng&agrave;nh nghề m&agrave; người l&agrave;m to&agrave;n thời gian chiếm đa số. Kh&aacute;i niệm n&agrave;y đ&atilde; từng được Walter Scott đưa ra trong quyển tiểu thuyết nổi tiếng Ivanhoe để m&ocirc; tả về những người l&iacute;nh đ&aacute;nh thu&ecirc; thời trung cổ...</strong></span></p>\n<p>Người  l&agrave;m <a title=\"Freelancer \" href=\"http://www.jobbid.vn\">Freelancer </a>c&oacute; thể nhận nhiều <a title=\"việc b&aacute;n thời gian\" href=\"http://www.jobbid.vn\">việc b&aacute;n thời gian</a> kh&aacute;c nhau v&agrave; tự thu xếp thời gian  theo c&aacute;ch của m&igrave;nh miễn sao c&ocirc;ng việc đạt kết quả tốt. Tuy nhi&ecirc;n, cũng  cần ph&acirc;n biệt r&otilde; Freelancer với những người c&oacute; c&ocirc;ng việc bấp b&ecirc;nh, cung  cấp dịch vụ, sản phẩm một c&aacute;ch ngẫu hứng v&agrave; t&ugrave;y tiện đơn giản v&igrave; họ  kh&ocirc;ng c&oacute; kế hoạch.</p>\n<p>Ở những nước ph&aacute;t triển, nghề Freelancer  rất được tr&acirc;n trọng v&igrave; những Freelancer thường phải l&agrave; những người rất  giỏi v&agrave; l&agrave; chuy&ecirc;n gia thực sự. Trong khi đ&oacute; ở những nước đang ph&aacute;t  triển, Freelancer thường đồng nghĩa với việc kh&ocirc;ng c&oacute; khả năng t&igrave;m kiếm  việc l&agrave;m ổn định. Hiện nay ở nước ta xu hướng Freelancer tập trung v&agrave;o  hai nh&oacute;m nghề ch&iacute;nh:</p>\n<p>Nh&oacute;m thứ nhất gồm những nghề đ&ograve;i hỏi sự  đ&agrave;o tạo b&agrave;i bản chuy&ecirc;n s&acirc;u v&agrave; d&agrave;y dạn kinh nghiệm, như tư vấn luật, tư  vấn th&agrave;nh lập - t&aacute;i cấu tr&uacute;c doanh nghiệp, kế to&aacute;n, b&aacute;c sỹ, dịch thuật,  lập tr&igrave;nh, IT...</p>\n<p>Nh&oacute;m thứ 2 thi&ecirc;n về s&aacute;ng tạo như sản xuất &acirc;m  nhạc, đạo diễn chương tr&igrave;nh, tổ chức sự kiện, nhiếp ảnh gia, thiết kế  (đồ họa, nội thất, thời trang,..), kiến tr&uacute;c sư, stylist, viết b&aacute;o, PR,  copywriter...</p>\n<p><strong>Những thuận lợi của nghề Freelancer</strong></p>\n<p>Nh&igrave;n từ b&ecirc;n ngo&agrave;i, Freelancer quả l&agrave; một xu hướng l&agrave;m việc thật l&yacute; tưởng với những ưu điểm nổi trội:</p>\n<p><img style=\"float: left; margin-left: 6px; margin-right: 6px; border: 0pt none;\" title=\"Freelancer v&agrave; c&ocirc;ng việc b&aacute;n thời gian\" src=\"http://www.saga.vn/Saga_Gallery/ChauHa/BizKnowledge/quit.jpg\" alt=\"Freelancer v&agrave; c&ocirc;ng việc b&aacute;n thời gian\" width=\"300\" height=\"376\" />Thứ  nhất, bạn sẽ ho&agrave;n to&agrave;n được th&aacute;o bỏ khỏi những r&agrave;ng buộc khắt khe của  cuộc sống c&ocirc;ng sở. Bạn kh&ocirc;ng cần đ&uacute;ng 8h s&aacute;ng đến c&ocirc;ng ty, chẳng phải  chịu đựng &aacute;nh mắt kh&oacute; chịu của sếp mỗi l&uacute;c đi l&agrave;m trễ, kh&ocirc;ng phải b&oacute;  cứng trong bộ đồng phục suốt 8 tiếng đồng hồ...</p>\n<p>Thứ hai, l&agrave; một  Freelancer bạn sẽ được thả sức ph&aacute;t huy sức s&aacute;ng tạo v&agrave; điều chỉnh c&aacute;c &yacute;  tưởng m&agrave; chẳng sợ sếp can thiệp v&igrave; giờ đ&acirc;y ch&iacute;nh bạn đ&oacute;ng vai tr&ograve; l&agrave;m  sếp của m&igrave;nh.</p>\n<p>Thứ ba, bạn ho&agrave;n to&agrave;n c&oacute; thể thay đổi phong c&aacute;ch  l&agrave;m việc thậm ch&iacute; l&agrave; loại h&igrave;nh, t&iacute;nh chất c&ocirc;ng việc... sau khi đ&atilde; cảm  thấy \"ch&aacute;n\" một dạng c&ocirc;ng việc n&agrave;o đ&oacute;.</p>\n<p>Thứ tư, khi bạn đ&atilde; l&agrave;  một Freelancer được biết đến v&agrave; tin cậy th&igrave; việc bạn nắm được những hợp  đồng lớn, bỏ t&uacute;i v&agrave;i ng&agrave;n đ&ocirc; chỉ trong một th&aacute;ng sẽ trở n&ecirc;n dễ d&agrave;ng.  Thực tế cho thấy nhiều c&ocirc;ng ty sẵn s&agrave;ng trả c&ocirc;ng kh&aacute; cao cho những  Freelancer ph&ugrave; hợp với c&ocirc;ng việc m&agrave; họ đang thiếu người. Mức thu nhập  h&agrave;ng th&aacute;ng của một Freelancer cao gấp hai, ba lần so với c&aacute;c đồng nghiệp  l&agrave;m to&agrave;n thời gian cũng l&agrave; chuyện b&igrave;nh thường. Sau một lần &ldquo;tr&uacute;ng quả&rdquo;  như thế, bạn c&oacute; thể vi vu du lịch đ&acirc;u đ&oacute; cả tuần lễ nghỉ xả hơi m&agrave; chẳng  phải bận t&acirc;m đến việc nộp đơn xin ph&eacute;p ai.</p>\n<p>Thứ năm, nếu c&oacute; một  nh&oacute;m bạn c&ugrave;ng l&agrave;m việc theo kiểu Freelancer th&igrave; thật qu&aacute; tuyệt. Việc  trao đổi c&ocirc;ng việc, &yacute; tưởng, hỗ trợ nhau sẽ đạt được mức tối đa. Mỗi  người trong nh&oacute;m cũng sẽ ph&aacute;t huy hết khả năng v&agrave; tinh thần tr&aacute;ch nhiệm  của m&igrave;nh với c&ocirc;ng việc.</p>\n<p>Ưu điểm thứ s&aacute;u m&agrave; nghề Freelancer mang  lại đ&oacute; ch&iacute;nh l&agrave; việc mở mang c&aacute;c mối quan hệ, t&iacute;ch lũy th&ecirc;m nhiều kinh  nghiệm l&agrave;m việc độc lập cũng như sự tự gi&aacute;c của bạn</p>\n<p><strong>V&agrave;... kh&oacute; khăn chồng chất</strong></p>\n<p>B&ecirc;n cạnh những ưu điểm kể tr&ecirc;n, Freelancer cũng đồng nghĩa với việc bạn sẽ phải đối mặt với kh&ocirc;ng &iacute;t những kh&oacute; khăn v&agrave; rủi ro.</p>\n<p>Khi  đ&atilde; l&agrave; một Freelancer, c&oacute; nghĩa l&agrave; bạn được tự do nhưng bạn phải biết  c&aacute;ch sử dụng sự tự do ấy đạt hiệu quả tối đa. Mọi g&aacute;nh nặng tr&aacute;ch nhiệm  đều tr&uacute;t l&ecirc;n vai bạn, bạn vừa l&agrave;m chủ, vừa l&agrave;m c&ocirc;ng cho ch&iacute;nh m&igrave;nh, lại  ki&ecirc;m th&ecirc;m kế to&aacute;n thu chi v&agrave; h&agrave;ng trăm việc lặt vặt kh&aacute;c. Nếu thiếu kỷ  luật, kh&ocirc;ng kh&eacute;o quản l&yacute; về thời gian v&agrave; c&ocirc;ng việc, mọi kế hoạch của bạn sẽ bị ph&aacute; sản từ l&uacute;c n&agrave;o kh&ocirc;ng biết.</p>\n<p>Rủi  ro tiếp theo l&agrave; bạn kh&ocirc;ng được hưởng mức lương ổn định c&ugrave;ng những phụ  cấp, đ&atilde;i ngộ của c&ocirc;ng ty n&agrave;o cả, hoặc nếu bạn thất bại trong một kế  hoạch g&igrave; đấy, kh&ocirc;ng ai kh&aacute;c ngo&agrave;i ch&iacute;nh bạn phải chịu tr&aacute;ch nhiệm tất cả  thiệt hại, v&agrave; dĩ nhi&ecirc;n điều n&agrave;y sẽ ảnh hưởng kh&aacute; lớn đến uy t&iacute;n của  bạn. C&oacute; lẽ v&igrave; vậy m&agrave; kh&ocirc;ng &iacute;t người gọi Freelancer l&agrave; nghề &ldquo;kh&ocirc;ng c&oacute;  hậu&rdquo;, kh&ocirc;ng c&oacute; sự bảo đảm cho tương lai.</p>\n<p>Nếu bạn l&agrave;m việc ổn  định tại một c&ocirc;ng ty lớn th&igrave; thương hiệu của c&ocirc;ng ty cũng ch&iacute;nh l&agrave;  thương hiệu của bạn. Nhưng khi l&agrave;m Freelancer, bạn sẽ phải tự x&acirc;y dựng thương hiệu cho ch&iacute;nh m&igrave;nh.  Ch&iacute;nh v&igrave; thế nếu bạn chưa được nhiều người biết đến hoặc chưa c&oacute; nhiều  kinh nghiệm, khả năng t&igrave;m kiếm kh&aacute;ch h&agrave;ng sẽ trở n&ecirc;n kh&oacute; khăn hơn.</p>\n<p>L&agrave;m  sếp của bản th&acirc;n cũng c&oacute; nghĩa l&agrave; bạn phải quyết đo&aacute;n, tự gi&aacute;c v&agrave;  nghi&ecirc;m khắc với ch&iacute;nh m&igrave;nh. Nếu bạn l&agrave; nh&acirc;n vi&ecirc;n của một c&ocirc;ng ty, sai  lầm của bạn c&oacute; thể được cấp tr&ecirc;n ph&aacute;t hiện ra trước khi th&agrave;nh sản phẩm  giao cho kh&aacute;ch h&agrave;ng, l&agrave;m Freelancer, bạn phải trở th&agrave;nh \"tỉnh t&aacute;o vi&ecirc;n\"  của ch&iacute;nh m&igrave;nh. Nếu phạm sai lầm, bạn c&oacute; thể l&acirc;m v&agrave;o t&igrave;nh trạng \"ế ẩm\"  trong một thời gian d&agrave;i.</p>\n<p>Quan trọng nhất, Freelancer đồng nghĩa  với vốn sống, bề d&agrave;y kinh nghiệm v&agrave; chuy&ecirc;n m&ocirc;n s&acirc;u trong c&ocirc;ng việc.  Hiếm ai vừa tốt nghiệp đ&atilde; trở th&agrave;nh một Freelancer giỏi. Muốn vậy it  nhất, bạn cũng phải trải qua v&agrave;i năm trong một v&agrave;i c&ocirc;ng ty lớn nhỏ, c&oacute;  những sản phẩm chất lượng tốt, đạt đến độ ch&iacute;n nhất định về nghề nghiệp,  tạo được bi&ecirc;n độ quan hệ rộng song song với việc ho&agrave;n thiện h&agrave;ng loạt  kỹ năng về thuyết tr&igrave;nh, đ&agrave;m ph&aacute;n, l&agrave;m việc nh&oacute;m với c&aacute;c Freelancer kh&aacute;c trong c&ugrave;ng dự &aacute;n...</p>\n<p><img src=\"http://www.saga.vn/Saga_Gallery/ChauHa/BizKnowledge/startup.jpg\" border=\"0\" alt=\"startup.jpg\" hspace=\"6\" width=\"250\" height=\"187\" align=\"right\" />Ngay  cả khi đ&atilde; l&agrave; những Freelancer d&agrave;y dạn kinh nghiệm, sẽ c&oacute; l&uacute;c bạn cảm  thấy mệt mỏi sau qu&atilde;ng thời gian \"lăn lộn\" với nghề, khi th&igrave; c&ocirc;ng việc  kh&ocirc;ng xuể, phải chạy đua với thời gian đến mức l&agrave;m qua qu&yacute;t ẩu tả mất cả  uy t&iacute;n, l&uacute;c lại ngồi kh&ocirc;ng, chẳng kiếm được đồng n&agrave;o suốt mấy th&aacute;ng  trời. Hơn nữa, ng&agrave;y c&agrave;ng nhiều cạnh tranh trong nghề Freelancer, thậm  ch&iacute; c&oacute; cả chuyện ph&aacute; gi&aacute;, gi&agrave;nh mối, nhận việc với gi&aacute; rẻ mạt...</p>\n<p>T&oacute;m  lại bản th&acirc;n c&ocirc;ng việc Freelancer kh&ocirc;ng c&oacute; g&igrave; sai, vấn đề l&agrave; lựa chọn  của bạn c&oacute; đ&uacute;ng hướng v&agrave; ph&ugrave; hợp với bản th&acirc;n kh&ocirc;ng. Nếu l&agrave; người th&iacute;ch  phi&ecirc;u lưu v&agrave; c&aacute; t&iacute;nh mạnh, đủ bản lĩnh đối ph&oacute; với những biến cố, c&oacute; thể  Freelancer sẽ l&agrave; con đường th&iacute;ch hợp với bạn. C&ograve;n nếu bạn mong muốn  cuộc sống &ecirc;m đềm, c&ocirc;ng việc v&agrave; thu nhập ổn định, th&igrave; h&atilde;y chung thủy với  lựa chọn &ldquo;người của c&ocirc;ng ty&rdquo;. Được v&agrave; mất, đ&aacute; hộc hay hoa hồng, t&ugrave;y  thuộc v&agrave;o bạn.</p>\n<p><strong>Những vấn đề cần lưu &yacute; khi trở th&agrave;nh Freelancer</strong></p>\n<p><strong>T&agrave;i ch&iacute;nh (Finances):</strong> Đ&acirc;y l&agrave; một trong những vấn đề quan trọng h&agrave;ng đầu m&agrave; bạn cần phải quan  t&acirc;m khi bắt đầu quyết định trở th&agrave;nh một Freelancer. Bạn phải chắc chắn  lu&ocirc;n c&oacute; đủ tiền trong t&agrave;i khoản để c&oacute; thể gi&uacute;p bạn trụ vững khi \"ế ẩm\".</p>\n<p><strong>M&ocirc;i trường l&agrave;m việc (Working Environment):</strong> Nếu bạn c&oacute; một m&ocirc;i trường l&agrave;m việc thoải m&aacute;i ở nh&agrave; th&igrave; đ&oacute;&nbsp;l&agrave; tốt nhất.  Tuy nhi&ecirc;n sẽ thật tiện lợi nếu ngo&agrave;i nh&agrave; ra bạn c&ograve;n c&oacute; thể tận dụng bất  cứ nơi đ&acirc;u l&agrave;m th&agrave;nh văn ph&ograve;ng l&agrave;m việc.</p>\n<p><strong>Portfolio:</strong> Khi l&agrave; một Freelancer, tập portfolio sẽ thể hiện bạn v&agrave; c&ocirc;ng việc của  bạn. Nếu l&agrave; một người ph&aacute;t triển phần mềm th&igrave; n&oacute; lại c&agrave;ng cần thiết phải  c&oacute; trong tay. N&oacute; gi&uacute;p bạn nhấn mạnh dự &aacute;n mới nhất bạn l&agrave;m, kỹ thuật  của bạn, ng&ocirc;n ngữ bạn sử dụng, c&ocirc;ng cụ bạn d&ugrave;ng, v&agrave; kiến thức bạn đ&atilde;  học. H&atilde;y bảo vệ những t&aacute;c phẩm của m&igrave;nh, ph&aacute;t triển n&oacute; cũng như ph&aacute;t  triển kỹ năng bản th&acirc;n m&igrave;nh.</p>\n<p><strong>C&aacute;c kỳ nghỉ (Holidays):</strong> Sau khi ho&agrave;n th&agrave;nh một số dự &aacute;n, h&atilde;y t&iacute;nh đến thời gian để nghỉ ngơi.  N&oacute; gi&uacute;p tinh thần bạn thoải m&aacute;i trở lại, nghĩ ra được th&ecirc;m những &yacute; tưởng  mới...</p>\n<p><strong>Nỗi sợ h&atilde;i (The Fear):</strong> Mọi người  đều sợ h&atilde;i ở một số vấn đề: Sợ sẽ kh&ocirc;ng c&oacute; thu nhập trong một thời gian  d&agrave;i, hoặc sợ kh&ocirc;ng c&oacute; việc để l&agrave;m. Kh&ocirc;ng sao đ&acirc;u, những khoảng thời gian  như vậy, h&atilde;y d&agrave;nh thời gian đ&oacute; để ph&aacute;t triển những kỹ năng mới hoặc đi  du lịch thư gi&atilde;n...</p>\n<p><em>Nguồn : </em>www.saga.vn</p>\n</span></span></p>','2011-03-06 21:47:38','admin@jobbid.vn',470,1,NULL),(9,'cua-hieu-nha-hang-chat-vat-tuyen-nhan-vien','Cửa hiệu, nhà hàng chật vật tuyển nhân viên',0,'Không yêu cầu cao về trình độ vậy mà nhiều cửa hàng hay những ki ốt chợ, chủ kinh doanh tại Hà Nội vẫn đau đầu với vấn đề tuyển dụng.','<p><span style=\"font-size: small; color: #888888;\"><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" src=\"http://vnexpress.net/Files/Subject/3b/a2/73/3f/tuyen-1.jpg\" alt=\"\" width=\"250\" /><strong>Kh&ocirc;ng y&ecirc;u cầu cao về tr&igrave;nh độ vậy m&agrave; nhiều cửa h&agrave;ng hay  những ki ốt chợ, chủ kinh doanh tại H&agrave; Nội vẫn đau đầu với vấn đề tuyển  dụng.</strong></span></p>\n<p class=\"Normal\">Ri&ecirc;ng tr&ecirc;n tuyến đường Phạm Ngọc Thạch &ndash; Ch&ugrave;a Bộc &ndash;  T&acirc;y Sơn đ&atilde; c&oacute; tới hơn hai mươi cửa hiệu treo biển &ldquo;<a title=\"tuyển nh&acirc;n vi&ecirc;n\" href=\"http://www.jobbid.vn\">tuyển nh&acirc;n vi&ecirc;n</a>&rdquo;. Vấn  đề tương tự cũng diễn ra tại nhiều nh&agrave; h&agrave;ng. C&oacute; những nơi cần tuyển tới  10 nh&acirc;n vi&ecirc;n gồm cả chạy b&agrave;n, tr&ocirc;ng xe lẫn nấu ăn....</p>\n<p class=\"Normal\" style=\"text-align: left;\">Chị Th&uacute;y Hạnh, chủ một shop thời trang tr&ecirc;n phố Phạm  Ngọc Thạch cho hay, trước chị cũng c&oacute; một c&ocirc; b&eacute; b&aacute;n h&agrave;ng rất m&aacute;t tay.  \"Nhưng ra Tết, con b&eacute; nằng nặc đ&ograve;i ở nh&agrave; lấy chồng, tăng lương thế n&agrave;o  cũng kh&ocirc;ng l&ecirc;n. N&oacute; k&ecirc;u con g&aacute;i c&oacute; thời, l&ecirc;n H&agrave; Nội l&agrave;m, qu&aacute; tuổi th&igrave; ế\",  chị kể.</p>\n<p class=\"Normal\">Khổ hơn chị Hạnh, anh H&ugrave;ng, chủ qu&aacute;n ăn sang trọng  tr&ecirc;n đường Huỳnh Th&uacute;c Kh&aacute;ng đang đau đầu v&igrave; lo tuyển một l&uacute;c 5 nh&acirc;n vi&ecirc;n  nữ chạy b&agrave;n. &ldquo;Mấy c&ocirc; ấy bỏ về khu c&ocirc;ng nghiệp l&agrave;m hết rồi, k&ecirc;u ở đ&oacute;  được đ&oacute;ng bảo hiểm&rdquo;, anh H&ugrave;ng than thở.</p>\n<p class=\"Normal\">Theo t&igrave;m hiểu của <em>VnExpress.net</em>, hầu hết c&aacute;c  chủ bu&ocirc;n b&aacute;n nhỏ đều kh&ocirc;ng y&ecirc;u cầu cao về tr&igrave;nh độ học vấn khi tuyển  nh&acirc;n vi&ecirc;n. &ldquo;Chỉ cần ưa nh&igrave;n, ăn n&oacute;i hoạt b&aacute;t l&agrave; nhận. Học vấn kh&ocirc;ng quan  trọng, học hết lớp mấy cũng được, kh&eacute;o l&eacute;o v&agrave; b&aacute;n được h&agrave;ng l&agrave; tuyển&rdquo;,  chị Hạnh cho biết th&ecirc;m.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/73/3f/tuyen-2.jpg\" border=\"1\" alt=\"Nhiều cửa h&agrave;ng c&oacute; nhu cầu tuyển nh&acirc;n vi&ecirc;n c&oacute; tr&igrave;nh độ tay nghề chuy&ecirc;n m&ocirc;n. Ảnh: Xu&acirc;n Ngọc\" width=\"450\" height=\"360\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Nhiều cửa h&agrave;ng c&oacute; nhu cầu <a title=\"Tuyển Sinh Vi&ecirc;n L&agrave;m Th&ecirc;m B&aacute;n Thời Gian\" href=\"http://www.jobbid.vn/duan/view/91/tuyen-sinh-vien-lam-them-ban-thoi-gian\">tuyển nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng</a>. Ảnh: <em>Xu&acirc;n Ngọc</em></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Do đặc th&ugrave; c&ocirc;ng việc, nhiều nơi cũng đăng tuyển những  nh&acirc;n vi&ecirc;n c&oacute; tay nghề như biết sửa quần &aacute;o, biết chỉnh cỡ gi&agrave;y hay c&oacute;  kinh nghiệm l&agrave;m bồi b&agrave;n. C&aacute;c c&ocirc;ng việc như vậy thường c&oacute; mức lương 3-3,5  triệu đồng mỗi th&aacute;ng, cao hơn từ một triệu đến 1,5 triệu đồng so với  những c&ocirc;ng việc b&aacute;n h&agrave;ng đơn thuần. Nếu l&agrave;m b&aacute;n thời gian th&igrave; người lao  động c&oacute; thu nhập bằng một nửa như vậy. Mức lương n&agrave;y sẽ tăng dần theo  th&acirc;m ni&ecirc;n v&agrave; th&agrave;nh quả sản phẩm.</p>\n<p class=\"Normal\">Song những c&ocirc;ng việc n&agrave;y vẫn chưa thu h&uacute;t được nhiều  lao động. Bước ra từ một cửa h&agrave;ng c&oacute; nhu cầu tuyển nh&acirc;n vi&ecirc;n, H&agrave; My,  sinh vi&ecirc;n Đại học Sư phạm thất vọng cho biết, họ y&ecirc;u cầu l&agrave;m cả ng&agrave;y n&ecirc;n  c&ocirc; kh&ocirc;ng thể đảm đương nổi. &ldquo;Chủ h&agrave;ng n&oacute;i nếu l&agrave;m nửa ng&agrave;y cũng phải từ  8 giờ đến 15 giờ hoặc từ 15 giờ đến 22 giờ. Thời gian vắt ngang giờ học  như vậy n&ecirc;n m&igrave;nh đ&agrave;nh th&ocirc;i&rdquo;, H&agrave; My t&acirc;m sự.</p>\n<p class=\"Normal\">Th&ecirc;m v&agrave;o đ&oacute;, với nhiều người, đ&acirc;y chỉ l&agrave; c&ocirc;ng việc  &ldquo;chống ch&aacute;y&rdquo;. Nh&acirc;n vi&ecirc;n của một ki ốt trong chợ Ng&atilde; Tư Sở chia sẻ chị ra  trường, đang chờ xin việc n&ecirc;n l&agrave;m tạm ở đ&acirc;y, kiếm tiền trụ lại ở H&agrave;  Nội. Khi n&agrave;o xin được v&agrave;o c&ocirc;ng ty n&agrave;o đ&oacute;, chị sẽ nghỉ. Đ&acirc;y cũng l&agrave; l&yacute; do  khiến nhiều cửa h&agrave;ng li&ecirc;n tục tuyển v&agrave; thay nh&acirc;n vi&ecirc;n.</p>\n<p class=\"Normal\">C&ograve;n theo chị Hường, cựu nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng với tr&ecirc;n 5  năm kinh nghiệm cho hay, c&ocirc;ng việc b&aacute;n h&agrave;ng tuy kh&ocirc;ng đ&ograve;i hỏi bằng cấp  nhưng kh&ocirc;ng đơn giản. Chị Hường n&oacute;i: &ldquo;Từ c&aacute;ch n&oacute;i l&agrave;m sao cho lọt tai  kh&aacute;ch, c&aacute;ch treo quần &aacute;o khiến m&agrave;u sắc c&aacute;c bộ t&ocirc;n l&ecirc;n cho nhau để bắt  mắt đến c&aacute;ch ch&agrave;o hỏi, thậm ch&iacute; lu&ocirc;n mồm khen m&agrave; kh&ocirc;ng bị k&ecirc;u &lsquo;dẻo  miệng, x&aacute;o rỗng&rsquo; l&agrave; cả một qu&aacute; tr&igrave;nh t&iacute;ch lũy. Những người mới v&agrave;o nghề  chưa quen th&igrave; kh&oacute; l&agrave;m lắm. M&agrave; l&agrave;m &lsquo;thượng đế&rsquo; phật &yacute; một lần th&igrave; c&oacute; dải  thảm đỏ, họ cũng chẳng th&egrave;m lui tới nữa&rdquo;.</p>\n<p class=\"Normal\">&ldquo;Đ&oacute; l&agrave; chưa kể đến việc nếu kh&ocirc;ng b&aacute;n được h&agrave;ng, chủ  sẽ mặt nặng m&agrave;y nhẹ, ra v&agrave;o một c&acirc;u, thậm ch&iacute; c&ograve;n bảo m&igrave;nh v&iacute;a nặng, v&ocirc;  duy&ecirc;n. &Aacute;p lực như vậy n&ecirc;n nhiều người kh&ocirc;ng trụ được với việc&rdquo;, chị  Hường cho biết th&ecirc;m.</p>\n<p class=\"Normal\">Tuy nhi&ecirc;n, theo chị Th&uacute;y, chủ một cửa h&agrave;ng b&aacute;n gi&agrave;y  d&eacute;p, với những người kh&ocirc;ng c&oacute; bằng cấp th&igrave; b&aacute;n h&agrave;ng l&agrave; c&ocirc;ng việc kh&aacute; l&yacute;  tưởng. &ldquo;L&agrave;m l&acirc;u sẽ th&agrave;nh quen, so với mức lương đ&oacute;, đi l&agrave;m gi&uacute;p việc hay  b&aacute;n dạo ngo&agrave;i phố c&ograve;n khổ hơn nhiều. Đ&acirc;y mưa nắng kh&ocirc;ng lo, chịu kh&oacute;  học hỏi kinh nghiệm v&agrave; chắt chiu th&igrave; cũng c&oacute; cơ hội d&agrave;nh dụm vốn mở một  cửa h&agrave;ng nhỏ sau n&agrave;y cho m&igrave;nh&rdquo;.</p>\n<p class=\"Normal\"><strong>Xu&acirc;n Ngọc</strong></p>\n<p class=\"Normal\"><em>(nguồn VnExpress.net)</em><strong></strong></p>','2011-03-09 08:47:28','admin@jobbid.vn',180,1,NULL),(10,'13-cach-tim-hieu-cong-viec-ban-thoi-gian','13 cách tìm hiểu công việc bán thời gian',0,'Ai nói không thể giàu khi làm điều mình thích? Có thể làm công việc mình ưa thích theo kiểu bán thời gian (part time) như những sáng kiến sau: ','<p><span style=\"color: #333333; font-size: small;\">\n<div id=\"ar-content-html\">\n<p><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"13 c&aacute;ch t&igrave;m hiểu c&ocirc;ng việc b&aacute;n thời gian\" src=\"http://divivu.com/uploads/work/news/eda_t1259564137_S002.jpg\" alt=\"13 c&aacute;ch t&igrave;m hiểu c&ocirc;ng việc b&aacute;n thời gian\" width=\"300\" height=\"239\" /><span style=\"color: #888888;\"><strong>Ai n&oacute;i kh&ocirc;ng thể gi&agrave;u khi l&agrave;m điều m&igrave;nh th&iacute;ch? C&oacute; thể l&agrave;m c&ocirc;ng việc m&igrave;nh ưa th&iacute;ch theo kiểu <a title=\"b&aacute;n thời gian\" href=\"http://www.jobbid.vn\">b&aacute;n thời gian</a> (<a title=\"l&agrave;m việc part-time\" href=\"http://www.jobbid.vn\">part time</a>) như những s&aacute;ng kiến sau:</strong></span></p>\n<p>Nếu bạn thức cả đ&ecirc;m để vắt &oacute;c ra kế hoạch r&uacute;t lui hay c&aacute;ch kiếm th&ecirc;m  tiền, th&igrave; h&atilde;y t&igrave;m một khuynh hướng c&oacute; thể ph&aacute;t triển mạnh, quảng c&aacute;o để  mọi người biết đến rồi điều chỉnh cho ph&ugrave; hợp với sở th&iacute;ch một đối tượng  kh&aacute;n giả đặc biệt. Ch&igrave;a kh&oacute;a để biến một &yacute; tưởng, t&agrave;i năng hay chuy&ecirc;n  m&ocirc;n độc đ&aacute;o th&agrave;nh cỗ xe vững chắc l&agrave; biết để &yacute; đến cuộc sống xung quanh,  v&igrave; &yacute; tưởng lu&ocirc;n c&oacute; quanh bạn rất nhiều.</p>\n<p>V&iacute; dụ: ng&agrave;y c&agrave;ng c&oacute; nhiều phụ nữ t&igrave;m c&aacute;ch tho&aacute;t khỏi c&ocirc;ng việc gia  đ&igrave;nh. Tổ chức c&acirc;u lạc bộ s&aacute;ch, tiệc rượu, trang sức hay dạ hội l&agrave; những  hoạt động vừa vui vẻ vừa c&oacute; thể kiếm tiền.</p>\n<p>Hoặc dạy một kĩ năng mới, kỹ thuật cao v&agrave; ri&ecirc;ng biệt n&agrave;o đ&oacute;? Ai cũng  c&oacute; một lĩnh vực chuy&ecirc;n m&ocirc;n để b&aacute;n cho người kh&aacute;c. Trung t&acirc;m gi&aacute;o dục  thường xuy&ecirc;n của cộng đồng l&agrave; nơi th&iacute;ch hợp để bạn tiếp thị chuy&ecirc;n m&ocirc;n  của m&igrave;nh.</p>\n<p>Dưới đ&acirc;y l&agrave; một số &yacute; tưởng gi&uacute;p bạn t&igrave;m ra chỗ th&iacute;ch hợp nhất cho m&igrave;nh:</p>\n</div>\n</span></p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n<tbody>\n<tr>\n<td><span style=\"font-size: small;\">Ch&igrave;a kh&oacute;a để biến một &yacute; tưởng, t&agrave;i năng hay chuy&ecirc;n m&ocirc;n độc đ&aacute;o th&agrave;nh cỗ xe vững chắc l&agrave; biết để &yacute; đến cuộc sống xung quanh...</span></td>\n</tr>\n</tbody>\n</table>\n<p><span style=\"color: #333333; font-size: small;\">\n<div id=\"ar-content-html\">\n<p><strong>1. Dịch vụ c&aacute; nh&acirc;n:</strong> Bạn c&oacute; thể gi&uacute;p người kh&aacute;c tiết  kiệm thời gian kh&ocirc;ng? Chạy việc cho cấp tr&ecirc;n, chuẩn bị thủ tục ho&agrave;n  thuế, hay dắt ch&oacute; h&agrave;ng x&oacute;m đi dạo l&agrave; những dịch vụ gi&aacute; trị.</p>\n<p><strong>2. L&agrave;m vườn v&agrave; l&agrave;m đẹp phong cảnh.</strong> Thử nghĩ đến việc  trồng vườn. Ng&agrave;y nay những chủ nh&agrave; kh&ocirc;ng c&oacute; đủ thời gian hoặc đam m&ecirc;  trồng tỉa vẫn nhận ra tầm quan trọng của vỉa h&egrave;. Thiết kế phong cảnh,  chăm s&oacute;c c&acirc;y v&agrave; l&agrave;m vườn thu&ecirc; đang thịnh h&agrave;nh. Nếu th&iacute;ch gần gũi thi&ecirc;n  nhi&ecirc;n v&agrave; kh&iacute; hậu trong l&agrave;nh, bạn c&oacute; thể chọn nghề l&agrave;m vườn, gồm nhiều  chuy&ecirc;n m&ocirc;n kh&aacute;c nhau như trồng c&acirc;y, chăm s&oacute;c c&acirc;y thương nghiệp trồng  trong nh&agrave; k&iacute;nh, gi&uacute;p c&aacute;c lớp học g&ocirc;n hay l&agrave;m dịch vụ nh&agrave; đất tư nh&acirc;n.  Nếu được chỉ dẫn b&agrave;i bản, bạn sẽ biết c&aacute;ch d&ugrave;ng tia nước t&aacute;c động đến  c&acirc;y trồng ở c&aacute;c v&ugrave;ng kh&aacute;c nhau sao cho hiệu quả nhất.</p>\n<p><strong>3. Hoạt động giải tr&iacute; ngo&agrave;i trời.</strong> Nhiều người kh&ocirc;ng  t&aacute;ch rời c&ocirc;ng việc v&agrave; vui chơi. Họ sẽ miễn cưỡng bấm đồng hồ để dậy sớm  buổi s&aacute;ng, hay giới hạn m&igrave;nh trong ph&ograve;ng l&agrave;m việc để kiếm sống. Nếu y&ecirc;u  tự do, c&oacute; lẽ bạn muốn l&agrave;m ăn ri&ecirc;ng với đối t&aacute;c l&agrave; Đất Mẹ. C&oacute; thể kinh  doanh bằng c&aacute;ch cung cấp b&egrave; gỗ, hướng dẫn đạp xe leo n&uacute;i, đi xe b&ograve; chụp  h&igrave;nh, đi bộ đường d&agrave;i, leo n&uacute;i đ&aacute;, hay l&agrave;m việc quanh c&ocirc;ng vi&ecirc;n th&agrave;nh  phố.</p>\n<p><strong>4. Dịch vụ chăm s&oacute;c th&uacute; nu&ocirc;i. </strong>Kinh doanh c&oacute; thể n&agrave;o  khởi đi từ những ch&uacute; ch&oacute; kh&ocirc;ng? Dịch vụ v&agrave; h&agrave;ng h&oacute;a cao cấp d&agrave;nh cho th&uacute;  nu&ocirc;i đang ng&agrave;y một th&agrave;nh c&ocirc;ng. Theo một nghi&ecirc;n cứu mới đ&acirc;y của Đơn Vị  Tiếp Thị, Hoa Kỳ đạt doanh thu 30 tỉ đ&ocirc;la một năm. Thức ăn, dịch vụ chăm  s&oacute;c, dầu gội, l&agrave;m đẹp v&agrave; kh&aacute;ch sạn cho th&uacute; cưng l&agrave; những sản phẩm, dịch  vụ tạo n&ecirc;n nền c&ocirc;ng nghiệp n&agrave;y. Nếu bạn kh&eacute;o điều khiển ch&oacute; th&igrave; dạy ch&oacute;  cũng l&agrave; một ng&agrave;nh n&oacute;ng đem lại thu nhập.</p>\n<p><strong>5. Thiết kế nơi l&agrave;m việc. </strong>Bạn c&oacute; thể trở th&agrave;nh nh&agrave;  thiết kế kh&ocirc;ng gian l&agrave;m việc tương lai. Khi c&ocirc;ng nghiệp ph&aacute;t triển th&igrave;  văn ph&ograve;ng phải bắt mắt từ m&aacute;i hi&ecirc;n, dụng cụ đến m&agrave;u sắc, &aacute;nh s&aacute;ng. C&agrave;ng  ng&agrave;y c&agrave;ng c&oacute; nhiều người cần chuy&ecirc;n gia bổ sung điều kiện sinh h&oacute;a.  Những lĩnh vực chuy&ecirc;n m&ocirc;n gồm: nơi l&agrave;m việc ở khu c&ocirc;ng nghiệp, an to&agrave;n  nghề nghiệp, thiết kế nội thất, phần cứng m&aacute;y t&iacute;nh, giao tiếp m&aacute;y  t&iacute;nh-con người, độ tin cậy sản phẩm, sản phẩm cho người ti&ecirc;u d&ugrave;ng v&agrave; m&ocirc;i  trường ảo.</p>\n<p><strong>6. Tư vấn phong thủy. </strong>Những năm gần đ&acirc;y, số người  quan t&acirc;m đến phong thủy đ&atilde; gia tăng. Họ t&igrave;m kiếm sự thỏa m&atilde;n v&agrave; hiệu quả  cao trong nghề nghiệp, kinh doanh v&agrave; cuộc sống. Phong thủy l&agrave; m&ocirc;n nghệ  thuật cổ xưa, đề cao sức khỏe t&acirc;m linh v&agrave; vật chất th&ocirc;ng qua việc hướng  dẫn người chơi hướng tốt nhất để đặt văn ph&ograve;ng v&agrave; nh&agrave; cửa. C&aacute;c chuy&ecirc;n  gia c&oacute; tr&igrave;nh độ được thu&ecirc; để &ldquo;xem&rdquo; cho khu d&acirc;n cư v&agrave; khu thương mại. Ph&iacute;  trả cho c&aacute;c tư vấn vi&ecirc;n l&agrave; từ 235 đến 1.000 Mỹ kim cho hai tiếng tư  vấn, t&ugrave;y độ lớn của t&agrave;i sản. Một v&agrave;i x&iacute; nghiệp trả đến 25 ng&agrave;n đ&ocirc; cho  những dự &aacute;n lớn. Học ph&iacute; cho những kh&oacute;a n&agrave;y, bao gồm giờ l&ecirc;n lớp, cố vấn  v&agrave; đ&agrave;o tạo c&oacute; thể l&ecirc;n đến 3,500 đ&ocirc;la.</p>\n<p><strong>7. Dịch vụ chăm s&oacute;c sức khỏe.</strong> Do hệ thống chăm s&oacute;c  bảo vệ sức khỏe ng&agrave;y c&agrave;ng ch&uacute; trọng ph&ograve;ng bệnh, c&aacute;c nước T&acirc;y phương đ&atilde;  chấp nhận những phương ph&aacute;p mới để chữa bệnh v&agrave; sống khỏe to&agrave;n diện.  Những liệu ph&aacute;p chữa bệnh như trị liệu, m&aacute;t xa, ch&acirc;m cứu v&agrave; yoga được tư  nh&acirc;n lẫn c&ocirc;ng ty y&ecirc;u cầu rất nhiều. Những người quan t&acirc;m đến sức khỏe  t&igrave;m mua loại thực phẩm hữu cơ kh&ocirc;ng đường hoặc chất bảo quản.</p>\n<p><strong>8. Dịch vụ l&agrave;m đẹp. </strong>C&aacute;c chương tr&igrave;nh quảng c&aacute;o tr&ecirc;n  TV như Đ&ocirc;i Mắt B&iacute; Ẩn của Người Ch&acirc;n Ch&iacute;nh (Queer Eye for the Straight  Guy) đ&atilde; khiến ph&aacute;i nam ng&agrave;y c&agrave;ng quan t&acirc;m đến sức hấp dẫn của ngoại  h&igrave;nh. Trước l&agrave;n s&oacute;ng n&agrave;y, một tr&agrave;o lưu l&agrave;m đẹp v&agrave; thư gi&atilde;n d&agrave;nh ri&ecirc;ng  cho qu&iacute; &ocirc;ng đ&atilde; ra đời v&agrave; nhanh ch&oacute;ng l&ecirc;n ng&ocirc;i. Những dịch vụ n&oacute;ng nhất  hiện c&oacute; gồm l&agrave;m s&aacute;ng da, m&aacute;t xa mặt v&agrave; cơ thể, đắp mặt bằng tảo biển.</p>\n<p><strong>9. T&acirc;m linh.</strong> Những người thi&ecirc;n về t&acirc;m linh cho rằng  sống nh&acirc;n &aacute;i l&agrave; c&ocirc;ng việc cả đời của họ. Ng&agrave;y nay, c&oacute; rất nhiều nghề  nghiệp cho những ai nghĩ m&igrave;nh c&oacute; sứ mệnh khơi nguồn cảm hứng cho người  kh&aacute;c. V&iacute; dụ, nghệ sĩ hay nghệ nh&acirc;n t&ocirc;n gi&aacute;o (phục vụ trong những cửa  h&agrave;ng, xưởng l&agrave;m nến, đồ kim ho&agrave;n, hộp nữ trang, ảnh Gi&aacute;ng sinh v&agrave; đồ lưu  niệm Do Th&aacute;i gi&aacute;o hay C&ocirc;ng gi&aacute;o); người tổ chức cắm trại, tư vấn vi&ecirc;n/  người hướng dẫn hoạt động nh&agrave; thờ; t&aacute;c giả t&ocirc;n gi&aacute;o (cho s&aacute;ch b&aacute;o v&agrave;  thiệp); v&agrave; người hướng dẫn tĩnh t&acirc;m. Họ hướng dẫn người kh&aacute;c tr&ecirc;n h&agrave;nh  tr&igrave;nh về c&ugrave;ng đ&iacute;ch cuộc đời dựa v&agrave;o Kinh th&aacute;nh/ lịch sử.</p>\n<p><strong>10. Phục vụ giới thượng lưu.</strong> Đừng qu&ecirc;n cuộc b&ugrave;ng nổ  trẻ em sau chiến tranh thế giới thứ hai ở Mỹ đ&atilde; sinh ra những lợi &iacute;ch  g&igrave;. Đến nay nh&oacute;m d&acirc;n số tr&ecirc;n năm mươi tuổi n&agrave;y vẫn l&agrave; đối tượng th&ocirc;ng  minh v&agrave; năng động. Họ cần nh&agrave; cung cấp sản phẩm, dịch vụ v&agrave; th&ocirc;ng tin để  đ&aacute;p ứng nhu cầu giải tr&iacute;, gi&aacute;o dục v&agrave; đời sống. Phục vụ cho nh&oacute;m d&acirc;n cư  n&agrave;y c&oacute; nhiều ng&agrave;nh mang lại lợi nhuận cao, như: t&aacute;c giả s&aacute;ch phục vụ  giới thượng lưu, người ph&aacute;t triển mạng, c&ocirc;ng ty du lịch, bảo hiểm, bất  động sản, đ&agrave;o tạo kỹ sư m&aacute;y t&iacute;nh</p>\n<p><strong>11. Dịch vụ viết.</strong> Nếu c&oacute; khiếu thể hiện &yacute; tưởng bằng  c&aacute;ch viết th&igrave; bạn n&ecirc;n trở th&agrave;nh nh&agrave; l&ecirc;n kế hoạch kinh doanh. Nhiều  doanh nghiệp để vuột mất hợp đồng, quỹ hay kh&aacute;ch h&agrave;ng chỉ v&igrave; kh&ocirc;ng giỏi  truyền đạt qua giấy b&uacute;t. Ng&agrave;y nay doanh nghiệp cần những người giỏi viết  để tiếp thị, thảo chiến lược, vận động v&agrave; l&ecirc;n kế hoạch. Ngo&agrave;i ra, nhu  cầu về t&aacute;c giả tự do chuy&ecirc;n viết giấy tờ chuyển nhượng, hệ thống nhập  xuất cơ bản, c&ocirc;ng nghệ th&ocirc;ng tin, ph&aacute;t triển kinh tế v&agrave; kinh doanh tổng  thể cũng kh&aacute; cao. Việc dự &aacute;n gồm y&ecirc;u cầu dự &aacute;n, hướng dẫn đ&agrave;o tạo đo&agrave;n  thể, t&agrave;i liệu m&aacute;y t&iacute;nh, bản đ&aacute;nh gi&aacute;, giấy ph&eacute;p ứng dụng của ch&iacute;nh phủ,  thư b&aacute;o lập ph&aacute;p v&agrave; xuất nhập h&agrave;nh ch&iacute;nh. Ph&iacute; thấp nhất khoảng 100 đ&ocirc;la/  giờ, cao hơn từ 30.000 tới 60.000 đ&ocirc;la/ năm.</p>\n<p><strong>12. Thiết kế nội thất. </strong>Nh&agrave; l&agrave; nơi thể hiện vẻ đẹp  của nghệ thuật. Cuộc b&ugrave;ng nổ trẻ em với thu nhập tự do v&agrave; xu hướng &ldquo;tạo  k&eacute;n&rdquo; tr&ecirc;n to&agrave;n quốc đ&atilde; khiến ng&agrave;nh trang tr&iacute; nội thất v&agrave; dịch vụ thiết  kế ph&aacute;t triển rộng khắp, từ nh&agrave; kh&aacute;ch đến nh&agrave; hưu dưỡng, nh&agrave; tĩnh t&acirc;m  đến ph&ograve;ng tắm cao cấp. Tham gia v&agrave;o đội ngũ những người say m&ecirc; l&agrave;m gi&agrave;u  n&agrave;y gồm c&oacute; kiến tr&uacute;c sư, nh&agrave; thiết kế nội thất, kiến tr&uacute;c phong cảnh v&agrave;  người x&acirc;y hồ bơi. C&aacute;c lĩnh vực kh&aacute;c gồm chuy&ecirc;n gia quản l&yacute; dự &aacute;n cho  c&ocirc;ng ty nội thất, tiện nghi c&ocirc;ng sở, thiết kế kh&aacute;ch sạn, viện chăm s&oacute;c  da hay nh&agrave; hưu dưỡng.</p>\n<p><strong>13. Dịch vụ nấu ăn.</strong> Th&ecirc;m v&agrave;o đ&oacute;, ăn ở nh&agrave; cũng l&agrave;  một mốt mới c&agrave;ng ng&agrave;y c&agrave;ng được nhiều người ưa chuộng. Tiệc tối đem lại  nhiều lợi nhuận. Nếu bạn c&oacute; khiếu nấu ăn th&igrave; thật tuyệt, v&igrave; rất nhiều  người cần bạn. Ngo&agrave;i việc cung cấp lương thực, bạn c&ograve;n c&oacute; thể dạy nấu  ăn, gi&uacute;p chuẩn bị thực đơn hay hướng dẫn tại nh&agrave;. Nhiều người muốn ăn  uống khỏe mạnh v&igrave; l&iacute; do sức khỏe lẫn khẩu vị n&ecirc;n bạn c&oacute; thể hoạt động  với tư c&aacute;ch l&agrave; bếp trưởng tư hay nh&agrave; dinh dưỡng.</p>\n</div>\n<div style=\"text-align: right;\"><em>Nguồn ENTREPRENEUR - dịch VŨ THỊ MINH UY&Ecirc;N (doanhnhansaigon)</em></div>\n</span></p>','2011-03-10 12:41:09','admin@jobbid.vn',302,1,NULL),(11,'song-than-cao-10-met-ap-vao-nhat-ban','Sóng thần cao 10 mét ập vào Nhật Bản',0,'Sóng thần ập vào bờ biển phía đông bắc Nhật Bản hôm nay sau cơn địa chấn mạnh 8,9 độ Richter, lớn nhất trong vòng hơn 100 năm nay. Ba người được xác định đã thiệt mạng trong động đất.','<h2 class=\"Lead\"><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" src=\"http://vnexpress.net/Files/Subject/3b/a2/75/5d/earthquake.jpg\" alt=\"Xe hơi, xe tải bị s&oacute;ng cuốn tr&ocirc;i sau động đất. Ảnh: Sky News.\" width=\"400\" height=\"187\" /><span style=\"font-size: small; color: #888888;\">S&oacute;ng thần ập v&agrave;o bờ biển ph&iacute;a đ&ocirc;ng bắc Nhật Bản h&ocirc;m nay  sau cơn địa chấn mạnh 8,9 độ Richter, lớn nhất trong v&ograve;ng hơn 100 năm  nay. Ba người được x&aacute;c định đ&atilde; thiệt mạng trong động đất.</span><a class=\"Lead\" href=\"http://vnexpress.net/gl/the-gioi/2011/03/cac-nuoc-ban-bo-canh-bao-song-than/\"></a></h2>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td></td>\n</tr>\n<tr>\n<td class=\"Image\"></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Động đất xảy ra l&uacute;c 14h46 chiều nay (giờ Tokyo) ở độ  s&acirc;u 10 km, c&aacute;ch Tokyo 382 km về ph&iacute;a đ&ocirc;ng bắc, cơ quan khảo s&aacute;t địa chất  Mỹ cho biết v&agrave; th&ecirc;m rằng động đất mạnh tới 8,9 độ Richter. Giới chức  Nhật cho biết 3 người đ&atilde; thiệt mạng sau cơn địa chấn dữ dội n&agrave;y.</p>\n<p class=\"Normal\">\"Tim bắn ra khỏi lồng ngực. Chưa bao giờ cảm gi&aacute;c động  đất như lần n&agrave;y\", Phạm Hồng Long, nghi&ecirc;n cứu sinh Việt Nam đang sống ở  ngoại &ocirc; Tokyo, thốt l&ecirc;n tr&ecirc;n mạng x&atilde; hội facebook. \"Cả t&ograve;a nh&agrave; nghi&ecirc;n  cứu 5 tầng của bọn m&igrave;nh rung lắc, k&ecirc;u răng rắc\".</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"60%\" align=\"center\" bgcolor=\"#63b1b1\">\n<tbody>\n<tr>\n<td class=\"Normal\" align=\"center\"><a class=\"Normal\" href=\"http://vnexpress.net/gl/the-gioi/2011/03/song-than-cao-10-met-ap-vao-nhat-ban/page_3.asp\">Video s&oacute;ng thần nh&igrave;n từ tr&ecirc;n cao</a></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\"><em>AFP</em> cho hay động đất h&ocirc;m nay k&eacute;o theo những  đợt s&oacute;ng thần cao 4 m&eacute;t v&agrave;o bờ biển đ&ocirc;ng bắc Nhật Bản. Tại cảng Sensai,  tỉnh Miyagi, s&oacute;ng thần c&ograve;n l&ecirc;n cao tới 10 m&eacute;t. Truyền h&igrave;nh Nhật ph&aacute;t đi  h&igrave;nh ảnh những ng&ocirc;i nh&agrave; bị s&oacute;ng thần cuốn tr&ocirc;i. Đường băng tại s&acirc;n bay  Sensai ngập nước trong khi h&agrave;ng chục người phải đứng tr&ecirc;n n&oacute;c của t&ograve;a  nh&agrave; chờ. Trong khi đ&oacute;, h&atilde;ng tin <em>Kyodo</em> cho biết s&oacute;ng thần cao 7 m&eacute;t ập v&agrave;o bờ biển ở tỉnh Fukushima.</p>\n<p class=\"Normal\">&Iacute;t nhất 10 vụ hỏa hoạn được ghi nhận ở Tokyo, <em>AFP</em> đưa tin. Nhiều t&ograve;a nh&agrave; rung chuyển trong v&agrave;i ph&uacute;t. Hệ thống t&agrave;u điện  ngầm ngừng hoạt động, tiếng xe c&ograve;i h&uacute; khắp nơi c&ograve;n d&acirc;n ch&uacute;ng sơ t&aacute;n khỏi  c&aacute;c t&ograve;a nh&agrave;.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/75/5d/tusunami-4.jpg\" border=\"1\" alt=\"Hỏa hoạn tại một nh&agrave; m&aacute;y lọc dầu gần Tokyo sau động đất. Ảnh: AP.\" width=\"480\" height=\"269\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Hỏa hoạn tại một nh&agrave; m&aacute;y lọc dầu ở đ&ocirc;ng Tokyo sau động đất. Ảnh: <em>AFP</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">\"Chưa bao giờ thấy trận động đất lớn vậy. Tr&ecirc;n tầng 9,  rung lắc dữ dội, đồ đạc đổ khắp nơi, m&aacute;y t&iacute;nh xập xuống b&agrave;n, cốc nước  cũng lăn\", một nghi&ecirc;n cứu sinh người Việt ở Nhật kể. \"Tất cả mọi người  chui hết xuống gầm b&agrave;n, mặt mũi t&aacute;i m&eacute;t! Mấy người Nhật bảo chưa bao giờ  thấy trận lớn thế n&agrave;y\".</p>\n<p class=\"Normal\">Osamu Akiya, 46 tuổi, đang l&agrave;m việc trong văn ph&ograve;ng ở  Tokyo th&igrave; động đất xảy ra. Gi&aacute; s&aacute;ch, m&aacute;y t&iacute;nh bị x&ocirc; xuống nền nh&agrave;. \"T&ocirc;i  đ&atilde; chứng kiến nhiều vụ động đất rồi nhưng chưa bao giờ thấy c&oacute; vụ n&agrave;o  thế n&agrave;y\", anh n&oacute;i. \"T&ocirc;i kh&ocirc;ng biết đ&ecirc;m nay c&oacute; về nh&agrave; được kh&ocirc;ng nữa\".</p>\n<p class=\"Normal\">Trước đ&oacute;, Nhật Bản đưa ra cảnh b&aacute;o s&oacute;ng thần ở mức cao nhất, cho rằng s&oacute;ng c&oacute; thể cao tới 6m.</p>\n<p class=\"Normal\">Chứng kho&aacute;n ch&acirc;u &Aacute; đồng loạt giảm gi&aacute; sau th&ocirc;ng tin về  động đất v&agrave; s&oacute;ng thần ở Nhật khiến bầu kh&ocirc;ng kh&iacute; của thị trường vốn đ&atilde;  ảm đạm lại c&agrave;ng xấu th&ecirc;m. Chỉ số Nikkei của Nhật v&agrave; Hansen của Hong Kong  sụt điểm.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/75/5d/honshu-island.jpg\" border=\"1\" alt=\"Ảnh của Cơ quan địa chất Mỹ. T&acirc;m chấn động đất (v&ograve;ng tr&ograve;n đỏ) ở ph&iacute;a đ&ocirc;ng đảo Honshu của Nhật. Ảnh: AFP.\" width=\"300\" height=\"416\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Ảnh của Cơ quan địa chất Mỹ. T&acirc;m chấn động đất (v&ograve;ng tr&ograve;n đỏ) ở ph&iacute;a đ&ocirc;ng đảo Honshu của Nhật. Ảnh: AFP.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Cơ quan cảnh b&aacute;o s&oacute;ng thần Th&aacute;i B&igrave;nh Dương cũng ph&aacute;t  cảnh b&aacute;o đối với một loạt c&aacute;c v&ugrave;ng l&atilde;nh thổ như Đ&agrave;i Loan, Nga, quần đảo  Mariana, Guam, Philippines, quần đảo Marshall, Indonesia, Papua New  Guinea, Nauru, Micronesia v&agrave; Hawaii.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"420\" align=\"center\" bgcolor=\"#92c9c9\">\n<tbody>\n<tr>\n<td class=\"Normal\" align=\"center\"><a class=\"Normal\" href=\"http://vnexpress.net/gl/the-gioi/2011/03/song-than-cao-10-met-ap-vao-nhat-ban/page_2.asp\">Video s&oacute;ng thần ập v&agrave;o cảng Sendai, đ&ocirc;ng bắc Nhật Bản</a></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Lần cuối c&ugrave;ng một trận động đất dữ dội xảy ra ở Tokyo  l&agrave; v&agrave;o năm 1923 khi cơn địa chấn Kanto Vĩ đại cướp đi sinh mạng của  140.000 người, đa phần l&agrave; trong c&aacute;c vụ hỏa hoạn. Năm 1825, động đất mang  t&ecirc;n Ansei Edo cũng khiến th&agrave;nh phố n&agrave;y bị hư hại nhiều. Gần đ&acirc;y nhất,  trận động đất Kobe năm 1995 khiến 6.400 người chết.</p>\n<p class=\"Normal\">Năm 2004, hơn 220.000 người thiệt mạng khi địa chấn  mạnh 9,1 độ Richter ập đến Indonesia, k&eacute;o theo s&oacute;ng thần t&agrave;n ph&aacute; c&aacute;c khu  vực quanh Ấn Độ Dương.</p>\n<p class=\"Normal\"><em>Hải Ninh (nguồn vnexpress.net)</em><strong><br /></strong></p>','2011-03-11 16:37:16','admin@jobbid.vn',173,1,NULL),(12,'nghe-go-captcha-thue-tai-viet-nam','Nghề gõ Captcha thuê tại Việt Nam',0,'Trong các công việc bán thời gian kiếm tiền trực tuyến (MMO), gõ Captcha thuê đang thu hút một lượng không nhỏ cư dân mạng Việt Nam theo đuổi với mức thu nhập trung bình từ 1,2 đến 1,8 triệu đồng mỗi tháng.','<div style=\"margin: 10px 0pt; text-align: justify;\"><span style=\"font-size: small; color: #888888;\"><strong> <span id=\"ctl00_ContentPlaceHolder1_ContentNews1_lblInitial\">Trong  c&aacute;c  nghề kiếm tiền trực tuyến (MMO), g&otilde; Captcha thu&ecirc; đang thu h&uacute;t một  lượng  kh&ocirc;ng nhỏ cư d&acirc;n mạng Việt Nam theo đuổi với mức thu nhập trung  b&igrave;nh từ  1,2 đến 1,8 triệu đồng mỗi th&aacute;ng.</span></strong></span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Sở hữu đ&ocirc;i tay nhanh nhẹn, sẵn s&agrave;ng  ngồi lỳ trước  m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh nhiều tiếng đồng hồ, bạn đ&atilde; đủ tố chất  trở th&agrave;nh một  người g&otilde; Captcha chuy&ecirc;n nghiệp tr&ecirc;n mạng ảo. Tuy nhi&ecirc;n,  c&oacute; l&agrave;m mới biết,  c&ocirc;ng việc kiếm tiền online n&agrave;y chẳng dễ như mơ.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold; font-size: small;\">G&otilde; ph&iacute;m kiếm tiền, nghề tuyệt vời cho cư d&acirc;n mạng?</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Captcha l&agrave; h&igrave;nh ảnh chứa đoạn m&atilde; x&aacute;c  nhận gi&uacute;p ph&acirc;n biệt đối tượng đang truy cập l&agrave; người hay m&aacute;y. C&ocirc;ng cụ  rất  hiệu quả trong ngăn chặn h&agrave;nh vi lập t&agrave;i khoản diễn đ&agrave;n, website,  e-mail  số lượng lớn nhờ c&aacute;c chương tr&igrave;nh tự động.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><img style=\"border: 1px solid; margin: 5px;\" src=\"http://genk.vcmedia.vn/Images/Uploaded/Share/2011/03/09/examplesoftextcaptchasdb0.jpg\" border=\"0\" alt=\"\" width=\"500\" /></span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Để vượt qua r&agrave;o chắn n&agrave;y, hacker buộc  phải nhờ đội  ngũ những người g&otilde; thu&ecirc; Captcha để x&aacute;c nhận việc lập t&agrave;i  khoản, đẩy  nhanh qu&aacute; tr&igrave;nh đăng k&iacute; h&agrave;ng loạt. Với đặc th&ugrave; c&ocirc;ng việc,  y&ecirc;u cầu ứng  tuyển chỉ đ&ograve;i hỏi tốc độ g&otilde; ph&iacute;m nhanh, thời gian online  nhiều.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Mất chưa đến một gi&acirc;y với từ kh&oacute;a <span style=\"font-style: italic;\">&ldquo;g&otilde; Capcha kiếm tiền&rdquo;</span>,   người viết nhận được gần 16 ng&agrave;n kết quả trả về từ Google, số lượng  lớn  trong đ&oacute; l&agrave; c&aacute;c mẩu tin t&igrave;m người g&otilde; Captcha thu&ecirc; tại Việt Nam.  Theo  th&ocirc;ng tin, bạn thường được nhận ngay khi li&ecirc;c lạc với ph&iacute;a tuyển  dụng  (kh&ocirc;ng cần qua thử việc). C&ocirc;ng việc tiến h&agrave;nh từ xa theo h&igrave;nh thức   online.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><img style=\"border: 1px solid; margin: 5px;\" src=\"http://genk.vcmedia.vn/Images/Uploaded/Share/2011/03/09/Captcha.jpg\" border=\"0\" alt=\"\" width=\"500\" /></span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Trung b&igrave;nh cứ g&otilde; th&agrave;nh c&ocirc;ng 1000  Captcha, ph&iacute;a  tuyển dụng cam kết chi trả từ 15 đến 20 ng&agrave;n đồng. Tiền  ho&agrave;n to&agrave;n t&iacute;nh  theo đầu việc thực hiện, v&agrave; được thanh to&aacute;n khi đạt mức  nhất định (v&iacute;  dụ: 50 ng&agrave;n đồng) qua h&igrave;nh thức chuyển khoản. Ch&iacute;nh nhờ  y&ecirc;u cầu đơn  giản, nghề kiếm tiền online n&agrave;y thu h&uacute;t rất đ&ocirc;ng người tham  gia tại Việt  Nam. Một th&agrave;nh vi&ecirc;n của Diễn đ&agrave;n tin học t&acirc;m sự: <span style=\"font-style: italic;\">&ldquo;Nghề   g&otilde; Captcha đ&atilde; c&oacute; từ rất l&acirc;u tr&ecirc;n thế giới, tại Việt Nam lượng người   tham gia c&ocirc;ng việc n&agrave;y kh&aacute; nhiều, chủ yếu l&agrave; c&aacute;c bạn học sinh, sinh   vi&ecirc;n&rdquo;<span style=\"font-style: normal;\">.</span></span></span></div>\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold; font-size: small;\">C&ocirc;ng việc của những người sống về đ&ecirc;m</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Trong vai kẻ muốn thử sức với nghề g&otilde; ph&iacute;m,&nbsp;<span style=\"font-weight: bold;\">GenK.vn</span> được c&aacute;c th&agrave;nh vi&ecirc;n diễn đ&agrave;n www. kiemtien... giới thiệu đến V.H &ndash; nh&acirc;n   vật từng c&oacute; kinh nghiệm hơn một năm trong nghề g&otilde; Captcha.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Theo V.H, điều đầu ti&ecirc;n muốn nhắn nhủ  l&agrave; đừng tự  tin v&agrave;o khả năng đ&aacute;nh m&aacute;y nhanh của m&igrave;nh. Trong nghề n&agrave;y, kỹ  năng g&otilde; văn  bản chưa đủ để &ldquo;ăn tiền&rdquo;. Hầu hết c&aacute;c&nbsp;Captcha được tạo  th&agrave;nh từ tổ hợp  chữ c&aacute;i, chữ số thậm ch&iacute; cả c&aacute;c k&yacute; tự ngẫu nhi&ecirc;n. Việc  ho&agrave;n th&agrave;nh một  đoạn m&atilde; v&igrave; thế đ&ograve;i hỏi phải lần m&ograve; kh&aacute; l&acirc;u, kh&ocirc;ng giống  chuyện g&otilde; lại  c&aacute;c từ c&oacute; nghĩa.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">&ldquo;<span style=\"font-style: italic;\">Trước  đ&acirc;y c&ograve;n  dễ, 2 năm trở lại đ&acirc;y, v&igrave; thường xuy&ecirc;n bị vượt mặt, n&ecirc;n độ  qu&aacute;i của c&aacute;c  đoạn m&atilde; ng&agrave;y c&agrave;ng tăng. C&aacute;c k&yacute; tự ẩn hiện đ&aacute;nh đố người  xem, việc nhầm  lẫn giữa c&aacute;c k&yacute; tự như i-l, 0-o... thường xuy&ecirc;n xảy ra  với c&aacute;c tay mơ  trong nghề. Theo đ&uacute;ng luật, chỉ sai một k&yacute; tự, cả đoạn  m&atilde; vừa g&otilde; đi  tong&rdquo; - </span>nh&acirc;n vật cho biết th&ecirc;m.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><img style=\"border: 1px solid; margin: 5px;\" src=\"http://genk.vcmedia.vn/Images/Uploaded/Share/2011/03/09/P1020833-copy.jpg\" border=\"0\" alt=\"\" width=\"500\" /></span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Theo chia sẻ, giai đoạn mới v&agrave;o nghề,  tỷ lệ g&otilde;  th&agrave;nh c&ocirc;ng chỉ đạt v&agrave;i chục Captcha trong một giờ. Quen việc  hơn, hiệu  quả g&otilde; c&oacute; thể n&acirc;ng tới 300 m&atilde;/ giờ. Một người được gọi  l&agrave;&nbsp;l&agrave;nh nghề&nbsp;c&oacute;  thể đảm bảo tỷ lệ th&agrave;nh c&ocirc;ng đến 400 m&atilde;/ giờ hoặc&nbsp;thậm  ch&iacute; hơn. Như vậy,  khi đ&atilde; c&oacute; kinh nghiệm, một ng&agrave;y bạn c&oacute; thể ho&agrave;n th&agrave;nh  từ 2000-3000 m&atilde;  (tương đương khoảng 5-6 giờ).</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Từng 1 năm lăn lộn với nghề, tự tin nắm r&otilde; thế giới kiếm tiền trực tuyến n&agrave;y, V.H chia sẻ: <span style=\"font-style: italic;\">&ldquo;Người   g&otilde; Captcha thu&ecirc; ở Việt Nam chủ yếu hoạt động theo hai kiểu: h&agrave;nh nghề   tự do &ndash; tức l&agrave; l&agrave;m trực tiếp cho website của nước ngo&agrave;i như   www.kolotibab... hoặc tham gia v&agrave;o team n&agrave;o đ&oacute; do người trong nước đứng   đầu. Kiểu l&agrave;m thứ hai được chọn nhiều hơn v&igrave; thuận lợi cho việc nhận   tiền c&ocirc;ng&rdquo;<span style=\"font-style: normal;\">.</span></span></span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Với h&igrave;nh thức hoạt động theo nh&oacute;m,  người g&otilde; thu&ecirc;  được tham gia v&agrave;o c&aacute;c dự &aacute;n do &ocirc;ng chủ chỉ đạo (người n&agrave;y  đứng ra đại  diện để&nbsp;hợp&nbsp;đồng với&nbsp;c&aacute;c tổ chức nước ngo&agrave;i). Trong nh&oacute;m  thường&nbsp;chia  tiếp ra người g&otilde; thu&ecirc;, người quản l&yacute; với mức trả c&ocirc;ng ch&ecirc;nh  lệch nhau  kh&ocirc;ng nhiều.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><img style=\"border: 1px solid; margin: 5px;\" src=\"http://genk.vcmedia.vn/Images/Uploaded/Share/2011/03/09/89186f4d9be62.jpg\" border=\"0\" alt=\"\" width=\"500\" /></span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Thường th&igrave; sau khoảng&nbsp;thời gian&nbsp;h&agrave;nh  nghề&nbsp;nhất  định, bạn sẽ được trả mức tiền c&ocirc;ng cao hơn ban đầu (hoặc  nhận c&aacute;c khoản  hoa hồng tương đương). Nhưng những nh&oacute;m/ tổ chức &aacute;p dụng  cơ chế n&agrave;y  cũng quy định khắt khe về định lượng c&ocirc;ng việc. Buộc người  tham gia phải  cam kết ho&agrave;n th&agrave;nh số lượng Captcha tối thiểu trong ng&agrave;y.  Nếu &iacute;t hơn,  tiền kh&ocirc;ng được thanh to&aacute;n, hoặc chịu phạt.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Đ&aacute;ng n&oacute;i hơn, qu&aacute; tr&igrave;nh g&otilde; Captcha  phải tiến h&agrave;nh  song song với hoạt động của chương tr&igrave;nh do hacker điều  khiển trực tiếp.  Do l&agrave;m chủ yếu với c&aacute;c tổ chức nước ngo&agrave;i n&ecirc;n hầu hết  d&acirc;n trong nghề ở  Việt Nam chịu cảnh &ldquo;lệch m&uacute;i giờ&rdquo;.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><img style=\"border: 1px solid; margin: 5px;\" src=\"http://genk.vcmedia.vn/Images/Uploaded/Share/2011/03/09/P1020844.jpg\" border=\"0\" alt=\"\" width=\"500\" /></span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Một ng&agrave;y l&agrave;m việc của V.H bắt đầu v&agrave;o  l&uacute;c 12  giờ&nbsp;đ&ecirc;m v&agrave; k&eacute;o d&agrave;i đến gần s&aacute;ng (c&oacute; khi&nbsp;đến 7,8 tiếng li&ecirc;n  tục)&nbsp;- đ&acirc;y  cũng l&agrave; khung giờ m&agrave; d&acirc;n trong nghề hoạt động s&ocirc;i nổi nhất,  do c&aacute;c m&atilde;  Captcha mới trả về nhanh, li&ecirc;n tục. Tuy nhi&ecirc;n, đ&oacute; l&agrave; khi  thuận lợi,  nhưng&nbsp;cũng nhiều&nbsp;l&uacute;c hệ thống hoạt động thiếu ổn định.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Nhiều thời điểm, chờ m&atilde;i chẳng thấy  h&igrave;nh ảnh mới  n&agrave;o xuất hiện, c&oacute; l&uacute;c lại trả về dồn dập kh&ocirc;ng xử l&yacute; kịp,  gặp trục trặc  trong việc gửi lại kết quả... cũng l&agrave;m gi&aacute;n đoạn đ&aacute;ng kể  c&ocirc;ng việc của  nhiều người m&agrave; V.H&nbsp;được&nbsp;biết.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold; font-size: small;\">G&otilde; Captcha thu&ecirc;, được chẳng nhiều m&agrave; mất kh&ocirc;ng &iacute;t</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Chẳng cần thử, bạn c&oacute; thể thấy ngay  với c&ocirc;ng việc  một người g&otilde; Captcha thu&ecirc;&nbsp;tại Việt Nam&nbsp;đang tham gia  th&igrave;&nbsp;sức khỏe l&agrave; yếu  tố đầu ti&ecirc;n bị ảnh hưởng. Thay đổi giờ giấc sinh  hoạt, l&agrave;m việc chủ yếu  về đ&ecirc;m, ngồi lỳ trước m&agrave;n h&igrave;nh với mức độ tập  trung cao độ... sẽ ph&aacute;t  sinh h&agrave;ng loạt bệnh l&yacute; về mắt, cột sống, thần  kinh... về l&acirc;u d&agrave;i hậu quả  thường nghi&ecirc;m trọng.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Ngo&agrave;i ra, nếu thử t&iacute;nh tr&ecirc;n mức độ l&agrave;m  việc trung  b&igrave;nh (bỏ qua vấn đề hệ thống thiếu ổn định) khoảng  2000-3000 m&atilde;/ ng&agrave;y  (5-6 tiếng), bạn được nhận từ 40 đến 60 ng&agrave;n đồng/  ng&agrave;y, một th&aacute;ng thu  nhập v&agrave;o khoảng 1,2 đến 1,8 triệu đồng.</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Con số n&agrave;y kh&ocirc;ng cao hơn nghề gia sư  hay bồi b&agrave;n m&agrave;  sinh vi&ecirc;n c&oacute; thể l&agrave;m ở bất cứ đ&acirc;u. Thậm ch&iacute;, t&iacute;nh to&aacute;n  tr&ecirc;n đ&atilde; lờ đi  hẳn chi ph&iacute; tiền điện, tiền internet phải chi trả cho mỗi  tiếng l&agrave;m việc  online (kh&ocirc;ng nhỏ).</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\">Chắc hẳn&nbsp;v&igrave; những l&yacute; do ở tr&ecirc;n m&agrave; V.H  đ&atilde; &ldquo;giải  nghệ&rdquo; sau hơn 1 năm thử sức, gắn b&oacute; với nghề kh&aacute; l&acirc;u, anh  chứng kiến  nhiều bạn trẻ t&igrave;m đến rồi lại ra đi v&igrave; kh&ocirc;ng chịu nổi &aacute;p lực  c&ocirc;ng việc.  Nghề&nbsp;ảo&nbsp;kiếm được tiền thật, y&ecirc;u cầu đơn giản l&agrave; thế nhưng  cũng khiến  người ta phải đ&aacute;nh đổi qu&aacute; nhiều, &ldquo;g&otilde; Captcha thu&ecirc;&rdquo; vẫn  chẳng dễ ăn như  người ta vẫn tưởng?</span></div>\n<p style=\"text-align: right;\"><span style=\"font-size: small;\"><em>Theo genk</em></span></p>','2011-03-13 19:49:23','admin@jobbid.vn',722,1,NULL),(13,'viet-blog-thue-nghe-ban-thoi-gian-cho-teen','Viết blog thuê - nghề bán thời gian cho teen',0,'Công việc bán thời gian - viết blog chẳng bao giờ lỗi thời cả, không chỉ thế mà nó lại càng trở nên thông dụng và thu hút được nhiều bạn trẻ.','<p class=\"sapo\"><span style=\"color: #888888;\"><strong><span style=\"font-size: small;\">C&ocirc;ng việc viết blog chẳng bao giờ lỗi thời cả, kh&ocirc;ng chỉ thế m&agrave; n&oacute; lại c&agrave;ng trở n&ecirc;n th&ocirc;ng dụng v&agrave; thu h&uacute;t được nhiều bạn trẻ.</span></strong></span></p>\n<div class=\"content\">\n<p><span style=\"font-size: small;\">Đ&oacute; l&agrave; quy tr&igrave;nh chung của việc  thu&ecirc; một blogger viết với mục đ&iacute;ch quảng b&aacute;, hoặc đi s&acirc;u ph&acirc;n t&iacute;ch, hoặc  ph&ecirc; ph&aacute;n một sản phẩm, dịch vụ, hay đơn giản l&agrave; một sự kiện n&agrave;o đ&oacute;  trong đời sống. C&aacute;c teen c&oacute; muốn &ldquo;khởi nghiệp&rdquo; bằng h&igrave;nh thức viết l&aacute;ch  n&agrave;y kh&ocirc;ng? D&ugrave; sao, đ&acirc;y cũng l&agrave; bước đệm để trở th&agrave;nh một người đưa tin,  hay một nh&agrave; b&aacute;o trong tương lai.</span></p>\n<div><span style=\"font-weight: bold; font-size: small;\">Bắt đầu từ việc kiếm tiền</span></div>\n<div>&nbsp;</div>\n<span style=\"font-weight: bold; font-size: small;\">\n<div><img style=\"margin: 5px;\" src=\"http://k14.vcmedia.vn/Images/Uploaded/Share/2010/08/03/44c040810DSvietblog01.jpg\" alt=\"\" width=\"550\" align=\"center\" /></div>\n<div><span style=\"font-style: italic; font-weight: normal;\">Nếu viết theo c&aacute;ch suy nghĩ của bản th&acirc;n với nội dung thật diễn đạt biết đ&acirc;u bạn sẽ nhận được một m&oacute;n tiền thưởng nho nhỏ.</span></div>\n</span>\n<p><span style=\"font-size: small;\">Blog l&agrave; nơi ch&uacute;ng m&igrave;nh c&oacute; thể viết ra tất cả mọi suy  nghĩ nếu muốn. Ph&agrave;n n&agrave;n về một &yacute; tưởng dịch vụ tồi của&nbsp;một sản phẩm  n&agrave;o&nbsp;đấy&nbsp;hay x&uacute;c động về một bộ phim, ng&uacute;ng nguẩy với một loại sữa vừa  được b&aacute;n ra thị trường... Đ&oacute; l&agrave; cơ hội kiếm tiền lớn cho c&aacute;c teen ở nước  ngo&agrave;i. Một teen blogger ở <span style=\"font-weight: bold;\">Mỹ </span>đ&atilde;&nbsp;từng&nbsp;được một h&atilde;ng nước giải kh&aacute;t nổi tiếng&nbsp;<span style=\"font-weight: bold;\">7Up</span> gọi đến thưởng c&ocirc;ng khi g&oacute;p phần &ldquo;khen ngợi&rdquo; một sản phẩm mới của h&atilde;ng n&agrave;y. Đủ để biết bạn ấy đ&atilde; bất ngờ ra sao.</span></p>\n<p><span style=\"font-size: small;\">C&ograve;n đối với những bạn&nbsp;teen&nbsp;ở nước&nbsp;ta&nbsp;th&igrave; sao nhỉ? Với  những bạn c&oacute; kĩ năng tiếng Anh v&agrave; khả năng ph&acirc;n t&iacute;ch tốt, th&igrave; thật  chẳng c&oacute; c&ocirc;ng việc n&agrave;o ph&ugrave; hợp hơn. Ban ng&agrave;y đi học, buổi tối đọc v&agrave;  nghi&ecirc;n cứu viết một blog thật để đời, dần dần mọi người sẽ biết tới bạn,  v&agrave; biết đ&acirc;u đấy, với ch&uacute;t may mắn, trong t&agrave;i khoản sẽ c&oacute; &ldquo;tiền đ&ocirc;&rdquo;.</span></p>\n<p><span style=\"font-size: small;\">Với những blogger <span style=\"font-weight: bold;\">Việt</span>,  th&igrave; đời sống c&oacute; v&ocirc; c&ugrave;ng nhiều điều cho bạn kh&aacute;m ph&aacute;. Vừa để luyện tay  nghề viết l&aacute;ch! Vừa luyện mắt quan s&aacute;t, vừa luyện khả năng xử l&iacute; nhanh!  Thử viết về <span style=\"font-weight: bold;\">S&agrave;i G&ograve;n </span>về đ&ecirc;m, hay nh&igrave;n nhận về một <span style=\"font-weight: bold;\">S&agrave;i G&ograve;n</span> cổ điển vừa bị đập n&aacute;t, hay về một <span style=\"font-weight: bold;\">H&agrave; Nội </span>chẳng  c&ograve;n m&agrave;u xưa cũ&hellip; Th&ecirc;m một v&agrave;i bức ảnh cụ thể v&agrave; s&aacute;t sao, vậy l&agrave; ch&uacute;ng  m&igrave;nh vừa ghi lại được một khoảnh khắc &ldquo;lịch sử&rdquo;m&agrave; ho&agrave;n to&agrave;n bằng cảm x&uacute;c  ri&ecirc;ng đấy nh&eacute;.</span></p>\n<div><span style=\"font-size: small;\">Hay thử ph&acirc;n t&iacute;ch dịch vụ đưa đồ ăn tận nh&agrave; của <span style=\"font-weight: bold;\">KFC,</span> hay trổ t&agrave;i l&agrave;m một nh&agrave; gi&aacute;m định sữa, hay đi post comment giới thiệu  sản phẩm online&hellip; Tất cả những nhận x&eacute;t hữu &iacute;ch tới thị trường <span style=\"font-weight: bold;\">Việt Nam </span>tr&ecirc;n  tất cả mọi mặt h&agrave;ng đều được ghi nhận. Viết về n&oacute; bằng ch&iacute;nh suy nghĩ  của bạn sẽ nhanh ch&oacute;ng được &ldquo;truyền miệng&rdquo; hơn việc xem một quảng c&aacute;o  tr&ecirc;n truyền h&igrave;nh.</span></div>\n<p><span style=\"font-size: small;\">Cho c&aacute;c Teen ham th&iacute;ch tiếng Anh v&agrave; kiếm tiền, c&aacute;c bạn thử v&agrave;o địa chỉ n&agrave;y nh&eacute;: <a href=\"http://www.blog-republic.com/\" target=\"_blank\">www.blog-republic.com</a> hay <a href=\"https://payperpost.com/\" target=\"_blank\">https://payperpost.com</a><a href=\"https://payperpost.com/\">. </a>Ở  đ&acirc;y tha hồ cơ hội cho c&aacute;c bạn &ldquo;tung chưởng&rdquo; tiếng Anh v&agrave; khả năng ph&acirc;n  t&iacute;ch của m&igrave;nh. Chủ đề cần viết rất đa dạng, từ điện thoại đến du lịch,  từ sức khỏe đến m&oacute;n ăn &hellip;</span></p>\n<p><span style=\"font-weight: bold; font-size: small;\">V&agrave; kết quả c&ograve;n nhiều hơn thế</span></p>\n<p><span style=\"font-size: small;\">Viết blog thu&ecirc; sẽ cho ch&uacute;ng m&igrave;nh nhiều hơn l&agrave; những  g&igrave; ta tưởng. Kiến thức sẽ một ng&agrave;y một lấp đầy l&ecirc;n. Sự cạnh tranh trong  tư duy sẽ tự khẳng định vị tr&iacute; của teen blogger. Sự cạnh tranh ấy sẽ  gi&uacute;p ch&uacute;ng m&igrave;nh c&oacute; th&ecirc;m nhiều người bạn cực cool. Chưa hết, những phản  hồi đa chiều từ c&aacute;c comment sẽ cho ch&uacute;ng m&igrave;nh nhiều &yacute; tưởng v&agrave; c&oacute; khi l&agrave;  &ldquo;giật thon th&oacute;t&rdquo; ấy chứ.</span></p>\n<p><span style=\"font-size: small;\">V&agrave; nhiều hơn thế nữa. &Yacute; tưởng dần dần được bật ra  ngay tắp tự trong đầu, v&igrave; ch&uacute;ng m&igrave;nh đ&atilde; tự r&egrave;n luyện được th&oacute;i quen quan  s&aacute;t rồi m&agrave;. G&oacute;c nh&igrave;n về sự việc, sự vật sẽ theo đ&oacute; m&agrave; sắc cạnh hơn v&agrave;  nhiều mặt hơn.</span></p>\n<div><span style=\"font-size: small;\">Nếu ch&uacute;ng m&igrave;nh ham th&iacute;ch, sẽ thấy đ&acirc;y l&agrave; một dạng  thức marketing nổi bật b&acirc;y giờ, hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; một h&igrave;nh thức PR c&oacute;  lợi &ldquo;kh&ocirc;ng tưởng tượng nổi&rdquo;. Nếu đi s&acirc;u v&agrave;o nữa, sẽ thấy những loại  h&igrave;nh blog thu&ecirc; kh&aacute;c nữa, thậm ch&iacute; c&oacute; cả việc &ldquo;đ&oacute;ng vai&rdquo; một nh&acirc;n vật n&agrave;o  đ&oacute; v&agrave; chăm lo tươm tất cho blog của họ kia.</span></div>\n<div>&nbsp;</div>\n<div><span style=\"font-size: small;\"><img style=\"margin: 5px;\" src=\"http://k14.vcmedia.vn/Images/Uploaded/Share/2010/08/03/545040810DSvietblog04.jpg\" alt=\"\" width=\"450\" align=\"center\" /></span></div>\n<div><span style=\"font-style: italic; font-size: small;\">Viết blog kh&ocirc;ng chỉ l&agrave; một nghề thu h&uacute;t c&aacute;c bạn trẻ từ rất l&acirc;u, m&agrave; n&oacute; c&ograve;n kh&ocirc;ng bao giờ lỗi thời cả.</span></div>\n<p><span style=\"font-size: small;\">Nhưng viết blog th&igrave; phải c&oacute; thời gian v&agrave; cũng cần r&egrave;n  giũa lời lẽ đủ truyền cảm để thu h&uacute;t người đọc đến với &ldquo;tờ b&aacute;o&rdquo; c&aacute; nh&acirc;n  của m&igrave;nh nữa đấy. Nếu h&ocirc;m nay giọng văn của ch&uacute;ng m&igrave;nh chưa đủ độ, th&igrave;  học tập từ những b&agrave;i b&aacute;o kh&aacute;c sẽ gi&uacute;p &ldquo;tăng tr&igrave;nh&rdquo; ngay. Sau một thời  gian &ldquo;chạy nước r&uacute;t&rdquo; với c&aacute;c deadline của người đặt h&agrave;ng, ch&uacute;ng m&igrave;nh tha  hồ kiểm so&aacute;t thời gian ung dung m&agrave; chẳng sợ &ldquo;vắt ch&acirc;n l&ecirc;n cổ nữa&rdquo;. Thật  c&oacute; &iacute;ch phải kh&ocirc;ng?</span></p>\n<p><span style=\"font-size: small;\">Bước v&agrave;o một nghề mới, ch&uacute;ng m&igrave;nh sẽ kh&ocirc;ng khỏi vấp  phải những mặt tr&aacute;i của n&oacute;. Nhưng h&atilde;y thử xem nh&eacute;, mọi thứ c&ograve;n chờ đợi  trước mắt ch&uacute;ng m&igrave;nh m&agrave;, phải kh&ocirc;ng?</span></p>\n</div>','2011-03-14 22:37:55','admin@jobbid.vn',269,1,NULL),(15,'mot-cong-viec-part-time-thu-vi!','Một công việc part time thú vị!',0,'Trong các trò chơi truyền hình, hoặc các sự kiện văn hóa ta thường bắt gặp hình ảnh những bạn trẻ cổ vũ rất nhiệt tình, bài bản và chuyên nghiệp. Họ là những hoạt náo viên làm part time - những người “truyền lửa” cho các showgame.','<p><span style=\"font-size: small; font-family: tahoma,arial,helvetica,sans-serif;\"><strong><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"Một c&ocirc;ng việc parttime th&uacute; vị\" src=\"http://www2.vietbao.vn/images/viet3/viec_lam/30196146_cheerer2.jpg\" alt=\"Một c&ocirc;ng việc parttime th&uacute; vị\" width=\"300\" height=\"210\" /><span style=\"color: #888888;\">Trong c&aacute;c tr&ograve; chơi truyền h&igrave;nh, hoặc c&aacute;c sự kiện văn h&oacute;a ta thường bắt gặp h&igrave;nh ảnh những bạn trẻ cổ vũ rất nhiệt t&igrave;nh, b&agrave;i bản v&agrave; chuy&ecirc;n nghiệp. Họ l&agrave; những hoạt n&aacute;o vi&ecirc;n - những người &ldquo;truyền lửa&rdquo; cho c&aacute;c showgame.</span></strong></span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Hai năm trở lại đ&acirc;y được coi l&agrave; sự b&ugrave;ng nổ của c&aacute;c showgame tr&ecirc;n truyền h&igrave;nh, c&aacute;c sự kiện do c&aacute;c c&ocirc;ng ty tổ chức. V&agrave; để cho c&aacute;c chương tr&igrave;nh n&agrave;y thực sự s&ocirc;i động, hấp dẫn, kh&ocirc;ng thể thiếu được h&igrave;nh ảnh của đội cổ vũ.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Để lại ấn tượng cho người xem truyền h&igrave;nh trong mỗi chương tr&igrave;nh, ngo&agrave;i c&aacute;c đội chơi c&ograve;n l&agrave; h&igrave;nh ảnh của những kh&aacute;n giả nhiệt t&igrave;nh ph&iacute;a dưới h&ograve; reo, tay cầm những quả b&ocirc;ng lắc lư theo điệu nhạc để cổ vũ suốt buổi cho những người chơi truyền h&igrave;nh.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Đ&oacute; ch&iacute;nh l&agrave; những hoạt n&aacute;o vi&ecirc;n hay c&ograve;n gọi l&agrave;&nbsp;<em>cheerer</em>, những người \"truyền lửa\" để tạo ra kh&ocirc;ng kh&iacute; vui tươi, phấn kh&iacute;ch trong c&aacute;c showgame. Hiện nay, l&agrave;m hoạt n&aacute;o vi&ecirc;n cũng l&agrave; một c&ocirc;ng việc l&agrave;m th&ecirc;m được rất nhiều bạn trẻ lựa chọn.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Ấn tượng của kh&aacute;n giả khi đi xem quay chương tr&igrave;nh \"Ai l&agrave; triệu ph&uacute;\" l&agrave; h&igrave;nh ảnh một anh ch&agrave;ng đi lại li&ecirc;n tục hướng dẫn kh&aacute;n giả chỗ ngồi, sau đ&oacute; lại huơ tay ra k&yacute; hiệu \"Vỗ tay n&agrave;o c&aacute;c bạn ơi!\", \"Vỗ tay to l&ecirc;n n&agrave;o\"... V&agrave; rồi ph&iacute;a dưới kh&aacute;n giả rất đ&ocirc;ng người c&ugrave;ng l&agrave;m theo hiệu lệnh một c&aacute;ch thuần thục tạo ra một kh&ocirc;ng kh&iacute; thật s&ocirc;i động. Đ&oacute; ch&iacute;nh l&agrave; những hoạt n&aacute;o vi&ecirc;n truyền h&igrave;nh.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Mặc d&ugrave; đối tượng ch&iacute;nh của showgame l&agrave; kh&aacute;n giả xem truyền h&igrave;nh nhưng kh&aacute;n giả tại trường quay lại c&oacute; một &yacute; nghĩa v&ocirc; c&ugrave;ng đặc biệt. Sự c&oacute; mặt trực tiếp của những cổ động vi&ecirc;n tại trường quay cho thấy sức sống của showgame cũng như việc \"tiếp lửa\" cho những người chơi v&agrave; những vị kh&aacute;ch truyền h&igrave;nh.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Nếu một chương tr&igrave;nh quay m&agrave; mọi h&agrave;nh động, mọi lời n&oacute;i đều rơi v&agrave;o y&ecirc;n lặng th&igrave; sẽ rất tẻ nhạt. Ch&iacute;nh những tiếng vỗ tay, tiếng h&ograve; la thậm ch&iacute; l&agrave; tiếng hu&yacute;t s&aacute;o đ&atilde; tạo th&ecirc;m sức sống, động lực cho những người chơi v&agrave; sự sinh động cho mỗi chương tr&igrave;nh.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Để ghi h&igrave;nh một chương tr&igrave;nh, c&aacute;c đạo diễn showgames đều rất coi trọng v&agrave; chăm ch&uacute;t kỹ lưỡng mỗi h&igrave;nh ảnh về c&aacute;c cổ động vi&ecirc;n. Khi người chơi xuất hiện, kh&aacute;n giả vỗ tay cuồng nhiệt. Người chơi thất bại, kh&aacute;n giả &ldquo;ồ&rdquo; l&ecirc;n tiếc nuối. Người chơi chiến thắng, kh&aacute;n giả c&ugrave;ng h&ograve; h&eacute;t chung vui...</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">V&agrave; tất nhi&ecirc;n, tất cả mọi sự đồng bộ v&agrave; ăn khớp n&agrave;y đều dưới sự chỉ đạo của một người l&agrave; trợ l&yacute; trường quay ki&ecirc;m nhiệm quản l&yacute; chỉ đạo c&aacute;c họat n&aacute;o vi&ecirc;n gọi chung l&agrave;<em>cheermaneger</em>.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Th&ocirc;ng thường, nh&agrave; đ&agrave;i thường cần một số lượng lớn những hoạt n&aacute;o vi&ecirc;n nhiệt t&igrave;nh để phụ vụ cho c&aacute;c showgame n&ecirc;n đ&acirc;y cũng l&agrave; cơ hội cho c&aacute;c bạn học sinh, sinh vi&ecirc;n tranh thủ l&agrave;m th&ecirc;m. C&ocirc;ng việc của họ l&agrave; người cổ vũ chuy&ecirc;n nghiệp của nh&agrave; đ&agrave;i: đến ngồi xem chương tr&igrave;nh v&agrave; vỗ tay.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Th&ugrave; lao trung b&igrave;nh một cheerer l&agrave; 20.000-30.000 đồng cho 1 gameshow trong trường quay.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Bạn Minh Phương (SV Học viện B&aacute;o ch&iacute; &amp; Tuy&ecirc;n truyền), một cheerer của VTV cho biết: Được bạn b&egrave; giới thiệu, m&igrave;nh cũng đến đăng k&yacute; l&agrave;m cheerer cho một số chương tr&igrave;nh showgame. Cảm gi&aacute;c đầu ti&ecirc;n l&agrave; rất vui v&igrave; m&igrave;nh được tận mắt xem, thấy được kh&ocirc;ng kh&iacute; của chương tr&igrave;nh. Mỗi khi c&oacute; đội thắng lại reo h&ograve; cổ vũ nhiệt t&igrave;nh rất th&uacute; vị.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Tuy nhi&ecirc;n, cũng kh&ocirc;ng &iacute;t cheerer than thở v&igrave; một chương tr&igrave;nh quay thường mất 3 tiếng đồng hồ m&agrave; lu&ocirc;n phải giữ khu&ocirc;n mặt tươi tỉnh lại phải tập trung cao độ để phối hợp với đồng đội được ăn khớp, nhịp nh&agrave;ng. Chỉ cần một bạn l&agrave;m kh&ocirc;ng đ&uacute;ng th&igrave; cả đội phải diễn đi diễn lại.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">C&ograve;n với những cheerer được chọn cho buổi quay trực tiếp, y&ecirc;u cầu cũng cao hơn: Quen việc, c&oacute; &yacute; thức tổ chức v&agrave; l&agrave;m việc theo nh&oacute;m bởi họ sẽ kh&ocirc;ng c&oacute; cơ hội diễn lại như trong trường quay. V&agrave; tất nhi&ecirc;n, thu nhập cũng cao hơn, thường l&agrave; 50.000 đồng/buổi.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Chỉ l&agrave;m việc b&aacute;n thời gian, v&igrave; vậy những cheer l&agrave; c&aacute;c học sinh, sinh vi&ecirc;n c&oacute; thể tranh thủ sắp xếp thời gian học tập để tham gia l&agrave;m hoạt n&aacute;o vi&ecirc;n, vừa kiếm th&ecirc;m thu nhập, lại vừa gi&uacute;p tự tin hơn trong giao tiếp v&agrave; c&aacute;c hoạt động tập thể.</span></p>\n<p><span style=\"font-family: tahoma,arial,helvetica,sans-serif; font-size: small;\">Điều th&uacute; vị nhất của c&ocirc;ng việc n&agrave;y l&agrave; được sống trong bầu kh&ocirc;ng kh&iacute; vui tươi, s&ocirc;i nổi, thoải m&aacute;i. Mang đến sự s&ocirc;i động tươi vui cho chương tr&igrave;nh đ&oacute; cũng l&agrave; th&agrave;nh c&ocirc;ng của c&aacute;c hoạt n&aacute;o vi&ecirc;n.</span></p>\n<p><span style=\"font-size: small; font-family: tahoma,arial,helvetica,sans-serif;\"><strong>Đức Huy</strong></span></p>\n<p><span style=\"font-size: small; font-family: tahoma,arial,helvetica,sans-serif;\">Việt B&aacute;o&nbsp;(Theo_DanTri)</span></p>','2011-03-16 15:20:12','admin@jobbid.vn',409,1,NULL),(16,'bi-hai-chuyen-lam-them-cua-nu-sinh-vien','Bi hài chuyện làm thêm của nữ sinh viên',0,'Bị học trò gửi thư tình, khách hàng rủ xem phim sex hoặc gạ\"vui vẻ\" sau giờ làm... là tình huống \"dở khóc, dở cười\" của nhiều nữ sinh viên đi làm thêm những công việc bán thời gian.','<p class=\"Lead\"><span style=\"color: #888888;\"><strong>Bị học tr&ograve; gửi thư t&igrave;nh, kh&aacute;ch h&agrave;ng rủ xem phim sex hoặc  gạ\"vui vẻ\" sau giờ l&agrave;m... l&agrave; t&igrave;nh huống \"dở kh&oacute;c, dở cười\" của nhiều nữ  sinh vi&ecirc;n đi l&agrave;m th&ecirc;m.</strong></span></p>\n<p class=\"Normal\">Mới \"ch&acirc;n ướt ch&acirc;n r&aacute;o\" l&ecirc;n TP HCM học đại học năm thứ  nhất nhưng do gia cảnh kh&oacute; khăn, Mai chạy đ&ocirc;n đ&aacute;o kiếm th&ecirc;m việc l&agrave;m  b&aacute;n thời gian cho một tiệm Internet.</p>\n<p class=\"Normal\">Buổi chiều cuối tuần, một thanh ni&ecirc;n bước v&agrave;o qu&aacute;n  thu&ecirc; m&aacute;y. Sau khi nhờ c&ocirc; nh&acirc;n vi&ecirc;n khởi động m&aacute;y, anh ta hồn nhi&ecirc;n hỏi:  \"Em b&aacute;n th&ecirc;m cho anh c&aacute;i nick chat nữ nh&eacute;\", khiến Mai ngớ người, kh&ocirc;ng  biết giải th&iacute;ch thế n&agrave;o. C&ocirc; nh&acirc;n vi&ecirc;n sau đ&oacute; c&ograve;n mất cả tiếng đồng hồ để  dạy vị kh&aacute;ch c&aacute;ch chat tr&ecirc;n mạng.</p>\n<p class=\"Normal\">Cũng theo nữ sinh n&agrave;y, chuyện kh&aacute;ch nam v&agrave;o tiệm chỉ  để xem web đen l&agrave; thường xuy&ecirc;n, c&oacute; lần nhắc nhở c&ograve;n bị họ mắng. \"C&oacute;  người c&ograve;n k&ecirc;u em tới m&aacute;y khi tr&ecirc;n m&agrave;n h&igrave;nh l&agrave; h&igrave;nh ảnh đ&ocirc;i nam nữ l&agrave;m  t&igrave;nh khiến em t&aacute;i cả mặt. Quay vội đi nhưng anh ta cứ k&eacute;o tay đ&ograve;i em  ngồi coi chung khiến bao nhi&ecirc;u cặp mắt dồn v&agrave;o em\", Mai chia sẻ.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/EE/4A/hinh%5B1%5D.jpg\" border=\"1\" alt=\"\" width=\"350\" height=\"503\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">L&agrave;m gia sư, tiếp thị, PG... nhiều sinh vi&ecirc;n gặp những t&igrave;nh huống \"cười ra nước mắt\". Ảnh minh họa: <em>An Nhơn.</em></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Chuyện dạy th&ecirc;m của Ng&acirc;n, sinh vi&ecirc;n năm thứ ba Đại học  sư phạm cũng c&oacute; t&igrave;nh huống \"bối rối\". C&ocirc; nhận phụ đạo cho v&agrave;i học sinh  đủ c&aacute;c cấp nhưng c&oacute; cậu học sinh lớp 10 th&igrave; tỏ ra \"đặc biệt\" hơn cả. H&ocirc;m  n&agrave;o dạy xong, cậu n&agrave;y cũng nằn n&igrave; rủ c&ocirc; gi&aacute;o đi uống nước cho vui.</p>\n<p class=\"Normal\">\"Nghĩ cũng l&agrave; để tạo mối quan hệ th&acirc;n mật với tr&ograve;, ai  ngờ h&ocirc;m nọ dạy về mở c&aacute;i giỏ x&aacute;ch ra thấy một l&aacute; thư d&agrave;i với những lời  lẽ y&ecirc;u thương, lặp đi lặp lại c&acirc;u em y&ecirc;u c&ocirc; nhiều lắm v&agrave; kh&ocirc;ng thể sống  thiếu c&ocirc;&hellip; khiến m&igrave;nh rất ngỡ ng&agrave;ng&rdquo;, Ng&acirc;n kể. Sau h&ocirc;m đ&oacute;, c&ocirc; gia sư vẫn  cố gắng đi dạy nhưng sợ học tr&ograve; nơm nớp, chỉ mong sao cho nhanh hết  th&aacute;ng để nghỉ.</p>\n<p class=\"Normal\">Đối với nhiều nữ sinh ở S&agrave;i G&ograve;n, PG (Promotion Girl)  l&agrave; nghề part - time l&yacute; tưởng. &ldquo;Nguy&ecirc;n tắc v&agrave;ng của nghề l&agrave; lu&ocirc;n lu&ocirc;n  cười d&ugrave; c&oacute; đứng 8 tiếng hay nhận những lời lẽ tr&ecirc;u chọc th&ocirc; thiển... Đặc  biệt, những người đ&agrave;n &ocirc;ng từ gi&agrave; đến trẻ đều phải gọi l&agrave; anh&rdquo;, Ngọc,  sinh vi&ecirc;n Đại học Khoa học x&atilde; hội v&agrave; nh&acirc;n văn chia sẻ.</p>\n<p class=\"Normal\">Chẳng phải ng&agrave;y n&agrave;o cũng c&oacute; &ldquo;s&ocirc;&rdquo; để l&agrave;m th&ecirc;m nhưng mỗi  th&aacute;ng, từ PG hội chợ, PG Game, tiếp thị bia... Ngọc cũng đ&atilde; kiếm th&ecirc;m  khoảng 3 triệu để trang trải cho việc học. Nhưng để c&oacute; được con số n&agrave;y,  c&ocirc; cũng đ&atilde; nhiều lần rơi nước mắt.</p>\n<p class=\"Normal\">&ldquo;Mặc những bộ trang phục ngắn cũn trước h&agrave;ng trăm con  mắt lu&ocirc;n chăm ch&uacute; h&igrave;nh v&agrave;o những phần nhạy cảm&hellip; khiến m&igrave;nh đ&ocirc;i khi ứa  nước mắt&rdquo;, c&ocirc; g&aacute;i chia sẻ.</p>\n<p class=\"Normal\">L&ecirc; Thu, sinh vi&ecirc;n trường Luật TP HCM cũng c&oacute; đ&ocirc;i lần  rơi v&agrave;o trạng th&aacute;i &ldquo;dở cười dở kh&oacute;c&rdquo; khi nhận l&agrave;m PG cho một Game  Online. Đang cười tươi r&oacute;i, nh&uacute;n nhảy theo tiếng nhạc, một g&atilde; thanh ni&ecirc;n  d&iacute; bộ mặt nham nhở v&agrave;o s&aacute;t \"khu vực nhảy cảm\" của Thu, lại c&ograve;n đưa tay  vuốt đ&ugrave;i c&ocirc; nhưng Thu chỉ \"chỉ biết cắn răng chịu đựng m&agrave; kh&ocirc;ng d&aacute;m phản  ph&aacute;o\". C&oacute; lần, c&ocirc; lại nhận được mảnh giấy với những d&ograve;ng chữ: \"Xong  chương tr&igrave;nh n&agrave;y, đi với anh nh&eacute;, chắc chắn em sẽ c&oacute; th&ecirc;m nhiều tiền\".</p>\n<p class=\"Normal\">Vy, sinh vi&ecirc;n Đại học Khoa học Tự nhi&ecirc;n cho biết, c&oacute;  thời gian c&ocirc; \"ph&aacute;t đi&ecirc;n v&igrave; tin nhắn điện thoại\". Trong một lần tiếp thị,  c&ocirc; cũng gặp phải một kh&aacute;ch h&agrave;ng \"nham nhở\". Chẳng biết bằng c&aacute;ch n&agrave;o,  anh ta c&oacute; được số điện thoại v&agrave; li&ecirc;n tục gửi c&aacute;c tin nhắn bất kể ng&agrave;y  đ&ecirc;m, đại loại như: &ldquo;Em đẹp qu&aacute; cho anh l&agrave;m quen&rdquo;, &ldquo;Đi với anh kh&ocirc;ng em&rdquo;,  hay &ldquo;ngực em to qu&aacute;&rdquo;&hellip;</p>\n<p class=\"Normal\">Tuy vậy, c&ocirc; nữ sinh n&agrave;y cũng nh&igrave;n nhận, d&ugrave; c&oacute; lắm  những lo toan v&agrave; những vui buồn trong c&ocirc;ng việc l&agrave;m th&ecirc;m, nhưng ch&iacute;nh  những điều đ&oacute; sẽ l&agrave; h&agrave;nh trang kiến thức, kinh nghiệm thực tế v&ocirc; c&ugrave;ng  qu&yacute; b&aacute;u cho c&aacute;c bạn sinh vi&ecirc;n trước khi bước v&agrave;o đời.</p>\n<p class=\"Normal\"><strong>Minh Thư&nbsp;</strong><em> (theo vnexpress.net)</em><strong><br /></strong></p>','2011-03-18 16:37:46','admin@jobbid.vn',323,1,NULL),(17,'sinh-vien-tinh-le-khat-viec-lam-them','Sinh viên tỉnh lẻ khát việc làm thêm',0,'Số tiền mà gia đình ở quê gửi lên không đầy cho các khoản chi tiêu hàng tháng tại thành phố, khi mà giá cả tăng lên hàng ngày. Không còn cách nào khác nhiều sinh viên (SV) tại tỉnh lẻ tìm việc để làm thêm làm bán thời gian nhưng quá chật vật…','<div class=\"fon33 mt1\"><span style=\"color: #888888;\"><strong>(D&acirc;n tr&iacute;) - Số tiền m&agrave; gia đ&igrave;nh ở qu&ecirc; gửi l&ecirc;n  kh&ocirc;ng đầy cho c&aacute;c khoản chi ti&ecirc;u h&agrave;ng th&aacute;ng tại th&agrave;nh phố, khi m&agrave; gi&aacute; cả  tăng l&ecirc;n h&agrave;ng ng&agrave;y. Kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c nhiều sinh vi&ecirc;n (SV) tại  tỉnh lẻ t&igrave;m việc để <a title=\"l&agrave;m th&ecirc;m l&agrave;m b&aacute;n thời gian\" href=\"http://www.jobbid.vn\">l&agrave;m th&ecirc;m l&agrave;m b&aacute;n thời gian</a> nhưng qu&aacute; chật vật&hellip;</strong></span></div>\n<div><span style=\"font-weight: bold;\">&ldquo;Đỏ mắt&rdquo; t&igrave;m việc l&agrave;m th&ecirc;m</span>&nbsp;</div>\n<p>Để chống chọi với cuộc sống chật vật nơi thị th&agrave;nh khi m&agrave; gi&aacute; cả  tăng, nhiều SV theo học c&aacute;c trường Đại học, Cao đẳng tr&ecirc;n địa b&agrave;n th&agrave;nh  phố Phủ L&yacute; (H&agrave; Nam) đổ x&ocirc; đi t&igrave;m việc l&agrave;m th&ecirc;m để kiếm tiền trang trải  cuộc sống v&agrave; học tập.</p>\n<div>Bạn Lại Thị Thảo Vy, SV trường CĐ Ph&aacute;t thanh Truyền h&igrave;nh 1 t&acirc;m sự:  &ldquo;Năm ngo&aacute;i trở về trước th&igrave; c&ograve;n đỡ chứ kể từ sau Tết, đặc biệt l&agrave; từ h&ocirc;m  xăng dầu tăng gi&aacute; th&igrave; mọi thứ đều tăng theo như gi&aacute; ph&ograve;ng trọ, điện  nước &hellip;đều tăng theo. &ldquo;Lương&rdquo; h&agrave;ng th&aacute;ng bố mẹ gửi l&ecirc;n th&igrave; vẫn như trước  n&ecirc;n chi ti&ecirc;u kh&ocirc;ng đủ, em đ&atilde; t&iacute;nh t&igrave;m việc l&agrave;m th&ecirc;m nhưng ở đ&acirc;y đi t&igrave;m  việc l&agrave;m ph&ugrave; hợp kh&oacute; qu&aacute;, em t&igrave;m mấy h&ocirc;m rồi vẫn chưa c&oacute; việc g&igrave;&rdquo;.</div>\n<div>&nbsp;</div>\n<div><img style=\"margin: 5px;\" src=\"http://dantri.vcmedia.vn/Uploaded/2011/03/18/IMG_0144.jpg\" alt=\"\" width=\"450\" align=\"center\" /></div>\n<div><span style=\"font-family: Tahoma; font-size: 10pt;\">Nhiều SV may mắn lắm mới t&igrave;m được chỗ l&agrave;m th&ecirc;m</span>&nbsp;</div>\n<div>&nbsp;</div>\n<div>Kh&aacute;c xa với H&agrave; Nội v&agrave; một số th&agrave;nh phố lớn kh&aacute;c, SV học tập ở Phủ  L&yacute; rất kh&oacute; t&igrave;m cho m&igrave;nh một c&ocirc;ng việc ph&ugrave; hợp để l&agrave;m th&ecirc;m. Vẻ mặt buồn  rầu v&agrave; mệt mỏi sau hai ng&agrave;y đạp xe đạp rong ruổi tr&ecirc;n c&aacute;c ng&otilde; phố để t&igrave;m  việc, bạn Trần Phương Ngọc, SV Cơ sở 2, trường ĐH C&ocirc;ng nghiệp H&agrave; Nội  n&oacute;i:</div>\n<div>&nbsp;</div>\n<div>&ldquo;Đ&atilde; hai h&ocirc;m nay em đạp xe đi gần như khắp c&aacute;c con đường tr&ecirc;n th&agrave;nh  phố, v&agrave;o c&aacute;c qu&aacute;n c&agrave; ph&ecirc;, nh&agrave; h&agrave;ng, qu&aacute;n ăn để hỏi nhưng tất cả đều  kh&ocirc;ng tuyển nh&acirc;n vi&ecirc;n nữa. Mệt lắm, ng&agrave;y mai em đi th&ecirc;m h&ocirc;m nữa m&agrave; kh&ocirc;ng  t&igrave;m được th&igrave; th&ocirc;i&rdquo;.</div>\n<p>&ldquo;Trước đ&acirc;y, mỗi th&aacute;ng gia đ&igrave;nh gửi cho 1,3 - 1,6 triệu đồng, chi ti&ecirc;u  tiết kiệm h&agrave;ng th&aacute;ng cũng tạm đủ. Nhưng gần đ&acirc;y gi&aacute; cả mọi thứ đều tăng  n&ecirc;n d&ugrave; tiết kiệm thế n&agrave;o cũng kh&ocirc;ng đủ. M&agrave; cứ xin th&ecirc;m bố mẹ th&igrave; cũng  kh&oacute;, v&igrave; ho&agrave;n cảnh gia đ&igrave;nh ở qu&ecirc; cũng kh&oacute; khăn. C&aacute;ch tốt nhất l&agrave; phải đi  l&agrave;m th&ecirc;m&rdquo;, đ&oacute; l&agrave; t&acirc;m sự chung của rất nhiều SV.</p>\n<div>Chưa bao giờ ở th&agrave;nh phố Phủ L&yacute; lại c&oacute; nhiều SV đi t&igrave;m việc l&agrave;m  th&ecirc;m như hiện nay. Vừa cất tấm bảng hiệu viết mấy chữ sơ s&agrave;i th&ocirc;ng b&aacute;o  tuyển nh&acirc;n vi&ecirc;n b&aacute;n quần &aacute;o, b&agrave; Phạm Thị Trung - một chủ cửa h&agrave;ng shop  quần &aacute;o tr&ecirc;n đường L&ecirc; C&ocirc;ng Thanh, th&agrave;nh phố Phủ L&yacute; n&oacute;i:</div>\n<div>&nbsp;</div>\n<div>&ldquo;T&ocirc;i mới đăng biển tuyển nh&acirc;n vi&ecirc;n chưa được một ng&agrave;y đ&atilde; c&oacute; mấy  chục em SV đến hỏi việc. T&ocirc;i cần tuyển c&oacute; hai người n&ecirc;n đ&atilde; đủ từ h&ocirc;m  qua, nhưng bận qu&aacute; chưa cất được biển th&ocirc;ng b&aacute;o n&ecirc;n mới s&aacute;ng sớm nay đ&atilde;  c&oacute; mấy em qua hỏi rồi, b&acirc;y giờ phải cất đi kh&ocirc;ng mất c&ocirc;ng th&ecirc;m nhiều  người v&agrave;o hỏi&rdquo;.</div>\n<div>&nbsp;</div>\n<div>\n<div><img src=\"http://dantri.vcmedia.vn/Uploaded/2011/03/18/IMG_0004.JPG\" alt=\"\" width=\"450\" align=\"center\" /></div>\n<div><span style=\"font-family: Tahoma; font-size: 10pt;\">Gi&aacute;&nbsp;cả tăng khiến nhiều&nbsp;SV rất chật vật.</span></div>\n</div>\n<p><span style=\"font-weight: bold;\">Nai lưng l&agrave;m th&ecirc;m nhưng&hellip;nhận lương &ldquo;b&egrave;o&rdquo;</span></p>\n<div>C&ocirc;ng việc dễ t&igrave;m v&agrave; ph&ugrave; hợp nhất của SV đ&oacute; l&agrave; đi gia sư, nhưng ở  Phủ L&yacute; c&ocirc;ng việc n&agrave;y rất &iacute;t. Quanh đi quẩn lại th&igrave; SV cũng chỉ c&oacute; thể  l&agrave;m th&ecirc;m những việc như b&aacute;n qu&aacute;n c&agrave; ph&ecirc;, phục vụ nh&agrave; h&agrave;ng, qu&aacute;n ăn; cửa  h&agrave;ng quần &aacute;o. Đ&atilde; thế mức lương cũng kh&aacute; b&egrave;o bọt v&agrave; kh&ocirc;ng xứng đ&aacute;ng với  sức lực, thời gian m&agrave; những SV n&agrave;y bỏ ra.</div>\n<p>Vũ Thị Thảo, SV trường CĐ Thuỷ lợi Bắc Bộ đi l&agrave;m cho một nh&agrave; h&agrave;ng ăn  tr&ecirc;n đường Nguyễn Viết Xu&acirc;n t&acirc;m sự: &ldquo;Em l&agrave;m ca từ 13h -22h, mỗi th&aacute;ng  được 600.000đ. C&ocirc;ng việc từ bưng b&ecirc; cho đến rửa b&aacute;t em đều c&oacute; phần. Đ&atilde;  thế những l&uacute;c ốm đau đột xuất xin nghỉ nhưng chủ qu&aacute;n kh&ocirc;ng cho v&agrave; nếu  c&oacute; th&igrave; c&ograve;n bị trừ lương nữa. N&oacute;i chung l&agrave; kh&ocirc;ng c&oacute; th&aacute;ng n&agrave;o được nhận  đủ 600.000đ cả&rdquo;.</p>\n<div>Tuy nhi&ecirc;n kh&ocirc;ng phải SV n&agrave;o t&igrave;m được việc l&agrave;m th&ecirc;m cũng bớt vất vả  hơn, m&agrave; việc l&agrave;m th&ecirc;m lại ảnh hưởng rất lớn đến qu&aacute; tr&igrave;nh học tập. Những  SV đi l&agrave;m th&ecirc;m dường như phải căng sức ra để vượt qua.</div>\n<div>&nbsp;</div>\n<div>Nhiều khi thời kho&aacute; biểu thay đổi, nhiều SV đ&agrave;nh phải chấp nhận  nghỉ học để đi l&agrave;m cho đ&uacute;ng lịch kh&ocirc;ng người ta lại cho nghỉ việc. Đ&oacute; l&agrave;  chưa kể những SV nữ l&agrave;m th&ecirc;m tại c&aacute;c nh&agrave; h&agrave;ng thường gặp nhiều chuyện  phiền phức. Những l&uacute;c như thế cũng đ&agrave;nh chấp nhận kh&ocirc;ng d&aacute;m ph&agrave;n n&agrave;n nếu  kh&ocirc;ng muốn nghỉ việc.</div>\n<p><span style=\"font-weight: bold;\">Nguyễn Hải - Duy Tuy&ecirc;n </span><em>(theo dantri.com.vn)</em><span style=\"font-weight: bold;\"><br /></span></p>','2011-03-21 22:29:51','admin@jobbid.vn',266,1,NULL),(18,'nghe-pa-cho-nguoi-khuyet-tat','Nghề PA cho người khuyết tật',0,'Các bạn sinh viên, có sức khỏe, làm việc nghiêm túc, hiểu về quyền sống độc lập của người khuyết tật (NKT) và tham gia các đợt tập huấn của Trung tâm Sống độc lập Hà Nội, nếu muốn sẽ có trong tay mình cơ hội để trở thành một PA với mức lương từ 1,3 triệu ','<p><span style=\"font-size: small;\"><strong><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"Nghề PA cho người khuyết tật\" src=\"http://222.255.28.57:2020/Upload/Image/2011/3/8/08032011160220.jpg\" alt=\"Nghề PA cho người khuyết tật\" width=\"300\" height=\"232\" /></strong></span><strong><span style=\"color: #888888;\">C&aacute;c bạn sinh vi&ecirc;n, c&oacute; sức khỏe, l&agrave;m việc nghi&ecirc;m t&uacute;c, hiểu về quyền sống độc lập của người khuyết tật (NKT) v&agrave; tham gia c&aacute;c đợt tập huấn của Trung t&acirc;m Sống độc lập H&agrave; Nội, nếu muốn sẽ c&oacute; trong tay m&igrave;nh cơ hội để trở th&agrave;nh một PA với mức lương từ 1,3 triệu &ndash; 2,6 triệu đồng/th&aacute;ng.</span></strong><br /><span style=\"font-size: small;\"><strong><br />Nghề PA</strong></span></p>\n<p><span style=\"font-size: small;\">PA (Personal Assistance - Người trợ  gi&uacute;p c&aacute; nh&acirc;n) l&agrave; một nghề tại Trung t&acirc;m Sống độc lập c&oacute; nhiệm vụ gi&uacute;p  NKT h&ograve;a nhập cộng đồng. PA kh&aacute;c với những t&igrave;nh nguyện vi&ecirc;n như th&ocirc;ng  thường ta vấn thấy. T&igrave;nh nguyện vi&ecirc;n l&agrave;m việc tr&ecirc;n tinh thần tự nguyện  kh&ocirc;ng c&oacute; chế độ. PA cũng kh&ocirc;ng hẳn l&agrave; c&ocirc;ng việc chăm s&oacute;c người bệnh như  những người gi&uacute;p việc trong bệnh viện hay l&agrave;m m&agrave; l&agrave; một c&ocirc;ng việc ho&agrave;n  to&agrave;n kh&aacute;c. PA tại Trung t&acirc;m Sống độc lập H&agrave; Nội l&agrave;m c&ocirc;ng việc gi&uacute;p đỡ  cho c&aacute; nh&acirc;n NKT trong việc sinh hoạt h&agrave;ng ng&agrave;y như đi lại, vệ sinh, thay  quần &aacute;o, nấu ăn... Đặc biệt, PA kh&ocirc;ng l&agrave;m việc nh&agrave; thường xuy&ecirc;n cho cả  gia đ&igrave;nh m&agrave; l&agrave; người hỗ trợ c&aacute; nh&acirc;n NKT để họ c&oacute; thể tự chủ, tự quyết  trong cuộc sống v&agrave; thực hiện được quyền con người của m&igrave;nh.</span></p>\n<p><span style=\"font-size: small;\">Gi&aacute;m đốc Dự &aacute;n Sống độc lập, chị  Nguyễn Hồng H&agrave; cho biết, từ khi ra đời năm 2009 đến nay, đ&atilde; c&oacute; tr&ecirc;n 80  bạn sinh vi&ecirc;n đăng k&yacute; tham gia l&agrave;m PA cho Trung t&acirc;m Sống độc lập H&agrave; Nội.  Theo chị H&agrave; th&igrave; đ&acirc;y l&agrave; một c&ocirc;ng việc rất ph&ugrave; hợp với c&aacute;c bạn sinh vi&ecirc;n  v&igrave; thời gian l&agrave;m việc rất linh động, c&oacute; thể <a title=\"l&agrave;m việc b&aacute;n thời gian\" href=\"http://www.jobbid.vn\">l&agrave;m việc b&aacute;n thời gian</a> (<a title=\"part-time\" href=\"http://www.jobbid.vn\">part-time</a>) với c&aacute;c bạn sinh vi&ecirc;n đang đi học hoặc l&agrave;m cả ng&agrave;y  (full-time) với những sinh vi&ecirc;n mới tốt nghiệp nhưng chưa xin được việc  ph&ugrave; hợp với ng&agrave;nh học của m&igrave;nh. &ldquo;Sinh vi&ecirc;n l&agrave; những người trẻ tuổi n&ecirc;n  rất năng động v&agrave; hoạt b&aacute;t, th&iacute;ch kh&aacute;m ph&aacute; v&agrave; giao lưu. C&ocirc;ng việc n&agrave;y l&agrave;m  tăng cơ hội trải nghiệm cuộc sống. Bạn sinh vi&ecirc;n n&agrave;o theo được nghề PA  th&igrave; kinh nghiệm thực tế cũng như sự r&egrave;n luyện trong nghề n&agrave;y sẽ rất  thuận lợi cho cuộc sống về sau n&agrave;y của họ&rdquo;. Chị H&agrave; chia sẻ về nghề PA.</span></p>\n<p><span style=\"font-size: small;\">Trịnh Duy Thuận, qu&ecirc; Thanh H&oacute;a l&agrave; một  PA đ&atilde; c&oacute; gần một năm kinh nghiệm. Thuận t&acirc;m sự l&agrave; bạn mới tốt nghiệp  Khoa Sửa chữa &ocirc; t&ocirc; tại Trung t&acirc;m Dạy nghề Thanh Xu&acirc;n v&agrave; hiện đang l&agrave;m  marketing cho một c&ocirc;ng ty n&ecirc;n bạn kh&aacute; rảnh rỗi về thời gian. T&igrave;nh cờ qua  người bạn th&acirc;n, Thuận biết tới c&ocirc;ng việc PA tại Trung t&acirc;m Sống độc lập  H&agrave; Nội số 42 Kim M&atilde; Thượng, H&agrave; Nội n&ecirc;n gửi đơn tham gia. Sau khi trải  qua c&aacute;c kh&oacute;a huấn luyện, đ&agrave;o tạo của trung t&acirc;m, Thuận được cử l&agrave;m PA cho  bạn Ph&iacute; Quang Huy ở phố Khương Thượng, H&agrave; Nội. Thuận cho biết, ban đầu  bạn cũng cảm thấy sợ sợ khi lần đầu tiếp x&uacute;c với NKT. Tuy nhi&ecirc;n, sau một  thời gian Thuận đ&atilde; ho&agrave;n to&agrave;n thay đổi quan niệm đ&oacute; v&agrave; trở th&agrave;nh một  người bạn t&acirc;m đầu &yacute; hợp với Huy từ l&uacute;c n&agrave;o kh&ocirc;ng hay. &ldquo;Những NKT họ cũng  c&oacute; mơ ước, c&oacute; kh&aacute;t khao như bao người b&igrave;nh thường kh&aacute;c, sống c&ugrave;ng với  họ thời gian qua, ngo&agrave;i việc được hưởng lương 1,3 triệu đồng/th&aacute;ng m&igrave;nh  c&ograve;n học hỏi được từ họ rất nhiều kinh nghiệm, l&yacute; tưởng cho cuộc sống của  m&igrave;nh sau n&agrave;y. V&agrave; điều đ&aacute;ng qu&yacute; nhất l&agrave; m&igrave;nh hiểu được t&acirc;m tự, suy nghĩ  về cuộc sống của NKT hơn&rdquo;. Thuận bộc bạch.</span></p>\n<p><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Qua t&igrave;m hiểu ch&uacute;ng t&ocirc;i  nhận thấy hầu hết những người tham gia c&ocirc;ng việc PA đều l&agrave; c&aacute;c bạn sinh  vi&ecirc;n đang học tại c&aacute;c trường c&oacute; trụ sở tại H&agrave; Nội như: Đại học  KHXH&amp;NV, Đại học Lao động x&atilde; hội, Học viện B&aacute;o ch&iacute; v&agrave; Tuy&ecirc;n truyền,  Đại học C&ocirc;ng nghiệp... B&igrave;nh thường, nếu được nhận v&agrave;o l&agrave;m PA tại Trung  t&acirc;m Sống độc lập, c&aacute;c bạn sinh vi&ecirc;n sẽ được trả 10.000 đồng/giờ với hai  th&aacute;ng thử việc đầu v&agrave; 13.000 đồng/giờ sau khi được k&yacute; hợp đồng ch&iacute;nh  thức. Thời gian l&agrave;m việc part-time tối đa l&agrave; 4 tiếng/ng&agrave;y v&agrave; 8  tiếng/ng&agrave;y với full-time. Như vậy, một th&aacute;ng l&agrave;m việc b&aacute;n thời gian một  PA thu nhập được hơn 1,35 triệu đồng, c&ograve;n đối với những người l&agrave;m việc  cả ng&agrave;y sẽ l&agrave; 2,7 triệu đồng/th&aacute;ng, một số tiền kh&ocirc;ng nhỏ đối với c&aacute;c  bạn sinh vi&ecirc;n.</span></p>\n<p><span style=\"font-size: small;\"><strong>Trưởng th&agrave;nh từ nghề PA</strong></span></p>\n<p><span style=\"font-size: small;\"> </span></p>\n<table border=\"0\" cellspacing=\"2\" cellpadding=\"2\" width=\"285\" align=\"right\">\n<tbody>\n<tr>\n<td bgcolor=\"#fffcc0\">\n<p><span style=\"font-size: small;\"><em>Chị Nguyễn Hồng H&agrave; &ndash;  Gi&aacute;m đốc Trung t&acirc;m Sống độc lập: &ldquo;Đ&atilde; c&oacute; nhiều &yacute; kiến trong một cuộc hội  thảo của ng&agrave;nh c&ocirc;ng t&aacute;c x&atilde; hội đề nghị c&ocirc;ng nhận PA l&agrave; một nghề chuy&ecirc;n  nghiệp, giống như nghề c&ocirc;ng t&aacute;c x&atilde; hội. Thực chất nghề n&agrave;y ở c&aacute;c nước  ti&ecirc;n tiến như Anh, Mỹ, Nhật Bản, H&agrave;n Quốc đ&atilde; được c&ocirc;ng nhận từ rất l&acirc;u  rồi. Nhu cầu cần được hỗ trợ để sống độc lập với NKT nước ta l&agrave; ch&iacute;nh  đ&aacute;ng n&ecirc;n sắp tới đ&acirc;y sẽ l&agrave; một nghề rất ph&aacute;t triển&rdquo;.</em></span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><span style=\"font-size: small;\"> Chị Nguyễn Hồng H&agrave; &ndash; Gi&aacute;m đốc Dự &aacute;n Sống độc lập cho biết, điều kiện để  trở th&agrave;nh một PA hỗ trợ NKT rất đơn giản. Ngo&agrave;i những điều kiện như đ&atilde;  n&oacute;i ở phần đầu, chỉ cần mang k&egrave;m theo những điều kiện đ&oacute; l&agrave; một bộ sơ  yếu l&yacute; lịch c&oacute; dấu x&aacute;c nhận của ch&iacute;nh quyền địa phương (trong trường hợp  l&agrave; sinh vi&ecirc;n c&oacute; thể d&ugrave;ng giấy giới thiệu của nh&agrave; trường v&agrave; thẻ sinh  vi&ecirc;n để thay thế) mang đến trụ sở của Trung t&acirc;m Sống độc lập H&agrave; Nội l&agrave;  mọi c&ocirc;ng việc gần như ho&agrave;n tất.</span></p>\n<p><span style=\"font-size: small;\">Theo chia sẻ của bạn Nguyễn Thị L&acirc;m,  sinh vi&ecirc;n năm cuối Trường Đại học KHXH&amp;NV, thu nhập hơn 1  triệu/th&aacute;ng từ nghề PA đối với bạn l&agrave; kh&aacute; cao. Nhưng đ&oacute; kh&ocirc;ng phải l&agrave;  mục đ&iacute;ch ch&iacute;nh của L&acirc;m khi quyết định chọn nghề PA v&agrave; gắn b&oacute; với n&oacute; 9  th&aacute;ng nay. Mục ti&ecirc;u cao cả m&agrave; L&acirc;m v&agrave; hầu hết c&aacute;c bạn trẻ hướng tới ch&iacute;nh  l&agrave; được được trải nghiệm thực tế cũng học hỏi được rất nhiều kinh  nghiệm sống&nbsp;th&ocirc;ng qua c&ocirc;ng việc v&agrave; c&aacute;c kh&oacute;a huấn luyện kỹ năng được tổ  chức li&ecirc;n tục của Trung t&acirc;m Sống độc lập H&agrave; Nội. &ldquo;Rất nhiều người bạn  của em vốn trước đ&acirc;y được bố mẹ chiều chuộng n&ecirc;n kh&ocirc;ng phải động tay v&agrave;o  bất cứ c&ocirc;ng việc g&igrave;. Vậy m&agrave; chỉ sau một thời gian l&agrave;m PA đ&atilde; biết nấu  ăn, thậm ch&iacute; nấu ăn ngon l&agrave; đằng kh&aacute;c. Bởi v&igrave; khi l&agrave;m PA, c&aacute;c bạn ấy  được học dạy nấu ăn, kỹ năng giao tiếp, kỹ năng hỗ trợ, c&aacute;ch kiềm chế  bản th&acirc;n, c&aacute;ch l&agrave;m cho người kh&aacute;c h&agrave;i l&ograve;ng v&agrave; v&ocirc; số kỹ năng bổ &iacute;ch kh&aacute;c  n&ecirc;n chỉ trong một thời gian ngắn hầu hết bạn n&agrave;o cũng trưởng th&agrave;nh v&agrave;  chững chạc hẳn l&ecirc;n&rdquo;. L&acirc;m chia sẻ.</span></p>\n<p><em><span style=\"font-size: small;\">(theo </span>nongnghiep.vn)</em></p>','2011-03-22 23:06:58','admin@jobbid.vn',218,0,NULL),(20,'tuyen-dung-sinh-vien-ngay-tai-truong','Tuyển dụng sinh viên ngay tại trường',0,'Sáng 15-3, bảy doanh nghiệp trên địa bàn TP.HCM, Bình Dương, Đồng Nai đã đến Trường ĐH Ngoại ngữ - tin học (HUFLIT) để tuyển dụng sinh viên trong ngày hội việc làm “Sinh viên HUFLIT và các nhà tuyển dụng Nhật Bản” do trường phối hợp cùng Tổ chức Phát triể','<p class=\"pHead\"><span style=\"color: #888888;\"><strong>TT - S&aacute;ng 15-3, bảy doanh nghiệp tr&ecirc;n địa b&agrave;n TP.HCM,  B&igrave;nh Dương, Đồng Nai đ&atilde; đến Trường ĐH Ngoại ngữ - tin học (HUFLIT) để  tuyển dụng sinh vi&ecirc;n trong ng&agrave;y hội việc l&agrave;m &ldquo;Sinh vi&ecirc;n HUFLIT v&agrave; c&aacute;c  nh&agrave; tuyển dụng Nhật Bản&rdquo; do trường phối hợp c&ugrave;ng Tổ chức Ph&aacute;t triển quốc  tế của Nhật Bản (JODC) tổ chức.</strong></span></p>\n<p class=\"pHead\">&nbsp;</p>\n<table class=\"tLegend\" style=\"border-collapse: separate;\" border=\"0\" cellspacing=\"2\" cellpadding=\"0\" width=\"40\" align=\"center\">\n<tbody>\n<tr>\n<td><img class=\"lImage\" src=\"http://phienbancu.tuoitre.vn/tianyon/ImageView.aspx?ThumbnailID=486999\" border=\"1\" alt=\"\" hspace=\"0\" /></td>\n</tr>\n<tr>\n<td>C&ocirc;ng ty Digital Works Vietnam phỏng vấn trực tiếp ứng vi&ecirc;n cho vị tr&iacute; th&ocirc;ng dịch vi&ecirc;n tiếng Nhật - Ảnh: H&Agrave; B&Igrave;NH</td>\n</tr>\n</tbody>\n</table>\n<p class=\"pBody\">Tại buổi tuyển dụng, c&aacute;c doanh nghiệp đ&atilde; tiếp nhận hồ  sơ, đồng thời trực tiếp phỏng vấn c&aacute;c ứng vi&ecirc;n l&agrave; sinh vi&ecirc;n sắp tốt  nghiệp v&agrave;o c&aacute;c vị tr&iacute; như nh&acirc;n vi&ecirc;n kế to&aacute;n, thư k&yacute; văn ph&ograve;ng, th&ocirc;ng  dịch vi&ecirc;n tiếng Nhật, nh&acirc;n vi&ecirc;n h&agrave;nh ch&iacute;nh, c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; quản  trị kinh doanh. C&oacute; 20 vị tr&iacute; l&agrave;m việc to&agrave;n thời gian v&agrave; một số vị tr&iacute;  l&agrave;m <a title=\"việc b&aacute;n thời gian\" href=\"http://www.jobbid.vn\">việc b&aacute;n thời gian</a> đ&atilde; được tuyển dụng.</p>\n<p class=\"pAuthor\">H&Agrave; B&Igrave;NH (theo<em> tuoitre.com.vn</em>)</p>','2011-03-25 15:50:26','admin@jobbid.vn',210,0,NULL),(21,'nu-sinh-lam-nguoi-yeu-thue','Nữ sinh làm người yêu thuê',0,'Nhẩm đi nhẩm lại những thông tin về gia đình khách hàng, Hằng vừa nghĩ xem mai nên mặc gì, trang điểm thế nào, thậm chí nói cười ra sao để không bị hớ. Ngày mai, cô sẽ nhập vai em gái người yêu của một giám đốc về thăm nhà \'anh rể\'.(công việc bán thời gia','<h2 class=\"Lead\" style=\"font-weight: bold; font-size: 12pt; color: #000000; font-family: \'Times New Roman\'; text-decoration: none;\"><span style=\"color: #888888;\">Nhẩm đi nhẩm lại những th&ocirc;ng tin về gia đ&igrave;nh kh&aacute;ch h&agrave;ng, Hằng vừa nghĩ xem mai n&ecirc;n mặc g&igrave;, trang điểm thế n&agrave;o, thậm ch&iacute; n&oacute;i cười ra sao để kh&ocirc;ng bị hớ. Ng&agrave;y mai, c&ocirc; sẽ nhập vai em g&aacute;i người y&ecirc;u của một gi&aacute;m đốc về thăm nh&agrave; \'anh rể\'.</span></h2>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Từng nhận hợp đồng l&agrave;m người tr&ograve; chuyện v&agrave; đi uống c&agrave; ph&ecirc; c&ugrave;ng một doanh nh&acirc;n đ&atilde; v&agrave;o tuổi băm, chưa người y&ecirc;u nhưng lần n&agrave;y với Hằng quả l&agrave; \"ca\" kh&oacute;. Vị kh&aacute;ch vừa c&atilde;i nhau với bạn g&aacute;i trong khi anh n&agrave;y lại tr&oacute;t hứa sẽ mang n&agrave;ng về ra mắt gia đ&igrave;nh. Anh ch&agrave;ng đ&agrave;nh n&oacute;i dối người th&acirc;n rằng bạn bị ốm v&agrave; tức tốc đi thu&ecirc; người đ&oacute;ng thế giả l&agrave;m em g&aacute;i thay \"chị\" về thăm hỏi gia đ&igrave;nh anh rể tương lai. Tận dụng mọi kinh nghiệm từ cuộc t&igrave;nh vừa qua, Hằng v&agrave;o vai thật ngọt v&agrave; khớp duy chỉ c&oacute; một sự cố khiến c&ocirc; kh&ocirc;ng thể n&agrave;o qu&ecirc;n...</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"http://ngoisao.net/news/thoi-cuoc/2011/03/163113-nu-sinh-lam-nguoi-yeu-thue/anh-2.jpg\" border=\"1\" alt=\"\" hspace=\"0\" vspace=\"0\" width=\"450\" height=\"450\" align=\"center\" /></p>\n</td>\n</tr>\n<tr>\n<td class=\"Image\" style=\"font-size: 9pt; color: #4c4c4c; font-family: Arial; text-decoration: none;\">Dịch vụ người đ&oacute;ng thế rất phổ biến ở c&aacute;c nước nhưng mới xuất hiện ở Việt Nam hai năm.<span class=\"Apple-converted-space\">&nbsp;</span><em>Ảnh minh họa.</em></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Người đ&oacute;ng thế hay l&agrave;m người y&ecirc;u thu&ecirc;, một c&ocirc;ng việc c&ograve;n xa lạ v&agrave; kh&aacute; mới mẻ nhưng lại c&oacute; sức h&uacute;t mạnh với giới trẻ hiện nay đặc biệt l&agrave; sinh vi&ecirc;n. Ra trường, Hằng cũng n&ocirc;n n&oacute;ng l&ecirc;n mạng t&igrave;m việc v&agrave; t&igrave;nh cờ đọc được mẩu quảng c&aacute;o tuyển cộng t&aacute;c vi&ecirc;n của c&ocirc;ng ty Vinamost chuy&ecirc;n cho thu&ecirc; người đ&oacute;ng thế c&aacute;c vai trong cuộc sống đầu ti&ecirc;n ở Việt Nam. Một phần muốn t&iacute;ch lũy kinh nghiệm, c&aacute;c mối quan hệ x&atilde; hội trước khi xin v&agrave;o l&agrave;m đ&uacute;ng chuy&ecirc;n ng&agrave;nh ng&acirc;n h&agrave;ng của m&igrave;nh, một phần cũng v&igrave; t&ograve; m&ograve;, c&ocirc; nộp hồ sơ rồi tr&uacute;ng tuyển.</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Hợp đồng đầu ti&ecirc;n của Hằng nằm trong g&oacute;i c&agrave; ph&ecirc; @ v&agrave; nhiệm vụ của c&ocirc; chỉ việc ăn mặc đẹp đi uống nước c&ugrave;ng kh&aacute;ch, lắng nghe họ t&acirc;m sự v&agrave; thỉnh thoảng cười thật tươi. Kh&aacute;ch h&agrave;ng của c&ocirc; l&agrave; một doanh nh&acirc;n trẻ bị gia đ&igrave;nh giục cưới vợ. Anh n&agrave;y muốn c&oacute; những buổi c&agrave; ph&ecirc; c&ugrave;ng c&ocirc; g&aacute;i trẻ, đẹp để chia sẻ cuộc sống ngo&agrave;i kinh doanh v&agrave; biết đ&acirc;u sẽ b&eacute;n duy&ecirc;n. Vốn kh&eacute;o l&eacute;o v&agrave; biết c&aacute;ch ăn n&oacute;i, thời sinh vi&ecirc;n từng l&agrave;m th&ecirc;m nhiều c&ocirc;ng việc n&ecirc;n Hằng bạo dạn v&agrave; tr&ocirc;ng gi&agrave; dặn hơn so với c&aacute;i tuổi 23 của m&igrave;nh.</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Cuộc gặp đầu ti&ecirc;n diễn ra nhẹ nh&agrave;ng bởi chủ đề chỉ xoay quanh việc \"mẹ anh th&iacute;ch đi ch&ugrave;a; em c&oacute; th&iacute;ch đi ch&ugrave;a kh&ocirc;ng?\". Chỉ với 2h ngồi c&agrave; ph&ecirc; c&ugrave;ng kh&aacute;ch, Hằng kiếm được hơn 250 ngh&igrave;n đồng. C&ocirc;ng việc đơn giản, thu nhập tốt, c&ocirc; tiếp tục nhận hợp đồng tiếp theo, giả l&agrave;m em g&aacute;i người y&ecirc;u của một gi&aacute;m đốc trẻ theo \"anh rể tương lai\" về thăm gia đ&igrave;nh. Do c&oacute; ngoại h&igrave;nh v&agrave; nhiều điểm giống với bạn g&aacute;i anh ta n&ecirc;n Hằng đ&atilde; được \"chấm\".</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">C&ocirc; sinh vi&ecirc;n trường Kinh tế Quốc d&acirc;n kể: \"V&igrave; đ&oacute;ng l&agrave; em g&aacute;i của người y&ecirc;u n&ecirc;n m&igrave;nh cũng kh&ocirc;ng phải diễn c&aacute;c pha t&igrave;nh cảm nhưng vẫn run lắm v&igrave; biết nhiều họ h&agrave;ng nh&agrave; anh ấy đang chờ đợi để nh&igrave;n thấy mặt c&ocirc; em g&aacute;i của người y&ecirc;u con trai m&igrave;nh. Mọi cử chỉ, lời n&oacute;i sẽ bị ch&uacute; &yacute;, chỉ cần nhầm ch&uacute;t th&ocirc;ng tin sẽ khiến m&igrave;nh l&uacute;ng t&uacute;ng\".</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Mặc d&ugrave; trước l&uacute;c v&agrave;o vai, c&ocirc; đ&atilde; gặp kh&aacute;ch để nắm r&otilde; tiểu sử, th&ocirc;ng tin cần thiết về người th&acirc;n cũng như \"độ kh&oacute;\" của bố mẹ \"anh rể\", Hằng vẫn kh&ocirc;ng khỏi ngại ng&ugrave;ng khi phải theo người lạ về gặp gỡ v&agrave; n&oacute;i những c&acirc;u chuyện \"như thật\". Nh&agrave; gi&aacute;m đốc c&aacute;ch H&agrave; Nội c&oacute; 35 c&acirc;y số, c&oacute; một chị g&aacute;i đ&atilde; lập gia đ&igrave;nh v&agrave; sống gần bố mẹ. V&igrave; đường xa, lại đi bằng xe m&aacute;y n&ecirc;n c&ocirc; kh&ocirc;ng d&aacute;m mặc qu&aacute; cầu kỳ v&agrave; trang điểm điểm ấn tượng để bị ch&uacute; &yacute;. Dọc đường đi, hai người c&ugrave;ng nhau \"&ocirc;n luyện\" c&aacute;c th&ocirc;ng tin. Vị kh&aacute;ch như được thể tr&uacute;t bầu t&acirc;m sự vụ x&iacute;ch m&iacute;ch với người y&ecirc;u v&agrave; cũng l&agrave; để Hằng nắm t&igrave;nh h&igrave;nh r&otilde; hơn. Buổi đ&oacute;n tiếp diễn ra thật ấm c&uacute;ng v&agrave; su&ocirc;n sẻ khi mọi c&acirc;u hỏi ph&iacute;a người th&acirc;n \"anh rể\" đều được Hằng trả lời tr&ocirc;i chảy v&agrave; đầy cảm x&uacute;c. Đến phần gặp gỡ bạn b&egrave;, c&ocirc; n&agrave;ng su&yacute;t bị lộ v&igrave; bị c&aacute;c bạn của anh n&agrave;y \"hỏi xo&aacute;y\".</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">\"Một người bạn của kh&aacute;ch h&agrave;ng hỏi:<span class=\"Apple-converted-space\">&nbsp;</span><em>Em l&agrave; người Ba V&igrave; &agrave;?<span class=\"Apple-converted-space\">&nbsp;</span></em>M&igrave;nh bảo<span class=\"Apple-converted-space\">&nbsp;</span><em>v&acirc;ng.<span class=\"Apple-converted-space\">&nbsp;</span></em>Sau đ&oacute; anh n&agrave;y bảo muốn nghe em n&oacute;i giọng địa phương. M&igrave;nh bối rối một l&uacute;c v&igrave; chưa từng nghe giọng n&oacute;i ở đ&oacute; bao giờ. Chắc anh ấy nghi ngờ v&igrave; m&igrave;nh n&oacute;i giọng H&agrave; Nội kh&ocirc;ng pha. Nếu l&uacute;c đ&oacute; m&agrave; giới thiệu qu&ecirc; Nghệ An chắc m&igrave;nh lộ mất\", c&ocirc; n&agrave;ng sinh năm 1988 ph&aacute; l&ecirc;n cười khi nhớ lại kỷ niệm ấy.</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Kết th&uacute;c một ng&agrave;y đ&oacute;ng thế, Hằng nhận được th&ugrave; lao 500.000 đồng v&agrave; được th&ecirc;m cả một người bạn. Sau gần hai th&aacute;ng bị \"thử th&aacute;ch\" với vị tr&iacute; cộng t&aacute;c vi&ecirc;n, hiện Hằng mới được nhận v&agrave;o l&agrave;m nh&acirc;n vi&ecirc;n ch&iacute;nh thức chuy&ecirc;n sắp xếp c&aacute;c cuộc gặp cho kh&aacute;ch h&agrave;ng n&agrave;o c&oacute; nhu cầu c&agrave; ph&ecirc;. C&ocirc; t&acirc;m sự th&ecirc;m, c&ocirc;ng việc n&agrave;y c&ograve;n mới ở Việt Nam, lại kh&aacute; nhạy cảm n&ecirc;n dễ bị hiểu nhầm l&agrave; một dịch vụ tr&aacute; h&igrave;nh. Mặc d&ugrave; được ph&iacute;a c&ocirc;ng ty hỗ trợ c&aacute;c \"nghiệp vụ\" ph&ograve;ng vệ v&agrave; chắc chắn về độ tin cậy của kh&aacute;ch h&agrave;ng nhưng \"c&ocirc;ng ty chỉ đảm bảo mức độ an to&agrave;n khoảng 80% đến 90 % c&ograve;n lại 10 % l&agrave; tự m&igrave;nh phải bảo vệ m&igrave;nh\". Tỏ ra tự tin song Hằng vẫn kh&ocirc;ng khỏi e ngại khi phải tiết lộ với người th&acirc;n hay bạn b&egrave; về c&ocirc;ng việc đang l&agrave;m.</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">\"Kh&ocirc;ng &iacute;t người nghi ngờ dịch vụ l&uacute;c nghe n&oacute;i tới người y&ecirc;u thu&ecirc; hay đ&oacute;ng giả l&agrave;m bạn g&aacute;i. M&igrave;nh chưa c&oacute; bạn trai n&ecirc;n kh&ocirc;ng sợ bị bắt gặp hay ghen tu&ocirc;ng nhưng cũng kh&ocirc;ng muốn để bạn b&egrave;, người th&acirc;n biết m&igrave;nh l&agrave;m việc n&agrave;y để tr&aacute;nh bị hỏi han hay những suy nghĩ kh&ocirc;ng đ&uacute;ng\", c&ocirc; th&agrave;nh thật.</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Cũng giống Hằng, Minh, sinh vi&ecirc;n Đại học Lao động X&atilde; hội H&agrave; Nội, t&ograve; m&ograve; với c&ocirc;ng việc đ&oacute;ng thế khi đọc được th&ocirc;ng tin tuyển dụng của một c&ocirc;ng ty cho thu&ecirc; người y&ecirc;u. Giọng n&oacute;i nhẹ nh&agrave;ng, cuốn h&uacute;t, nữ sinh năm nhất tiết lộ: \"Mới đầu em nghĩ người đ&oacute;ng thế chắc giống cascadeur đ&oacute;ng phim v&agrave; thực sự kh&ocirc;ng h&igrave;nh dung được c&ocirc;ng việc. Sau khi nghe anh gi&aacute;m đốc giải th&iacute;ch, em hiểu ngay m&igrave;nh phải l&agrave;m g&igrave;\".</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Từng l&agrave;m người mẫu ảnh, Minh tự tin v&agrave; bạo dạn khi tiếp x&uacute;c với người lạ. Gia đ&igrave;nh kinh doanh bất động sản, thường xuy&ecirc;n phải tiếp c&aacute;c vị kh&aacute;ch VIP v&agrave; giới kinh doanh n&ecirc;n c&ocirc;ng việc \"lắng nghe v&agrave; tr&ograve; chuyện\" kh&ocirc;ng qu&aacute; kh&oacute; khăn với c&ocirc;. Mới đ&acirc;y c&ocirc; vừa nhận hợp đồng đi ăn tối với một doanh nh&acirc;n miền Trung tại một kh&aacute;ch sạn sang trọng. &Ocirc;ng kh&aacute;ch đ&atilde; lớn tuổi, muốn c&oacute; một người ăn tối c&ugrave;ng khi ra Bắc c&ocirc;ng t&aacute;c. Cuộc gặp kh&aacute; gấp g&aacute;p, 18h30 nhận cuộc gọi, 19h c&ocirc; đ&atilde; phải tới chỗ hẹn. Do c&ugrave;ng l&agrave; người miền trong n&ecirc;n c&ocirc; cũng dễ d&agrave;ng tiếp chuyện kh&aacute;ch hơn.</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">\"Lần đ&oacute; em phải chuẩn bị quần &aacute;o v&agrave; trang điểm gấp g&aacute;p. Kh&ocirc;ng thể chọn trang phục x&igrave; tin, nh&iacute; nhảnh được bởi kh&aacute;ch đ&atilde; c&oacute; tuổi n&ecirc;n em chọn v&aacute;y v&agrave; trang điểm thật nhẹ nh&agrave;ng. Trong cuộc tr&ograve; chuyện, em cũng kh&ocirc;ng cần n&oacute;i nhiều, chỉ nghe v&agrave; gật đầu hưởng ứng. &Ocirc;ng ấy rất vui như gặp người quen v&agrave; c&ograve;n kể cho em nghe nhiều về những ng&ocirc;i nh&agrave; của m&igrave;nh\", Minh kể.</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Theo &ocirc;ng Dương Ngọc To&agrave;n, Gi&aacute;m đốc c&ocirc;ng ty VinaEscort chuy&ecirc;n cung cấp người đ&oacute;ng thế, cho thu&ecirc; người y&ecirc;u, bạn g&aacute;i đi dự tiệc hoặc thư k&yacute; gi&aacute;m đốc..., dịch vụ lạ hiện rất hot. Phần lớn c&aacute;c cộng t&aacute;c vi&ecirc;n của c&ocirc;ng ty đều c&ograve;n l&agrave; sinh vi&ecirc;n trẻ trung, ngoại h&igrave;nh cao r&aacute;o, xinh đẹp c&oacute; thể đ&aacute;p ứng c&aacute;c đơn đặt h&agrave;ng l&agrave;m PG, đ&oacute;ng l&agrave;m người y&ecirc;u, bạn g&aacute;i, thư k&yacute; hay nhập bất cứ vai diễn n&agrave;o. &Ocirc;ng To&agrave;n cho biết, với mục đ&iacute;ch gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; cuộc sống th&uacute; vị ngo&agrave;i c&ocirc;ng việc, c&oacute; người sẻ chia v&agrave; t&acirc;m sự, c&aacute;c cộng t&aacute;c vi&ecirc;n được huấn luyện nhiều kỹ năng n&oacute;i chuyện, giao tiếp, thậm ch&iacute; cả c&aacute;ch tự vệ trong c&aacute;c trường hợp bất ngờ. Ngo&agrave;i c&aacute;c c&ocirc; g&aacute;i, kh&ocirc;ng &iacute;t c&aacute;c nam sinh cũng đăng k&yacute; <a title=\"c&ocirc;ng việc b&aacute;n thời gian n&agrave;y\" href=\"http://www.jobbid.vn\">c&ocirc;ng việc b&aacute;n thời gian n&agrave;y</a>. Mức th&ugrave; lao cho một cuộc hẹn sẽ phụ thuộc v&agrave;o \"chiều cao v&agrave; nhan sắc\" của người đ&oacute;ng thế.</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\">Trong những ng&agrave;y lễ, Tết, lượng kh&aacute;ch h&agrave;ng t&igrave;m đến dịch vụ của c&ocirc;ng ty tr&ecirc;n kh&aacute; đ&ocirc;ng. \"Ng&agrave;y quốc tế phụ nữ, ch&uacute;ng t&ocirc;i đ&atilde; nhận được hơn 15 đơn đặt h&agrave;ng c&agrave; ph&ecirc; @. Kh&aacute;ch h&agrave;ng phần lớn l&agrave; nam giới muốn t&igrave;m một người bạn g&aacute;i ngồi tr&ograve; chuyện với m&igrave;nh. Mức gi&aacute; khoảng 800 ngh&igrave;n đồng một cuộc hẹn\".</p>\n<p class=\"Normal\" style=\"font-size: 11pt; color: #000000; font-family: \'Times New Roman\'; line-height: 19px;\"><strong>B&igrave;nh Minh </strong><em>(nguồn ngoisao.net)</em><strong><br /></strong></p>','2011-03-28 15:03:14','admin@jobbid.vn',324,0,NULL),(22,'lao-dong-tre-vua-lam-vua-hoc','Lao động trẻ vừa làm vừa học',0,'Tim viec ban thoi gian - Để giảm bớt gánh nặng về tài chính cho gia đình và theo đuổi ước mơ học hành, nhiều lao động trẻ từ nông thôn ra thành phố tìm việc đã chọn hướng đi vừa học, vừa làm.','<p><span style=\"font-size: x-small;\"><strong><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"tim viec ban thoi gian\" src=\"http://nongnghiep.vn/Upload/Image/2011/3/15/15032011122949.JPG\" alt=\"tim viec ban thoi gian\" width=\"310\" height=\"232\" /><span style=\"font-size: small; color: #888888;\">Để giảm bớt g&aacute;nh nặng về t&agrave;i  ch&iacute;nh cho gia đ&igrave;nh v&agrave; theo đuổi ước mơ học h&agrave;nh, nhiều lao động trẻ từ  n&ocirc;ng th&ocirc;n ra th&agrave;nh phố t&igrave;m việc đ&atilde; chọn hướng đi vừa học, vừa l&agrave;m.</span></strong></span></p>\n<p><span style=\"font-size: small;\"><strong>Theo đuổi giấc mơ giảng đường</strong></span></p>\n<p><span style=\"font-size: small;\">Tốt nghiệp THPT loại kh&aacute; nhưng do ho&agrave;n  cảnh gia đ&igrave;nh kh&oacute; khăn n&ecirc;n Nguyễn Thị Huyền (qu&ecirc; H&agrave; Tĩnh) phải g&aacute;c lại  giấc mơ v&agrave;o đại học để bước v&agrave;o cuộc sống mưu sinh nặng nhọc. Sau tết  T&acirc;n M&atilde;o, Huyền v&agrave;o B&igrave;nh Dương kiếm việc l&agrave;m th&ecirc;m v&agrave; tiếp tục ấp ủ ước mơ  l&agrave;m c&ocirc; gi&aacute;o từ ng&agrave;y b&eacute;. &ldquo;Em vừa được nhận v&agrave;o l&agrave;m tại một c&ocirc;ng ty chế  tạo linh kiện điện tử của H&agrave;n Quốc trong KCN Việt Hương với mức lương  2,2 triệu đồng/th&aacute;ng. Trừ chi ph&iacute; ăn ở, nh&agrave; trọ, nếu sống tằn tiện em  cũng c&ograve;n ch&uacute;t dư để đi học &ocirc;n tại trung t&acirc;m luyện thi đại học, cao đẳng  B&igrave;nh Dương&rdquo;, Huyền t&acirc;m sự. Sau giờ tan ca, Huyền v&agrave; hai người bạn c&ugrave;ng  ph&ograve;ng trọ lại mang cặp s&aacute;ch đến lớp học &ocirc;n.</span></p>\n<p><span style=\"font-size: small;\">Thi rớt đại học, kh&ocirc;ng c&oacute; điều kiện để  ở nh&agrave; &ocirc;n thi, Trần Văn Vĩnh (qu&ecirc; Quảng Nam) phải khăn g&oacute;i v&agrave;o th&agrave;nh phố  kiếm việc l&agrave;m vừa tranh thủ thời gian rảnh &ocirc;n thi. &ldquo;Em đ&atilde; gửi hồ sơ xin  v&agrave;o l&agrave;m ch&acirc;n giữ xe ở qu&aacute;n c&agrave; ph&ecirc; gần ph&ograve;ng trọ. Mức lương 1,2 triệu  đồng/th&aacute;ng kh&ocirc;ng nhiều, nhưng em c&oacute; thời gian để &ocirc;n b&agrave;i v&agrave; l&agrave;m th&ecirc;m một  số c&ocirc;ng việc ngo&agrave;i giờ kh&aacute;c. Sắp tới em sẽ nộp hồ sơ thi v&agrave;o Khoa tiếng  Anh , Trường ĐHKH XH&amp;NV TP.HCM\", Vĩnh cho biết.</span></p>\n<p><span style=\"font-size: small;\">Theo Trung t&acirc;m Giới thiệu việc l&agrave;m  tỉnh B&igrave;nh Dương, trong thời gian qua trung t&acirc;m n&agrave;y tiếp nhận một số  lượng lớn hồ sơ xin việc của c&aacute;c lao động trẻ, c&oacute; tr&igrave;nh độ tốt nghiệp  phổ th&ocirc;ng. Họ chủ yếu cần <a title=\"t&igrave;m việc b&aacute;n thời gian\" href=\"http://www.jobbid.vn\">t&igrave;m việc b&aacute;n thời gian</a> như: nh&acirc;n vi&ecirc;n  b&aacute;n h&agrave;ng, gia sư, chạy b&agrave;n qu&aacute;n c&agrave; ph&ecirc;&hellip; để c&oacute; cơ hội đi học th&ecirc;m. C&oacute;  mặt tại Trung t&acirc;m, ch&uacute;ng t&ocirc;i gặp rất nhiều lao động trẻ đến mua hồ sơ,  t&igrave;m việc. Hầu hết lao động trẻ ưu ti&ecirc;n chọn c&aacute;c doanh nghiệp c&oacute; hỗ trợ  v&agrave; ưu đ&atilde;i cho lao động c&oacute; nguyện vọng học th&ecirc;m. &ldquo;Ch&uacute;ng t&ocirc;i thường chọn  c&aacute;c doanh nghiệp c&oacute; &yacute; định cung cấp kinh ph&iacute; đ&agrave;o tạo nghề cho lao động,  sau đ&oacute; ở lại phục vụ cho doanh nghiệp để nộp hồ sơ xin việc. Cuộc sống  c&ocirc;ng nh&acirc;n kh&oacute; khăn n&ecirc;n d&ugrave; muốn đi học l&ecirc;n cao nữa cũng kh&ocirc;ng c&oacute; điều  kiện&rdquo;, một lao động cho hay.</span></p>\n<p><span style=\"font-size: small;\"><strong>Lấy ngắn nu&ocirc;i d&agrave;i</strong></span></p>\n<p><span style=\"font-size: small;\">Nắm bắt nhu cầu của lực lượng lao động  trẻ, c&aacute;c doanh nghiệp cũng đưa ra c&aacute;c ưu đ&atilde;i, tạo điều kiện cho c&ocirc;ng  nh&acirc;n được đi học l&ecirc;n cao như: hỗ trợ 50% tiền ph&ograve;ng trọ, sắp xếp thời  gian l&agrave;m v&agrave; học hợp l&yacute; cho c&ocirc;ng nh&acirc;n, mở c&aacute;c lớp đ&agrave;o tạo nghề tại c&ocirc;ng  ty&hellip; Tại bảng th&ocirc;ng tin tuyển dụng, nhiều lao động trẻ quan t&acirc;m đến c&aacute;c  c&ocirc;ng ty thuộc chuy&ecirc;n ng&agrave;nh cơ kh&iacute;, chế tạo m&aacute;y, bảo tr&igrave; m&aacute;y&hellip; </span></p>\n<p><span style=\"font-size: small;\">&ldquo;Em đang c&oacute; dự định học th&ecirc;m trung cấp  cơ kh&iacute;, n&ecirc;n chọn nộp hồ sơ ở c&aacute;c doanh nghiệp c&oacute; đ&agrave;o tạo chuy&ecirc;n ng&agrave;nh  n&agrave;y. Ở đ&acirc;y, doanh nghiệp vừa đ&agrave;i thọ chi ph&iacute; học v&agrave; được hưởng lương  theo sản phẩm. Trước mắt l&agrave; c&oacute; chỗ học v&agrave; l&agrave;m ổn định, sau n&agrave;y đủ điều  kiện sẽ học tiếp l&ecirc;n đại học&rdquo;, anh Dương, một lao động trẻ cho biết.</span></p>\n<p><span style=\"font-size: small;\"> </span></p>\n<table border=\"0\" cellspacing=\"2\" cellpadding=\"2\" width=\"285\" align=\"right\">\n<tbody>\n<tr>\n<td bgcolor=\"#fffcc0\">\n<p><span style=\"font-size: small;\"><em>Nhiều lao động trẻ  hiện nay đang chọn xu hướng vừa học vừa l&agrave;m với hy vọng t&igrave;m được một  c&ocirc;ng việc ph&ugrave; hợp với khả năng của m&igrave;nh, c&oacute; thu nhập ổn định v&agrave; giảm  g&aacute;nh nặng kinh tế cho gia đ&igrave;nh.</em></span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><span style=\"font-size: small;\"> Với phương &aacute;n &ldquo;lấy ngắn nu&ocirc;i d&agrave;i\" n&agrave;y, nhiều bạn trẻ đ&atilde; c&oacute; tiền trang  trải học ph&iacute; v&agrave; c&aacute;c chi ph&iacute; sinh hoạt đắt đỏ. Hiện đang học tại chức năm  2 Khoa Kế to&aacute;n tại Trường Đại học kinh tế - Kỹ thuật B&igrave;nh Dương, Ho&agrave;ng  Thị Lan (qu&ecirc; Quảng B&igrave;nh) vừa xin đi l&agrave;m c&ocirc;ng nh&acirc;n tại KCN Việt Nam &ndash;  Singapore. Mức lương cơ bản của Lan hiện nay l&agrave; 1,8 triệu đồng/th&aacute;ng,  nếu tăng ca thường xuy&ecirc;n th&igrave; thu nhập cũng đạt xấp xỉ tr&ecirc;n 3,5 triệu  đồng/th&aacute;ng. Gần 2 năm qua, Lan vừa đi học vừa đi l&agrave;m th&ecirc;m để lấy tiền  chi trả học ph&iacute; gần 2,2 triệu đồng/th&aacute;ng. </span></p>\n<p><span style=\"font-size: small;\">&ldquo;Ch&uacute;ng em phải l&agrave;m đơn xin c&ocirc;ng ty v&agrave;  nh&agrave; trường linh động sắp xếp lịch học v&agrave; lịch l&agrave;m cụ thể để tr&aacute;nh bị  tr&ugrave;ng. Cuộc sống tụi em c&ograve;n gặp nhiều kh&oacute; khăn, nhưng khi nghĩ đến tương  lai c&oacute; tấm bằng đại học trong tay, em c&agrave;ng vững ch&iacute;. Sau n&agrave;y tốt nghiệp  rồi em sẽ xin về c&ocirc;ng ty l&agrave;m&rdquo;, Lan hồ hởi khoe.</span></p>\n<p><span style=\"font-size: small;\">Con đường học h&agrave;nh kh&ocirc;ng su&ocirc;n sẻ, vừa  học hết lớp 9, Trần Văn Th&agrave;nh (qu&ecirc; B&igrave;nh Định) đ&atilde; phải nghỉ học để phụ  gi&uacute;p gia đ&igrave;nh. Hơn 5 năm l&agrave;m c&ocirc;ng nh&acirc;n ở c&aacute;c KCN, KCX&hellip; Th&agrave;nh cũng d&agrave;nh  dụm được một &iacute;t vốn để l&agrave;m ăn. Nhưng cuộc sống quanh năm đi l&agrave;m thu&ecirc;,  l&agrave;m mướn với mức lương chưa đến 3 triệu đồng/th&aacute;ng khiến anh quyết t&acirc;m  đi học. &ldquo;M&igrave;nh phải đi học để c&oacute; tay nghề, c&aacute;c c&ocirc;ng ty b&acirc;y giờ chỉ ưu  ti&ecirc;n tuyển lao động đ&atilde; qua đ&agrave;o tạo. D&ugrave; kh&oacute; khăn đến đ&acirc;u m&igrave;nh cũng phải  r&aacute;ng học&rdquo;, anh t&acirc;m sự. Ngo&agrave;i giờ l&agrave;m ở c&ocirc;ng ty, Th&agrave;nh đang theo học  ng&agrave;nh điện d&acirc;n dụng tại trường Trung cấp Kinh tế - kỹ thuật B&igrave;nh Dương.</span></p>\n<p><em><span style=\"font-size: small;\">Nguồn </span>nongnghiep.vn</em></p>','2011-03-29 21:13:25','admin@jobbid.vn',376,0,NULL),(23,'nghe-la-cua-sinh-vien','Nghề lạ của sinh viên',0,'Tim viec ban thoi gian - Làm mẫu mặt cho các lớp học trang điểm, đọc sách cho người già, vỗ tay trong các gameshow… đã trở thành công việc làm thêm của rất nhiều sinh viên trong thời giá cả leo thang. ','<p class=\"lead\"><span style=\"color: #888888;\"><strong>L&agrave;m mẫu mặt cho c&aacute;c lớp học trang điểm, đọc s&aacute;ch cho  người gi&agrave;, vỗ tay trong c&aacute;c gameshow&hellip; đ&atilde; trở th&agrave;nh c&ocirc;ng việc l&agrave;m th&ecirc;m  của rất nhiều sinh vi&ecirc;n trong thời gi&aacute; cả leo thang.</strong></span></p>\n<p class=\"Normal\">\"Mẫu mặt&rdquo; l&agrave; t&ecirc;n gọi của c&ocirc;ng việc l&agrave;m  người mẫu tại c&aacute;c lớp trang điểm đang nở rộ ở H&agrave; Nội. Nghề n&agrave;y thu h&uacute;t  c&aacute;c bạn nữ th&iacute;ch trang điểm, nhưng kh&ocirc;ng c&oacute; điều kiện khi chỉ phải ngồi  bất động 1-2 tiếng mỗi buổi để l&agrave;m mẫu cho học vi&ecirc;n &ldquo;t&ocirc; tr&aacute;t&rdquo;.</p>\n<p class=\"Normal\">Mức lương cho c&ocirc;ng việc n&agrave;y kh&aacute; rẻ, chỉ 25.000 đồng  một giờ. Thu Minh, sinh vi&ecirc;n ĐH Phương Đ&ocirc;ng, chia sẻ: &ldquo;Gọi l&agrave; việc chứ  m&igrave;nh c&oacute; phải l&agrave;m g&igrave; đ&acirc;u, ngồi để họ trang điểm cho n&ecirc;n lương lậu thế  m&igrave;nh cũng kh&ocirc;ng ph&agrave;n n&agrave;n g&igrave;&rdquo;.</p>\n<p class=\"Normal\">C&ocirc;ng việc đơn giản nhưng cũng c&oacute; nhiều mặt tr&aacute;i. Một  &ldquo;mẫu mặt&rdquo; c&oacute; kinh nghiệm cho biết mỹ phẩm được sử dụng tại c&aacute;c lớp học  thường l&agrave; h&agrave;ng chợ, chất lượng thấp hoặc hết hạn n&ecirc;n rất độc hại cho da.  &ldquo;Lớp m&igrave;nh từng c&oacute; bạn bị dị ứng nổi mụn, mẩn đỏ hoặc nặng hơn phải nằm  viện da liễu. Tiền lương kh&ocirc;ng đủ b&ugrave; tiền thuốc v&agrave; viện ph&iacute;&rdquo;, c&ocirc; n&oacute;i.</p>\n<p class=\"Normal\">Những tai nạn như vậy kh&ocirc;ng hề hiếm v&agrave; hầu như bạn n&agrave;o  theo nghề mẫu mặt cũng được cảnh b&aacute;o, nhưng số lượng nữ sinh đi l&agrave;m  kh&ocirc;ng hề giảm. L&yacute; giải cho việc n&agrave;y, Thu Minh cho biết: &ldquo;V&igrave; c&ocirc;ng việc  đơn giản, kh&ocirc;ng y&ecirc;u cầu bằng cấp. Hơn nữa, b&acirc;y giờ<a title=\"t&igrave;m việc b&aacute;n thời gian\" href=\"http://www.jobbid.vn\"> t&igrave;m việc b&aacute;n thời gian</a> rất kh&oacute; n&ecirc;n m&igrave;nh sẵn s&agrave;ng l&agrave;m để c&oacute; th&ecirc;m thu nhập&rdquo;.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://images.timnhanh.com/tintuc/20110330/Image/1703939735_lam_mau.jpg\" alt=\"1703939735_lam_mau\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">C&ocirc;ng việc l&agrave;m mẫu mặt, mẫu t&oacute;c gi&uacute;p sinh vi&ecirc;n c&oacute; th&ecirc;m thu nhập, nhưng cũng hứng nhiều rủi ro. Ảnh minh họa: <em>Ho&agrave;ng H&agrave;.</em></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Ở mức độ phức tạp hơn, gần đ&acirc;y nghề đọc s&aacute;ch cho người  gi&agrave; hoặc người bệnh kh&ocirc;ng c&oacute; khả năng đi lại đang l&agrave; <a title=\"c&ocirc;ng việc part-time\" href=\"http://www.jobbid.vn\">c&ocirc;ng việc  part-time</a> được sinh vi&ecirc;n ở c&aacute;c th&agrave;nh phố lớn y&ecirc;u th&iacute;ch.</p>\n<p class=\"Normal\">Gi&aacute; cho một giờ l&agrave;m l&agrave; 50.000 đồng, c&ocirc;ng việc k&eacute;o d&agrave;i  bao l&acirc;u t&ugrave;y thuộc v&agrave;o&hellip; người nghe. &ldquo;Việc n&agrave;y rất th&uacute; vị v&igrave; m&igrave;nh đọc được  nhiều s&aacute;ch, t&iacute;ch lũy th&ecirc;m kiến thức. Ngo&agrave;i việc đọc s&aacute;ch được trả tiền,  nếu may mắn m&igrave;nh c&ograve;n c&oacute; một người bạn để chia sẻ niềm y&ecirc;u th&iacute;ch s&aacute;ch&rdquo;,  Phương Anh, sinh vi&ecirc;n Học viện H&agrave;nh ch&iacute;nh quốc gia chia sẻ.</p>\n<p class=\"Normal\">Phương Anh cho biết th&ecirc;m đ&ocirc;i khi cũng gặp phải những  kh&aacute;ch h&agrave;ng rất kh&oacute; t&iacute;nh. &ldquo;C&oacute; người muốn nghe cả ng&agrave;y m&agrave; m&igrave;nh kh&ocirc;ng đủ  sức đọc d&ugrave; họ trả rất nhiều tiền. Ngược lại, cũng c&oacute; lần m&igrave;nh m&eacute;o mặt  khi phải đạp xe rất xa nhưng chỉ đọc được 30 ph&uacute;t th&igrave; bị đuổi về&rdquo;, c&ocirc;  kể.</p>\n<p class=\"Normal\">Việc gặp phải kh&aacute;ch h&agrave;ng qu&aacute; gi&agrave;, th&iacute;nh gi&aacute;c k&eacute;m phải  đọc to v&agrave; lặp lại nhiều lần, hoặc họ ch&ecirc; giọng kh&ocirc;ng tốt, bị đuổi về&hellip; l&agrave;  những rủi ro thường gặp khi l&agrave;m nghề n&agrave;y. Một nữ sinh cho biết từng  phải đọc truyện sex theo y&ecirc;u cầu của &ocirc;ng kh&aacute;ch 63 tuổi khiến c&ocirc; rất bức  x&uacute;c, nhưng chẳng d&aacute;m phản kh&aacute;ng v&igrave; họ Thượng đế. Tuy nhi&ecirc;n, c&ocirc; chỉ l&agrave;m ở  đ&oacute; một lần rồi kh&ocirc;ng bao giờ quay lại nữa.</p>\n<p class=\"Normal\">Mấy năm gần đ&acirc;y khi nh&agrave; đ&agrave;i nở rộ c&aacute;c gameshow th&igrave;  cũng đồng nghĩa xuất hiện một c&ocirc;ng việc mới - vỗ tay thu&ecirc;. Ban đầu l&agrave;  cộng t&aacute;c vi&ecirc;n của c&aacute;c đ&agrave;i được cử l&agrave;m th&ecirc;m việc hướng dẫn kh&aacute;n giả vỗ  tay trong c&aacute;c gameshow, như <em>Chiếc n&oacute;n diệu kỳ, H&atilde;y chọn gi&aacute; đ&uacute;ng</em>&hellip;  Sau n&agrave;y, c&ocirc;ng việc được &ldquo;chuy&ecirc;n nghiệp h&oacute;a&rdquo; khi c&oacute; đội ngũ chuy&ecirc;n ngồi ở  ghế kh&aacute;n giả v&agrave; chỉ c&oacute; nhiệm vụ vỗ tay hoặc đu đưa theo nhạc.</p>\n<p class=\"Normal\">Thu Hương, sinh vi&ecirc;n ĐH Giao th&ocirc;ng Vận tải, một &ldquo;vỗ  tay vi&ecirc;n&rdquo; kỳ cựu h&agrave;o hứng kể: &ldquo;Hồi năm thứ nhất, m&igrave;nh hay l&agrave;m cho chương  tr&igrave;nh <em>Sao Online</em>. C&ocirc;ng việc đơn giản, chỉ thỉnh thoảng vỗ tay  hay đu đưa theo nhạc khi được y&ecirc;u cầu, vừa l&agrave;m lại vừa được nghe c&aacute;c ca  sĩ h&aacute;t. M&igrave;nh nhớ nhất lần được gặp thần tượng Th&aacute;i Th&ugrave;y Linh. H&ocirc;m đấy  m&igrave;nh cổ vũ hết m&igrave;nh&rdquo;.</p>\n<p class=\"Normal\">Cơ hội được gặp thần tượng, được xuất hiện tr&ecirc;n truyền  h&igrave;nh ch&iacute;nh l&agrave; sức h&uacute;t c&aacute;c bạn trẻ lựa chọn c&ocirc;ng việc &ldquo;vỗ tay thu&ecirc;&rdquo;, tuy  rằng lương kh&aacute; \"b&egrave;o\" chỉ 50.000-70.000 đồng cho một buổi diễn. Kh&ocirc;ng  chỉ vậy, c&aacute;c show diễn thường kh&aacute; d&agrave;i hoặc phải quay đi quay lại n&ecirc;n  sinh vi&ecirc;n thường ngồi cả buổi hoặc thậm ch&iacute; cả ng&agrave;y ở trường quay.</p>\n<p class=\"Normal\">&ldquo;Những lần phải quay cả ng&agrave;y, b&ecirc;n đ&agrave;i chi tiền ăn trưa  nhưng kh&ocirc;ng nhiều. Trong l&uacute;c quay để giữ im lặng, tất cả mọi người  kh&ocirc;ng được n&oacute;i chuyện hay nghe điện thoại. Hồi đầu m&igrave;nh mới l&agrave;m th&igrave;  th&iacute;ch, nhưng nhanh ch&aacute;n lắm v&igrave; mất thời gian v&agrave; g&ograve; b&oacute; qu&aacute;&rdquo;, Thu Giang  cho biết.</p>\n<div style=\"float: right;\">Theo VnExpress</div>','2011-03-31 21:09:09','admin@jobbid.vn',379,0,NULL),(24,'cau-sv-nam-3-lap-trang-web-viec-lam-cho-sv','Cậu SV năm 3 lập trang web việc làm cho SV',0,'Cong viec ban thoi gian - viec lam tu do - tim viec ban thoi gian - Ở tuổi đôi mươi, Lại Thế Long (sinh viên năm thứ 3, Trường ĐH Thương mại) đã là chủ xị của ClubVieclam.com với số nhân sự chính thức gần 50 người cùng thu nhập hàng chục triệu đồng/tháng.','<div class=\"fon33 mt1\"><span style=\"color: #888888;\"><strong>Ở tuổi đ&ocirc;i mươi, Lại Thế Long (sinh vi&ecirc;n năm thứ  3, Trường ĐH Thương mại) đ&atilde; l&agrave; \"chủ xị\" của ClubVieclam.com với số nh&acirc;n  sự ch&iacute;nh thức gần 50 người c&ugrave;ng thu nhập h&agrave;ng chục triệu đồng/th&aacute;ng.</strong></span></div>\n<p><span style=\"font-weight: bold;\">\"D&ugrave; đ&oacute;i, d&ugrave; no nhưng c&aacute;i chữ th&igrave; kh&ocirc;ng thể thiếu\" </span></p>\n<p>Gia đ&igrave;nh Long c&oacute; 5 anh chị em, cha l&agrave; thương binh hạng 3/4 mẹ l&agrave;m  nghề n&ocirc;ng n&ecirc;n gặp nhiều kh&oacute; khăn. Mấy s&agrave;o ruộng cũng chỉ đủ cho gia đ&igrave;nh  ăn đến th&aacute;ng 3. Bởi vậy m&agrave; ngay từ khi c&ograve;n nhỏ, Long c&ugrave;ng mấy chị em  phải lặn lội m&ograve; cua, bắt ốc, h&aacute;i đỗ, m&oacute;t lạc tr&ecirc;n những c&aacute;nh đồng sau  m&ugrave;a vụ.</p>\n<p>Cuộc sống vất vả l&agrave; thế nhưng mẹ Long vẫn cố gắng thức khuya dậy sớm  lo cho anh chị em Long c&aacute;i ăn, c&aacute;i mặc những mong con được c&aacute;i chữ n&ecirc;n  người. Dẫu vậy, ho&agrave;n cảnh gia đ&igrave;nh kh&oacute; khăn đ&atilde; khiến người anh trai v&agrave;  hai chị g&aacute;i của Long kh&ocirc;ng thể tiếp tục đến trường. Trong gia đ&igrave;nh, chỉ  c&oacute; Long v&agrave; người em &uacute;t l&agrave; may mắn được học h&agrave;nh đến nơi đến chốn.</p>\n<div>\"T&ocirc;i vẫn nhớ như in h&igrave;nh ảnh chị g&aacute;i ngồi &ocirc;m cột nh&agrave; kh&oacute;c một m&igrave;nh  mỗi khi nh&igrave;n bạn b&egrave; c&ugrave;ng trang lứa cắp s&aacute;ch tới trường, rồi c&acirc;u n&oacute;i của  mẹ ng&agrave;y n&agrave;o như vẫn c&ograve;n văng vẳng b&ecirc;n tai: Nh&agrave; m&igrave;nh ngh&egrave;o nhưng mẹ sẽ  kh&ocirc;ng để c&aacute;c con thất học, d&ugrave; đ&oacute;i, d&ugrave; no nhưng c&aacute;i chữ th&igrave; kh&ocirc;ng thể  thiếu\", Long chia sẻ.</div>\n<div>&nbsp;</div>\n<div><span style=\"font-size: 10pt; font-family: Tahoma;\">\n<div><img style=\"margin: 5px;\" src=\"http://dantri.vcmedia.vn/Uploaded/2011/03/15/sv%209x.jpg\" alt=\"\" width=\"300\" align=\"center\" /></div>\n</span></div>\n<div><span style=\"font-size: 10pt; font-family: Tahoma;\">Lại Thế Long (sinh vi&ecirc;n năm thứ 3, Trường ĐH Thương mại) - &ocirc;ng chủ của ClubVieclam.com.</span></div>\n<p><span style=\"font-weight: bold;\">ClubVieclam.com của \"anh ch&agrave;ng v&ocirc; dụng\" </span></p>\n<p>Những ng&agrave;y mới l&ecirc;n H&agrave; Nội trọ học, Long n&oacute;i cuộc sống của cậu thực sự  nh&agrave;m ch&aacute;n. Hằng ng&agrave;y, cậu chỉ l&ecirc;n lớp rồi tan học lại trở về k&yacute; t&uacute;c x&aacute;.</p>\n<p>\"T&ocirc;i thấy ch&aacute;n nản v&igrave; thời gian tr&ocirc;i đi v&ocirc; nghĩa. Bố mẹ vất vả nu&ocirc;i  t&ocirc;i ăn học, rồi cứ đ&agrave; n&agrave;y, ra trường, chắc lại phải vất vả mất tiền xin  việc. T&ocirc;i cảm thấy m&igrave;nh thật v&ocirc; dụng\", Long n&oacute;i.</p>\n<p>Nghĩ bản th&acirc;n cần phải thay đổi, Long quyết định t&igrave;m việc l&agrave;m th&ecirc;m.  Thế nhưng kh&ocirc;ng may, cậu bạn lại mất tiền v&agrave;o tay một c&ocirc;ng ty \"ma\". Sau  nhiều th&aacute;ng t&igrave;m kiếm, Long được c&aacute;c anh chị kh&oacute;a trước của trường ĐH  Thương mại giới thiệu cho c&ocirc;ng việc ph&aacute;t tờ rơi với mức lương 50.000  đồng/buổi chiều.</p>\n<p>Đầu ti&ecirc;n chỉ l&agrave; những tờ rơi b&igrave;nh thường, sau dần cậu mở rộng ra  nhiều sản phẩm kh&aacute;c như dịch vụ in ấn, cung cấp nh&acirc;n sự <a title=\"l&agrave;m việc part-time\" href=\"http://www.jobbid.vn\">l&agrave;m việc part-time</a> (PG,  b&ecirc; tr&aacute;p cưới hỏi&hellip;), tổ chức sự kiện&hellip; Tuy nhi&ecirc;n, sự thiếu ổn định về nh&acirc;n  sự ng&agrave;y đ&oacute; khiến Long gặp rất nhiều kh&oacute; khăn. Từ đ&oacute;, &yacute; tưởng  ClubVieclam.com đ&atilde; ra đời với mục ti&ecirc;u tập trung v&agrave;o việc đ&agrave;o tạo kỹ  năng l&agrave;m việc thực tiễn v&agrave; ph&aacute;t triển <a title=\"c&ocirc;ng việc part-time\" href=\"http://www.jobbid.vn\">c&ocirc;ng việc part-time</a>.</p>\n<p>\"Tưởng lập một club dễ d&agrave;ng, ai ngờ gặp qu&aacute; nhiều kh&oacute; khăn, c&oacute; l&uacute;c  tưởng chừng như sụp đổ. Ai d&aacute;m theo một sinh vi&ecirc;n năm thứ nhất như  m&igrave;nh?\", Long nhớ lại.</p>\n<p>Giờ đ&acirc;y, sau gần 2 năm, từ một nh&oacute;m bạn chơi th&acirc;n, ClubVieclam.com đ&atilde;  trở th&agrave;nh tổ chức hoạt động hiệu quả. Đ&acirc;y cũng l&agrave; một trong số &iacute;t những  c&acirc;u lạc bộ sinh vi&ecirc;n c&oacute; nguồn thu lớn về t&agrave;i ch&iacute;nh.</p>\n<p>ClubVieclam.com hiện thu h&uacute;t sự quan t&acirc;m của h&agrave;ng trăm sinh vi&ecirc;n tr&ecirc;n  địa b&agrave;n H&agrave; Nội. Với sự quyết t&acirc;m c&ugrave;ng đam m&ecirc; kinh doanh ch&aacute;y bỏng, Lại  Thế Long v&agrave; nh&oacute;m bạn đang biến ClubVieclam.com trở th&agrave;nh h&igrave;nh mẫu đ&aacute;ng  học hỏi cho sinh vi&ecirc;n c&aacute;c trường đại học, cao đẳng.</p>\n<p>Theo <span style=\"font-style: italic;\">SVVN</span></p>','2011-04-04 10:42:32','admin@jobbid.vn',224,0,NULL),(25,'sinh-vien-duoc-va-mat-voi-cong-viec-ban-thoi-gian','Sinh viên được và mất với công việc bán thời gian',0,'Cong viec ban thoi gian - Freelancer (lao động tự do) là thuật ngữ quen thuộc song cũng khá lạ lẫm với không ít sinh viên. Freelancer có thể dễ dàng kiếm được vài trăm đô mỗi tháng nhưng những khó khăn, vất vả họ gặp phải là không hề nhỏ.','<p><span style=\"color: #888888;\"><strong style=\"padding: 0px; margin: 0px;\">Freelancer (lao động tự do) l&agrave; thuật ngữ quen thuộc song cũng kh&aacute; lạ lẫm với kh&ocirc;ng &iacute;t sinh vi&ecirc;n. Freelancer c&oacute; thể dễ d&agrave;ng kiếm được v&agrave;i trăm đ&ocirc; mỗi th&aacute;ng nhưng những kh&oacute; khăn, vất vả họ gặp phải l&agrave; kh&ocirc;ng hề nhỏ.</strong></span><br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" /><strong style=\"padding: 0px; margin: 0px;\">Được nhiều tiền v&agrave; kinh nghiệm</strong><br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Những người vừa tiếp cận freelance hẳn sẽ rất ngỡ ng&agrave;ng khi biết được gi&aacute; trị của chất x&aacute;m m&agrave; c&aacute;c freelancer bỏ ra. Với mỗi c&ocirc;ng việc, c&aacute;c freelancer c&oacute; thể nhận được v&agrave;i chục đến v&agrave;i trăm, thậm ch&iacute; h&agrave;ng ngh&igrave;n đ&ocirc; từ c&aacute;c kh&aacute;ch h&agrave;ng. Đối tượng kh&aacute;ch h&agrave;ng rất đa dạng, c&oacute; thể l&agrave; c&aacute;c lao động b&igrave;nh thường hay sinh vi&ecirc;n, nghi&ecirc;n cứu sinh v&agrave; thậm ch&iacute; cả những người c&oacute; học vị cao hơn.<br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Nguyễn Th&agrave;nh Trung, sinh vi&ecirc;n Khoa C&ocirc;ng nghệ Th&ocirc;ng tin, Đại học C&ocirc;ng nghệ H&agrave; Nội, vốn l&agrave; một freelancer l&acirc;u năm. Trung tự h&agrave;o cho biết về khoản tiền m&igrave;nh kiếm được từ c&ocirc;ng việc: \"Mỗi một th&aacute;ng m&igrave;nh kiếm được xấp xỉ 1000 đ&ocirc;. C&ocirc;ng việc thường l&agrave; giải b&agrave;i tập cho c&aacute;c sinh vi&ecirc;n nước ngo&agrave;i. T&ugrave;y v&agrave;o độ phức tạp của b&agrave;i tập, m&igrave;nh thường lấy từ 30-50 đ&ocirc;. Thỉnh thoảng m&igrave;nh cũng nhận c&aacute;c project (dự &aacute;n) v&agrave;i trăm đ&ocirc;. Vừa c&oacute; nhiều tiền, vừa được l&agrave;m quen với &aacute;p lực c&ocirc;ng việc, vừa tạo được tiếng tăm, tội g&igrave; kh&ocirc;ng l&agrave;m.\"<br style=\"padding: 0px; margin: 0px;\" /></p>\n<div style=\"padding: 0px; margin: 0px;\">\n<table class=\"image center\" style=\"padding: 0px; margin: 0px auto; text-align: center; border-collapse: collapse; background-color: #eeeeee; width: 480px; color: #004488;\" cellspacing=\"0\" cellpadding=\"0\" width=\"400\" align=\"center\">\n<tbody style=\"padding: 0px; margin: 0px;\">\n<tr style=\"padding: 0px; margin: 0px;\">\n<td style=\"padding: 0px; margin: 0px; text-align: center;\"><img style=\"padding: 0px; margin: 0px; border-width: 0px; max-width: 480px;\" src=\"http://imgs.vietnamnet.vn/Images/2011/04/01/10/20110401103737_can%20phai%20co%20su%20can%20nhac%20ki%20cang%20khi%20quyet%20dinh%20lam%20freelance.jpg\" alt=\"\" /></td>\n</tr>\n<tr style=\"padding: 0px; margin: 0px;\">\n<td class=\"image_desc\" style=\"padding: 5px; margin: 0px; text-align: center; color: #004488; background-color: #eeeeee; border-collapse: collapse;\">Cần c&acirc;n nhắc trước khi quyết định trở th&agrave;nh freelancer</td>\n</tr>\n</tbody>\n</table>\n</div>\n<p>Theo Trung, đối tượng m&agrave; c&aacute;c freelancer sinh vi&ecirc;n n&ecirc;n hướng đến ch&iacute;nh l&agrave; c&aacute;c sinh vi&ecirc;n ở c&aacute;c trường quốc tế. C&oacute; rất nhiều sinh vi&ecirc;n \"đầu rỗng t&uacute;i dầy\" hoặc kh&ocirc;ng c&oacute; thời gian l&agrave;m b&agrave;i tập sẽ đăng y&ecirc;u cầu l&ecirc;n c&aacute;c trang web freelance. C&aacute;c freelancer chỉ việc accept (chấp nhận), giải b&agrave;i tập, gửi cho kh&aacute;ch h&agrave;ng v&agrave; nhận tiền qua t&agrave;i khoản.&nbsp;<span class=\"Apple-converted-space\">&nbsp;</span><br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Hải Đăng, sinh vi&ecirc;n Đại học B&aacute;ch khoa H&agrave; Nội, l&agrave; một \"chuy&ecirc;n gia freelancer\" về lập tr&igrave;nh m&aacute;y t&iacute;nh. Kh&ocirc;ng đầu tư nhiều thời gian cho c&ocirc;ng việc freelance nhưng bạn cũng kiếm về v&agrave;i trăm đ&ocirc; mỗi th&aacute;ng. Đối với bạn, đ&acirc;y l&agrave; một c&ocirc;ng việc cực k&igrave; đơn giản v&agrave; cũng dễ kiếm tiền: \"Code (viết m&atilde; lập tr&igrave;nh) 6 d&ograve;ng được 10 bảng, tin nổi kh&ocirc;ng?\"<br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Đăng c&ograve;n bật m&iacute; về những c&aacute;ch \"c&acirc;u tiền\" của kh&aacute;ch h&agrave;ng \"g&agrave;\": \"C&oacute; những b&agrave;i tập dễ ợt nhưng m&igrave;nh cứ n&oacute;i l&agrave; rất kh&oacute;, rất phức tạp. Thế l&agrave; \"con g&agrave;\" cuống qu&iacute;t tăng tiền l&ecirc;n ngay!\"<br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" /><strong style=\"padding: 0px; margin: 0px;\">Mất thời gian v&agrave; c&ocirc;ng sức</strong><br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Đối với sinh vi&ecirc;n, c&ocirc;ng việc ch&iacute;nh vẫn l&agrave; học. Tuy nhi&ecirc;n, với khoản thu hấp dẫn kh&ocirc;ng kh&oacute; g&igrave; c&oacute; được, nhiều sinh vi&ecirc;n đ&atilde; lao v&agrave;o l&agrave;m freelancer như một c&ocirc;ng việc full time.<span class=\"Apple-converted-space\">&nbsp;</span><br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Quang Ph&uacute;c, sinh vi&ecirc;n Khoa Vật l&iacute; kĩ thuật, Đại học C&ocirc;ng nghệ H&agrave; Nội kh&ocirc;ng kh&aacute;c g&igrave; một \"con thi&ecirc;u th&acirc;n\" của c&ocirc;ng việc freelance. Mới bước v&agrave;o nghề được v&agrave;i th&aacute;ng m&agrave; khoản thu nhập của Ph&uacute;c đ&atilde; l&ecirc;n đến hơn một ng&agrave;n đ&ocirc;. L&agrave;m việc qu&ecirc;n ăn, qu&ecirc;n ngủ, v&agrave; đương nhi&ecirc;n cả qu&ecirc;n học nữa. Người Ph&uacute;c gầy rộc đi tr&ocirc;ng thấy. Bạn b&egrave; khuy&ecirc;n bảo th&igrave; Ph&uacute;c gạt đi, cho rằng bạn b&egrave; \"ghen tị\" khi thấy m&igrave;nh c&oacute; nhiều tiền v&agrave; hi vọng tới chiếc xe m&aacute;y xịn v&agrave; chiếc laptop Vaio mới, thay cho chiếc laptop đ&atilde; bốn tuổi của m&igrave;nh. Nhưng c&oacute; lẽ, điều bạn n&ecirc;n hi vọng b&acirc;y giờ l&agrave; việc vượt qua k&igrave; thi học k&igrave; sắp tới!<br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Hải Yến, nữ lập tr&igrave;nh vi&ecirc;n tương lai ki&ecirc;m freelancer đang học tại Đại học FPT,&nbsp; l&agrave; nạn nh&acirc;n trong một c&uacute; lừa đau đớn. V&igrave; qu&aacute; cả tin, sau khi l&agrave;m xong project được giao, bạn đ&atilde; gửi thẳng cả source (file nguồn) cho kh&aacute;ch h&agrave;ng. Kết quả, 1000 đ&ocirc; m&agrave; bạn đặt quyết t&acirc;m gi&agrave;nh được mấy tuần nay đ&atilde; bị kh&aacute;ch h&agrave;ng Ấn Độ \"b&ugrave;ng\". H&oacute;a ra, bạn với kh&aacute;ch h&agrave;ng thỏa thuận ri&ecirc;ng sẽ chuyển khoản Paypal, kh&ocirc;ng th&ocirc;ng qua website để tr&aacute;nh mất ph&iacute; nhưng kh&aacute;ch h&agrave;ng đ&atilde; \"im thin th&iacute;t v&agrave; lặn mất tăm\".<br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Kh&ocirc;ng giấu nổi sự ấm ức, Yến cho biết: \"Từ giờ m&igrave;nh phải chọn những kh&aacute;ch h&agrave;ng c&oacute; nhiều review (nhận x&eacute;t) tốt v&agrave; phải nhận tiền qua web freelance. Đ&aacute;ng lẽ m&igrave;nh phải gửi bản demo (bản thử nghiệm) để kh&aacute;ch h&agrave;ng duyệt v&agrave; gửi tiền trước chứ kh&ocirc;ng gửi thẳng cả source như thế\".<br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" /><strong style=\"padding: 0px; margin: 0px;\">Cần c&acirc;n nhắc giữa được v&agrave; mất</strong><br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />Kh&ocirc;ng thể phủ nhận những gi&aacute; trị m&agrave; c&ocirc;ng việc freelance mang lại, song với sinh vi&ecirc;n, c&ocirc;ng việc n&agrave;y cần một sự sắp xếp hiệu quả sao cho kh&ocirc;ng ảnh hưởng tới học tập v&agrave; sinh hoạt của m&igrave;nh. Freelance kh&ocirc;ng phải l&agrave; một c&ocirc;ng việc ổn định, l&uacute;c c&ocirc;ng việc ngập đầu, l&uacute;c lại ngồi chơi xơi nước. V&igrave; thế cần phải c&oacute; sự c&acirc;n nhắc kĩ c&agrave;ng giữa việc theo đuổi freelance với việc tập trung học tập để c&oacute; một c&ocirc;ng việc ổn định sau khi ra trường.<br style=\"padding: 0px; margin: 0px;\" /></p>\n<div style=\"padding: 0px; margin: 0px;\">\n<table class=\"quote center\" style=\"padding: 0px; margin: 0px auto; text-align: center; border: 1px solid #d6dce8; background-color: #eeeeee;\" width=\"400\" align=\"center\">\n<tbody style=\"padding: 0px; margin: 0px;\">\n<tr style=\"padding: 0px; margin: 0px;\">\n<td style=\"padding: 3px 5px; margin: 0px; color: black; text-align: justify;\">\n<div style=\"padding: 0px; margin: 0px; text-align: justify;\">Freelancer l&agrave; người lao động độc lập, kh&ocirc;ng chịu sự quản l&iacute; của bất k&igrave; chủ lao động n&agrave;o. C&aacute;c lĩnh vực trong Freelance gồm: b&aacute;o ch&iacute;, xuất bản, viết văn, trang điểm, bi&ecirc;n tập, lập kế hoạch sự kiện, quản l&iacute; sự kiện, lập tr&igrave;nh m&aacute;y t&iacute;nh, thiết kế web, thiết kế đồ họa, ph&aacute;t triển website, bi&ecirc;n dịch, hướng dẫn vi&ecirc;n...<br style=\"padding: 0px; margin: 0px;\" /><br style=\"padding: 0px; margin: 0px;\" />C&aacute;c trang web freelance l&agrave; trung gian của kh&aacute;ch h&agrave;ng v&agrave; freelancer. Tiền của kh&aacute;ch h&agrave;ng gửi cho freelancer sẽ được website khấu trừ để l&agrave;m ph&iacute; ph&aacute;t triển web. Ngo&agrave;i khoản thu của website, freelancer c&ograve;n phải chịu khoản ph&iacute; chuyển t&agrave;i khoản quốc tế. Tuy nhi&ecirc;n, hai mức ph&iacute; n&agrave;y kh&ocirc;ng đ&aacute;ng kể, chỉ khoảng v&agrave;i % tiền c&ocirc;ng của c&ocirc;ng việc<br style=\"padding: 0px; margin: 0px;\" /></div>\n</td>\n</tr>\n</tbody>\n</table>\n</div>\n<p><br style=\"padding: 0px; margin: 0px;\" /><strong style=\"padding: 0px; margin: 0px;\">Đỗ Quang </strong><em><strong style=\"padding: 0px; margin: 0px;\">(theo vietnamnet.vn)</strong></em><strong style=\"padding: 0px; margin: 0px;\"></strong><strong style=\"padding: 0px; margin: 0px;\"></strong><strong style=\"padding: 0px; margin: 0px;\"><br /></strong></p>','2011-04-05 08:46:47','admin@jobbid.vn',336,0,NULL),(26,'hieu-qua-san-giao-dich-viec-lam-khong-nhu-mong-doi','Hiệu quả sàn giao dịch việc làm không như mong đợi',0,'Thông tin đấu thầu - Nhiều sàn giao dịch việc làm cả năm chỉ hoạt động một lần, còn những sàn hoạt động khá đều đặn thì chỉ mang tính hình thức...','<div class=\"w_260 f_l \" style=\"margin: 0px; padding: 0px; float: left; width: 260px; border-right: 30px solid #ffffff; border-bottom: 30px solid #ffffff;\">\n<div class=\"w_260 f_l\" style=\"margin: 0px; padding: 0px; float: left; width: 260px;\"><br class=\"Apple-interchange-newline\" /><img style=\"border-width: 0px;\" title=\"Hiệu quả s&agrave;n giao dịch việc l&agrave;m kh&ocirc;ng như mong đợi\" src=\"http://vneconomy1.vcmedia.vn/ThumbImages/Images/Uploaded/Share/2010/09/09/sangiaodich_260.gif\" alt=\"picture\" width=\"260\" /></div>\n<div class=\"w_260 f_l\" style=\"margin: 0px; padding: 0px; float: left; width: 260px;\">\n<p style=\"margin: 5px 0px 0px; padding: 0px;\"><span style=\"color: #808080; font-family: Arial; font-size: x-small;\">Hoạt động tuyển dụng tại một s&agrave;n giao dịch việc l&agrave;m.</span></p>\n</div>\n</div>\n<p style=\"margin: 0px; padding: 0px;\">&nbsp;</p>\n<div id=\"divBodyTinChiTiet\" style=\"margin: 0px; padding: 0px; font-size: 11pt;\"><span style=\"color: #000000; line-height: 1.5; font-size: 11pt; font-family: Arial;\"><span style=\"font-weight: bold; font-size: 11pt; color: #888888;\">Nhiều s&agrave;n giao dịch việc l&agrave;m cả năm chỉ hoạt động một lần, c&ograve;n những s&agrave;n hoạt động kh&aacute; đều đặn th&igrave; chỉ mang t&iacute;nh h&igrave;nh thức...</span><br style=\"font-weight: bold; font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />Những nhận định n&agrave;y đ&atilde; được ra tại hội nghị đ&aacute;nh gi&aacute; hiệu quả hoạt động của c&aacute;c s&agrave;n giao dịch việc l&agrave;m giai đoạn 2006 - 2010 v&agrave; c&ocirc;ng t&aacute;c dự b&aacute;o nhu cầu nh&acirc;n lực, diễn ra s&aacute;ng 9/9 tại H&agrave; Nội.<br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />&Ocirc;ng Nguyễn Đại Đồng, Cục trưởng Cục Việc l&agrave;m, Bộ Lao động - Thương binh v&agrave; X&atilde; hội cho biết, &yacute; tưởng x&acirc;y dựng cầu nối giữa doanh nghiệp v&agrave; người lao động, hay n&oacute;i c&aacute;ch kh&aacute;c giữa cung v&agrave; cầu lao động hoạt động từ năm 1990, th&ocirc;ng qua c&aacute;c hội chợ việc l&agrave;m. Sau n&agrave;y m&ocirc; h&igrave;nh n&agrave;y được ph&aacute;t triển th&agrave;nh s&agrave;n giao dịch việc l&agrave;m.<br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />&Ocirc;ng Đồng cho rằng, mục đ&iacute;ch của ng&agrave;nh lao động đặt ra ở m&ocirc; h&igrave;nh s&agrave;n giao giao dịch việc l&agrave;m kh&aacute;c với hội chợ việc l&agrave;m l&agrave; tổ chức hoạt động thường xuy&ecirc;n, chuy&ecirc;n nghiệp v&agrave; hiệu quả hơn, c&oacute; thể hoạt động nhiều lần trong một th&aacute;ng. Tuy nhi&ecirc;n, thực tế, hiệu quả của c&aacute;c s&agrave;n giao dịch việc l&agrave;m kh&ocirc;ng được như mong đợi.<span class=\"Apple-converted-space\">&nbsp;</span><br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />Theo b&aacute;o c&aacute;o của Bộ Lao động - Thương binh v&agrave; X&atilde; hội, hiện nay hầu hết c&aacute;c tỉnh, th&agrave;nh tr&ecirc;n cả nước đều c&oacute; c&aacute;c s&agrave;n giao dịch việc l&agrave;m. Trong đ&oacute;, c&oacute; 44 s&agrave;n đang hoạt động định kỳ, thường xuy&ecirc;n với tần suất 1 phi&ecirc;n/th&aacute;ng, nhiều trung t&acirc;m tổ chức s&agrave;n giao dịch việc l&agrave;m với tần suất 2 phi&ecirc;n/th&aacute;ng.<br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />Mức đầu tư cho một s&agrave;n giao dịch việc l&agrave;m khoảng từ 5 đến 7 tỷ đồng. Tuy nhi&ecirc;n, tỷ lệ t&igrave;m việc qua trung t&acirc;m mới chỉ đạt 14%; tỷ lệ người sử dụng lao động qua trung t&acirc;m cũng mới chỉ đạt 16,4%.<span class=\"Apple-converted-space\">&nbsp;</span><br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />B&aacute;o c&aacute;o từ c&aacute;c trung t&acirc;m giới thiệu việc l&agrave;m của c&aacute;c địa phương cũng cho thấy hiệu quả cung ứng lao động th&ocirc;ng qua c&aacute;c s&agrave;n hiện vẫn thấp.<br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />Cụ thể, tại phi&ecirc;n giao dịch việc l&agrave;m lần thứ 19 hồi th&aacute;ng 4 của t&igrave;nh B&igrave;nh Dương, c&oacute; 317 doanh nghiệp đăng k&yacute; tuyển dụng với hơn 20.000 chỗ l&agrave;m trống nhưng chỉ c&oacute; hơn 1.000 lao động tới tham gia tuyển dụng trực tiếp. Tỉnh Vĩnh Ph&uacute;c từ đầu năm đến nay cũng đ&atilde; tổ chức 6 phi&ecirc;n giao dịch việc l&agrave;m với nhu cầu cần tuyển dụng khoảng 12.000 lao động. Tuy nhi&ecirc;n, số lao động được tuyển cũng chỉ đ&aacute;p ứng được khoảng 10%.<span class=\"Apple-converted-space\">&nbsp;</span><br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />Tại Trung t&acirc;m Giới thiệu việc l&agrave;m H&agrave; Nội, &ocirc;ng Vũ Trung Ch&iacute;nh, Gi&aacute;m đốc Trung t&acirc;m cho biết, từ năm 2007 đến th&aacute;ng 6/2010,&nbsp; nơi n&agrave;y đ&atilde; tổ chức 56 phi&ecirc;n giao dịch với 4.868 đơn vị doanh nghiệp tham gia. Số người đến phi&ecirc;n giao dịch việc l&agrave;m 150.800 người, b&igrave;nh qu&acirc;n gần 2.700 người/phi&ecirc;n; số người được c&aacute;c nh&agrave; tuyển dụng phỏng vấn trực tiếp v&agrave; tiếp nhận hồ sơ tại c&aacute;c phi&ecirc;n giao dịch việc l&agrave;m l&agrave; 116.135 lượt lao động; trung b&igrave;nh mỗi phi&ecirc;n giao dịch việc l&agrave;m c&oacute; 1.874 người được phỏng vấn v&agrave; tiếp nhận hồ sơ.<span class=\"Apple-converted-space\">&nbsp;</span><br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />Tuy nhi&ecirc;n, với những con số xem ra rất &ldquo;ho&agrave;nh tr&aacute;ng&rdquo; tr&ecirc;n, &ocirc;ng Ch&iacute;nh vẫn thừa nhận, khả năng đ&aacute;p ứng cầu lao động của s&agrave;n chưa cao, số lượng mới chỉ đ&aacute;p ứng được khoảng 20% nhu cầu của c&aacute;c doanh nghiệp.<span class=\"Apple-converted-space\">&nbsp;</span><br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />Tại hội nghị, b&agrave; Nguyễn Hải V&acirc;n, Ph&oacute; cục trưởng Cục Việc l&agrave;m, Gi&aacute;m đốc Trung t&acirc;m quốc gia Dự b&aacute;o v&agrave; th&ocirc;ng tin thị trường lao động cũng đưa ra nhận định, hệ thống dịch vụ việc l&agrave;m hiện thời chủ yếu tổ chức c&aacute;c hoạt động định kỳ của s&agrave;n giao dịch, mới chỉ đ&aacute;p ứng được khoảng 10 - 15% nhu cầu thực tế về tư vấn v&agrave; giải quyết việc l&agrave;m.<span class=\"Apple-converted-space\">&nbsp;</span><br style=\"font-size: 11pt;\" /><br style=\"font-size: 11pt;\" />Nguy&ecirc;n nh&acirc;n của sự k&eacute;m n&oacute;i tr&ecirc;n, theo b&agrave; V&acirc;n, l&agrave; do c&ocirc;ng t&aacute;c ph&acirc;n t&iacute;ch th&ocirc;ng tin thị trường lao động c&ograve;n nhiều hạn chế, chưa c&oacute; đ&aacute;nh gi&aacute; s&acirc;u về thị trường lao động tại c&aacute;c địa phương. B&ecirc;n cạnh đ&oacute;, một số địa phương sử dụng vốn đầu tư chưa đ&uacute;ng mục đ&iacute;ch; đội ngũ c&aacute;n bộ thuộc lĩnh vực n&agrave;y thiếu cả về số lượng v&agrave; sự chuy&ecirc;n nghiệp.</span></div>\n<div style=\"margin: 0px; padding: 0px; font-size: 11pt;\"></div>\n<div style=\"margin: 0px; padding: 0px; font-size: 11pt;\"><em><span style=\"color: #000000; line-height: 1.5; font-size: 11pt; font-family: Arial;\">Nguồn </span>vneconomy.vn</em></div>','2011-04-06 10:47:09','admin@jobbid.vn',160,0,NULL),(27,'kinh-nghiem-tim-viec-cho-nguoi-moi-dinh-cu','Kinh nghiệm tìm việc cho người mới định cư',0,'cong viec part time - cong viec ban thoi gian - Các kỹ năng và kinh nghiệm để tìm kiếm việc làm là một trong những kỹ năng quan trọng nhất cần có của bất cứ ai định cư tại vùng đất mới.','<h2 class=\"Lead\" style=\"font-family: \'Times New Roman\'; font-size: 11pt; color: #5f5f5f; font-weight: bold;\"><img style=\"padding: 5px; float: left;\" title=\"cong viec part time\" src=\"http://vnexpress.net/Files/Subject/3b/a2/84/4b/top.jpg\" alt=\"cong viec part time\" width=\"250\" height=\"195\" />C&aacute;c kỹ năng v&agrave; kinh nghiệm để t&igrave;m kiếm việc l&agrave;m l&agrave; một trong những kỹ năng quan trọng nhất cần c&oacute; của bất cứ ai định cư tại v&ugrave;ng đất mới.</h2>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">Ch&agrave;o c&aacute;c anh, chị tr&ecirc;n diễn đ&agrave;n Người Việt Năm Ch&acirc;u của b&aacute;o<span class=\"Apple-converted-space\">&nbsp;</span><em>VnExpress</em>.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">C&aacute;c kỹ năng v&agrave; kinh nghiệm để t&igrave;m kiếm việc l&agrave;m l&agrave; một trong những kỹ năng quan trọng nhất cần c&oacute; của bất cứ ai định cư tại v&ugrave;ng đất mới. Trong b&agrave;i viết n&agrave;y, t&ocirc;i mong muốn c&oacute; sự chia sẻ v&agrave; đ&oacute;ng g&oacute;p của nhiều anh, chị c&ugrave;ng tham gia để g&oacute;p phần tạo ra một cẩm nang cho cộng đồng người Việt trong việc t&igrave;m kiếm việc l&agrave;m, ổn định cuộc sống.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">Dưới đ&acirc;y l&agrave; những kinh nghiệm v&agrave; tri thức m&agrave; t&ocirc;i c&oacute; được tại Canada, xin chia sẻ với c&aacute;c anh, chị v&agrave; hy vọng c&oacute; th&ecirc;m nhiều &yacute; kiến đ&oacute;ng g&oacute;p bổ &iacute;ch kh&aacute;c:</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">1. T&igrave;m hiểu th&ocirc;ng tin, x&aacute;c định mục đ&iacute;ch</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- X&aacute;c định r&otilde; những việc bạn c&oacute; thể l&agrave;m: những việc l&agrave;m fulltime, c&aacute;c việc l&agrave;m b&aacute;n thời gian phụ thuộc v&agrave;o năng lực, sở th&iacute;ch v&agrave; điều kiện của bản th&acirc;n. C&oacute; những l&uacute;c cần phải lấy ngắn nu&ocirc;i d&agrave;i bằng c&aacute;c c&ocirc;ng việc đơn giản v&agrave; b&aacute;n thời gian khi mới tạo lập cuộc sống. Đối với việc l&agrave;m b&aacute;n thời gian cũng rất đa dạng, v&igrave; Canada trả lương theo giờ (l&agrave;m giờ n&agrave;o t&iacute;nh tiền giờ đ&oacute;), n&ecirc;n c&aacute;c &ocirc;ng chủ khi thu&ecirc; nh&acirc;n vi&ecirc;n cũng kh&aacute; linh hoạt về thời gian, thậm ch&iacute; l&agrave;m ng&acirc;n h&agrave;ng cũng c&oacute; nh&acirc;n vi&ecirc;n l&agrave;m <a title=\"c&ocirc;ng việc part time\" href=\"http://www.jobbid.vn\">c&ocirc;ng việc part time</a>.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- T&igrave;m hiểu th&ocirc;ng tin qua c&aacute;c website của ch&iacute;nh phủ d&agrave;nh cho người mới tới: để hiểu r&otilde; những dịch vụ của ch&iacute;nh phủ cung cấp cho người mới tới, c&aacute;c quyền lợi v&agrave; nghĩa vụ li&ecirc;n quan.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- T&igrave;m hiểu về xu hướng ph&aacute;t triển c&aacute;c ng&agrave;nh nghề: c&aacute;c ng&agrave;nh nghề đang thiếu nh&acirc;n lực tại li&ecirc;n bang v&agrave; tại địa phương, điều kiện về bằng cấp v&agrave; tr&igrave;nh độ cần thiết đối với từng nghề th&ocirc;ng qua c&aacute;c dữ liệu thống k&ecirc; của ch&iacute;nh phủ, qua website hướng dẫn của ch&iacute;nh phủ.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Mua tạp ch&iacute; d&agrave;nh cho người mới tới ph&aacute;t h&agrave;nh định kỳ, đ&acirc;y l&agrave; tạp ch&iacute; hướng dẫn rất cần thiết v&agrave; c&oacute; nhiều th&ocirc;ng tin bổ &iacute;ch để tạo lập cuộc sống mới ở Canada. Rất nhiều kinh nghiệm phong ph&uacute; của những người định cư th&agrave;nh c&ocirc;ng trong việc tạo lập cuộc sống mới m&agrave; ch&uacute;ng ta c&oacute; thể học tập.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">2. Tham gia v&agrave;o c&aacute;c cộng đồng nghề nghiệp</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Tham gia v&agrave;o c&aacute;c forum tr&ecirc;n mạng: c&aacute;c diễn đ&agrave;n n&agrave;y sẽ cung cấp c&aacute;c c&acirc;u hỏi v&agrave; những t&igrave;nh huống rất thực tế của những người c&ugrave;ng nghề nghiệp v&agrave; c&ugrave;ng mối quan t&acirc;m với bạn. Dữ liệu được lưu trữ từ qu&aacute; khứ tới hiện tại v&agrave; c&oacute; thể tham khao online bất cứ thời gian n&agrave;o.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Tạo ra c&aacute;c quan hệ hỗ trợ cho ph&aacute;t triển nghề nghiệp của bạn</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ Quan hệ với cộng đồng người Việt: những người c&ugrave;ng ng&ocirc;n ngữ, văn h&oacute;a v&agrave; đ&atilde; định cư l&acirc;u năm tại nước sở tại, họ c&oacute; nhiều kinh nghiệm để chia sẻ từ c&aacute;ch kiếm việc, kinh doanh v&agrave; kinh nghiệm sống.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ Tham gia c&aacute;c c&acirc;u lạc bộ d&agrave;nh cho người mới tới: c&aacute;c c&acirc;u lạc bộ n&agrave;y thường do ch&iacute;nh phủ địa phương tạo ra nhằm tạo m&ocirc;i trường giao lưu cho những người mới tới. Bằng c&aacute;ch tham gia c&aacute;c c&acirc;u lạc bộ n&agrave;y bạn sẽ c&oacute; nhiều bạn b&egrave; hơn v&agrave; c&oacute; th&ecirc;m nhhững kinh nghiệm của những người c&ugrave;ng cảnh ngộ, c&aacute;ch thức v&agrave; c&aacute;c b&agrave;i học m&agrave; những người đ&oacute; đ&atilde; trải qua.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Tham gia v&agrave;o c&aacute;c c&acirc;u lạc bộ nghề nghiệp, gặp gỡ những người c&oacute; kinh nghiệm trong lĩnh vực bạn muốn l&agrave;m, đồng thời c&oacute; thể qua trung t&acirc;m hỗ trợ người mới tới, họ c&oacute; thể t&igrave;m kiếm những người c&oacute; kinh nghiệm trong lĩnh vực bạn lựa chọn nghề nghiệp để hẹn gặp v&agrave; chia sẻ kinh nghiệm cho bạn. Đ&acirc;y l&agrave; những người l&agrave;m việc h&ograve;an to&agrave;n thiện nguyện.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Tham gia v&agrave;o c&aacute;c cộng đồng v&agrave; hoạt động x&atilde; hội kh&aacute;c nhau nếu bạn cảm thấy hợp với sở th&iacute;ch v&agrave; c&oacute; lợi cho ph&aacute;t triển mối quan hệ v&agrave; nghề nghiệp sau n&agrave;y.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">3. X&acirc;y dựng kế hoạch học tập</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Học tập fulltime hay b&aacute;n thời gian: T&ugrave;y theo điều kiện kinh tế v&agrave; ho&agrave;n cảnh từng người để đăng k&yacute; học fulltime hay học b&aacute;n thời gian. Tại Canada c&oacute; rất nhiều kh&oacute;a học kh&aacute;c nhau v&agrave; sinh vi&ecirc;n c&oacute; thể lựa chọn thời gian (c&oacute; những buổi học v&agrave;o buổi tối, v&agrave;o chủ nhật, thứ bảy...) v&agrave; c&aacute;ch thức học ph&ugrave; hợp với m&igrave;nh. Kh&ocirc;ng nhất thiết l&uacute;c n&agrave;o cũng phải c&oacute; mặt tr&ecirc;n lớp, chỉ cần l&agrave; vượt qua đầy đủ c&aacute;c kỳ thi l&agrave; được cấp bằng (c&oacute; nhiều người chỉ lấy t&agrave;i liệu về tự học, thời gian l&ecirc;n lớp cũng rất &iacute;t).</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Đối với những người đ&atilde; c&oacute; bằng cấp v&agrave; kinh nghiệm l&agrave;m việc tại Việt Nam th&igrave; n&ecirc;n tận dụng n&oacute;, đừng tự ti rằng c&aacute;c bằng cấp v&agrave; kinh nghiệm n&agrave;y kh&ocirc;ng c&oacute; gi&aacute; trị, Cụ thể nếu bạn từng học đại học tại Việt Nam th&igrave; khi học lại đại học b&ecirc;n Canada c&ugrave;ng ng&agrave;nh sẽ kh&ocirc;ng phải chịu nhiều điều kiện v&agrave; học nhiều m&ocirc;n học như người chưa từng học ng&agrave;nh đ&oacute;. Hoặc c&oacute; nhiều doanh nghiệp khi họ cần người l&agrave;m, nếu bạn c&oacute; bằng cấp tại Việt Nam sẽ được ưu ti&ecirc;n tuyển dụng hơn l&agrave; người kh&aacute;c kh&ocirc;ng c&oacute; kinh nghiệm v&agrave; chủ doanh nghiệp sẽ đ&agrave;o tạo bổ sung th&ecirc;m kỹ năng cần thiết để l&agrave;m tốt <a title=\"c&ocirc;ng việc part time\" href=\"http://www.jobbid.vn\">c&ocirc;ng việc part time</a>. Tại Canada cũng c&oacute; một dịch vụ nhằm đ&aacute;nh gi&aacute; tr&igrave;nh độ v&agrave; kỹ năng của người nhập cư, so s&aacute;nh với ti&ecirc;u chuẩn nghề nghiệp của Canada, sau đ&oacute; tư vấn cho người nhập cư bổ xung th&ecirc;m những kiến thức, kỹ năng ph&ugrave; hợp, kh&ocirc;ng nhất thiết học lại hết từ đầu.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Học thực tế</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ Trở th&agrave;nh kh&aacute;ch h&agrave;ng trước khi l&agrave;m nh&acirc;n vi&ecirc;n. V&iacute; dụ bạn muốn v&agrave;o l&agrave;m việc trong ng&agrave;nh ng&acirc;n h&agrave;ng th&igrave; trước ti&ecirc;n n&ecirc;n trở th&agrave;nh kh&aacute;ch h&agrave;ng của ng&acirc;n h&agrave;ng. Chỉ cần với một số tiền nhỏ để mở t&agrave;i khỏan th&igrave; bạn c&oacute; thể sử dụng rất nhiều c&aacute;c dịch vụ kh&aacute;c nhau, qua đ&oacute; bạn ho&agrave;n to&agrave;n hiểu được kh&aacute; nhiều về c&aacute;c c&ocirc;ng việc sau n&agrave;y bạn sẽ phải l&agrave;m v&agrave; t&acirc;m l&yacute; kh&aacute;ch h&agrave;ng khi m&agrave; bạn đ&atilde; từng sử dụng thực tế sản phẩm của ng&acirc;n h&agrave;ng.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ L&agrave;m c&ocirc;ng t&aacute;c thiện nguyện trước khi đi l&agrave;m. Trước khi bắt tay v&agrave;o một nghề n&agrave;o đ&oacute;, nếu c&oacute; cơ hội l&agrave;m thiện nguyện th&igrave; bạn n&ecirc;n xung phong l&agrave;m. Đ&acirc;y l&agrave; c&aacute;ch rất tốt để học, t&iacute;ch lũy kinh nghiệm thực tế v&agrave; mở rộng quan hệ bạn b&egrave;.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Học c&aacute;c kỹ năng l&agrave;m việc cần thiết</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ Kỹ năng giao tiếp trong doanh nghiệp</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ Tiếng Anh.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ Kỹ năng viết đơn xin việc</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ Kỹ năng phỏng vấn</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">+ C&aacute;c quyền v&agrave; nghĩa vụ của người lao động</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">Những kỹ năng n&agrave;y tại Canada được đ&agrave;o tạo miễn ph&iacute; cho người mới tới n&ecirc;n bạn chỉ cần t&igrave;m những kh&oacute;a học v&agrave; đăng k&yacute; học.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">4. T&igrave;m kiếm việc l&agrave;m</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- C&aacute;c website v&agrave; dịch vụ hỗ trợ t&igrave;m kiếm việc l&agrave;m (người lao động được miễn ph&iacute;).</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Sử dụng c&aacute;c mối quan hệ đ&atilde; c&oacute; được do bạn tự tạo ra trước đ&oacute; (Tại Canada nhiều c&ocirc;ng việc qua bạn b&egrave; v&agrave; người th&acirc;n quen giới thiệu).</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Tham gia c&aacute;c ng&agrave;y hội nghề nghiệp: l&agrave; cơ hội để trao đổi với c&aacute;c doanh nghiệp c&oacute; nhu cầu tuyển dụng tại địa phương. T&ocirc;i đ&atilde; tham gia c&aacute;c hội chợ như n&agrave;y v&agrave; thu được rất nhiều th&ocirc;ng tin. C&aacute;c doanh nghiệp kh&aacute; nhiệt t&igrave;nh giải th&iacute;ch cặn kẽ v&agrave; đưa những th&ocirc;ng tin chi tiết cho bạn về những vị tr&iacute; họ cần, c&ocirc;ng việc đảm nhận, bằng cấp cần, giới thiệu về doanh nghiệp của họ.</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\">- Trực tiếp truy cập v&agrave;o website của c&aacute;c c&ocirc;ng ty m&agrave; bạn quan t&acirc;m xem họ c&oacute; nhu cầu tuyển dụng nh&acirc;n vi&ecirc;n hay kh&ocirc;ng?</p>\n<p class=\"Normal\" style=\"font-family: \'Times New Roman\'; font-size: 11.8pt; color: #000000;\"><strong>Nguyễn Hồng Hải (</strong><em>nguồn vnexpress.net</em><strong>)<br /></strong></p>','2011-04-08 15:44:06','admin@jobbid.vn',204,0,NULL),(28,'nhung-dieu-co-ban-cua-mot-bien-tap-vien','Những điều cơ bản của một biên tập viên',0,'Cong viec part time - Thong tin dau thau - Trước sự phát triển rầm rộ của các hãng truyền thông, báo đài,…, nghề biên tập viên đã trở nên có giá hơn bao giờ hết. Nếu bạn có ý định bước chân vào lĩnh vực này, hãy tham khảo 7 bí quyết dưới đây, chúng sẽ giú','<p>Trước sự ph&aacute;t triển rầm rộ của c&aacute;c h&atilde;ng truyền  th&ocirc;ng, b&aacute;o đ&agrave;i,&hellip;, nghề bi&ecirc;n tập vi&ecirc;n đ&atilde; trở n&ecirc;n c&oacute; gi&aacute; hơn bao giờ hết.  Nếu bạn c&oacute; &yacute; định bước ch&acirc;n v&agrave;o lĩnh vực n&agrave;y, h&atilde;y tham khảo 7 b&iacute; quyết  dưới đ&acirc;y, ch&uacute;ng sẽ gi&uacute;p bạn nhanh ch&oacute;ng th&agrave;nh c&ocirc;ng hơn.</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\"><span style=\"font-weight: bold;\">1. Đăng k&iacute; v&agrave;o c&aacute;c kh&oacute;a học viết</span></p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">Một  bi&ecirc;n tập vi&ecirc;n giỏi sẽ c&oacute; khả năng viết tốt. H&atilde;y tham gia v&agrave;o một v&agrave;i  lớp học viết v&agrave; thử tự tay soạn ra những đoạn văn &yacute; nghĩa. Bạn cần phải  nhận thức được ngữ ph&aacute;p, c&acirc;u c&uacute; ph&ugrave; hợp, v&agrave; kiểm so&aacute;t được ng&ocirc;n ngữ m&agrave;  bạn viết ra. Thật kh&oacute; để trở th&agrave;nh một người bi&ecirc;n tập giỏi nếu những c&acirc;u  đơn giản bạn cũng kh&ocirc;ng viết được.</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<div style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\"><span style=\"font-weight: bold;\"><img src=\"http://dantri.vcmedia.vn/Uploaded/2011/04/07/8b7615-job.jpg\" alt=\"\" /></span></div>\n<div style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</div>\n<div style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\"><span style=\"font-weight: bold;\">2. Li&ecirc;n hệ với b&aacute;o địa phương </span></div>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">Ngay  cả khi bạn c&oacute; thể trở th&agrave;nh bi&ecirc;n tập vi&ecirc;n b&aacute;n thời gian, th&igrave; một ch&uacute;t  kinh nghiệm thu thập được cũng rất hữu &iacute;ch. Cũng kh&ocirc;ng c&oacute; hại g&igrave; khi n&oacute;i  chuyện với bi&ecirc;n tập vi&ecirc;n của tờ b&aacute;o n&agrave;o đ&oacute; để t&igrave;m hiểu c&ocirc;ng việc chi  tiết ra sao. Bạn c&oacute; thể học được nhiều điều m&igrave;nh chưa biết v&agrave; bạn c&ograve;n c&oacute;  thể biết được đ&acirc;y liệu c&oacute; phải việc m&igrave;nh thực sự muốn l&agrave;m hay kh&ocirc;ng.</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\"><span style=\"font-weight: bold;\">3. Đọc thật nhiều</span></p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">Cũng  kh&ocirc;ng bất ngờ khi ch&uacute;ng ta thường xuy&ecirc;n ph&aacute;t hiện c&aacute;c lỗi sai lặt vặt  tr&ecirc;n những mẩu tin, quảng c&aacute;o, v&agrave; đ&ocirc;i khi cả trong tiểu thuyết. L&agrave;m một  bi&ecirc;n tập vi&ecirc;n đ&ograve;i hỏi bạn phải đọc nhiều v&agrave; nhanh. Bạn c&agrave;ng đọc nhiều,  th&igrave; c&agrave;ng dễ ph&aacute;t hiện lỗi sai m&agrave; kh&ocirc;ng cần mất cả ng&agrave;y đọc đi đọc lại  một mẩu tin.</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\"><span style=\"font-weight: bold;\">4. Li&ecirc;n hệ với c&aacute;c tạp chỉ đang tuyển bi&ecirc;n tập vi&ecirc;n</span></p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<div style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">T&ugrave;y thuộc v&agrave;o loại tạp ch&iacute; m&agrave; bạn t&igrave;m việc, bạn c&oacute; thể nhận được một <a title=\"c&ocirc;ng việc part time\" href=\"http://www.jobbid.vn\">c&ocirc;ng việc part time</a> hoặc to&agrave;n thời gian.&nbsp;</div>\n<div style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</div>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\"><span style=\"font-weight: bold;\">5. Thể hiện niềm y&ecirc;u th&iacute;ch l&agrave;m bi&ecirc;n tập</span></p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">Bạn  phải thể hiện một ch&uacute;t đam m&ecirc; khi đăng k&iacute; v&agrave;o vị tr&iacute; bi&ecirc;n tập. Nếu bạn  từng c&oacute; kinh nghiệm trước đ&oacute;, h&atilde;y nhấn mạnh điều n&agrave;y với nh&agrave; tuyển dụng  để họ c&oacute; thể biết được. Bạn c&agrave;ng thể hiện m&igrave;nh đam m&ecirc; bao nhi&ecirc;u, th&igrave; nh&agrave;  tuyển dụng sẽ c&agrave;ng để &yacute; đến bạn bấy nhi&ecirc;u.</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\"><span style=\"font-weight: bold;\">6. C&oacute; bằng li&ecirc;n quan đến b&aacute;o ch&iacute;, văn học, hoặc tiếng anh</span></p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">Chỉ  cần c&oacute; một trong ba bằng li&ecirc;n quan đến c&aacute;c lĩnh vực tr&ecirc;n sẽ gi&uacute;p bạn dễ  d&agrave;ng tiến gần đến nghề bi&ecirc;n tập hơn v&igrave; hiện nay việc khai th&aacute;c tin tức  nước ngo&agrave;i rất phổ biến tr&ecirc;n hầu hết c&aacute;c b&aacute;o.</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\"><span style=\"font-weight: bold;\">7. C&oacute; kinh nghiệm l&agrave;m nh&agrave; văn</span></p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">C&aacute;ch  tốt nhất để trở th&agrave;nh một bi&ecirc;n tập vi&ecirc;n giỏi l&agrave; phải c&oacute; kinh nghiệm  viết l&aacute;ch, cho d&ugrave; l&agrave; &iacute;t hay nhiều. Cũng giống như việc đọc nhiều, viết  nhiều sẽ gi&uacute;p bạn c&oacute; được nền tảng của ng&ocirc;n ngữ viết v&agrave; dễ d&agrave;ng bi&ecirc;n tập  c&aacute;c mẩu tin viết.</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt;\">&nbsp;</p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt; text-align: right;\"><span style=\"font-weight: bold;\">Nguyễn Th&uacute;y</span></p>\n<p style=\"margin: 0in 0in 0pt; vertical-align: baseline; line-height: 15pt; text-align: right;\">Theo <span style=\"font-style: italic;\">AWT</span></p>','2011-04-13 15:18:53','admin@jobbid.vn',234,0,NULL),(29,'biet-can-bang-de-lam-part-time-cuu-canh-cho-nhan-vien-thoi-buoi-nay','Biết cân bằng để làm part-time, cứu cánh cho nhân viên thời buổi này',0,'cong viec part time - viec lam ban thoi gian - Trong những năm 80, các chương trình và các bộ phim truyền hình nói về khả năng cân bằng công việc của những bà mẹ vừa lo đi làm kiếm tiền, vừa chăm lo tốt việc nhà đã thu hút được sự chú ý của cả nước Mỹ','<p><span style=\"font-size: x-small;\">-&nbsp;</span><span><span style=\"line-height: 18px;\"><span style=\"font-size: x-small;\">Trong  những năm 80, c&aacute;c chương tr&igrave;nh v&agrave; c&aacute;c bộ phim truyền h&igrave;nh n&oacute;i về khả  năng c&acirc;n bằng c&ocirc;ng việc của những b&agrave; mẹ vừa lo đi l&agrave;m kiếm tiền, vừa  chăm lo tốt việc nh&agrave; đ&atilde; thu h&uacute;t được sự ch&uacute; &yacute; của cả nước Mỹ. </span></span></span></p>\n<p style=\"text-align: justify; line-height: 150%;\">&nbsp;</p>\n<p style=\"text-align: center; line-height: 115%;\"><strong><span style=\"font-size: x-small;\"><img style=\"cursor: pointer;\" src=\"http://atpvietnam.com/Library/Images/dangers-of-social-networking.jpeg\" alt=\"\" /><br /></span></strong></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Kh&ocirc;ng  thể ngờ rằng những người phụ nữ đ&oacute; đ&atilde; c&acirc;n bằng được c&ocirc;ng việc b&ecirc;n ngo&agrave;i  với tr&aacute;ch nhiệm gia đ&igrave;nh. L&uacute;c n&agrave;y, dường như mọi người hay &iacute;t nhất l&agrave;  c&aacute;c vị chủ doanh nghiệp đ&atilde; nhận ra rằng rất kh&oacute; khăn cho những người  c&ocirc;ng nh&acirc;n khi họ phải c&acirc;n bằng cuộc sống nơi c&ocirc;ng sở v&agrave; cuộc sống ri&ecirc;ng  tư của m&igrave;nh.<br /><br /><strong>Cần c&oacute; nhiều tiền</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: x-small;\">Trong một  khảo s&aacute;t của Career Builder đầu năm 2010, 19% nh&acirc;n vi&ecirc;n c&oacute; xu hướng t&igrave;m  th&ecirc;m cho m&igrave;nh một c&ocirc;ng việc thứ 2 trong năm nay để c&oacute; được nhiều tiền  hơn. Những bản hợp đồng l&agrave;m việc tự do v&agrave; những c&ocirc;ng việc b&aacute;n thời gian  kh&ocirc;ng c&ograve;n gi&uacute;p họ kiếm th&ecirc;m c&aacute;c khoản thu nhập phụ trợ cần thiết để  trang chải cuộc sống h&agrave;ng ng&agrave;y nữa.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Chuy&ecirc;n  gia truyền th&ocirc;ng Laura Lang đ&atilde; điều h&agrave;nh tốt c&ocirc;ng ty chuy&ecirc;n về lĩnh vực  quan hệ c&ocirc;ng ch&uacute;ng v&agrave; tiếp thị trong suốt 20 năm qua, trước khi sự suy  tho&aacute;i kinh tế ảnh hưởng kh&ocirc;ng tốt tới c&aacute;c kh&aacute;ch h&agrave;ng của c&ocirc;. Th&aacute;ng 3 vừa  qua, Lang t&igrave;m được cho m&igrave;nh một c&ocirc;ng việc dự ph&ograve;ng, v&agrave; rồi n&oacute; đ&atilde; trở  th&agrave;nh c&ocirc;ng việc cố định l&acirc;u d&agrave;i của c&ocirc;.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Lang  n&oacute;i rằng &ldquo;Hiện tại t&ocirc;i đang l&agrave;m cả hai c&ocirc;ng việc c&ugrave;ng một l&uacute;c v&agrave; t&ocirc;i  đang cố gắng t&igrave;m c&aacute;ch tho&aacute;t khỏi t&igrave;nh trạng t&agrave;i ch&iacute;nh hiện tại th&ocirc;ng qua  c&ocirc;ng việc cố định mới của m&igrave;nh&rdquo;. Tuy nhi&ecirc;n để c&acirc;n bằng cả hai l&agrave; việc  kh&ocirc;ng hề dễ d&agrave;ng v&agrave; c&ocirc; ấy đ&atilde; quyết định dần dần sẽ bỏ hẳn c&ocirc;ng việc  trước kia.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">&ldquo;Rất  kh&oacute; để c&oacute; thể đưa ra một quyết định, nhưng t&ocirc;i đ&atilde; chọn sẽ từ bỏ ho&agrave;n  to&agrave;n c&ocirc;ng việc tiếp thị bấy l&acirc;u nay của t&ocirc;i. Để sống s&oacute;t trong nền kinh  tế hiện nay quả thật rất kh&oacute; khăn, t&ocirc;i cần sự ổn định. V&agrave; hiển nhi&ecirc;n c&aacute;c  c&ocirc;ng ty lớn hơn c&oacute; thể đ&aacute;p ứng được điều đ&oacute;&rdquo;, c&ocirc; n&oacute;i.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: x-small;\">Tuy  nhi&ecirc;n, đối với một số nh&acirc;n vi&ecirc;n một c&ocirc;ng việc duy nhất kh&ocirc;ng đủ để trang  trải cuộc sống. Jessica Ayers cho rằng l&agrave;m 2 c&ocirc;ng việc c&ugrave;ng l&uacute;c l&agrave; cần  thiết để c&oacute; thể kiếm đủ tiền. Một tuần c&ocirc; phải mất &iacute;t nhất 40h/tuần để  l&agrave;m việc cho một c&ocirc;ng ty quan hệ c&ocirc;ng ch&uacute;ng v&agrave; l&agrave; lễ t&acirc;n b&aacute;n thời gian  tại một c&acirc;u lạc bộ thể thao. Mặc d&ugrave; việc c&acirc;n bằng cả 2 c&ocirc;ng việc n&agrave;y  khiến c&ocirc; phải thức dậy l&uacute;c 4:30 s&aacute;ng 2 lần/ tuần để bắt đầu một ng&agrave;y l&agrave;m  việc, v&agrave; chỉ được nghỉ duy nhất ng&agrave;y thứ 7, th&igrave; Ayers vẫn cho rằng sự  hi sinh n&agrave;y l&agrave; cần thiết.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><strong><span style=\"font-size: x-small;\">Những bất lợi v&agrave; thuận lợi của việc c&acirc;n bằng nhiều c&ocirc;ng việc </span></strong></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Deanna  Miller cũng đối mặt với một t&igrave;nh huống tương tự. Trong khi t&igrave;m một c&ocirc;ng  việc to&agrave;n thời gian, c&ocirc; đ&atilde; chấp nhận l&agrave;m một c&ocirc;ng việc b&aacute;n thời gian 25  tiếng một tuần v&agrave; dần đảm nhiệm th&ecirc;m vai tr&ograve; một nh&agrave; b&aacute;o tụ do. Hơn thế  nữa, c&ocirc; đ&atilde; bắt đầu một c&ocirc;ng việc thứ hai - to&agrave;n thời gian với hợp đồng  l&agrave;m việc 20h/ tuần. Thật may mắn, cuối c&ugrave;ng c&ocirc; cũng đ&atilde; t&igrave;m được một c&ocirc;ng  việc cố định, tuy nhi&ecirc;n Miller vẫn tiếp tục c&ocirc;ng việc b&aacute;n thời gian kia  của m&igrave;nh, điều đ&oacute; c&oacute; nghĩa l&agrave; c&ocirc; ấy thường xuy&ecirc;n phải l&agrave;m việc cả 7  ng&agrave;y một tuần.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Miller  n&oacute;i rằng &ldquo;Kh&oacute; khăn lớn nhất của t&ocirc;i l&agrave; t&ocirc;i kh&ocirc;ng c&oacute; đủ thời gian cho  gia đ&igrave;nh, bạn b&egrave; v&agrave; t&ocirc;i cần phải sắp xếp lại thời gian của m&igrave;nh. Như  th&aacute;ng 8 vừa qua t&ocirc;i kh&ocirc;ng chỉ l&agrave;m việc tới 40h từ thứ 2 tới thứ 6 m&agrave; c&ograve;n  l&agrave;m th&ecirc;m 14h v&agrave;o hai ng&agrave;y cuối tuần, v&agrave; nhận th&ecirc;m một dự &aacute;n viết b&aacute;o tự  do, c&ocirc;ng việc n&agrave;y ngốn cảu t&ocirc;i tới 30h một th&aacute;ng. Thời gian qua, t&ocirc;i  vừa n&oacute;i với bạn b&egrave; v&agrave; gia đ&igrave;nh l&agrave; t&ocirc;i kh&ocirc;ng c&oacute; thời gian rảnh trong  th&aacute;ng 8 v&agrave; h&atilde;y ki&ecirc;n nhẫn. V&igrave; vợ chồng t&ocirc;i chưa c&oacute; con, n&ecirc;n chồng t&ocirc;i v&agrave;  t&ocirc;i chỉ quấn qu&yacute;t b&ecirc;n nhau khi ch&uacute;ng t&ocirc;i c&oacute; thể, tuy nhi&ecirc;n ch&uacute;ng t&ocirc;i  biết rằng điều n&agrave;y sẽ kh&ocirc;ng m&atilde;i l&agrave; cuộc sống của ch&uacute;ng t&ocirc;i&rdquo;.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Mặc  d&ugrave; chồng của Miller đang t&igrave;m kiếm một c&ocirc;ng việc ổn định, nhưng họ vẫn  thấy được mặt tốt của việc cần c&oacute; hai việc l&agrave;m một l&uacute;c. Miller cho biết  &ldquo;Thật sự, ch&uacute;ng t&ocirc;i vừa trả xong khoản nợ cuối c&ugrave;ng v&agrave; vẫn tiếp tục l&agrave;m  những việc vặt, những việc l&agrave;m tự do để c&oacute; thể c&oacute; đủ tiền để mua nh&agrave; v&agrave;  nu&ocirc;i dạy con c&aacute;i sau n&agrave;y&rdquo;. </span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><strong><span style=\"font-size: x-small;\">Gi&aacute; trị của việc &ldquo;l&agrave;m nhiều việc một l&uacute;c&rdquo; </span></strong></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Nếu  như bạn đang ở trong t&igrave;nh trạng gặp gỡ đồng nghiệp c&ograve;n nhiều hơn cả gặp  con th&igrave; việc lắng nghe những mặt t&iacute;ch cực của việc l&agrave;m nhiều việc một  l&uacute;c kh&ocirc;ng phải l&agrave; điều bạn quan t&acirc;m. Mặc d&ugrave; đối với nhiều người, phải  l&agrave;m hai đến ba c&ocirc;ng việc một l&uacute;c l&agrave; kh&ocirc;ng th&iacute;ch hợp cho lắm, nhưng  Denise Beeson lại nghĩ rằng đ&oacute; l&agrave; c&aacute;ch th&ocirc;ng minh để l&agrave;m ăn. </span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Beeson  cho biết &ldquo;Hiện tại t&ocirc;i c&oacute; 3 c&ocirc;ng việc: T&ocirc;i dạy cho c&aacute;c lớp học nhỏ về  quản l&yacute; kinh doanh tại một trường cao đẳng tại địa phương, tư vấn về  tiếp thị v&agrave; nhu cầu t&agrave;i ch&iacute;nh cho c&aacute;c doanh nghiệp nhỏ, v&agrave; cuối c&ugrave;ng l&agrave;  b&aacute;n c&aacute;c chương tr&igrave;nh vay vốn thương mại. Với 3 nguồn thu nhập, t&ocirc;i nhận  ra rằng khi t&ocirc;i mất một việc, hai c&ocirc;ng việc c&ograve;n lại vẫn c&oacute; thể gi&uacute;p t&ocirc;i  sống tốt&rdquo;.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Sự  thật l&agrave; Beeson tin c&acirc;u thần ch&uacute; của c&ocirc;&nbsp;&ldquo;L&agrave;m thế n&agrave;o để sống nếu kh&ocirc;ng  c&oacute; việc l&agrave;m&rdquo; đ&atilde; chuẩn bị tinh thần cho c&ocirc; để đ&oacute;n nhận những xu hướng  việc l&agrave;m sắp tới.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\">Nền  kinh tế kh&oacute; khăn cũng khiến cuộc sống của c&aacute;c nh&acirc;n vi&ecirc;n gặp trở ngại.  Nếu như bạn c&oacute; thể t&igrave;m thấy một mặt t&iacute;ch cực của vấn đề n&agrave;y, th&igrave; c&oacute; lẽ  ch&uacute;ng ta c&oacute; thể thấy c&aacute;c nh&acirc;n vi&ecirc;n khỏe như thế n&agrave;o, v&agrave; khả năng của họ  l&agrave; g&igrave;. <br /><br />Họ c&oacute; thể điều hướng nghề nghiệp để chuẩn bị mọi thứ cho ch&iacute;nh họ trong trường hợp mất một việc l&agrave;m trong tương lai.</span></p>\n<p style=\"text-align: justify; line-height: 115%;\"><span style=\"font-size: x-small;\"><strong>Lan Anh</strong> </span><em><span style=\"font-size: x-small;\">(Nguồn CBS, MSN)</span></em></p>','2011-04-16 19:42:24','admin@jobbid.vn',317,0,NULL),(30,'viec-lam-ban-thoi-gian-xau-hoc-thue','Việc làm bán thời gian xấu: Học thuê',0,'viec lam ban thoi gian - tim viec ban thoi gian - 50.000 đồng/buổi học, 200.000 đồng/buổi thi - những khoản tiền này hiện đang là lương part-time của nhiều sinh viên khi đi học thuê tại các lớp liên thông, tại chức. Vấn đề không mới nhưng vẫn diễn ra, tại','<p><span class=\"tomtat\" style=\"font-family: tahoma; font-weight: bold; text-align: justify;\">(SVVN)50.000 đồng/buổi học, 200.000 đồng/buổi thi - những khoản tiền n&agrave;y hiện đang l&agrave; &ldquo;lương&rdquo; part-time của nhiều sinh vi&ecirc;n khi đi học thu&ecirc; tại c&aacute;c lớp li&ecirc;n th&ocirc;ng, tại chức. Vấn đề kh&ocirc;ng mới nhưng vẫn diễn ra, tại sao?</span></p>\n<p><strong><br />Nghề &ldquo;h&aacute;i ra tiền&rdquo;</strong><br />Đang học li&ecirc;n th&ocirc;ng tại trường ĐH Lao động - X&atilde; hội, H. cho biết, trong<span class=\"Apple-converted-space\">&nbsp;</span>lớp<span id=\"link0\" class=\"VietAdTextLink\" style=\"text-decoration: underline; display: inline; border-bottom: 1px solid #2b9900; padding-bottom: 0px; color: #006400; background-color: transparent; cursor: pointer; white-space: nowrap;\">&nbsp;</span><span class=\"Apple-converted-space\">&nbsp;</span>thỉnh thoảng lại c&oacute; những gương mặt &ldquo;lạ&rdquo;. C&ograve;n trẻ, lại mau mắn n&ecirc;n H. thường xuy&ecirc;n được c&aacute;c bậc anh chị nhờ t&igrave;m người học hộ. Mỗi buổi học k&eacute;o d&agrave;i 2 &ndash; 3 tiếng, sinh vi&ecirc;n học thu&ecirc; sẽ được &ldquo;chủ&rdquo; trả 30.000 đồng. Theo H., đ&acirc;y l&agrave; mức th&ugrave; lao &ldquo;b&egrave;o&rdquo; nhất trong c&aacute;c trường. &ldquo;Ở trường ĐH<span class=\"Apple-converted-space\">&nbsp;</span>Kinh tế<span class=\"Apple-converted-space\">&nbsp;</span>hay Học viện Ng&acirc;n h&agrave;ng, th&ugrave; lao phải l&agrave; 50.000 đồng/buổi&rdquo; &ndash; H. cho biết.</p>\n<p>Tiếp x&uacute;c với T., một sinh vi&ecirc;n c&oacute; &ldquo;th&acirc;m ni&ecirc;n&rdquo; trong &ldquo;nghề&rdquo; học hộ, T. cũng cho hay, c&ocirc; đang học gi&uacute;p một chị &ldquo;bầu b&iacute;&rdquo; tại Học viện Ng&acirc;n h&agrave;ng. Tiền c&ocirc;ng ở đ&acirc;y l&agrave; 50.000 đồng/buổi học v&agrave; 200.000 đồng/buổi thi. Kh&ocirc;ng chỉ c&oacute; lớp T. đang &ldquo;diễn thế ch&acirc;n&rdquo; mới c&oacute; hiện tượng n&agrave;y m&agrave; tại lớp kh&aacute;c của Học viện Ng&acirc;n h&agrave;ng cũng diễn ra t&igrave;nh trạng tương tự. Một lần v&agrave;o &ldquo;nhầm lớp&rdquo;, T. đ&atilde; &ldquo;đụng&rdquo; phải một sinh vi&ecirc;n kh&aacute;c cũng đi học thu&ecirc;. T. bảo, c&ocirc;ng việc học hộ rất đơn giản, chỉ việc đến &ldquo;điểm danh&rdquo; rồi ngồi &ldquo;ng&aacute;p&rdquo; hoặc &ldquo;ngủ&rdquo; l&agrave; đ&atilde; được tiền. Mỗi tuần học 3 buổi, tr&ograve;m tr&egrave;m mỗi th&aacute;ng thu nhập của T. từ c&ocirc;ng việc &ldquo;tay tr&aacute;i&rdquo; n&agrave;y cũng kh&ocirc;ng đến nỗi n&agrave;o. &ldquo;Học thu&ecirc; như thế n&agrave;y m&igrave;nh được hưởng 100% c&ocirc;ng sức bỏ ra. L&agrave;m gia sư, vừa vất vả, vừa mệt lại c&ograve;n bị trừ đầu trừ đu&ocirc;i cho c&aacute;c c&ocirc;ng ty m&ocirc;i giới&rdquo; &ndash; T. giảng giải. Ch&iacute;nh v&igrave; vậy, để tạo &ldquo;c&ocirc;ng ăn việc l&agrave;m&rdquo; cho c&aacute;c bạn kh&aacute;c, mỗi lần c&oacute; chị n&agrave;o đ&oacute; trong lớp cần người học hộ l&agrave; T. giới thiệu ngay.<span class=\"Apple-converted-space\">&nbsp;</span><br /><strong><br />Chuyện d&agrave;i nhiều tập</strong><br /><br />Học hộ nh&agrave; trường c&oacute; biết kh&ocirc;ng? T. khẳng định l&agrave; trường c&oacute; biết nhưng chỉ người đi thu&ecirc; mới bị &ldquo;xử l&yacute;&rdquo; c&ograve;n người học thu&ecirc; th&igrave; kh&ocirc;ng vấn đề g&igrave;. Được biết, hiện nay c&ocirc;ng t&aacute;c quản l&yacute; sinh vi&ecirc;n tại c&aacute;c lớp tại chức, li&ecirc;n th&ocirc;ng ở c&aacute;c trường đều bằng h&igrave;nh thức điểm danh v&agrave; kiểm tra thẻ. Tuy nhi&ecirc;n, thời gian học diễn ra buổi tối n&ecirc;n người học thu&ecirc; c&oacute; thể dễ d&agrave;ng đối ph&oacute;. Kh&ocirc;ng những thế, kể cả ở những buổi thi, người học thu&ecirc; chỉ cần viện l&yacute; do &ldquo;qu&ecirc;n thẻ&rdquo; l&agrave; vẫn c&oacute; thể thay vai tr&oacute;t lọt.<span class=\"Apple-converted-space\">&nbsp;</span><br /><br />Tất nhi&ecirc;n, kh&ocirc;ng phải lớp học n&agrave;o cũng lỏng lẻo. H. cho biết, năm ngo&aacute;i, tại lớp li&ecirc;n th&ocirc;ng của c&ocirc; ở trường ĐH Lao động - X&atilde; hội đ&atilde; c&oacute; một v&agrave;i người bị đ&igrave;nh chỉ học do bị nh&agrave; trường ph&aacute;t hiện thu&ecirc; người học hộ. Kh&ocirc;ng những thế, đối với những người học hộ cũng bị trường gửi c&ocirc;ng văn th&ocirc;ng b&aacute;o về trường v&agrave; về tận địa phương. Năm nay, trước t&igrave;nh trạng học kỳ mới bắt đầu được hơn một th&aacute;ng nhưng trong lớp đ&atilde; ph&aacute;t hiện người đang học hộ, nh&agrave; trường đ&atilde; cảnh b&aacute;o sẽ tiến h&agrave;nh kiểm tra gắt gao hơn.</p>\n<p><br /><br /></p>\n<div><img src=\"http://www.svvn.vn/upload/20101012/Hoc%20thue.JPG\" border=\"0\" alt=\"Hoc thue.JPG\" width=\"450\" height=\"211\" /></div>\n<div><br /><br />&nbsp;</div>\n<p><strong>Biểu hiện lệch lạc của x&atilde; hội s&iacute;nh bằng cấp</strong><br /><br />&Ocirc;ng Nguyễn Đồng Dũng, Trưởng ph&ograve;ng Đ&agrave;o tạo, trường ĐH Lao động &ndash; X&atilde; hội cho biết, v&agrave;o đầu mỗi năm học, trường thường phổ biến rất kỹ cho sinh vi&ecirc;n c&aacute;c lớp li&ecirc;n th&ocirc;ng, tại chức nắm được nội quy, quy chế, trong đ&oacute; c&oacute; vấn đề học hộ, học thu&ecirc;. C&aacute;c buổi học, ngo&agrave;i giảng vi&ecirc;n đứng lớp, c&aacute;c lớp c&ograve;n c&oacute; nh&acirc;n vi&ecirc;n của Ph&ograve;ng Đ&agrave;o tạo tham gia gi&aacute;m s&aacute;t. Nếu ph&aacute;t hiện trường hợp n&agrave;o học hộ, trường sẽ đ&igrave;nh chỉ học tập đối với người thu&ecirc; học. C&ograve;n người học thu&ecirc;, trường sẽ gửi th&ocirc;ng b&aacute;o về địa phương hoặc trường nơi người n&agrave;y đang theo học để xử l&yacute;.<span class=\"Apple-converted-space\">&nbsp;</span><br /><br />Biện ph&aacute;p m&agrave; &ocirc;ng Dũng n&ecirc;u ra ph&aacute;t huy hiệu quả đến đ&acirc;u, chắc bạn đọc cũng đ&atilde; r&otilde;. C&oacute; &ldquo;cung&rdquo; th&igrave; tất sẽ c&oacute; &ldquo;cầu&rdquo;. Nhiều chuy&ecirc;n gia gi&aacute;o dục nhận x&eacute;t, chuyện thu&ecirc; học - học thu&ecirc; cũng giống chuyện bằng tiến sĩ... mua ở nước ngo&agrave;i, đều l&agrave; những biểu hiện của tệ s&iacute;nh bằng cấp. Vậy n&ecirc;n chừng n&agrave;o bằng cấp c&ograve;n được coi l&agrave; ti&ecirc;u chuẩn tuyển dụng, cất nhắc (thay v&igrave; chỉ n&ecirc;n được xem như ti&ecirc;u ch&iacute; tham khảo), chừng đ&oacute;, vẫn sẽ c&ograve;n người được thu&ecirc; học v&agrave; phấn khởi coi đ&oacute; như một part-time hấp dẫn.<span class=\"Apple-converted-space\">&nbsp;</span><br /><strong><br /></strong><em><strong>&Ocirc;ng Ngũ Duy Anh</strong>, Vụ trưởng Vụ C&ocirc;ng t&aacute;c Học sinh, sinh vi&ecirc;n (Bộ GD &ndash; ĐT) cho biết: Đ&acirc;y l&agrave; điều đ&aacute;ng tr&aacute;ch. Mượn kiến thức của người kh&aacute;c để đ&aacute;nh b&oacute;ng t&ecirc;n tuổi của m&igrave;nh l&agrave; điều kh&ocirc;ng thể chấp nhận được. Bộ GD - ĐT khuyến c&aacute;o c&aacute;c cơ sở đ&agrave;o tạo kiểm tra người học một c&aacute;ch nghi&ecirc;m ngặt để l&agrave;m sao ngăn chặn t&igrave;nh trạng n&agrave;y, lớp học đi theo đ&uacute;ng hướng, người học đi học một c&aacute;ch nghi&ecirc;m t&uacute;c. Người đi thu&ecirc; người kh&aacute;c học hộ đ&atilde; c&oacute; chế t&agrave;i xử l&yacute; như đ&igrave;nh chỉ học tập hay buộc th&ocirc;i học, c&ograve;n người học thu&ecirc; cũng phải xử l&yacute; nghi&ecirc;m t&uacute;c. Ngăn chặn t&igrave;nh trạng n&agrave;y phải xuất ph&aacute;t từ ch&iacute;nh c&aacute;c thầy đứng lớp. C&aacute;c thầy kh&ocirc;ng thể đến lớp dạy m&agrave; kh&ocirc;ng biết người ngồi nghe l&agrave; ai. Bộ phận quản l&yacute; lớp cũng phải gi&aacute;m s&aacute;t chặt chẽ, kh&ocirc;ng thể dễ d&atilde;i.<br /><br /><strong>Nguyễn Thu Nga</strong>, một sinh vi&ecirc;n trường ĐH C&ocirc;ng đo&agrave;n cho rằng, học tập l&agrave; một c&ocirc;ng việc đ&ograve;i hỏi t&iacute;nh nghi&ecirc;m t&uacute;c, kh&ocirc;ng thể thu&ecirc; hay nhờ người kh&aacute;c l&agrave;m thay. Ch&iacute;nh v&igrave; thế, mặc d&ugrave; đi học thu&ecirc; c&oacute; thể gi&uacute;p Nga nhận được mức th&ugrave; lao cao hơn so với c&aacute;c c&ocirc;ng việc part-time kh&aacute;c (m&agrave; lại nh&agrave;n hơn) nhưng bạn sẽ kh&ocirc;ng bao giờ chấp nhận một &ldquo;lời đề nghị khiếm nh&atilde;&rdquo; như vậy. Nga mong c&aacute;c bạn sinh vi&ecirc;n d&ugrave; c&ograve;n kh&oacute; khăn h&atilde;y dũng cảm n&oacute;i &ldquo;kh&ocirc;ng&rdquo; với tệ nạn học thu&ecirc;!</em><br /><br /></p>\n<div><strong>H&agrave; Vi </strong>(nguồn svvn.vn)</div>','2011-04-20 15:32:37','admin@jobbid.vn',377,0,NULL),(31,'part-time-sau-0h','Part-time sau 0h',0,'cong viec part time - viec lam ban thoi gian  - Đà Nẵng 24h đêm. Giảng đường vắng lặng. KTX im ắng. Nhưng đâu đó trong thành phố vẫn còn những sinh viên khoác lên mình chiếc áo xanh công nhân thức trắng đêm để kiếm thêm thu nhập, trang trải cho cuộc sống…','<p><span class=\"tomtat\"><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"c&ocirc;ng việc part time\" src=\"http://www.svvn.vn/Controls/ThumbnailSizeOrigin.aspx?swidth=200&amp;sheight=0&amp;imageurl=upload/20100914/Da%20Nang.JPG\" alt=\"cong viec part time\" width=\"200\" height=\"133\" /><span style=\"color: #888888;\"><strong>(SVVN)Đ&agrave; Nẵng 24h đ&ecirc;m. Giảng đường vắng lặng. KTX im  ắng. Nhưng đ&acirc;u đ&oacute; trong th&agrave;nh phố vẫn c&ograve;n những sinh vi&ecirc;n kho&aacute;c l&ecirc;n  m&igrave;nh chiếc &aacute;o xanh c&ocirc;ng nh&acirc;n thức trắng đ&ecirc;m để kiếm th&ecirc;m thu nhập, trang  trải cho cuộc sống&hellip; </strong></span></span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><strong>Những giấc ngủ kh&ocirc;ng tr&ograve;n</strong></p>\n<p>Nằm  tr&ecirc;n quận Li&ecirc;n Chiểu, TP. Đ&agrave; Nẵng, Khu c&ocirc;ng nghiệp H&ograve;a Kh&aacute;nh l&agrave; nơi  đang thu h&uacute;t nhiều sinh vi&ecirc;n đến nộp hồ sơ xin việc, đặc biệt l&agrave; 3 c&ocirc;ng  ty: Mabuchi Motor, Điện tử Việt Hoa v&agrave; Maxtris.</p>\n<p>Do  ban ng&agrave;y phải c&oacute; mặt ở giảng đường n&ecirc;n hầu hết sinh vi&ecirc;n khi đi l&agrave;m  c&ocirc;ng nh&acirc;n đều đăng k&yacute; ca đ&ecirc;m (Mabuchi Motor v&agrave; Maxtris bắt đầu từ 22h  đến 6h s&aacute;ng h&ocirc;m sau; Điện tử Việt Hoa từ 18h đến 6h s&aacute;ng h&ocirc;m sau). V&igrave;  l&agrave;m việc ở khoảng thời gian như thế n&ecirc;n sinh vi&ecirc;n thường chấp nhận \"hy  sinh\" giấc ngủ, thời gian sinh hoạt theo đ&oacute; cũng bị x&aacute;o trộn.</p>\n<p>Hồ.  T. Anh (lớp 09CTL, trường ĐH Sư phạm) t&acirc;m sự: \"Đầu năm, b&agrave;i vở &iacute;t n&ecirc;n  m&igrave;nh tranh thủ đi l&agrave;m kiếm th&ecirc;m thu nhập, ban ng&agrave;y đi học n&ecirc;n phải l&agrave;m  ca đ&ecirc;m, l&agrave;m ca đ&ecirc;m lương cũng cao hơn (2,4 triệu đồng/th&aacute;ng). L&uacute;c đầu  th&igrave; hay mất ngủ, giờ quen rồi, rảnh l&uacute;c n&agrave;o th&igrave; m&igrave;nh phải cố m&agrave; ngủ,  kh&ocirc;ng ngủ th&igrave; chẳng c&oacute; sức m&agrave; đi l&agrave;m\". \"Ngủ ng&agrave;y c&agrave;y đ&ecirc;m\" n&ecirc;n nếu lịch  học ở trường cả ng&agrave;y th&igrave; quả l&agrave; &aacute;c mộng. Giải ph&aacute;p tạm thời m&agrave; sinh vi&ecirc;n  l&agrave;m đ&ecirc;m chọn l&agrave; c&uacute;p tiết (nếu kh&ocirc;ng điểm danh) để ở nh&agrave; ngủ b&ugrave;&hellip;</p>\n<p><strong>L&agrave;m c&ocirc;ng nh&acirc;n kh&ocirc;ng dễ</strong></p>\n<p>Kh&ocirc;ng cần ngoại h&igrave;nh đẹp, kỹ năng giao tiếp hay tr&igrave;nh độ ngoại ngữ. Một giấy kh&aacute;m sức khỏe, <span id=\"link2\" class=\"VietAdTextLink\" style=\"text-decoration: underline; border-bottom-style: solid; border-bottom-width: 1px; white-space: nowrap;\">tốt nghiệp</span> <span id=\"link1\" class=\"VietAdTextLink\" style=\"text-decoration: underline; border-bottom-style: solid; border-bottom-width: 1px; white-space: nowrap;\">lớp</span> 9 trở l&ecirc;n, độ tuổi từ 19 đến 29 l&agrave; đủ điều kiện l&agrave;m tại khu c&ocirc;ng  nghiệp. Nhưng kh&ocirc;ng phải v&igrave; thế m&agrave; nghĩ rằng l&agrave;m c&ocirc;ng nh&acirc;n l&agrave; một  part-time \"dễ xơi\". Theo kinh nghiệm của nhiều sinh vi&ecirc;n th&igrave; c&ocirc;ng việc  thường y&ecirc;u cầu những phẩm chất tỉ mỉ, chịu kh&oacute; v&agrave; ki&ecirc;n nhẫn.</p>\n<p>L.  T. Diệu, một sinh vi&ecirc;n năm thứ 2, nhận dập bush (một kh&acirc;u chuẩn bị linh  kiện để lắp r&aacute;p) tại Mabuchi Motor chia sẻ: \"C&ocirc;ng việc kh&ocirc;ng kh&oacute; nhưng  phải ch&uacute; &yacute; xem người ta hướng dẫn rồi l&agrave;m cho đ&uacute;ng, khi l&agrave;m phải cẩn  thận, sai l&agrave; bị tổ trưởng, chủ quản mắng liền. M&igrave;nh mới v&agrave;o l&agrave;m n&ecirc;n được  xếp b&ecirc;n cơ động, được y&ecirc;u cầu g&igrave; th&igrave; l&agrave;m nấy, chạy đi chạy lại mệt  lắm!\".</p>\n<p>Do l&agrave;m trong một bộ phận của một d&acirc;y  chuyền li&ecirc;n tục n&ecirc;n \"nhanh\" l&agrave; y&ecirc;u cầu bắt buộc v&agrave; cũng l&agrave; &aacute;p lực thực  sự: \"Nếu một đ&ecirc;m người ta giao cho m&igrave;nh 10.000 đơn vị h&agrave;ng th&igrave; phải chạy  cho hết, kh&ocirc;ng đủ h&agrave;ng sẽ bị viết giấy cảnh c&aacute;o, ng&agrave;y h&ocirc;m sau phải chạy  th&ecirc;m số lượng v&agrave;o cho đủ h&agrave;ng, nếu kh&ocirc;ng cuối th&aacute;ng sẽ bị&nbsp; trừ lương\" -  Thanh Chi (c&ocirc;ng nh&acirc;n đứng m&aacute;y, d&aacute;n h&agrave;ng nh&agrave; m&aacute;y nhựa Maxtris) cho biết.</p>\n<p>Khu  c&ocirc;ng nghiệp H&ograve;a Kh&aacute;nh l&agrave; m&ocirc;i trường chỉ d&agrave;nh cho những người c&oacute; sức  khỏe thuộc v&agrave;o loại \"khủng\". Thức khuya li&ecirc;n tục, đứng một chỗ nguy&ecirc;n  đ&ecirc;m (b&ecirc;n c&ocirc;ng đoạn), m&ocirc;i trường bao quanh l&agrave; điện, m&aacute;y lạnh, tiếng ồn  m&aacute;y m&oacute;c, bụi vải, h&oacute;a chất&hellip; gộp lại đủ để thấy sức khỏe sẽ bị t&agrave;n ph&aacute;  kh&ocirc;ng thương tiếc như thế n&agrave;o.</p>\n<p><strong>Trải nghiệm v&agrave; thấu hiểu</strong></p>\n<p>Kh&ocirc;ng  phải c&ocirc;ng việc phục vụ cho chuy&ecirc;n ng&agrave;nh học tập, c&agrave;ng kh&ocirc;ng phải c&ocirc;ng  việc của niềm đam m&ecirc;, thậm ch&iacute; l&agrave; rất vất vả nhưng l&agrave;m c&ocirc;ng nh&acirc;n cũng  mang lại cho sinh vi&ecirc;n nhiều trải nghiệm kh&aacute;c biệt. \"C&ocirc;ng việc n&agrave;y chưa  hẳn gọi l&agrave; thử th&aacute;ch bản th&acirc;n, đơn giản l&agrave; trải nghiệm m&agrave; th&ocirc;i. Mệt  thật, nhưng nhờ n&oacute; m&igrave;nh hiểu hơn được rất nhiều điều về cuộc sống. M&igrave;nh  nhận ra, nghề n&agrave;o cũng đ&aacute;ng qu&yacute; cả, bởi trước đ&acirc;y m&igrave;nh kh&ocirc;ng coi trọng  người lao động ch&acirc;n tay, nhưng khi đi l&agrave;m m&igrave;nh học được ở c&aacute;c c&ocirc;, c&aacute;c  chị rất nhiều điều như sự ki&ecirc;n nhẫn, khả năng chịu đựng&hellip;\" - Thanh Chi  (ĐH Đ&agrave; Nẵng) kể về những người bạn c&ocirc;ng nh&acirc;n lớn tuổi của m&igrave;nh.</p>\n<p>C&ugrave;ng trải nghiệm như Chi, Ho&agrave;i Xu&acirc;n (năm thứ 3, trường CĐ <span id=\"link3\" class=\"VietAdTextLink\" style=\"text-decoration: underline; border-bottom-style: solid; border-bottom-width: 1px; white-space: nowrap;\">Kinh tế</span> - Kế hoạch) l&yacute; giải: \"T&ugrave;y ho&agrave;n cảnh m&agrave; biết c&aacute;ch th&iacute;ch nghi, ng&agrave;y trước  th&igrave; cứ&nbsp; cho rằng m&igrave;nh l&agrave; sinh vi&ecirc;n, l&agrave; người \"c&oacute; chữ\" n&ecirc;n chạm t&iacute; l&agrave; tự  &aacute;i liền, nhưng ở đ&acirc;y, m&igrave;nh phải r&egrave;n t&iacute;nh chịu đựng\".</p>\n<p>C&ograve;n  với Đ&ocirc;ng (năm thứ 2, khoa Ngữ văn, trường ĐH Sư phạm), sau hai th&aacute;ng  thử sức, bạn đ&atilde; học được kỹ năng l&agrave;m việc nghi&ecirc;m t&uacute;c, nhanh nhẹn: \"L&agrave;m  ra l&agrave;m, chơi ra chơi, l&uacute;c n&agrave;o cũng phải học hỏi bởi c&ocirc;ng việc của m&igrave;nh  đ&ograve;i hỏi phải biết nhiều c&ocirc;ng đoạn. Đi l&agrave;m mới biết thương ba mẹ. Th&iacute;ch  nhất khi cầm những đồng tiền lương tr&ecirc;n tay do th&agrave;nh&nbsp; quả lao động đem  lại, cảm gi&aacute;c m&igrave;nh l&agrave; một người trưởng th&agrave;nh\".</p>\n<p>Khi  được hỏi c&oacute; dự định l&agrave;m l&acirc;u d&agrave;i ở Maxtris kh&ocirc;ng, Ho&agrave;i Xu&acirc;n (c&oacute; thu nhập  2,5 triệu đồng/th&aacute;ng) khuy&ecirc;n: \"Lương cao, nhưng m&igrave;nh nghĩ sinh vi&ecirc;n  kh&ocirc;ng n&ecirc;n ham kiếm tiền m&agrave; b&aacute;m trụ c&ocirc;ng việc n&agrave;y l&acirc;u. Thức khuya l&agrave;m  việc nhiều sẽ rất ảnh hưởng đến việc học. C&oacute; v&agrave;i lần mệt qu&aacute;, chịu kh&ocirc;ng  nổi, m&igrave;nh phải c&uacute;p tiết ở nh&agrave; ngủ b&ugrave;. M&igrave;nh chỉ l&agrave;m khoảng nửa th&aacute;ng nữa  th&ocirc;i, t&iacute;ch đủ tiền học ph&iacute; sẽ xin nghỉ\".</p>\n<p>C&oacute;  lẽ, khi đang c&ograve;n l&agrave; sinh vi&ecirc;n, \"nghề\" c&ocirc;ng nh&acirc;n chỉ n&ecirc;n l&agrave; một part  -time thời vụ, bởi bạn sẽ chẳng thể ho&agrave;n th&agrave;nh tốt việc học nếu lu&ocirc;n ở  trong t&igrave;nh trạng \"s&aacute;ng ngủ giảng đường - tối l&agrave;m th&ecirc;m\".</p>\n<p>(nguồn www.svvn.vn)</p>','2011-04-23 09:22:35','admin@jobbid.vn',482,0,NULL),(32,'cong-tac-vien-bao-–-part-time-hot-cua-teen-trong-mua-he-','Cộng tác viên báo – Part time hot của teen trong mùa hè ',0,'cong viec part time - thông tin đấu thầu- Qua những bài phỏng vấn, những buổi chụp hình, những bài phóng sự, các teen có dịp trò chuyện với cả những nhân vật nổi tiếng. Nhất là khi làm CTV của các tờ báo giải trí thì chuyện gặp các “Star” là “bình thường ','<p class=\"sapo\">Qua những b&agrave;i phỏng vấn, những buổi chụp h&igrave;nh, những b&agrave;i  ph&oacute;ng sự, c&aacute;c teen c&oacute; dịp tr&ograve; chuyện với cả những nh&acirc;n vật nổi tiếng.  Nhất l&agrave; khi l&agrave;m CTV của c&aacute;c tờ b&aacute;o giải tr&iacute; th&igrave; chuyện gặp c&aacute;c &ldquo;Star&rdquo; l&agrave;  &ldquo;b&igrave;nh thường như cơm bữa&rdquo;.</p>\n<div class=\"content\">\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold;\">Một part-time kh&ocirc;ng b&oacute; buộc thời gian</span></div>\n<div style=\"text-align: justify;\">Bận  rộn với việc học, nhiều teen kh&ocirc;ng thể d&agrave;nh qu&aacute; nhiều thời gian cho  việc l&agrave;m th&ecirc;m. Vậy th&igrave; đ&acirc;y thực sự l&agrave; một part-time hot cho teen trong  m&ugrave;a h&egrave; n&agrave;y. C&ocirc;ng việc Cộng T&aacute;c Vi&ecirc;n (CTV) cho những tờ soạn b&aacute;o kh&ocirc;ng  tốn qu&aacute; nhiều thời gian, chỉ cần teen c&oacute; khả năng viết l&aacute;ch kh&aacute; một  ch&uacute;t, kh&ocirc;ng hẳn l&agrave; phải xuất sắc đ&acirc;u nh&eacute;.</div>\n<div style=\"text-align: justify;\">Điều  quan trọng của một CTV l&agrave; c&oacute; khả năng nắm bắt tin tức một c&aacute;ch nhanh  ch&oacute;ng, tổng hợp tin v&agrave; đưa ra những sản phẩm đ&uacute;ng thời điểm. Ngo&agrave;i ra,  nếu c&oacute; khả năng viết ph&oacute;ng sự tốt, c&aacute;c teen c&ograve;n c&oacute; điều kiện thể hiện v&agrave;  ph&aacute;t huy khả năng của m&igrave;nh nữa đ&oacute;! Đặc biệt, những sản phẩm b&agrave;i viết do  teen tạo ra kh&ocirc;ng g&ograve; b&oacute; về thời gian (trừ những tin n&oacute;ng). C&aacute;c teen c&oacute;  thể thoải m&aacute;i viết sau thời gian học tập v&agrave; vui chơi. So với những  part-time kh&aacute;c th&igrave; thật thoải m&aacute;i về giờ giấc phải kh&ocirc;ng?</div>\n<div style=\"text-align: center;\"><img src=\"http://k14.vcmedia.vn/Images/Uploaded/Share/2010/06/21/22610DSbao.jpg\" alt=\"\" /><br /><span style=\"font-style: italic;\">Bạn cần c&oacute; khả năng viết l&aacute;ch kh&aacute; ổn khi muốn cộng t&aacute;c viết b&agrave;i nh&eacute;! (Ảnh minh họa)</span></div>\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold;\">Gi&uacute;p teen mở rộng mối quan hệ</span>&nbsp;</div>\n<div style=\"text-align: justify;\">Khi  trở th&agrave;nh CTV ch&iacute;nh thức của bất k&igrave; tờ b&aacute;o n&agrave;o th&igrave; chắc chắn c&aacute;c teen  sẽ c&oacute; dịp mở rộng những mối quan hệ của m&igrave;nh rất nhiều, bởi đ&oacute; kĩ năng  nghề nghiệp m&agrave; c&aacute;c CTV được đ&agrave;o tạo. Nhất l&agrave; những mối quan hệ với bạn  b&egrave; ở những nơi kh&aacute;c, hay trường kh&aacute;c.</div>\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold;\">Thanh Thảo</span>, 17 tuổi cho biết: <span style=\"font-style: italic;\">&ldquo;M&igrave;nh  l&agrave; CTV của một tờ b&aacute;o teen cũng được mấy th&aacute;ng th&ocirc;i, nhưng c&aacute;c anh chị  trong t&ograve;a soạn hướng dẫn m&igrave;nh rất nhiều. M&igrave;nh c&oacute; những buổi phỏng vấn,  t&igrave;m hiểu về thế giới học đường của c&aacute;c bạn teen. Qua đ&oacute;, m&igrave;nh c&ograve;n quen  th&ecirc;m cả một số thầy c&ocirc; gi&aacute;o th&agrave;nh t&iacute;ch &ldquo;cỡ bự&rdquo;, th&iacute;ch lắm ấy&rdquo;.</span></div>\n<div style=\"text-align: justify;\">Hay  qua những b&agrave;i phỏng vấn, những buổi chụp h&igrave;nh, những b&agrave;i ph&oacute;ng sự, c&aacute;c  teen c&ograve;n c&oacute; dịp tr&ograve; chuyện với cả những nh&acirc;n vật nổi tiếng. Nhất l&agrave; khi  l&agrave;m CTV của c&aacute;c tờ b&aacute;o giải tr&iacute; th&igrave; chuyện gặp v&agrave; tr&ograve; chuyện với c&aacute;c  &ldquo;Star&rdquo; l&agrave; &ldquo;b&igrave;nh thường như cơm bữa&rdquo;. Ngo&agrave;i ra, c&aacute;c teen c&ograve;n được hướng  dẫn c&aacute;ch giao tiếp khi bước ra x&atilde; hội. Đ&acirc;y l&agrave; một bước bật cần thiết khi  đi l&agrave;m. Thật tuyệt vời phải kh&ocirc;ng?</div>\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold;\">Lương thưởng hấp dẫn</span></div>\n<div style=\"text-align: justify;\">Kh&aacute;c  hẳn c&aacute;c part-time kh&aacute;c, part-time n&agrave;y kh&ocirc;ng c&oacute; lương, thưởng t&iacute;nh theo  thời gian m&agrave; theo chất lượng v&agrave; khả năng l&agrave;m việc. Để c&oacute; những b&agrave;i b&aacute;o  hay, những teen CTV cần l&agrave; những người cần mẫn. C&oacute; nhiều b&agrave;i viết m&agrave; để  thực hiện được th&igrave; kh&aacute; vất vả đ&ograve;i hỏi phải c&oacute; c&aacute;i t&acirc;m với nghề. Nhưng sự  vất vả ấy cũng sẽ nhanh ch&oacute;ng được đền b&ugrave; bởi lương thưởng cho những  b&agrave;i b&aacute;o xuất sắc v&ocirc; c&ugrave;ng hấp dẫn.</div>\n<div style=\"text-align: justify;\">Một  số t&ograve;a soạn c&ograve;n trả nhuận b&uacute;t theo chất lượng b&agrave;i viết. B&agrave;i viết c&agrave;ng  n&oacute;ng, c&agrave;ng được quan t&acirc;m th&igrave; nhuận b&uacute;t c&agrave;ng cao (ch&iacute;nh x&aacute;c do được  thưởng th&ecirc;m n&ecirc;n cao). Ngo&agrave;i ra, khi c&oacute; nhiều b&agrave;i b&aacute;o xuất sắc, c&aacute;c teen  c&ograve;n c&oacute; cơ hội thử sức với những vai tr&ograve; v&agrave; vị tr&iacute; cao hơn trong t&ograve;a soạn  b&aacute;o nữa ấy.</div>\n<div style=\"text-align: justify;\">Như cậu bạn t&ecirc;n <span style=\"font-weight: bold;\">Thanh Hải,</span> 17 tuổi, chỉ sau 3 th&aacute;ng chăm chỉ miệt m&agrave;i với c&ocirc;ng việc của một cộng  t&aacute;c vi&ecirc;n, cậu đ&atilde; d&agrave;nh dụm đủ tiền để tự tổ chức một c&aacute;i sinh nhật cực ổn  v&agrave; đ&aacute;ng nhớ c&ugrave;ng bạn b&egrave;. Ai cũng trầm trồ khi cậu c&ocirc;ng tử 17 tuổi kh&ocirc;ng  xin gia đ&igrave;nh 1 đồng khi tổ chức buổi sinh nhật n&agrave;y.</div>\n<div style=\"text-align: justify;\">Phần  thưởng lớn nhất của một nh&agrave; b&aacute;o, hay 1 CTV giỏi kh&ocirc;ng chỉ l&agrave; số tiền m&agrave;  teen kiếm được c&ograve;n l&agrave; danh tiếng của teen trong nghề, v&agrave; những cơ hội  thử sức nghề nghiệp mới. Ở lứa tuổi đi học m&agrave; c&oacute; điều kiện để ph&aacute;t triển  nghề nghiệp v&agrave; định hướng cho tương lai th&igrave; thật tuyệt vời nhỉ?</div>\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold;\">R&egrave;n luyện t&iacute;nh c&aacute;ch</span></div>\n<div style=\"text-align: justify;\">Khoảng  thời gian mới trở th&agrave;nh CTV, c&aacute;c teen chắc chắn sẽ phải thật cần mẫn v&agrave;  chăm chỉ, đ&ograve;i hỏi c&aacute;i t&acirc;m. Tất cả những th&ocirc;ng tin để viết b&agrave;i đều tự  m&igrave;nh đi t&igrave;m hiểu, chụp h&igrave;nh, viết b&agrave;i, l&agrave;m sao gởi đi cho kịp để b&agrave;i b&aacute;o  c&ograve;n n&oacute;ng. Thế nhưng những b&agrave;i viết ban đầu của c&aacute;c teen thường vấp phải  nhiều lỗi, n&ecirc;n c&oacute; khi teen viết nhưng kh&ocirc;ng l&ecirc;n trang, l&ecirc;n b&agrave;i. Đặc  biệt, những th&ocirc;ng tin n&oacute;ng th&igrave; cần được b&iacute;-mật ho&agrave;n to&agrave;n. N&oacute; cũng gi&uacute;p  teen tập t&iacute;nh ki&ecirc;n tr&igrave; v&agrave; khả năng giữ b&iacute; mật cực tốt.</div>\n<div style=\"text-align: justify;\"><span style=\"font-weight: bold;\">Thảo Anh</span> (CTV của một tờ b&aacute;o online) cho biết: <span style=\"font-style: italic;\">&ldquo;C&ocirc;ng  việc CTV b&aacute;o ch&iacute; gi&uacute;p m&igrave;nh cẩn thận hơn. C&oacute; thể n&oacute;i l&agrave;&hellip; cẩn thận đến  từng vần, từng chữ. Như khi m&igrave;nh viết một b&agrave;i ph&oacute;ng sự chẳng hạn. Nếu  m&igrave;nh viết sai, d&ugrave; chỉ 1 c&acirc;u từ, cũng c&oacute; thể khiến người đọc hiểu sai &yacute;  người viết, n&ecirc;n tất cả m&igrave;nh đều trau chuốt từng li, từng t&iacute; một&rdquo;.</span></div>\n<div style=\"text-align: justify;\">Ki&ecirc;n  tr&igrave;, nhẫn nại, chăm chỉ, phản ứng t&igrave;nh huống nhanh, giữ b&iacute; mật tốt l&agrave;  những t&iacute;nh c&aacute;ch ch&iacute;nh m&agrave; part-time n&agrave;y sẽ đem lại cho c&aacute;c bạn đấy. Vừa  kiếm được tiền ti&ecirc;u vặt, vừa tạo được cho ch&iacute;nh m&igrave;nh những cơ hội nghề  nghiệp, lại r&egrave;n luyện được t&iacute;nh c&aacute;ch của bản th&acirc;n th&igrave; thật &yacute; nghĩa phải  kh&ocirc;ng n&agrave;o? Vậy c&aacute;c teen nh&agrave; m&igrave;nh c&ograve;n chờ g&igrave; nữa m&agrave; kh&ocirc;ng thử sức với  part-time n&agrave;y nhỉ?</div>\n</div>','2011-04-27 11:01:43','admin@jobbid.vn',831,0,NULL),(33,'luong-part-time-tang-nguoi-lam-them-van-chat-vat-','Lương part-time tăng, người làm thêm vẫn chật vật ',0,'thông tin đấu thầu - tim viec ban thoi gian - Thù lao cho những công việc bán thời gian đều đã tăng so với trước nhưng vẫn không thấm vào đâu so với sự leo thang của giá sinh hoạt khiến người làm thêm phải vất vả xoay sở.','<h4 class=\"Lead\"><span style=\"color: #888888;\">Th&ugrave; lao cho những c&ocirc;ng việc b&aacute;n thời gian đều đ&atilde; tăng  so với trước nhưng vẫn kh&ocirc;ng thấm v&agrave;o đ&acirc;u so với sự leo thang của gi&aacute;  sinh hoạt khiến người l&agrave;m th&ecirc;m phải vất vả xoay sở.</span><br /><a class=\"Lead\" href=\"http://vnexpress.net/gl/kinh-doanh/2011/04/gia-ca-tang-cao-quan-an-binh-dan-tim-ke-giu-khach/\"></a></h4>\n<p class=\"Normal\">Mỗi tuần, Hồng Ngọc (sinh vi&ecirc;n ĐH Giao th&ocirc;ng vận tải)  đi dạy th&ecirc;m 3 buổi cho một học sinh tiểu học nh&agrave; ở đường Th&aacute;i Thịnh (H&agrave;  Nội). Với th&ugrave; lao 60.000 đồng một buổi, một th&aacute;ng, Ngọc được nhận khoảng  hơn 700.000 đồng. Tuy nhi&ecirc;n, theo c&ocirc;, số tiền n&agrave;y vẫn kh&ocirc;ng đủ để trang  trải chi ph&iacute; sinh hoạt ng&agrave;y một đắt đỏ.</p>\n<p class=\"Normal\">Ngọc t&iacute;nh to&aacute;n, một th&aacute;ng, c&oacute; 700.000 đồng tiền lương  dạy th&ecirc;m, cộng th&ecirc;m số tiền 800.000 đồng bố mẹ chu cấp, th&igrave; vừa xoẻn cho  c&aacute;c sinh hoạt ph&iacute; h&agrave;ng ng&agrave;y. Chi ph&iacute; cố định mỗi th&aacute;ng c&ocirc; sinh vi&ecirc;n n&agrave;y  phải d&ugrave;ng l&agrave; 600.000 đồng tiền thu&ecirc; nh&agrave;, 80.000 đồng tiền nước, từ  120.000 đồng đến 150.000 đồng tiền điện. C&ograve;n lại khoảng 700.000 đồng  Ngọc d&ugrave;ng trong việc ăn uống, đi lại.</p>\n<p class=\"Normal\">C&ocirc; sinh vi&ecirc;n n&agrave;y cho hay b&igrave;nh thường m&ugrave;ng 10 h&agrave;ng  th&aacute;ng b&agrave; chủ mới đến thu tiền nh&agrave;, nhưng nếu th&aacute;ng n&agrave;o bị thu sớm hơn  th&igrave; chắc chắn phải vay mượn để chi ti&ecirc;u nửa th&aacute;ng cuối. \"V&igrave; thường th&igrave;  15 h&agrave;ng th&aacute;ng, phụ huynh học sinh mới trả tiền dạy nửa th&aacute;ng đầu\", Ngọc  kể. C&ocirc; cũng th&ocirc;ng tin, trước kia, thường th&igrave; cuối th&aacute;ng mới lĩnh lương  dạy th&ecirc;m từ phụ huynh. Nhưng hiện tại, cuộc sống kh&oacute; khăn, t&uacute;i lu&ocirc;n  trong t&igrave;nh trạng \"vi&ecirc;m m&agrave;ng\", n&ecirc;n đề xuất phụ huynh học sinh nửa th&aacute;ng  cho lĩnh tiền một lần.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/95/7d/gia-su.jpg\" alt=\"Gi&aacute; cả tăng cao, nhiều c&ocirc;ng việc l&agrave;m th&ecirc;m cũng c&acirc;n nhắc tăng mức lương, thưởng, th&ugrave; lao. Ảnh minh họa: Tuệ Minh\" width=\"350\" height=\"539\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Gi&aacute; cả tăng cao, nhiều c&ocirc;ng việc l&agrave;m th&ecirc;m cũng c&acirc;n nhắc tăng mức lương, thưởng, th&ugrave; lao. Ảnh minh họa: <em>Tuệ Minh</em></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Ghi nhận của <em>VnExpress.net</em>, từ ng&agrave;y gi&aacute; tăng,  những c&ocirc;ng việc l&agrave;m th&ecirc;m d&agrave;nh cho sinh vi&ecirc;n như gia sư, tiếp thị hay  nh&acirc;n vi&ecirc;n ph&aacute;t tờ rơi, sản phẩm khuyến mại&hellip; cũng c&oacute; mức th&ugrave; lao cao hơn.  Hiện tại, lương gia sư dạy c&aacute;c lớp tiểu học cũng tăng khoảng  10.000-15.000 đồng, l&ecirc;n 50.000-60.000 đồng một ca so với trước. Tiền  c&ocirc;ng ph&aacute;t tờ rơi v&agrave; sản phẩm khuyến mại như sữa, giấy ăn,&hellip; tại c&aacute;c si&ecirc;u  thị, trường học cũng dao động 100.000-150.000 đồng một ca, t&ugrave;y sản phẩm.</p>\n<p class=\"Normal\">Tuy nhi&ecirc;n, số tiền n&agrave;y kh&ocirc;ng thấm g&igrave; so với mức tăng  của tất cả c&aacute;c h&agrave;ng h&oacute;a, dịch vụ thiết yếu. Hơn nửa năm trước, lương dạy  th&ecirc;m chỉ 50.000 đồng một ca. Với 50.000 đồng n&agrave;y c&oacute; thể mua được gần 6  kg gạo b&igrave;nh thường loại 9.000 đồng một kg. Nhưng hiện tại, th&ugrave; lao gia  sư tăng th&ecirc;m 10.000 đồng, th&igrave; 6 kg gạo cũng kh&ocirc;ng c&ograve;n gi&aacute; 9.000 đồng một  kg nữa m&agrave; tăng l&ecirc;n 11.000-12.000 đồng.</p>\n<p class=\"Normal\">Ho&agrave;ng Hải, sinh vi&ecirc;n ĐH Quốc gia H&agrave; Nội đang l&agrave;m nh&acirc;n  vi&ecirc;n phục vụ b&agrave;n một qu&aacute;n ăn tr&ecirc;n đường Kim M&atilde; cho hay, mỗi th&aacute;ng, lương  l&agrave;m th&ecirc;m của cậu cũng được 2,3 triệu đồng. So với c&aacute;ch đ&acirc;y nửa năm, mức  n&agrave;y đ&atilde; tăng th&ecirc;m 300.000 đồng. Song d&ugrave; với số tiền nhiều hơn, vẫn phải  chật vật lắm cậu mới đủ tiền trang trải cuộc sống ở trọ chứ kh&ocirc;ng c&oacute;  chuyện để d&agrave;nh được tiền như năm ngo&aacute;i.</p>\n<p class=\"Normal\">Hải than thở, tiền nh&agrave; mới tăng từ 1 triệu l&ecirc;n 1,2  triệu đồng một th&aacute;ng. Nước 60.000 đồng, điện 4.000 đồng một số. T&iacute;nh ra  mỗi th&aacute;ng, khoản tiền d&agrave;nh cho thu&ecirc; nh&agrave;, điện, nước cũng hết khoảng 1,6  triệu đồng. Th&ecirc;m chi ph&iacute; xăng xe, ăn uống v&agrave; điện thoại ng&oacute;t ngh&eacute;t  600.000 đồng nữa l&agrave; gần như cạn số tiền lương l&agrave;m th&ecirc;m. \"Năm ngo&aacute;i,  lương chỉ 2 triệu nhưng mỗi th&aacute;ng m&igrave;nh vẫn tiết kiệm được khoảng v&agrave;i  trăm ngh&igrave;n đồng, v&igrave; gi&aacute; cả c&aacute;i g&igrave; cũng dễ chịu chứ kh&ocirc;ng đến mức phải  ngửa cổ k&ecirc;u trời như năm nay\", Hải n&oacute;i.</p>\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">Trong xu thế gi&aacute; cả h&agrave;ng  h&oacute;a, dịch vụ leo thang, hầu hết c&aacute;c điểm giới thiệu việc l&agrave;m part-time  cũng chọn c&aacute;ch điều chỉnh mức lương, thay v&igrave; bớt mối hay cắt giảm nh&acirc;n  lực.</span></strong></p>\n<p class=\"Normal\">Anh Thịnh, nh&acirc;n vi&ecirc;n m&ocirc;i giới một trung t&acirc;m gia sư tại  phố Mai Dịch (H&agrave; Nội) cho biết, từ khi gi&aacute; c&aacute;c mặt h&agrave;ng thiết lập một  mặt bằng mới, phần lớn c&aacute;c trung t&acirc;m gia sư cũng phải điều chỉnh tăng  th&ugrave; lao cho người dạy. Mức tăng phổ biến từ 10.000 đồng đến 30.000 đồng  một ca, t&ugrave;y độ tuổi học sinh. C&agrave;ng c&aacute;c lớp tiểu học mức tăng c&agrave;ng thấp,  c&ograve;n những bậc kh&aacute;c như trung học cơ sở hay trung học phổ th&ocirc;ng, th&igrave; tăng  nhiều hơn.</p>\n<p class=\"Normal\">Anh B&igrave;nh l&agrave;m ở trung t&acirc;m gia sư tr&ecirc;n phố Trần Quốc  Ho&agrave;n cũng cho hay, trung t&acirc;m n&agrave;y mới đề xuất với c&aacute;c phụ huynh c&oacute; nhu  cầu tuyển gi&aacute;o vi&ecirc;n dạy th&ecirc;m mức th&ugrave; lao mới. So với mức cũ, ph&iacute; học mỗi  ca tăng th&ecirc;m khoảng 10.000-20.000 đồng.</p>\n<p class=\"Normal\">Anh B&igrave;nh kể trước đ&oacute;, rất nhiều người sau khi đến tham  khảo th&ugrave; lao dạy th&ecirc;m đều quay đi. Nguy&ecirc;n nh&acirc;n gi&aacute; qu&aacute; thấp so với mặt  bằng thu nhập chung b&acirc;y giờ. \"C&oacute; bạn c&ograve;n n&oacute;i thẳng với t&ocirc;i, lương trả  50.000 đồng một ca 2 tiếng rưỡi dạy học sinh cấp hai. Một tuần dạy hai  buổi, th&aacute;ng dạy đủ th&igrave; được 400.000 đồng, chẳng đủ tiền nh&agrave;, th&igrave; l&agrave;m sao  trụ được ở thủ đ&ocirc;\", anh B&igrave;nh kể.</p>\n<p class=\"Normal\"><strong>Tuệ Minh </strong>( nguồn vnexpress.net)</p>','2011-05-03 19:40:16','admin@jobbid.vn',318,0,NULL),(35,'chan-trong-chan-ngoai-kiem-tien-thoi-bao-gia','Chân trong chân ngoài kiếm tiền thời bão giá',0,'tim viec ban thoi gian - cong viec part time - Thu nhập từ công việc chính chỉ có 2-3 triệu đồng mỗi tháng giữa thời buổi vật giá leo thang, nhiều người phải cáng đáng thêm một vài nghề tay trái, làm bán thời gian để trang trải chi tiêu đang không ngừng t','<p class=\"Leadb\">Thu nhập từ c&ocirc;ng việc ch&iacute;nh chỉ c&oacute; 2-3 triệu đồng mỗi  th&aacute;ng giữa thời buổi vật gi&aacute; leo thang, nhiều người phải c&aacute;ng đ&aacute;ng th&ecirc;m  một v&agrave;i nghề tay tr&aacute;i, l&agrave;m b&aacute;n thời gian để trang trải chi ti&ecirc;u đang  kh&ocirc;ng ngừng tăng l&ecirc;n.</p>\n<p><br /> <img src=\"http://www.xaluan.com/images/news/Image/2011/05/10/1305016530.img.jpg\" border=\"0\" alt=\"Gi&aacute; cả leo thang l&agrave; một trong những nguy&ecirc;n nh&acirc;n khiến cho nhiều người nỗ lực chạy s&ocirc; l&agrave;m th&ecirc;m để đ&aacute;nh vật với cơn b&atilde;o gi&aacute;. Ảnh: Vũ L&ecirc;.\" width=\"480\" height=\"342\" /><br /><em>Gi&aacute;  cả leo thang l&agrave; một trong những nguy&ecirc;n nh&acirc;n khiến cho nhiều người nỗ  lực chạy s&ocirc; l&agrave;m th&ecirc;m để đ&aacute;nh vật với cơn b&atilde;o gi&aacute;. Ảnh: Vũ L&ecirc;.</em></p>\n<p>Với những người l&agrave;m c&ocirc;ng ăn lương, lo  việc h&agrave;nh ch&iacute;nh  văn ph&ograve;ng, lương ba cọc ba đồng giữa l&uacute;c mớ rau, k&yacute;  gạo thi nhau tăng  gi&aacute;, th&igrave; việc thắt lưng buộc bụng c&agrave;ng trở n&ecirc;n nghẹt  thở.</p>\n<p>Cứ mỗi ngay sau ca trực, anh Ng&ocirc; Trọng Nghĩa (TP HCM)  lại tất tả đến  nh&agrave; một tiệm kiềm để nhận kiềm cắt m&oacute;ng tay về nh&agrave; m&agrave;i  giũa. L&agrave; nh&acirc;n  vi&ecirc;n bảo vệ, đồng lương eo hẹp 2,5 triệu đồng mỗi th&aacute;ng,  anh Nghĩa đ&atilde;  đối ph&oacute; với t&igrave;nh trạng gi&aacute; cả tăng cao từ đầu năm đến nay  bằng c&aacute;ch t&igrave;m  th&ecirc;m việc l&agrave;m tại nh&agrave;.</p>\n<p>Chia sẻ với <em>B&aacute;o</em>, anh Nghĩa trải  l&ograve;ng: \"M&agrave;i được một chiếc  kiềm cắt m&oacute;ng tay ngo&agrave;i giờ tiền c&ocirc;ng l&agrave; 3.000  đồng. Ngo&agrave;i ca trực,  trung b&igrave;nh một ng&agrave;y t&ocirc;i m&agrave;i được 50-100 chiếc, kể  cũng c&oacute; th&ecirc;m  150.000-300.000 đồng, một th&aacute;ng &iacute;t nhất cũng kiếm th&ecirc;m được  dăm ba  triệu đồng nếu mọi việc thuận lợi\".</p>\n<p>Theo anh Nghĩa, việc l&agrave;m th&ecirc;m n&agrave;y cũng c&oacute; hạn chế l&agrave;  kh&ocirc;ng phải l&uacute;c  n&agrave;o cũng c&oacute; kiềm cho m&igrave;nh m&agrave;i, m&agrave; phải t&ugrave;y thuộc v&agrave;o c&ocirc;ng  việc của c&ocirc;ng  ty c&oacute; rảnh rỗi kh&ocirc;ng. Th&ecirc;m v&agrave;o đ&oacute;, sau khi m&agrave;i kiềm, hai  b&agrave;n tay  thường bị đầy vết xước v&agrave; chai cứng, rất kh&oacute; chịu. \"Giữa l&uacute;c mọi  thứ  đều thi nhau tăng gi&aacute;, l&agrave;m một c&ocirc;ng việc kh&ocirc;ng t&agrave;i n&agrave;o đủ sống n&ecirc;n  t&ocirc;i  phải cố. Vả lại, t&ocirc;i đ&atilde; quen dần với nghề tay tr&aacute;i n&agrave;y\", anh Nghĩa  bộc  bạch.</p>\n<p>Trong khi đ&oacute;, l&agrave; d&acirc;n ngoại tỉnh từ miền Trung v&agrave;o S&agrave;i  G&ograve;n lập  nghiệp, nh&acirc;n vi&ecirc;n kế to&aacute;n Trần Thị Thảo c&oacute; đến hai nghề tay  tr&aacute;i. H&agrave;ng  ng&agrave;y, sau 8 giờ đồng hồ đ&aacute;nh vật với những con số, chị bắt  đầu v&agrave;o ca  đ&ecirc;m ở một trung t&acirc;m ngoại ngữ tại quận 10 với c&ocirc;ng việc nh&acirc;n  vi&ecirc;n ghi  danh. Ng&agrave;y cuối tuần, nữ kế to&aacute;n n&agrave;y c&ograve;n đi bỏ mối vải ở chợ B&agrave;  Chiểu.  Thỉnh thoảng c&oacute; lời đề nghị khai b&aacute;o thuế dịch vụ, chị kh&ocirc;ng  quản ngại  thức khuya b&ecirc;n đống sổ s&aacute;ch của kh&aacute;ch h&agrave;ng để ho&agrave;n th&agrave;nh c&ocirc;ng  việc b&aacute;n  thời gian.</p>\n<p>Chị Thảo t&acirc;m sự: \"Lương nh&acirc;n vi&ecirc;n kế to&aacute;n như t&ocirc;i chỉ 3  triệu đồng  mỗi th&aacute;ng, vậy m&agrave; c&ograve;n phải ở nh&agrave; thu&ecirc;, lo bao nhi&ecirc;u thứ chi  ti&ecirc;u cơm &aacute;o  gạo tiền, t&iacute;ch lũy cho tương lai sau n&agrave;y v&agrave; gửi về qu&ecirc; &iacute;t  đồng cho bố  mẹ với c&aacute;c em. Nếu kh&ocirc;ng chụp giựt l&agrave;m th&ecirc;m đủ thứ việc th&igrave;  kh&ocirc;ng thể  trụ được ở S&agrave;i G&ograve;n\".</p>\n<p>Với c&ocirc;ng nh&acirc;n, cuộc sống thời b&atilde;o gi&aacute; c&agrave;ng bấp b&ecirc;nh  gấp bội. Vợ  chồng chị L&acirc;m Thị Th&uacute;y Liễu v&agrave; anh La Th&agrave;nh Nam l&agrave;m việc ở  khu c&ocirc;ng  nghiệp T&acirc;n B&igrave;nh thu nhập chỉ c&oacute; 4 triệu đồng mỗi th&aacute;ng. Do gi&aacute;  cả tăng  cao, đời sống kh&oacute; khăn, anh chị cũng ph&acirc;n c&ocirc;ng mỗi người t&igrave;m  th&ecirc;m một  việc l&agrave;m ngo&agrave;i giờ.</p>\n<p>Nhờ người đồng hương ở S&oacute;c Trăng giới thiệu, chị Liễu  t&igrave;m được ch&acirc;n  rửa b&aacute;t ở một qu&aacute;n ăn gần nh&agrave; trọ. Hết giờ l&agrave;m, chị chạy  vội đến qu&aacute;n  cơm để phụ việc, mỗi buổi l&agrave;m th&ecirc;m n&agrave;y chị được trả 30.000  đồng v&agrave; một  suất ăn. C&ograve;n anh Nam nhận việc thử b&oacute;ng đ&egrave;n cho một h&atilde;ng sản  xuất đồ  điện. C&ocirc;ng việc của anh sau khi tan ca l&agrave; cầm c&acirc;y v&iacute;t thử điện  để kiểm  tra b&oacute;ng đ&egrave;n, mỗi b&oacute;ng thử xong anh được trả 100 đồng. \"Trung  b&igrave;nh một  giờ đồng hồ t&ocirc;i thử được 100 b&oacute;ng đ&egrave;n, cũng kiếm th&ecirc;m được  10.000 đồng  mỗi giờ đồng hồ. Vợ chồng c&ugrave;ng cố gắng n&ecirc;n 2 th&aacute;ng nay ch&uacute;ng  t&ocirc;i mới  tho&aacute;t khỏi cảnh nơm nớp lo thiếu hụt tiền v&agrave;o cuối th&aacute;ng\", anh  Nam mở  l&ograve;ng.</p>\n<p>Trong t&igrave;nh cảnh bấp b&ecirc;nh hơn cả người c&oacute; bằng cấp hoặc  t&igrave;m được một  c&ocirc;ng việc ổn định, anh L&ecirc; Hiền Lương qu&ecirc; Ph&uacute; Thọ v&agrave;o S&agrave;i  G&ograve;n lập nghiệp  chỉ mới học hết lớp 12 v&agrave; l&agrave;m c&ocirc;ng việc bưng b&ecirc; phục vụ  tại một qu&aacute;n  ăn tại quận 1. Anh Lương cho biết: \"T&ocirc;i đ&atilde; nỗ lực l&agrave;m đến 2  ca một ng&agrave;y  n&ecirc;n ky c&oacute;p mỗi th&aacute;ng được hơn 4 triệu đồng. Nhờ c&oacute; ch&uacute;t  tiền tu&yacute;p  (tiền boa), t&ocirc;i mới c&aacute;ng đ&aacute;ng được c&aacute;c khoản chi ti&ecirc;u: tiền  nh&agrave;, điện,  nước, cơm... cứ thi nhau đội l&ecirc;n m&agrave; vẫn gửi về qu&ecirc; cho mẹ v&agrave;  em trai\".</p>\n<p>Anh nh&acirc;n vi&ecirc;n phục vụ kể th&ecirc;m, với t&igrave;nh h&igrave;nh hiện nay,  nếu chỉ l&agrave;m  một ca th&igrave; tổng thu nhập của anh chỉ đủ sống đến ng&agrave;y 20  mỗi th&aacute;ng l&agrave;  c&ugrave;ng. Ch&iacute;nh v&igrave; thế, anh đ&atilde; n&agrave;i nỉ người quản l&yacute; xem x&eacute;t cho  anh l&agrave;m 2  ca một ng&agrave;y. \"Ở chỗ t&ocirc;i c&oacute; nhiều sinh vi&ecirc;n cũng tranh thủ  thời gian l&agrave;m  th&ecirc;m. Tuy nhi&ecirc;n, họ c&ograve;n c&oacute; việc học để m&agrave; hy vọng, chứ t&ocirc;i  th&igrave; chẳng  c&oacute; g&igrave; cả. Nay mai vật gi&aacute; lại leo thang nữa th&igrave; t&ocirc;i kh&ocirc;ng  biết cuộc  sống c&ograve;n kh&oacute; khăn đến nhường n&agrave;o\", anh Lương thở vắn than d&agrave;i</p>\n<p><br />Nguồn đọc th&ecirc;m:  <a style=\"color: #003399;\" href=\"http://www.xaluan.com/modules.php?name=News&amp;file=article&amp;sid=263685#ixzz1LyUY7zbQ\">http://www.xaluan.com/modules.php?name=News&amp;file=article&amp;sid=263685#ixzz1LyUY7zbQ</a> <br /> <a style=\"color: #003399;\" href=\"http://www.xaluan.com/\" target=\"_blank\">http://www.xaluan.com/</a></p>','2011-05-11 01:14:59','admin@jobbid.vn',426,0,NULL),(36,'ra-mat-dich-vu-dang-tin-rao-vat-mien-phi-tren-jobbid.vn','Ra mắt dịch vụ đăng tin rao vặt miễn phí trên Jobbid.vn',0,'Nếu như bạn có nhu cầu cần rao vặt bất cứ gì, các bạn có thể sử dụng dịch vụ mới của chúng tôi.','<p><img style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" src=\"http://www.jobbid.vn/upload/images/1300086520_BANNER_QUANG_CAO(200x120).jpg\" alt=\"\" width=\"200\" height=\"120\" />Xin ch&agrave;o c&aacute;c th&agrave;nh vi&ecirc;n của <a href=\"../\">Jobbid.vn</a></p>\n<p>Nếu như bạn c&oacute; nhu cầu cần rao vặt bất cứ g&igrave;, c&aacute;c bạn c&oacute; thể sử dụng dịch vụ mới của ch&uacute;ng t&ocirc;i.<br />Để đăng tin rao vặt cho bạn, vui l&ograve;ng click v&agrave;o n&uacute;t <a title=\"dang_tin_rao_vat\" href=\"http://www.jobbid.vn/raovat/dang_tin_rao_vat\"><span style=\"font-weight: bold; color: #000099;\">ĐĂNG RAO VẶT</span></a> tr&ecirc;n thanh menu.<br />Box <span style=\"font-weight: bold; color: #000099;\">Tin Rao Vặt</span> ở cột b&ecirc;n phải sẽ hiển thị <span style=\"font-weight: bold; color: #ff0000; text-decoration: underline;\">7</span> tin rao vặt mới nhất.<br />Để tin rao vặt của bạn lu&ocirc;n ở tr&ecirc;n đầu, c&aacute;c bạn c&oacute; thể v&agrave;o tin rao của bạn v&agrave; nhấn v&agrave;o n&uacute;t <span style=\"font-weight: bold; color: #000099;\">Up Tin</span>, tin của bạn sẽ được đẩy l&ecirc;n h&agrave;ng tr&ecirc;n c&ugrave;ng (tất nhi&ecirc;n l&agrave; bạn cần phải đăng nhập v&agrave;o t&agrave;i khoản của bạn tr&ecirc;n <a href=\"../\">Jobbid.vn</a> th&igrave; mới c&oacute; thể up tin của bạn được).<br /><br />Rất cảm ơn c&aacute;c bạn đ&atilde; sử dụng dịch vụ của ch&uacute;ng t&ocirc;i.</p>','2011-05-12 00:40:04','admin@jobbid.vn',195,0,NULL),(37,'doi-thoai-part-time-nhung-cu-click-ra-tien','Đối thoại part-time: Những cú click ra tiền',0,'cong viec part time - cong viec ban thoi gian - Bạn có thể ngồi nhà và mua hàng bên Mỹ, bạn ở Hà Nội có thể mua hàng ở TPHCM, hay bất cứ nơi đâu... Bạn không cần một cửa hàng theo nguyên tắc truyền...','<html>\r\n	<head>\r\n		<title></title>\r\n	</head>\r\n	<body>\r\n		<p>\r\n			<span class=\"tomtat\" style=\"font-family: tahoma; font-weight: bold; text-align: justify; color: rgb(136, 136, 136);\"><img alt=\"cong viec part time\" height=\"200\" src=\"http://www.svvn.vn/upload/20100415/5.jpg\" style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"công việc part time\" width=\"300\" />(SVVN) Bạn c&oacute; thể ngồi nh&agrave; v&agrave; mua h&agrave;ng b&ecirc;n Mỹ, bạn ở H&agrave; Nội c&oacute; thể mua h&agrave;ng ở TPHCM, hay bất cứ nơi đ&acirc;u... Bạn kh&ocirc;ng cần một cửa h&agrave;ng theo nguy&ecirc;n tắc truyền thống với địa thế mặt phố, biển hiệu...sản phẩm của bạn vẫn nhiều người biết đến. Bạn đang l&agrave; sinh vi&ecirc;n, điều đ&oacute; kh&ocirc;ng quan trọng, miễn l&agrave; sản phẩm của bạn tạo được chữ t&iacute;n với cộng đồng online. Đ&oacute; l&agrave; những tiện &iacute;ch m&agrave; internet đ&atilde; mang đến cho cuộc sống hiện tại... v&agrave; cũng l&agrave; chủ đề của Đối thoại part-time diễn ra v&agrave;o tối qua (14/4/2010) tại to&agrave; soạn B&aacute;o Sinh Vi&ecirc;n Việt Nam, số 5 Ho&agrave; M&atilde;</span></p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			Tham dự buổi Đối thoại Part-time c&oacute; anh<span class=\"Apple-converted-space\">&nbsp;</span><strong>L&ecirc; Thanh H&agrave;</strong>- Ph&oacute; Tổng bi&ecirc;n tập b&aacute;o Sinh Vi&ecirc;n Việt Nam; anh<strong>Nguyễn Tuấn Anh</strong>- Thư k&yacute;&nbsp; to&agrave; soạn b&aacute;o SVVN, Gi&aacute;m đốc Arena-Multimedia, &ocirc;ng<span class=\"Apple-converted-space\">&nbsp;</span><strong>Đinh Tr&iacute; Dũng</strong>;Ph&oacute; Tổng gi&aacute;m đốc Li&ecirc;n doanh chợ điện tử eBay (Đơn vị h&agrave;ng đầu về TM điện tử tại Việt Nam), &nbsp;<strong>Đ&agrave;o Lan Hương</strong><span class=\"Apple-converted-space\">&nbsp;</span>v&agrave; Trưởng ph&ograve;ng kinh doanh<span class=\"Apple-converted-space\">&nbsp;</span><strong>Đỗ B&iacute;ch Nga</strong>;<span class=\"Apple-converted-space\">&nbsp;</span><strong>CEO Đinh Việt Hưng</strong>, người s&aacute;ng lập hệ thống thương mại điện tử cung cấp c&aacute;c<span class=\"Apple-converted-space\">&nbsp;</span>dịch vụ<span class=\"Apple-converted-space\">&nbsp;</span>thiết kế web:<span class=\"Apple-converted-space\">&nbsp;</span><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: 10pt;\">www.joomlart.com</span>&nbsp;v&agrave; hệ thống đấu thầu trực tuyến cho c&aacute;c dự &aacute;n IT, đặc biệt l&agrave; c&aacute;c dự &aacute;n m&atilde; nguồn mở cho kh&aacute;ch h&agrave;ng khắp thế giới<span class=\"Apple-converted-space\">&nbsp;</span><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: 10pt;\">www.joomlancers.com</span>; Gi&aacute;m đốc c&ocirc;ng ty TNHH Thưong mại dịch vụ xuất nhập khẩu Pi,<span class=\"Apple-converted-space\">&nbsp;</span><strong>Dương Minh Tiến</strong>; Nh&agrave; ruyển dụng<span class=\"Apple-converted-space\">&nbsp;</span><strong>Đinh Thị Thu Thuỷ,</strong><span class=\"Apple-converted-space\">&nbsp;</span>phụ tr&aacute;ch nh&acirc;n sự c&ocirc;ng ty TNHH Đầu tư ph&aacute;t triển KH-KT Dược phẩm ANZ Việt Nam c&ugrave;ng c&aacute;c bạn sinh vi&ecirc;n đến từ c&aacute;c trường ĐH, CĐ c&oacute; quan t&acirc;m.</p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			<img alt=\"2.jpg\" border=\"0\" height=\"185\" src=\"http://www.svvn.vn/upload/20100415/2.jpg\" width=\"450\" /></p>\r\n		<p>\r\n			<img alt=\"5.jpg\" border=\"0\" height=\"300\" src=\"http://www.svvn.vn/upload/20100415/5.jpg\" width=\"450\" /></p>\r\n		<p>\r\n			<img alt=\"4.jpg\" border=\"0\" height=\"287\" src=\"http://www.svvn.vn/upload/20100415/4.jpg\" width=\"450\" /></p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			<img alt=\"3.jpg\" border=\"0\" height=\"237\" src=\"http://www.svvn.vn/upload/20100415/3.jpg\" width=\"450\" /></p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			Chương tr&igrave;nh khai th&aacute;c c&acirc;u chuyện của 3 bạn trẻ về qu&aacute; tr&igrave;nh x&acirc;y dựng shop tr&ecirc;n chợ ảo, quảng c&aacute;o sản phẩm, những th&agrave;nh c&ocirc;ng hay sự cố ngo&agrave;i &yacute; muốn, quy tr&igrave;nh mua b&aacute;n, tạo dựng thương hiệu... Ngo&agrave;i ra chương tr&igrave;nh c&ograve;n l&agrave; những chia sẻ về kinh nghiệm, kỹ năng, b&iacute; quyết b&aacute;n h&agrave;ng qua mạng của c&aacute;c kh&aacute;ch mời.</p>\r\n	</body>\r\n</html>\r\n','2011-05-16 08:57:48','admin',710,0,'Doi thoai part-time: Nhung cu click ra tien <html>\r\n	<head>\r\n		<title></title>\r\n	</head>\r\n	<body>\r\n		<p>\r\n			<span class=\"tomtat\" style=\"font-family: tahoma; font-weight: bold; text-align: justify; color: rgb(136, 136, 136);\"><img alt=\"cong viec part time\" height=\"200\" src=\"http://www.svvn.vn/upload/20100415/5.jpg\" style=\"padding-right: 5px; padding-bottom: 5px; float: left;\" title=\"cong viec part time\" width=\"300\" />(SVVN) Ban c&oacute; the ngoi nh&agrave; v&agrave; mua h&agrave;ng b&ecirc;n My, ban o H&agrave; Noi c&oacute; the mua h&agrave;ng o TPHCM, hay bat cu noi d&acirc;u... Ban kh&ocirc;ng can mot cua h&agrave;ng theo nguy&ecirc;n tac truyen thong voi dia the mat pho, bien hieu...san pham cua ban van nhieu nguoi biet den. Ban dang l&agrave; sinh vi&ecirc;n, dieu d&oacute; kh&ocirc;ng quan trong, mien l&agrave; san pham cua ban tao duoc chu t&iacute;n voi cong dong online. D&oacute; l&agrave; nhung tien &iacute;ch m&agrave; internet d&atilde; mang den cho cuoc song hien tai... v&agrave; cung l&agrave; chu de cua Doi thoai part-time dien ra v&agrave;o toi qua (14/4/2010) tai to&agrave; soan B&aacute;o Sinh Vi&ecirc;n Viet Nam, so 5 Ho&agrave; M&atilde;</span></p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			Tham du buoi Doi thoai Part-time c&oacute; anh<span class=\"Apple-converted-space\">&nbsp;</span><strong>L&ecirc; Thanh H&agrave;</strong>- Ph&oacute; Tong bi&ecirc;n tap b&aacute;o Sinh Vi&ecirc;n Viet Nam; anh<strong>Nguyen Tuan Anh</strong>- Thu k&yacute;&nbsp; to&agrave; soan b&aacute;o SVVN, Gi&aacute;m doc Arena-Multimedia, &ocirc;ng<span class=\"Apple-converted-space\">&nbsp;</span><strong>Dinh Tr&iacute; Dung</strong>;Ph&oacute; Tong gi&aacute;m doc Li&ecirc;n doanh cho dien tu eBay (Don vi h&agrave;ng dau ve TM dien tu tai Viet Nam), &nbsp;<strong>D&agrave;o Lan Huong</strong><span class=\"Apple-converted-space\">&nbsp;</span>v&agrave; Truong ph&ograve;ng kinh doanh<span class=\"Apple-converted-space\">&nbsp;</span><strong>Do B&iacute;ch Nga</strong>;<span class=\"Apple-converted-space\">&nbsp;</span><strong>CEO Dinh Viet Hung</strong>, nguoi s&aacute;ng lap he thong thuong mai dien tu cung cap c&aacute;c<span class=\"Apple-converted-space\">&nbsp;</span>dich vu<span class=\"Apple-converted-space\">&nbsp;</span>thiet ke web:<span class=\"Apple-converted-space\">&nbsp;</span><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: 10pt;\">www.joomlart.com</span>&nbsp;v&agrave; he thong dau thau truc tuyen cho c&aacute;c du &aacute;n IT, dac biet l&agrave; c&aacute;c du &aacute;n m&atilde; nguon mo cho kh&aacute;ch h&agrave;ng khap the gioi<span class=\"Apple-converted-space\">&nbsp;</span><span style=\"font-family: Verdana,Arial,Helvetica,sans-serif; font-size: 10pt;\">www.joomlancers.com</span>; Gi&aacute;m doc c&ocirc;ng ty TNHH Thuong mai dich vu xuat nhap khau Pi,<span class=\"Apple-converted-space\">&nbsp;</span><strong>Duong Minh Tien</strong>; Nh&agrave; ruyen dung<span class=\"Apple-converted-space\">&nbsp;</span><strong>Dinh Thi Thu Thuy,</strong><span class=\"Apple-converted-space\">&nbsp;</span>phu tr&aacute;ch nh&acirc;n su c&ocirc;ng ty TNHH Dau tu ph&aacute;t trien KH-KT Duoc pham ANZ Viet Nam c&ugrave;ng c&aacute;c ban sinh vi&ecirc;n den tu c&aacute;c truong DH, CD c&oacute; quan t&acirc;m.</p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			<img alt=\"2.jpg\" border=\"0\" height=\"185\" src=\"http://www.svvn.vn/upload/20100415/2.jpg\" width=\"450\" /></p>\r\n		<p>\r\n			<img alt=\"5.jpg\" border=\"0\" height=\"300\" src=\"http://www.svvn.vn/upload/20100415/5.jpg\" width=\"450\" /></p>\r\n		<p>\r\n			<img alt=\"4.jpg\" border=\"0\" height=\"287\" src=\"http://www.svvn.vn/upload/20100415/4.jpg\" width=\"450\" /></p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			<img alt=\"3.jpg\" border=\"0\" height=\"237\" src=\"http://www.svvn.vn/upload/20100415/3.jpg\" width=\"450\" /></p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<p>\r\n			Chuong tr&igrave;nh khai th&aacute;c c&acirc;u chuyen cua 3 ban tre ve qu&aacute; tr&igrave;nh x&acirc;y dung shop tr&ecirc;n cho ao, quang c&aacute;o san pham, nhung th&agrave;nh c&ocirc;ng hay su co ngo&agrave;i &yacute; muon, quy tr&igrave;nh mua b&aacute;n, tao dung thuong hieu... Ngo&agrave;i ra chuong tr&igrave;nh c&ograve;n l&agrave; nhung chia se ve kinh nghiem, ky nang, b&iacute; quyet b&aacute;n h&agrave;ng qua mang cua c&aacute;c kh&aacute;ch moi.</p>\r\n	</body>\r\n</html>\r\n cong viec part time - cong viec ban thoi gian - Ban co the ngoi nha va mua hang ben My, ban o Ha Noi co the mua hang o TPHCM, hay bat cu noi dau... Ban khong can mot cua hang theo nguyen tac truyen...'),(38,'lam-them-khong-chi-vi-tien','Làm thêm không chỉ vì tiền',NULL,'tim viec lam them - tim viec lam ban thoi gian - Ngày càng có nhiều sinh viên đi làm thêm (part-time) để có thêm khoản thu nhập. Tuy nhiên, cũng có nhiều sinh viên làm thêm không phải vì tiền. Họ đi làm không...','<html>\r\n	<head>\r\n		<title></title>\r\n	</head>\r\n	<body>\r\n		<div class=\"NewsBrief\" style=\"padding: 0px 0px 5px;\">\r\n			<span style=\"color: rgb(136, 136, 136);\"><strong>(LĐ) - Ng&agrave;y c&agrave;ng c&oacute; nhiều sinh vi&ecirc;n đi l&agrave;m th&ecirc;m (part-time) để c&oacute; th&ecirc;m khoản thu nhập. Tuy nhi&ecirc;n, cũng c&oacute; nhiều sinh vi&ecirc;n l&agrave;m th&ecirc;m kh&ocirc;ng phải v&igrave; tiền. Họ đi l&agrave;m kh&ocirc;ng c&ocirc;ng nhưng rất nhiệt t&igrave;nh trong c&ocirc;ng việc.</strong></span></div>\r\n		<div class=\"NewsContent\">\r\n			<img alt=\"\" src=\"http://static.laodong.com.vn/2006.09/media/dbd65e6485e14f7796486254d9651299.jpg\" style=\"float: left; margin-right: 10px;\" />\r\n			<p style=\"margin: 0px; line-height: 16px; padding: 5px 0px; text-align: justify;\">\r\n				Buổi tọa đ&agrave;m &quot;Part-time kh&ocirc;ng phải v&igrave; tiền&quot; do B&aacute;o SVVN tổ chức mới đ&acirc;y đ&atilde; cho biết l&yacute; do v&igrave; sao họ lại c&oacute; đam m&ecirc; đ&oacute;.</p>\r\n			<p style=\"margin: 0px; line-height: 16px; padding: 5px 0px; text-align: justify;\">\r\n				Ng&ocirc; Chi L&ecirc; (SN 1987) l&agrave; một gương mặt trẻ ti&ecirc;u biểu cho sự tự tin, năng động. C&ocirc; từng đi l&agrave;m th&ecirc;m nhiều nơi khi c&ograve;n l&agrave; sinh vi&ecirc;n. C&ocirc; cựu sinh vi&ecirc;n ĐH Ngoại thương n&agrave;y hiện đang l&agrave;m việc tại Đại sứ qu&aacute;n Brazil. Để c&oacute; được vị tr&iacute; l&agrave;m tốt với mức lương hiện xấp xỉ 1.000USD như hiện nay, Chi L&ecirc; đ&atilde; phải mất nhiều c&ocirc;ng sức, thời gian để l&agrave;m việc cho Ph&aacute;i đo&agrave;n Ủy ban (UB) Ch&acirc;u &Acirc;u tại VN m&agrave; kh&ocirc;ng nhận được một đồng tiền c&ocirc;ng n&agrave;o.<br />\r\n				&nbsp;<br />\r\n				Chi L&ecirc; kể: &ldquo;Hồi c&ograve;n l&agrave; sinh vi&ecirc;n năm cuối, t&ocirc;i đ&atilde; mạnh dạn gửi hồ sơ sang ph&aacute;i đo&agrave;n xin thực tập. Việc xin thực tập v&agrave;o đ&acirc;y hết sức kh&oacute; khăn. T&ocirc;i đ&atilde; chờ c&acirc;u trả lời rất l&acirc;u nhưng vẫn ki&ecirc;n tr&igrave;. Chờ hơn 3 th&aacute;ng mới c&oacute; được sự đồng &yacute; của Ph&aacute;i đo&agrave;n UB Ch&acirc;u &Acirc;u. V&agrave;o đ&acirc;y, t&ocirc;i được ph&acirc;n về ban Thương mại với c&aacute;c việc như viết th&ocirc;ng c&aacute;o b&aacute;o ch&iacute; về c&aacute;c hoạt động kinh tế, thương mại song phương, soạn thảo c&aacute;c b&agrave;i ph&aacute;t biểu... Điều kh&oacute; khăn l&agrave; tất cả đều bằng tiếng Anh. Tiếng Anh l&uacute;c n&oacute;i kh&aacute;c nhiều so với văn phạm trong c&aacute;c văn bản n&ecirc;n thời gian đầu t&ocirc;i gặp nhiều kh&oacute; khăn&rdquo;.<span class=\"Apple-converted-space\">&nbsp;</span><br />\r\n				<br />\r\n				Nhưng dần dần sau khi được những người đi trước chỉ bảo, Chi L&ecirc; đ&atilde; quen với c&ocirc;ng việc.<br />\r\n				&nbsp;<br />\r\n				&ldquo;Những kiến thức bổ &iacute;ch trong thời gian l&agrave;m tại Ph&aacute;i đo&agrave;n UB Ch&acirc;u &Acirc;u gi&uacute;p t&ocirc;i tr&uacute;ng tuyển v&agrave;o Đại sứ qu&aacute;n Brazil trong kỳ tuyển dụng đầy kh&oacute; khăn&rdquo; - Chi L&ecirc; t&acirc;m sự. Trước đ&oacute;, Chi L&ecirc; cũng đ&atilde; cộng t&aacute;c viết b&agrave;i cho B&aacute;o SVVN, một thời gian l&agrave;m th&ecirc;m ở Đ&agrave;i Truyền h&igrave;nh VN.<span class=\"Apple-converted-space\">&nbsp;</span><br />\r\n				<br />\r\n				Nguyễn Hải H&agrave; - một bạn trẻ năng động kh&aacute;c cũng l&agrave; h&igrave;nh ảnh ti&ecirc;u biểu cho &yacute; ch&iacute; vươn l&ecirc;n, muốn khẳng định m&igrave;nh. C&ocirc; sinh vi&ecirc;n ĐH Kinh tế quốc d&acirc;n sau nhiều năm l&agrave;m việc trong c&aacute;c m&ocirc;i trường kh&aacute;c nhau hiện đang l&agrave;m việc tại Việt Nam Airlines ở ph&ograve;ng thương mại điện tử. Những c&ocirc;ng việc từng l&agrave;m th&ecirc;m của c&ocirc; g&aacute;i trẻ n&agrave;y l&ecirc;n đến con số 10 như l&agrave;m tư vấn cho SITC, l&agrave;m Promotion Girl cho Happident, tư vấn vi&ecirc;n mỹ phẩm, nh&acirc;n vi&ecirc;n so&aacute;t v&eacute; trong rạp chiếu phim, tham gia c&aacute;c hoạt động t&igrave;nh nguyện...<span class=\"Apple-converted-space\">&nbsp;</span><br />\r\n				<br />\r\n				Hải H&agrave; t&acirc;m sự: &ldquo;Điều quan trọng với m&igrave;nh khi đi l&agrave;m th&ecirc;m l&agrave; để c&oacute; th&ecirc;m kinh nghiệm v&agrave; những mối quan hệ. C&oacute; nhiều mối quan hệ sẽ gi&uacute;p m&igrave;nh rất nhiều sau n&agrave;y. C&aacute;c bạn sinh vi&ecirc;n n&ecirc;n đặt ra cho m&igrave;nh nhu cầu rằng t&ocirc;i muốn c&oacute; việc l&agrave;m. Sự th&ocirc;i th&uacute;c đ&oacute; sẽ gi&uacute;p c&aacute;c bạn t&igrave;m được việc v&agrave; sớm trưởng th&agrave;nh hơn&rdquo;.<span class=\"Apple-converted-space\">&nbsp;</span><br />\r\n				<br />\r\n				Cũng với suy nghĩ kh&ocirc;ng t&iacute;nh to&aacute;n thiệt hơn, c&aacute;c bạn trẻ trong nh&oacute;m Hanoikids lại mất nhiều thời gian để l&agrave;m tour guide (hướng dẫn vi&ecirc;n du lịch) kh&ocirc;ng c&ocirc;ng cho c&aacute;c vị kh&aacute;ch nước ngo&agrave;i. Đa số c&aacute;c bạn đều l&agrave; sinh vi&ecirc;n đang học tại H&agrave; Nội. Bạn Việt - th&agrave;nh vi&ecirc;n của nh&oacute;m cho biết: &ldquo;Tham gia hoạt động n&agrave;y gi&uacute;p ch&uacute;ng t&ocirc;i c&oacute; sự trao đổi văn h&oacute;a với kh&aacute;ch nước ngo&agrave;i, gi&uacute;p họ hiểu hơn về H&agrave; Nội, đồng thời cũng gi&uacute;p khả năng tiếng Anh của ch&uacute;ng t&ocirc;i tiến bộ hơn&rdquo;.<br />\r\n				<br />\r\n				Mỗi người một l&yacute; do, nhưng tất cả đều c&oacute; c&aacute;i nh&igrave;n về tương lai với mong muốn, những g&igrave; m&igrave;nh đ&atilde; l&agrave;m kh&ocirc;ng phải l&agrave; v&ocirc; &iacute;ch.</p>\r\n			<p style=\"margin: 0px; line-height: 16px; padding: 5px 0px; text-align: justify;\">\r\n				(nguồn laodong.com.vn)</p>\r\n		</div>\r\n	</body>\r\n</html>\r\n','2011-05-23 08:42:10','admin',293,0,'Lam them khong chi vi tien <html>\r\n	<head>\r\n		<title></title>\r\n	</head>\r\n	<body>\r\n		<div class=\"NewsBrief\" style=\"padding: 0px 0px 5px;\">\r\n			<span style=\"color: rgb(136, 136, 136);\"><strong>(LD) - Ng&agrave;y c&agrave;ng c&oacute; nhieu sinh vi&ecirc;n di l&agrave;m th&ecirc;m (part-time) de c&oacute; th&ecirc;m khoan thu nhap. Tuy nhi&ecirc;n, cung c&oacute; nhieu sinh vi&ecirc;n l&agrave;m th&ecirc;m kh&ocirc;ng phai v&igrave; tien. Ho di l&agrave;m kh&ocirc;ng c&ocirc;ng nhung rat nhiet t&igrave;nh trong c&ocirc;ng viec.</strong></span></div>\r\n		<div class=\"NewsContent\">\r\n			<img alt=\"\" src=\"http://static.laodong.com.vn/2006.09/media/dbd65e6485e14f7796486254d9651299.jpg\" style=\"float: left; margin-right: 10px;\" />\r\n			<p style=\"margin: 0px; line-height: 16px; padding: 5px 0px; text-align: justify;\">\r\n				Buoi toa d&agrave;m &quot;Part-time kh&ocirc;ng phai v&igrave; tien&quot; do B&aacute;o SVVN to chuc moi d&acirc;y d&atilde; cho biet l&yacute; do v&igrave; sao ho lai c&oacute; dam m&ecirc; d&oacute;.</p>\r\n			<p style=\"margin: 0px; line-height: 16px; padding: 5px 0px; text-align: justify;\">\r\n				Ng&ocirc; Chi L&ecirc; (SN 1987) l&agrave; mot guong mat tre ti&ecirc;u bieu cho su tu tin, nang dong. C&ocirc; tung di l&agrave;m th&ecirc;m nhieu noi khi c&ograve;n l&agrave; sinh vi&ecirc;n. C&ocirc; cuu sinh vi&ecirc;n DH Ngoai thuong n&agrave;y hien dang l&agrave;m viec tai Dai su qu&aacute;n Brazil. De c&oacute; duoc vi tr&iacute; l&agrave;m tot voi muc luong hien xap xi 1.000USD nhu hien nay, Chi L&ecirc; d&atilde; phai mat nhieu c&ocirc;ng suc, thoi gian de l&agrave;m viec cho Ph&aacute;i do&agrave;n Uy ban (UB) Ch&acirc;u &Acirc;u tai VN m&agrave; kh&ocirc;ng nhan duoc mot dong tien c&ocirc;ng n&agrave;o.<br />\r\n				&nbsp;<br />\r\n				Chi L&ecirc; ke: &ldquo;Hoi c&ograve;n l&agrave; sinh vi&ecirc;n nam cuoi, t&ocirc;i d&atilde; manh dan gui ho so sang ph&aacute;i do&agrave;n xin thuc tap. Viec xin thuc tap v&agrave;o d&acirc;y het suc kh&oacute; khan. T&ocirc;i d&atilde; cho c&acirc;u tra loi rat l&acirc;u nhung van ki&ecirc;n tr&igrave;. Cho hon 3 th&aacute;ng moi c&oacute; duoc su dong &yacute; cua Ph&aacute;i do&agrave;n UB Ch&acirc;u &Acirc;u. V&agrave;o d&acirc;y, t&ocirc;i duoc ph&acirc;n ve ban Thuong mai voi c&aacute;c viec nhu viet th&ocirc;ng c&aacute;o b&aacute;o ch&iacute; ve c&aacute;c hoat dong kinh te, thuong mai song phuong, soan thao c&aacute;c b&agrave;i ph&aacute;t bieu... Dieu kh&oacute; khan l&agrave; tat ca deu bang tieng Anh. Tieng Anh l&uacute;c n&oacute;i kh&aacute;c nhieu so voi van pham trong c&aacute;c van ban n&ecirc;n thoi gian dau t&ocirc;i gap nhieu kh&oacute; khan&rdquo;.<span class=\"Apple-converted-space\">&nbsp;</span><br />\r\n				<br />\r\n				Nhung dan dan sau khi duoc nhung nguoi di truoc chi bao, Chi L&ecirc; d&atilde; quen voi c&ocirc;ng viec.<br />\r\n				&nbsp;<br />\r\n				&ldquo;Nhung kien thuc bo &iacute;ch trong thoi gian l&agrave;m tai Ph&aacute;i do&agrave;n UB Ch&acirc;u &Acirc;u gi&uacute;p t&ocirc;i tr&uacute;ng tuyen v&agrave;o Dai su qu&aacute;n Brazil trong ky tuyen dung day kh&oacute; khan&rdquo; - Chi L&ecirc; t&acirc;m su. Truoc d&oacute;, Chi L&ecirc; cung d&atilde; cong t&aacute;c viet b&agrave;i cho B&aacute;o SVVN, mot thoi gian l&agrave;m th&ecirc;m o D&agrave;i Truyen h&igrave;nh VN.<span class=\"Apple-converted-space\">&nbsp;</span><br />\r\n				<br />\r\n				Nguyen Hai H&agrave; - mot ban tre nang dong kh&aacute;c cung l&agrave; h&igrave;nh anh ti&ecirc;u bieu cho &yacute; ch&iacute; vuon l&ecirc;n, muon khang dinh m&igrave;nh. C&ocirc; sinh vi&ecirc;n DH Kinh te quoc d&acirc;n sau nhieu nam l&agrave;m viec trong c&aacute;c m&ocirc;i truong kh&aacute;c nhau hien dang l&agrave;m viec tai Viet Nam Airlines o ph&ograve;ng thuong mai dien tu. Nhung c&ocirc;ng viec tung l&agrave;m th&ecirc;m cua c&ocirc; g&aacute;i tre n&agrave;y l&ecirc;n den con so 10 nhu l&agrave;m tu van cho SITC, l&agrave;m Promotion Girl cho Happident, tu van vi&ecirc;n my pham, nh&acirc;n vi&ecirc;n so&aacute;t v&eacute; trong rap chieu phim, tham gia c&aacute;c hoat dong t&igrave;nh nguyen...<span class=\"Apple-converted-space\">&nbsp;</span><br />\r\n				<br />\r\n				Hai H&agrave; t&acirc;m su: &ldquo;Dieu quan trong voi m&igrave;nh khi di l&agrave;m th&ecirc;m l&agrave; de c&oacute; th&ecirc;m kinh nghiem v&agrave; nhung moi quan he. C&oacute; nhieu moi quan he se gi&uacute;p m&igrave;nh rat nhieu sau n&agrave;y. C&aacute;c ban sinh vi&ecirc;n n&ecirc;n dat ra cho m&igrave;nh nhu cau rang t&ocirc;i muon c&oacute; viec l&agrave;m. Su th&ocirc;i th&uacute;c d&oacute; se gi&uacute;p c&aacute;c ban t&igrave;m duoc viec v&agrave; som truong th&agrave;nh hon&rdquo;.<span class=\"Apple-converted-space\">&nbsp;</span><br />\r\n				<br />\r\n				Cung voi suy nghi kh&ocirc;ng t&iacute;nh to&aacute;n thiet hon, c&aacute;c ban tre trong nh&oacute;m Hanoikids lai mat nhieu thoi gian de l&agrave;m tour guide (huong dan vi&ecirc;n du lich) kh&ocirc;ng c&ocirc;ng cho c&aacute;c vi kh&aacute;ch nuoc ngo&agrave;i. Da so c&aacute;c ban deu l&agrave; sinh vi&ecirc;n dang hoc tai H&agrave; Noi. Ban Viet - th&agrave;nh vi&ecirc;n cua nh&oacute;m cho biet: &ldquo;Tham gia hoat dong n&agrave;y gi&uacute;p ch&uacute;ng t&ocirc;i c&oacute; su trao doi van h&oacute;a voi kh&aacute;ch nuoc ngo&agrave;i, gi&uacute;p ho hieu hon ve H&agrave; Noi, dong thoi cung gi&uacute;p kha nang tieng Anh cua ch&uacute;ng t&ocirc;i tien bo hon&rdquo;.<br />\r\n				<br />\r\n				Moi nguoi mot l&yacute; do, nhung tat ca deu c&oacute; c&aacute;i nh&igrave;n ve tuong lai voi mong muon, nhung g&igrave; m&igrave;nh d&atilde; l&agrave;m kh&ocirc;ng phai l&agrave; v&ocirc; &iacute;ch.</p>\r\n			<p style=\"margin: 0px; line-height: 16px; padding: 5px 0px; text-align: justify;\">\r\n				(nguon laodong.com.vn)</p>\r\n		</div>\r\n	</body>\r\n</html>\r\n tim viec lam them - tim viec lam ban thoi gian - Ngay cang co nhieu sinh vien di lam them (part-time) de co them khoan thu nhap. Tuy nhien, cung co nhieu sinh vien lam them khong phai vi tien. Ho di lam khong...'),(39,'nguoi-ngheo-chat-vat-thoi-bao-gia','Người nghèo chật vật thời bão giá',20,'tim viec lam them - tim viec lam ban thoi gian - Đối với nhiều công nhân, những cụm từ như “lạm phát” hay “chỉ số giá tiêu dùng” (CPI) là một khái niệm xa lạ nhưng chắc chắn họ hiểu rằng cứ...','<html>\r\n	<head>\r\n		<title></title>\r\n	</head>\r\n	<body>\r\n		<table border=\"0\" height=\"201\" style=\"float: left;\" width=\"211\">\r\n			<tbody>\r\n				<tr>\r\n					<td>\r\n						<table align=\"left\" cellpadding=\"0\" cellspacing=\"0\" class=\"sgtoimageleft\">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										<img alt=\"\" class=\"sgtoimageleft_popup\" src=\"http://www.thesaigontimes.vn/Uploads/Articles/53977/714ad_-mg-7771.online_200.jpg\" style=\"width: 200px; height: 151px;\" /></td>\r\n								</tr>\r\n								<tr>\r\n									<td class=\"sgtoimageleft\">\r\n										<span style=\"font-size: x-small;\">Do gi&aacute; cả c&aacute;c mặt h&agrave;ng nhu yến phẩm đều tăng trong khi lương kh&ocirc;ng tăng bao nhi&ecirc;u n&ecirc;n nhiều c&ocirc;ng nh&acirc;n xin tăng ca hoặc l&agrave;m th&ecirc;m việc kh&aacute;c. Trong ảnh: c&ocirc;ng nh&acirc;n tại một cơ sở sản xuất dao, nh&iacute;p ở G&ograve; Vấp. Ảnh: Ngọc H&ugrave;ng</span></td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n						<p class=\"SGTOSummary\">\r\n							&nbsp;</p>\r\n					</td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n		<p class=\"SGTOSummary\">\r\n			<span style=\"color: rgb(136, 136, 136);\"><strong>(TBKTSG Online) - Đối với nhiều c&ocirc;ng nh&acirc;n, những cụm từ như &ldquo;lạm ph&aacute;t&rdquo; hay &ldquo;chỉ số gi&aacute; ti&ecirc;u d&ugrave;ng&rdquo; (CPI) l&agrave; một kh&aacute;i niệm xa lạ nhưng chắc chắn họ hiểu rằng cứ sau một đ&ecirc;m ngủ dậy họ lại ngh&egrave;o hơn v&igrave; gi&aacute; cả tăng vọt. Nhiều c&ocirc;ng nh&acirc;n tự nguy&ecirc;n xin tăng ca để được ăn tối kh&ocirc;ng mất tiền, nhiều người kh&aacute;c th&igrave; phải l&agrave;m th&ecirc;m việc để c&oacute; th&ecirc;m thu nhập trang trải cuộc sống.</strong></span></p>\r\n		<p class=\"SGTOSuperTitle\">\r\n			Xoay xở với đồng lương c&ograve;m</p>\r\n		<p>\r\n			Theo &ocirc;ng Hồ Xu&acirc;n L&acirc;m, Trưởng ph&ograve;ng quản l&yacute; lao động Ban quản l&yacute; c&aacute;c khu chế xuất v&agrave; khu c&ocirc;ng nghiệp TPHCM (Hepza), hiện c&oacute; khoảng 260.000 c&ocirc;ng nh&acirc;n tại 13 khu chế xuất, khu c&ocirc;ng nghiệp hưởng mức lương trung b&igrave;nh 2,5 -2,8 triệu đồng/th&aacute;ng. C&ograve;n t&iacute;nh tr&ecirc;n to&agrave;n th&agrave;nh phố th&igrave; số lượng c&ocirc;ng nh&acirc;n, người lao động tự do nhiều gấp v&agrave;i lần con số kể tr&ecirc;n.</p>\r\n		<p>\r\n			Tuy nhi&ecirc;n, cho d&ugrave; l&agrave;m việc ở đ&acirc;u th&igrave; họ đều c&oacute; một t&igrave;nh cảnh chung l&agrave; cuộc sống hiện qu&aacute; kh&oacute; khăn khi gi&aacute; cả mỗi ng&agrave;y một tăng cao như hiện nay.</p>\r\n		<p>\r\n			Khi được hỏi về c&aacute;c kh&aacute;i niệm &ldquo;lạm ph&aacute;t&rdquo; hay &ldquo;CPI&rdquo;, những người c&ocirc;ng nh&acirc;n ở khu chế xuất Linh Trung (Thủ Đức) kh&ocirc;ng biết giải th&iacute;ch thế n&agrave;o. Chị L&ecirc; Thị Mai qu&ecirc; ở Ph&uacute; Thọ, thật th&agrave; trả lời: &ldquo;T&ocirc;i kh&ocirc;ng biết lạm ph&aacute;t l&agrave; g&igrave; đ&acirc;u, chỉ biết rằng giờ đ&acirc;y c&aacute;i g&igrave; cũng tăng gi&aacute; v&ugrave;n vụt nhưng đồng lương lại kh&ocirc;ng tăng. Sau một đ&ecirc;m lại thấy m&igrave;nh ngh&egrave;o đi&rdquo;.</p>\r\n		<p>\r\n			Chị Nguyễn Thị Hồng, qu&ecirc; ở H&agrave; Tĩnh n&oacute;i th&ecirc;m: &ldquo;Giờ cầm tiền đi chợ m&agrave; x&oacute;t l&ograve;ng. H&ocirc;m trước gi&aacute; n&agrave;y, h&ocirc;m sau lại c&oacute; gi&aacute; mới. Bữa n&agrave;o đi l&agrave;m th&igrave; c&ograve;n đỡ chứ h&ocirc;m n&agrave;o nghỉ ở nh&agrave; th&igrave; tiền chợ một ng&agrave;y tối thiểu &iacute;t nhất cũng 70.000- 80.000 đồng, nhiều hơn 20.000 đồng so với trước đ&acirc;y nhưng thức ăn chẳng nhiều hơn, c&oacute; khi lại &iacute;t đi nữa&rdquo;.</p>\r\n		<p>\r\n			Gi&aacute;m đốc một c&ocirc;ng ty may tr&ecirc;n địa b&agrave;n TPHCM cho biết doanh nghiệp &ocirc;ng phải trợ cấp th&ecirc;m cho c&ocirc;ng nh&acirc;n từ 10- 15%/th&aacute;ng. &Ocirc;ng cũng xoay xở mọi c&aacute;ch để trả lương đ&uacute;ng hạn cho c&ocirc;ng nh&acirc;n d&ugrave; thu hẹp sản xuất, hạn chế tuyển người. &ldquo;Ng&agrave;nh may giờ gặp lắm kh&oacute; khăn, gi&aacute; cả nguy&ecirc;n liệu tăng cao, nhiều loại vật liệu tăng gấp 2-3 lần so với trước đ&acirc;y, chi ph&iacute; vận chuyển cũng tăng, tiền cơm trưa cho c&ocirc;ng nh&acirc;n cũng phải điều chỉnh tăng th&ecirc;m 2.000 đồng/suất&hellip; n&ecirc;n c&oacute; nhiều th&aacute;ng ch&uacute;ng t&ocirc;i phải chịu lỗ&rdquo;, &ocirc;ng n&oacute;i.</p>\r\n		<p>\r\n			Anh Nguyễn Thanh Hiền, chủ cơ sở sản xuất nh&iacute;p, dao ở G&ograve; Vấp chuy&ecirc;n cung cấp cho c&aacute;c đại l&yacute; b&aacute;n dụng cụ cắt t&oacute;c, cho biết: &quot;Trung b&igrave;nh thu nhập của c&ocirc;ng nh&acirc;n ở đ&acirc;y 2,5- 3 triệu đồng/th&aacute;ng. Mấy th&aacute;ng qua, do kh&oacute; khăn từ thị trường n&ecirc;n buộc phải thu hẹp sản xuất. Th&aacute;ng vừa rồi, buộc phải cho th&ecirc;m 3 c&ocirc;ng nh&acirc;n nghỉ việc nhưng phụ cấp cho anh em chỉ được nửa th&aacute;ng lương. Biết l&agrave; anh em buồn nhưng t&ocirc;i cũng cố gắng lắm rồi nhưng kh&ocirc;ng biết sẽ chịu đựng được bao l&acirc;u nữa&quot;.</p>\r\n		<p class=\"SGTOSuperTitle\">\r\n			Tự nguyện xin tăng ca, l&agrave;m th&ecirc;m việc</p>\r\n		<p>\r\n			Chị Minh Ngọc, một c&ocirc;ng nh&acirc;n qu&ecirc; ở B&igrave;nh Thuận đang l&agrave;m việc tại quận Thủ Đức (TPHCM), cho biết h&ocirc;m n&agrave;o tăng ca th&igrave; kh&ocirc;ng phải tốn tiền đi chợ chiều; c&ograve;n h&ocirc;m n&agrave;o kh&ocirc;ng tăng ca, bữa trưa ở c&ocirc;ng ty l&agrave; bữa cơm ch&iacute;nh của người c&ocirc;ng nh&acirc;n. &quot;Giờ ch&uacute;ng t&ocirc;i phải &lsquo;thắt lưng buộc bụng&rsquo;. Mấy h&ocirc;m kh&ocirc;ng tăng ca chiều, t&ocirc;i chỉ d&aacute;m mua 1 b&oacute; rau 3.000 đồng, cộng th&ecirc;m 2 c&aacute;i trứng vịt, tiền chợ tối cũng chỉ cỡ 15.000 đồng, chủ yếu l&agrave; l&oacute;t dạ cho qua ng&agrave;y&rdquo;, chị n&oacute;i.</p>\r\n		<p>\r\n			Anh L&ecirc; Văn Thưởng, 34 tuổi, c&ocirc;ng nh&acirc;n l&agrave;m dao, nh&iacute;p tại xưởng của anh Hiền chia sẻ, đồng lương c&ocirc;ng nh&acirc;n kh&ocirc;ng n&oacute;i ra ai cũng biết, than v&atilde;n nhiều cũng kh&ocirc;ng giải quyết được g&igrave;. Chuyện tr&ograve; được một l&uacute;c anh phải vội đi v&igrave; phải chở một người kh&aacute;ch v&agrave;o bệnh viện H&ugrave;ng Vương thăm người bệnh. Hơn 2 th&aacute;ng nay, anh Thưởng ngo&agrave;i l&agrave;m việc ở xưởng của anh Hiền, c&ograve;n tranh thủ chạy xe &ocirc;m để kiếm th&ecirc;m ch&uacute;t tiền phụ gi&uacute;p vợ.</p>\r\n		<p>\r\n			Dạo quanh phường 15, quận T&acirc;n B&igrave;nh - nơi tập trung nhiều cơ sở may mặc tư nh&acirc;n, đặc biệt tr&ecirc;n đoạn đường Cống Lở, đ&acirc;u đ&acirc;u cũng nh&igrave;n thấy m&aacute;y may, vải v&oacute;c chất đầy nh&agrave;, lấn ra cả đường đi v&agrave; tiếng m&aacute;y may chạy từ s&aacute;ng đến tối khuya.</p>\r\n		<table align=\"right\" cellpadding=\"7\" cellspacing=\"0\" class=\"sgtobox1right\" width=\"230\">\r\n			<tbody>\r\n				<tr>\r\n					<td>\r\n						<p>\r\n							Trao đổi với <em>Thời b&aacute;o Kinh tế S&agrave;i G&ograve;n Online</em>, &ocirc;ng Hồ Xu&acirc;n L&acirc;m, Trưởng ph&ograve;ng quản l&yacute; lao động Hepza cho biết, ng&agrave;y 2-6 tới, Hepza sẽ phối hợp với 70 doanh nghiệp để thực hiện chương tr&igrave;nh <a href=\"http://www.thesaigontimes.vn/Uploads/Articles/53977/a52b7_Ke-hoach-to-chuc-phien-cho-CN-tai-KCN-Tan-Tao.doc\" target=\"_blank\">b&aacute;n h&agrave;ng b&igrave;nh ổn lưu động tại c&aacute;c khu c&ocirc;ng nghiệp tr&ecirc;n địa b&agrave;n th&agrave;nh phố</a>.</p>\r\n						<p>\r\n							Theo Hepza, chương tr&igrave;nh n&agrave;y kh&ocirc;ng thể đ&aacute;p ứng được nhu cầu về thực phẩm của c&ocirc;ng nh&acirc;n nhưng phần n&agrave;o hỗ trợ c&ocirc;ng nh&acirc;n trong t&igrave;nh cảnh gi&aacute; cả của c&aacute;c mặt h&agrave;ng nhu yếu phẩm đang tăng l&ecirc;n từng ng&agrave;y như hiện nay.</p>\r\n					</td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n		<p>\r\n			Th&ocirc;ng qua một người bạn l&agrave;m c&ocirc;ng t&aacute;c x&atilde; hội, ch&uacute;ng t&ocirc;i mới gặp được Vi Văn Long, 19 tuổi, qu&ecirc; ở Lục Ngạn, Bắc Giang, l&agrave;m c&ocirc;ng nh&acirc;n may tr&ecirc;n đường Cống Lở.</p>\r\n		<p>\r\n			Mặc d&ugrave; đang tuổi ăn tuổi lớn nhưng Long kh&aacute; thấp, người gầy cộng th&ecirc;m m&aacute;i t&oacute;c d&agrave;i khiến ai mới gặp lần đầu sẽ nghĩ ngay Long c&oacute; thể l&agrave; tay &quot;anh chị&quot;. Nhưng ph&iacute;a sau bộ dạng ấy l&agrave; một con người hiền l&agrave;nh, chỉ biết cặm cụi l&agrave;m việc từ s&aacute;ng đến tối.</p>\r\n		<p>\r\n			Kh&aacute; mệt mỏi khi gặp ch&uacute;ng t&ocirc;i nhưng sau một hồi hỏi thăm th&igrave; Long cũng mạnh dạn hơn, n&oacute;i nhiều hơn v&agrave; thỉnh thoảng lại cười, phảng phất những n&eacute;t ng&acirc;y thơ tr&ecirc;n khu&ocirc;n mặt gi&agrave; trước tuổi.</p>\r\n		<p>\r\n			Với giọng đều đều, c&oacute; khi rất nhỏ, Long cho biết l&agrave; anh cả trong nh&agrave; c&oacute; 5 anh chị em, gia đ&igrave;nh sống dựa v&agrave;o nghề l&agrave;m rẫy, thu nhập bấp b&ecirc;nh n&ecirc;n Long theo cậu v&agrave;o th&agrave;nh phố để kiếm sống. Do được bao ở n&ecirc;n mỗi th&aacute;ng Long được nhận khoảng 1,2 triệu đồng gồm cả tăng ca. Long chỉ d&aacute;m ti&ecirc;u 200.000 đồng, c&ograve;n lại gửi về phụ gi&uacute;p ba mẹ ở qu&ecirc;.</p>\r\n		<p>\r\n			Long cho biết xin tăng cả để kh&ocirc;ng phải ra ngo&agrave;i chơi với lại được ăn bữa tối. &quot;Bạn em ai cũng xin tăng ca nhưng em nghe n&oacute;i l&agrave; hết th&aacute;ng 5 n&agrave;y th&igrave; sẽ kh&ocirc;ng phải tăng ca nữa v&igrave; chưa c&oacute; hợp đồng mới&quot;. V&igrave; thế, Long đang c&oacute; &yacute; định xin chuyển sang một c&ocirc;ng ty kh&aacute;c để c&oacute; lương cao hơn, được hưởng chế độ bảo hiểm nhưng do giấy tờ chưa hợp lệ n&ecirc;n kh&ocirc;ng l&agrave;m hồ sơ xin việc được.</p>\r\n		<p>\r\n			Anh Trần Hải H&agrave; (Ch&iacute;n Trẹo), 47 tuổi, l&agrave;m nghề đ&aacute;nh bắt c&aacute; tr&ecirc;n s&ocirc;ng V&agrave;m Thuật (G&ograve; Vấp), cuộc sống vốn đ&atilde; vất vả giờ c&ograve;n mệt mỏi hơn. Ng&agrave;y l&agrave;m việc của anh bắt đầu từ 7 giờ tối đến 4 giờ s&aacute;ng h&ocirc;m sau. Tuy vất vả cả đ&ecirc;m nhưng thu nhập của anh cũng chỉ ở mức 70.000- 80.000 đồng/ng&agrave;y, cả nh&agrave; 3 nh&acirc;n khẩu c&ugrave;ng chia sẻ số tiền m&agrave; anh Trẹo kiếm được. Anh cho biết, dạo n&agrave;y nhiều s&ocirc;ng rạch &ocirc; nhiễm qu&aacute;, t&ocirc;m c&aacute; ng&agrave;y c&agrave;ng &iacute;t đi, một m&igrave;nh anh l&agrave;m kh&ocirc;ng đủ nu&ocirc;i sống mấy miệng ăn.</p>\r\n		<p>\r\n			Để phụ th&ecirc;m gia đ&igrave;nh, hằng ng&agrave;y, sau giờ đi học, đứa con trai 7 tuổi của anh phải c&ugrave;ng mẹ đi lượm ve chai. Anh Trẹo t&acirc;m sự: &ldquo;Hai vợ chồng chỉ biết c&aacute;ch giảm khẩu phần ăn xuống để lo cho con ăn học. M&igrave;nh kh&ocirc;ng c&oacute; điều kiện đến trường nhưng phải cố lo cho con ăn học đ&agrave;ng ho&agrave;ng&rdquo;. Ước mơ của anh Trẹo đơn giản chỉ c&oacute; vậy.</p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<table align=\"center\" cellpadding=\"7\" cellspacing=\"0\" class=\"sgtobox1center\" width=\"100%\">\r\n			<tbody>\r\n				<tr>\r\n					<td>\r\n						<p>\r\n							B&aacute;o c&aacute;o đ&aacute;nh gi&aacute; đời sống của người ngh&egrave;o v&agrave; người l&agrave;m c&ocirc;ng ăn lương của Bộ Kế hoạch v&agrave; Đầu tư cho biết, khoảng 1,6 triệu c&ocirc;ng nh&acirc;n lao động trong 260 khu c&ocirc;ng nghiệp v&agrave; 15 khu kinh tế đang phải đối mặt với nhiều kh&oacute; khăn, thiếu thốn, tiền lương kh&ocirc;ng đủ trang trải cho nhu cầu cuộc sống.</p>\r\n						<p>\r\n							B&aacute;o c&aacute;o n&agrave;y cũng cho thấy thu nhập của c&ocirc;ng nh&acirc;n tại c&aacute;c khu c&ocirc;ng nghiệp chỉ đạt mức trung b&igrave;nh l&agrave; 2 triệu đồng/th&aacute;ng, chỉ bằng 68% so với mức thu nhập trung b&igrave;nh của c&ocirc;ng nh&acirc;n cả nước n&oacute;i chung. Số tiền n&agrave;y bao gồm c&aacute;c khoản phụ cấp như tiền đi lại, tiền tăng ca, tiền thu&ecirc; nh&agrave;&hellip;</p>\r\n						<p>\r\n							Theo Bộ Kế hoạch v&agrave; Đầu tư, mức thu nhập của người ngh&egrave;o tăng b&igrave;nh qu&acirc;n 10-20% trong khi gi&aacute; cả h&agrave;ng h&oacute;a dịch vụ lại tăng 30-50%. Người thu nhập thấp ở th&agrave;nh thị phải d&ugrave;ng hầu hết to&agrave;n bộ thu nhập của m&igrave;nh cho việc mua lương thực, thực phẩm, điện, nước, gas&hellip; đến mức hầu như kh&ocirc;ng c&ograve;n c&oacute; thể d&agrave;nh dụm được. Nhiều người đ&atilde; chấp nhận sống thiếu thốn.</p>\r\n					</td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n		<p>\r\n			Nguồn www.thesaigontimes.vn</p>\r\n	</body>\r\n</html>\r\n','2011-05-26 20:29:21','admin',109,0,'Nguoi ngheo chat vat thoi bao gia <html>\r\n	<head>\r\n		<title></title>\r\n	</head>\r\n	<body>\r\n		<table border=\"0\" height=\"201\" style=\"float: left;\" width=\"211\">\r\n			<tbody>\r\n				<tr>\r\n					<td>\r\n						<table align=\"left\" cellpadding=\"0\" cellspacing=\"0\" class=\"sgtoimageleft\">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										<img alt=\"\" class=\"sgtoimageleft_popup\" src=\"http://www.thesaigontimes.vn/Uploads/Articles/53977/714ad_-mg-7771.online_200.jpg\" style=\"width: 200px; height: 151px;\" /></td>\r\n								</tr>\r\n								<tr>\r\n									<td class=\"sgtoimageleft\">\r\n										<span style=\"font-size: x-small;\">Do gi&aacute; ca c&aacute;c mat h&agrave;ng nhu yen pham deu tang trong khi luong kh&ocirc;ng tang bao nhi&ecirc;u n&ecirc;n nhieu c&ocirc;ng nh&acirc;n xin tang ca hoac l&agrave;m th&ecirc;m viec kh&aacute;c. Trong anh: c&ocirc;ng nh&acirc;n tai mot co so san xuat dao, nh&iacute;p o G&ograve; Vap. Anh: Ngoc H&ugrave;ng</span></td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n						<p class=\"SGTOSummary\">\r\n							&nbsp;</p>\r\n					</td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n		<p class=\"SGTOSummary\">\r\n			<span style=\"color: rgb(136, 136, 136);\"><strong>(TBKTSG Online) - Doi voi nhieu c&ocirc;ng nh&acirc;n, nhung cum tu nhu &ldquo;lam ph&aacute;t&rdquo; hay &ldquo;chi so gi&aacute; ti&ecirc;u d&ugrave;ng&rdquo; (CPI) l&agrave; mot kh&aacute;i niem xa la nhung chac chan ho hieu rang cu sau mot d&ecirc;m ngu day ho lai ngh&egrave;o hon v&igrave; gi&aacute; ca tang vot. Nhieu c&ocirc;ng nh&acirc;n tu nguy&ecirc;n xin tang ca de duoc an toi kh&ocirc;ng mat tien, nhieu nguoi kh&aacute;c th&igrave; phai l&agrave;m th&ecirc;m viec de c&oacute; th&ecirc;m thu nhap trang trai cuoc song.</strong></span></p>\r\n		<p class=\"SGTOSuperTitle\">\r\n			Xoay xo voi dong luong c&ograve;m</p>\r\n		<p>\r\n			Theo &ocirc;ng Ho Xu&acirc;n L&acirc;m, Truong ph&ograve;ng quan l&yacute; lao dong Ban quan l&yacute; c&aacute;c khu che xuat v&agrave; khu c&ocirc;ng nghiep TPHCM (Hepza), hien c&oacute; khoang 260.000 c&ocirc;ng nh&acirc;n tai 13 khu che xuat, khu c&ocirc;ng nghiep huong muc luong trung b&igrave;nh 2,5 -2,8 trieu dong/th&aacute;ng. C&ograve;n t&iacute;nh tr&ecirc;n to&agrave;n th&agrave;nh pho th&igrave; so luong c&ocirc;ng nh&acirc;n, nguoi lao dong tu do nhieu gap v&agrave;i lan con so ke tr&ecirc;n.</p>\r\n		<p>\r\n			Tuy nhi&ecirc;n, cho d&ugrave; l&agrave;m viec o d&acirc;u th&igrave; ho deu c&oacute; mot t&igrave;nh canh chung l&agrave; cuoc song hien qu&aacute; kh&oacute; khan khi gi&aacute; ca moi ng&agrave;y mot tang cao nhu hien nay.</p>\r\n		<p>\r\n			Khi duoc hoi ve c&aacute;c kh&aacute;i niem &ldquo;lam ph&aacute;t&rdquo; hay &ldquo;CPI&rdquo;, nhung nguoi c&ocirc;ng nh&acirc;n o khu che xuat Linh Trung (Thu Duc) kh&ocirc;ng biet giai th&iacute;ch the n&agrave;o. Chi L&ecirc; Thi Mai qu&ecirc; o Ph&uacute; Tho, that th&agrave; tra loi: &ldquo;T&ocirc;i kh&ocirc;ng biet lam ph&aacute;t l&agrave; g&igrave; d&acirc;u, chi biet rang gio d&acirc;y c&aacute;i g&igrave; cung tang gi&aacute; v&ugrave;n vut nhung dong luong lai kh&ocirc;ng tang. Sau mot d&ecirc;m lai thay m&igrave;nh ngh&egrave;o di&rdquo;.</p>\r\n		<p>\r\n			Chi Nguyen Thi Hong, qu&ecirc; o H&agrave; Tinh n&oacute;i th&ecirc;m: &ldquo;Gio cam tien di cho m&agrave; x&oacute;t l&ograve;ng. H&ocirc;m truoc gi&aacute; n&agrave;y, h&ocirc;m sau lai c&oacute; gi&aacute; moi. Bua n&agrave;o di l&agrave;m th&igrave; c&ograve;n do chu h&ocirc;m n&agrave;o nghi o nh&agrave; th&igrave; tien cho mot ng&agrave;y toi thieu &iacute;t nhat cung 70.000- 80.000 dong, nhieu hon 20.000 dong so voi truoc d&acirc;y nhung thuc an chang nhieu hon, c&oacute; khi lai &iacute;t di nua&rdquo;.</p>\r\n		<p>\r\n			Gi&aacute;m doc mot c&ocirc;ng ty may tr&ecirc;n dia b&agrave;n TPHCM cho biet doanh nghiep &ocirc;ng phai tro cap th&ecirc;m cho c&ocirc;ng nh&acirc;n tu 10- 15%/th&aacute;ng. &Ocirc;ng cung xoay xo moi c&aacute;ch de tra luong d&uacute;ng han cho c&ocirc;ng nh&acirc;n d&ugrave; thu hep san xuat, han che tuyen nguoi. &ldquo;Ng&agrave;nh may gio gap lam kh&oacute; khan, gi&aacute; ca nguy&ecirc;n lieu tang cao, nhieu loai vat lieu tang gap 2-3 lan so voi truoc d&acirc;y, chi ph&iacute; van chuyen cung tang, tien com trua cho c&ocirc;ng nh&acirc;n cung phai dieu chinh tang th&ecirc;m 2.000 dong/suat&hellip; n&ecirc;n c&oacute; nhieu th&aacute;ng ch&uacute;ng t&ocirc;i phai chiu lo&rdquo;, &ocirc;ng n&oacute;i.</p>\r\n		<p>\r\n			Anh Nguyen Thanh Hien, chu co so san xuat nh&iacute;p, dao o G&ograve; Vap chuy&ecirc;n cung cap cho c&aacute;c dai l&yacute; b&aacute;n dung cu cat t&oacute;c, cho biet: &quot;Trung b&igrave;nh thu nhap cua c&ocirc;ng nh&acirc;n o d&acirc;y 2,5- 3 trieu dong/th&aacute;ng. May th&aacute;ng qua, do kh&oacute; khan tu thi truong n&ecirc;n buoc phai thu hep san xuat. Th&aacute;ng vua roi, buoc phai cho th&ecirc;m 3 c&ocirc;ng nh&acirc;n nghi viec nhung phu cap cho anh em chi duoc nua th&aacute;ng luong. Biet l&agrave; anh em buon nhung t&ocirc;i cung co gang lam roi nhung kh&ocirc;ng biet se chiu dung duoc bao l&acirc;u nua&quot;.</p>\r\n		<p class=\"SGTOSuperTitle\">\r\n			Tu nguyen xin tang ca, l&agrave;m th&ecirc;m viec</p>\r\n		<p>\r\n			Chi Minh Ngoc, mot c&ocirc;ng nh&acirc;n qu&ecirc; o B&igrave;nh Thuan dang l&agrave;m viec tai quan Thu Duc (TPHCM), cho biet h&ocirc;m n&agrave;o tang ca th&igrave; kh&ocirc;ng phai ton tien di cho chieu; c&ograve;n h&ocirc;m n&agrave;o kh&ocirc;ng tang ca, bua trua o c&ocirc;ng ty l&agrave; bua com ch&iacute;nh cua nguoi c&ocirc;ng nh&acirc;n. &quot;Gio ch&uacute;ng t&ocirc;i phai &lsquo;that lung buoc bung&rsquo;. May h&ocirc;m kh&ocirc;ng tang ca chieu, t&ocirc;i chi d&aacute;m mua 1 b&oacute; rau 3.000 dong, cong th&ecirc;m 2 c&aacute;i trung vit, tien cho toi cung chi co 15.000 dong, chu yeu l&agrave; l&oacute;t da cho qua ng&agrave;y&rdquo;, chi n&oacute;i.</p>\r\n		<p>\r\n			Anh L&ecirc; Van Thuong, 34 tuoi, c&ocirc;ng nh&acirc;n l&agrave;m dao, nh&iacute;p tai xuong cua anh Hien chia se, dong luong c&ocirc;ng nh&acirc;n kh&ocirc;ng n&oacute;i ra ai cung biet, than v&atilde;n nhieu cung kh&ocirc;ng giai quyet duoc g&igrave;. Chuyen tr&ograve; duoc mot l&uacute;c anh phai voi di v&igrave; phai cho mot nguoi kh&aacute;ch v&agrave;o benh vien H&ugrave;ng Vuong tham nguoi benh. Hon 2 th&aacute;ng nay, anh Thuong ngo&agrave;i l&agrave;m viec o xuong cua anh Hien, c&ograve;n tranh thu chay xe &ocirc;m de kiem th&ecirc;m ch&uacute;t tien phu gi&uacute;p vo.</p>\r\n		<p>\r\n			Dao quanh phuong 15, quan T&acirc;n B&igrave;nh - noi tap trung nhieu co so may mac tu nh&acirc;n, dac biet tr&ecirc;n doan duong Cong Lo, d&acirc;u d&acirc;u cung nh&igrave;n thay m&aacute;y may, vai v&oacute;c chat day nh&agrave;, lan ra ca duong di v&agrave; tieng m&aacute;y may chay tu s&aacute;ng den toi khuya.</p>\r\n		<table align=\"right\" cellpadding=\"7\" cellspacing=\"0\" class=\"sgtobox1right\" width=\"230\">\r\n			<tbody>\r\n				<tr>\r\n					<td>\r\n						<p>\r\n							Trao doi voi <em>Thoi b&aacute;o Kinh te S&agrave;i G&ograve;n Online</em>, &ocirc;ng Ho Xu&acirc;n L&acirc;m, Truong ph&ograve;ng quan l&yacute; lao dong Hepza cho biet, ng&agrave;y 2-6 toi, Hepza se phoi hop voi 70 doanh nghiep de thuc hien chuong tr&igrave;nh <a href=\"http://www.thesaigontimes.vn/Uploads/Articles/53977/a52b7_Ke-hoach-to-chuc-phien-cho-CN-tai-KCN-Tan-Tao.doc\" target=\"_blank\">b&aacute;n h&agrave;ng b&igrave;nh on luu dong tai c&aacute;c khu c&ocirc;ng nghiep tr&ecirc;n dia b&agrave;n th&agrave;nh pho</a>.</p>\r\n						<p>\r\n							Theo Hepza, chuong tr&igrave;nh n&agrave;y kh&ocirc;ng the d&aacute;p ung duoc nhu cau ve thuc pham cua c&ocirc;ng nh&acirc;n nhung phan n&agrave;o ho tro c&ocirc;ng nh&acirc;n trong t&igrave;nh canh gi&aacute; ca cua c&aacute;c mat h&agrave;ng nhu yeu pham dang tang l&ecirc;n tung ng&agrave;y nhu hien nay.</p>\r\n					</td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n		<p>\r\n			Th&ocirc;ng qua mot nguoi ban l&agrave;m c&ocirc;ng t&aacute;c x&atilde; hoi, ch&uacute;ng t&ocirc;i moi gap duoc Vi Van Long, 19 tuoi, qu&ecirc; o Luc Ngan, Bac Giang, l&agrave;m c&ocirc;ng nh&acirc;n may tr&ecirc;n duong Cong Lo.</p>\r\n		<p>\r\n			Mac d&ugrave; dang tuoi an tuoi lon nhung Long kh&aacute; thap, nguoi gay cong th&ecirc;m m&aacute;i t&oacute;c d&agrave;i khien ai moi gap lan dau se nghi ngay Long c&oacute; the l&agrave; tay &quot;anh chi&quot;. Nhung ph&iacute;a sau bo dang ay l&agrave; mot con nguoi hien l&agrave;nh, chi biet cam cui l&agrave;m viec tu s&aacute;ng den toi.</p>\r\n		<p>\r\n			Kh&aacute; met moi khi gap ch&uacute;ng t&ocirc;i nhung sau mot hoi hoi tham th&igrave; Long cung manh dan hon, n&oacute;i nhieu hon v&agrave; thinh thoang lai cuoi, phang phat nhung n&eacute;t ng&acirc;y tho tr&ecirc;n khu&ocirc;n mat gi&agrave; truoc tuoi.</p>\r\n		<p>\r\n			Voi giong deu deu, c&oacute; khi rat nho, Long cho biet l&agrave; anh ca trong nh&agrave; c&oacute; 5 anh chi em, gia d&igrave;nh song dua v&agrave;o nghe l&agrave;m ray, thu nhap bap b&ecirc;nh n&ecirc;n Long theo cau v&agrave;o th&agrave;nh pho de kiem song. Do duoc bao o n&ecirc;n moi th&aacute;ng Long duoc nhan khoang 1,2 trieu dong gom ca tang ca. Long chi d&aacute;m ti&ecirc;u 200.000 dong, c&ograve;n lai gui ve phu gi&uacute;p ba me o qu&ecirc;.</p>\r\n		<p>\r\n			Long cho biet xin tang ca de kh&ocirc;ng phai ra ngo&agrave;i choi voi lai duoc an bua toi. &quot;Ban em ai cung xin tang ca nhung em nghe n&oacute;i l&agrave; het th&aacute;ng 5 n&agrave;y th&igrave; se kh&ocirc;ng phai tang ca nua v&igrave; chua c&oacute; hop dong moi&quot;. V&igrave; the, Long dang c&oacute; &yacute; dinh xin chuyen sang mot c&ocirc;ng ty kh&aacute;c de c&oacute; luong cao hon, duoc huong che do bao hiem nhung do giay to chua hop le n&ecirc;n kh&ocirc;ng l&agrave;m ho so xin viec duoc.</p>\r\n		<p>\r\n			Anh Tran Hai H&agrave; (Ch&iacute;n Treo), 47 tuoi, l&agrave;m nghe d&aacute;nh bat c&aacute; tr&ecirc;n s&ocirc;ng V&agrave;m Thuat (G&ograve; Vap), cuoc song von d&atilde; vat va gio c&ograve;n met moi hon. Ng&agrave;y l&agrave;m viec cua anh bat dau tu 7 gio toi den 4 gio s&aacute;ng h&ocirc;m sau. Tuy vat va ca d&ecirc;m nhung thu nhap cua anh cung chi o muc 70.000- 80.000 dong/ng&agrave;y, ca nh&agrave; 3 nh&acirc;n khau c&ugrave;ng chia se so tien m&agrave; anh Treo kiem duoc. Anh cho biet, dao n&agrave;y nhieu s&ocirc;ng rach &ocirc; nhiem qu&aacute;, t&ocirc;m c&aacute; ng&agrave;y c&agrave;ng &iacute;t di, mot m&igrave;nh anh l&agrave;m kh&ocirc;ng du nu&ocirc;i song may mieng an.</p>\r\n		<p>\r\n			De phu th&ecirc;m gia d&igrave;nh, hang ng&agrave;y, sau gio di hoc, dua con trai 7 tuoi cua anh phai c&ugrave;ng me di luom ve chai. Anh Treo t&acirc;m su: &ldquo;Hai vo chong chi biet c&aacute;ch giam khau phan an xuong de lo cho con an hoc. M&igrave;nh kh&ocirc;ng c&oacute; dieu kien den truong nhung phai co lo cho con an hoc d&agrave;ng ho&agrave;ng&rdquo;. Uoc mo cua anh Treo don gian chi c&oacute; vay.</p>\r\n		<p>\r\n			&nbsp;</p>\r\n		<table align=\"center\" cellpadding=\"7\" cellspacing=\"0\" class=\"sgtobox1center\" width=\"100%\">\r\n			<tbody>\r\n				<tr>\r\n					<td>\r\n						<p>\r\n							B&aacute;o c&aacute;o d&aacute;nh gi&aacute; doi song cua nguoi ngh&egrave;o v&agrave; nguoi l&agrave;m c&ocirc;ng an luong cua Bo Ke hoach v&agrave; Dau tu cho biet, khoang 1,6 trieu c&ocirc;ng nh&acirc;n lao dong trong 260 khu c&ocirc;ng nghiep v&agrave; 15 khu kinh te dang phai doi mat voi nhieu kh&oacute; khan, thieu thon, tien luong kh&ocirc;ng du trang trai cho nhu cau cuoc song.</p>\r\n						<p>\r\n							B&aacute;o c&aacute;o n&agrave;y cung cho thay thu nhap cua c&ocirc;ng nh&acirc;n tai c&aacute;c khu c&ocirc;ng nghiep chi dat muc trung b&igrave;nh l&agrave; 2 trieu dong/th&aacute;ng, chi bang 68% so voi muc thu nhap trung b&igrave;nh cua c&ocirc;ng nh&acirc;n ca nuoc n&oacute;i chung. So tien n&agrave;y bao gom c&aacute;c khoan phu cap nhu tien di lai, tien tang ca, tien thu&ecirc; nh&agrave;&hellip;</p>\r\n						<p>\r\n							Theo Bo Ke hoach v&agrave; Dau tu, muc thu nhap cua nguoi ngh&egrave;o tang b&igrave;nh qu&acirc;n 10-20% trong khi gi&aacute; ca h&agrave;ng h&oacute;a dich vu lai tang 30-50%. Nguoi thu nhap thap o th&agrave;nh thi phai d&ugrave;ng hau het to&agrave;n bo thu nhap cua m&igrave;nh cho viec mua luong thuc, thuc pham, dien, nuoc, gas&hellip; den muc hau nhu kh&ocirc;ng c&ograve;n c&oacute; the d&agrave;nh dum duoc. Nhieu nguoi d&atilde; chap nhan song thieu thon.</p>\r\n					</td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n		<p>\r\n			Nguon www.thesaigontimes.vn</p>\r\n	</body>\r\n</html>\r\n tim viec lam them - tim viec lam ban thoi gian - Doi voi nhieu cong nhan, nhung cum tu nhu “lam phat” hay “chi so gia tieu dung” (CPI) la mot khai niem xa la nhung chac chan ho hieu rang cu...');

/*Table structure for table `violations` */

DROP TABLE IF EXISTS `violations`;

CREATE TABLE `violations` (
  `id` bigint(20) NOT NULL auto_increment,
  `url` varchar(255) default NULL,
  `account_id` bigint(20) default NULL,
  `status` int(11) default NULL,
  PRIMARY KEY  (`id`),
  KEY `FK_violations_accounts` (`account_id`),
  CONSTRAINT `FK_violations_accounts` FOREIGN KEY (`account_id`) REFERENCES `accounts` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `violations` */

/*Table structure for table `widgets` */

DROP TABLE IF EXISTS `widgets`;

CREATE TABLE `widgets` (
  `id` bigint(20) NOT NULL auto_increment,
