<div style="width:99%"><?phpforeach($lstComment as $comment) {	$link = $comment['comment']['url'];	if(substr($link,0,4)!='http' && $link!='#')		$link = 'http://'.$link;		?> 	<div style="-moz-border-radius: 4px 4px 4px 4px;background-color: white;border: 1px solid #6F6F6F; font-size: 10pt;    margin: 5px; min-height: 50px; padding: 4px; position: relative; text-align: left;width: 99%;">	<?php 	echo '<span style="font-weight:bold"><a class="link" style="padding:0" href="'.$link.'">'.$comment['comment']['ten'].'</a></span>';	echo ': <span class="sample">('.$html->getTimeFromSecond($comment[""]["timeofpost"]).' trước)</span><br/>';	echo $comment['comment']['noidung'];	if(isset($_SESSION['account']) && $_SESSION['account']['role']==1)		echo ' <span style="color:red;cursor:pointer" onclick="doDeleteComment(this,'.$comment['comment']['id'].')">Xóa Comment</span>';	?>	</div>	<?php}?>    <div class="yt-uix-pager">		<?php		if($pagesbefore>1)			echo '<button onclick="selectpage(1)" type="button" class=" yt-uix-button" ><span class="yt-uix-button-content">1</span></button> ...';		while($pagesbefore<$pagesindex) {			echo "<button onclick='selectpage($pagesbefore)' type='button' class=' yt-uix-button' ><span class='yt-uix-button-content'>$pagesbefore</span></button>";			$pagesbefore++;		}		echo "<button type='button' class='yt-uix-pager-selected yt-uix-button' ><span class='yt-uix-button-content'>$pagesindex</span></button>";		while($pagesnext>$pagesindex) {			$pagesindex++;			echo "<button onclick='selectpage($pagesindex)' type='button' class=' yt-uix-button' ><span class='yt-uix-button-content'>$pagesindex</span></button>";		}		if($pagesnext<$pageend)			echo '... <button onclick="selectpage('.$pageend.')" type="button" class=" yt-uix-button" ><span class="yt-uix-button-content">'.$pageend.'</span></button>';		?>    </div></div>